INSERT INTO `shuowen_voice_revel` VALUES (6861, '愆', 'qiān', '第十卷', '心部', '過也。从心，衍聲。或从寒省。籒文[言侃]。', '心;衍;寒;言;侃', '衍;寒;侃', 'null', NULL, '心;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
