INSERT INTO `shuowen_voice_revel` VALUES (6955, '㥏', 'tiǎn', '第十卷', '心部', '靑、徐謂慙曰㥏。从心，典聲。', '心;典', '典', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
