INSERT INTO `shuowen_voice_revel` VALUES (6957, '慙', 'cán', '第十卷', '心部', '媿也。从心，斬聲。', '心;斬', '斬', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6958, '恧', 'nǜ', '第十卷', '心部', '慙也。从心，而聲。', '心;而', '而', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6959, '怍', 'zuò', '第十卷', '心部', '慙也。从心，乍聲。', '心;乍', '乍', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6960, '憐', 'lián', '第十卷', '心部', '哀也。从心，粦聲。', '心;粦', '粦', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6961, '㦁', 'lián', '第十卷', '心部', '泣下也。从心，連聲。易曰「泣涕㦁如」。', '心;連', '連', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6962, '忍', 'rěn', '第十卷', '心部', '能也。从心，刃聲。', '心;刃', '刃', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6963, '㥝', 'mǐ', '第十卷', '心部', '厲也。一曰止也。从心，弭聲。讀若沔。', '心;弭;沔', '弭;沔', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6964, '㣻', 'yì', '第十卷', '心部', '懲也。从心，乂聲。', '心;乂', '乂', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6965, '懲', 'chénɡ', '第十卷', '心部', '㣻也。从心，徵聲。', '心;徵', '徵', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6966, '慵', 'yōnɡ', '第十卷', '心部', '嬾也。从心，庸聲。', '心;庸', '庸', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6967, '悱', 'fěi', '第十卷', '心部', '口悱悱也。从心，非聲。', '心;非', '非', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6968, '怩', 'ní', '第十卷', '心部', '衂怩，慙也。从心，尼聲。', '心;尼', '尼', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6969, '惉', 'zhān', '第十卷', '心部', '惉懘，煩聲也。从心，沾聲。', '心;沾', '沾', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6970, '懘', 'chì', '第十卷', '心部', '惉懘也。从心，滯聲。', '心;滯', '滯', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6971, '懇', 'kěn', '第十卷', '心部', '悃也。从心，豤聲。', '心;豤', '豤', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6972, '忖', 'cǔn', '第十卷', '心部', '度也。从心，寸聲。', '心;寸', '寸', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6973, '怊', 'chāo', '第十卷', '心部', '悲也。从心，召聲。', '心;召', '召', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6974, '慟', 'tònɡ', '第十卷', '心部', '大哭也。从心，動聲。', '心;動', '動', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6975, '惹', 'rě', '第十卷', '心部', '亂也。从心，若聲。', '心;若', '若', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6976, '恰', 'qià', '第十卷', '心部', '用心也。从心，合聲。', '心;合', '合', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6977, '悌', 'tì', '第十卷', '心部', '善兄弟也。从心，弟聲。', '心;弟', '弟', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6978, '懌', 'yì', '第十卷', '心部', '說也。从心，睪聲。', '心;睪', '睪', 'null', NULL, '心', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6979, '悾', 'kōnɡ', '第十卷', '心部', '怳也。从心，空聲。', '心;空', '空', 'null', NULL, '心', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6980, '惢', 'suǒ', '第十卷', '惢部', '心疑也。从三心。凡惢之屬皆从惢。讀若易「旅瑣瑣」。', '心;瑣', '瑣', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6981, '繠', 'ruǐ', '第十卷', '惢部', '𠂹也。从惢糸。', '惢;糸', '惢', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6982, '水', 'shuǐ', '第十一卷', '水部', '準也。北方之行。象眾水竝流，中有微陽之氣也。凡水之屬皆从水。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6983, '汃', 'bīn', '第十一卷', '水部', '西極之水也。从水，八聲。爾雅曰「西至於汃國，謂之四極」。', '水;八', '八', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6984, '河', 'hé', '第十一卷', '水部', '河水。出敦煌塞外昆侖山。發原注海。从水，可聲。', '水;可', '可', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6985, '泑', 'yōu', '第十一卷', '水部', '泑澤。在昆侖虛下。从水，幼聲。讀與𣢜同。', '水;幼;𣢜', '幼;𣢜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6986, '涷', 'dōnɡ', '第十一卷', '水部', '涷水。出發鳩山，入河。从水，東聲。', '水;東', '東', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6987, '涪', 'fú', '第十一卷', '水部', '涪水。出廣漢 剛邑道徼外，南入漢。从水，㕻聲。', '水;㕻', '㕻', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6988, '潼', 'tónɡ', '第十一卷', '水部', '潼水。出廣漢 梓潼北畍，南入褺江。从水，童聲。', '水;童', '童', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6989, '江', 'jiānɡ', '第十一卷', '水部', '江水。出蜀 湔氐徼外崏山，入海。从水，工聲。', '水;工', '工', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6990, '沱', 'tuó', '第十一卷', '水部', '江別流也。出崏山東，別爲沱。从水，它聲。', '水;它', '它', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6991, '浙', 'zhè', '第十一卷', '水部', '江水東至會稽山陰爲浙江。从水，㪿聲。', '水;㪿', '㪿', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6992, '𣴮', 'zāi', '第十一卷', '水部', '𣴮水。出蜀 汶江徼外，東南入江。从水，𢦒聲。', '水;𢦒', '𢦒', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6993, '湔', 'jiān', '第十一卷', '水部', '湔水。出蜀郡 緜虒 玉壘山，東南入江。从水，前聲。一曰湔，半澣也。', '水;前', '前', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6994, '沫', 'mò', '第十一卷', '水部', '沫水。出蜀西南徼外，東南入江。从水，末聲。', '水;末', '末', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6995, '溫;温', 'wēn', '第十一卷', '水部', '溫水。出楗爲 符，南入黔水。从水，𥁕聲。', '水;𥁕', '𥁕', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6996, '灊', 'qián', '第十一卷', '水部', '灊水。出巴郡 宕渠，西南入江。从水，鬵聲。', '水;鬵', '鬵', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6997, '沮', 'jū', '第十一卷', '水部', '沮水。出漢中 房陵，東入江。从水，且聲。', '水;且', '且', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6998, '滇', 'diān', '第十一卷', '水部', '益州池也。从水，眞聲。', '水;眞', '眞', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6999, '涂', 'tú', '第十一卷', '水部', '涂水。出益州 牧靡 南山，西北入繩。从水，余聲。', '水;余', '余', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7000, '沅', 'yuán', '第十一卷', '水部', '沅水。出牂柯 故且蘭，東北入江。从水，元聲。', '水;元', '元', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7001, '淹', 'yān', '第十一卷', '水部', '淹水。出越巂徼外，東入若水。从水，奄聲。', '水;奄', '奄', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7002, '溺', 'ruò', '第十一卷', '水部', '溺水。自張掖 刪丹西至酒泉 合黎，餘波入于流沙。从水，弱聲。桑欽所說。', '水;弱', '弱', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7003, '洮', 'táo', '第十一卷', '水部', '洮水。出隴西 臨洮，東北入河。从水，兆聲。', '水;兆', '兆', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7004, '涇', 'jīnɡ', '第十一卷', '水部', '涇水。出安定 涇陽 开頭山，東南入渭，雝州之川也。从水，巠聲。', '水;巠', '巠', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7005, '渭', 'wèi', '第十一卷', '水部', '渭水。出隴西 首陽 渭首亭 南谷，東南入河。从水，胃聲。杜林說夏書，㠯爲出烏鼠山，雝州浸。', '水;胃', '胃', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7006, '漾;瀁', 'yànɡ', '第十一卷', '水部', '漾水。出隴西 豲道，東至武都爲漢。从水，羕聲。古文从養。', '水;羕;養', '羕;養', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7007, '漢', 'hàn', '第十一卷', '水部', '漾也。東爲滄浪水。从水，難省聲。古文漢如此[或大水]。', '水;難;或;大;水', '難', 'null', NULL, '水;或;大;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7008, '浪', 'lànɡ', '第十一卷', '水部', '滄浪水也。南入江。从水，良聲。', '水;良', '良', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7009, '沔', 'miǎn', '第十一卷', '水部', '沔水。出武都 沮縣 東狼谷，東南入江。从水，丏聲。或曰入夏水。', '水;丏', '丏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7010, '湟', 'huánɡ', '第十一卷', '水部', '湟水。出金城 臨羌塞外，東入河。从水，皇聲。', '水;皇', '皇', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7011, '汧', 'qiān', '第十一卷', '水部', '汧水。出右扶風 汧縣西北，入渭。从水，开聲。', '水;开', '开', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7012, '澇', 'láo', '第十一卷', '水部', '澇水。出右扶風 鄠，北入渭。从水，勞聲。', '水;勞', '勞', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7013, '漆', 'qī', '第十一卷', '水部', '漆水。出右扶風 杜陵 岐山，東入渭。从水，桼聲。一曰漆，城池也。', '水;桼', '桼', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7014, '滻', 'chǎn', '第十一卷', '水部', '滻水。出京兆 藍田谷，入霸。从水，產聲。', '水;產', '產', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7015, '洛', 'luò', '第十一卷', '水部', '洛水。出左馮翊 歸德北夷畍中，東南入渭。从水，各聲。', '水;各', '各', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7016, '淯', 'yù', '第十一卷', '水部', '淯水。出弘農 盧氏山，東南入沔。从水，育聲。或曰出酈山西。', '水;育', '育', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7017, '汝', 'rǔ', '第十一卷', '水部', '汝水。出弘農 盧氏 還歸山，東入淮。从水，女聲。', '水;女', '女', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7018, '潩', 'yì', '第十一卷', '水部', '潩水。出河南 密縣 大隗山，南入潁。从水，異聲。', '水;異', '異', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7019, '汾', 'fén', '第十一卷', '水部', '汾水。出大原 晉陽山，西南入河。从水，分聲。或曰出汾陽北山，冀州浸。', '水;分', '分', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7020, '澮', 'ɡuì', '第十一卷', '水部', '澮水。出河東 彘 靃山，西南入汾。从水，會聲。', '水;會', '會', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7021, '沁', 'qìn', '第十一卷', '水部', '沁水。出上黨 穀遠 羊頭山，東南入河。从水，心聲。', '水;心', '心', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
