INSERT INTO `shuowen_voice_revel` VALUES (7085, '浯', 'wú', '第十一卷', '水部', '浯水。出琅邪 靈門 壺山，東北入濰。从水，吾聲。', '水;吾', '吾', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7086, '汶', 'wèn', '第十一卷', '水部', '汶水。出琅邪 朱虚 東泰山，東入濰。从水，文聲。桑欽說，汶水出泰山 萊蕪，西南入泲。', '水;文', '文', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7087, '治', 'zhì', '第十一卷', '水部', '治水。出東萊 曲城 陽丘山，南入海。从水，台聲。', '水;台', '台', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7088, '浸;寖', 'jìn', '第十一卷', '水部', '浸水。出魏郡 武安，東北入呼沱水。从水，𡨞聲。𡨞，籒文寑。', '水;𡨞', '𡨞', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7089, '湡', 'yú', '第十一卷', '水部', '湡水。出趙國 襄國之西山，東北入浸。从水，禺聲。', '水;禺', '禺', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7090, '㴲', 'sī', '第十一卷', '水部', '㴲水。出趙國 襄國，東入湡。从水，虒聲。', '水;虒', '虒', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7091, '渚', 'zhǔ', '第十一卷', '水部', '渚水。在常山 中丘 逢山，東入湡。从水，者聲。爾雅曰「小州曰渚」。', '水;者', '者', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7092, '洨', 'xiáo', '第十一卷', '水部', '洨水。出常山 石邑 井陘，東南入于泜。从水，交聲。𨙶國有洨縣。', '水;交', '交', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7093, '濟;𣺴', 'jǐ', '第十一卷', '水部', '𣺴水。出常山 房子 贊皇山，東入泜。从水，齊聲。', '水;齊', '齊', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7094, '泜', 'chí', '第十一卷', '水部', '泜水。在常山。从水，氐聲。', '水;氐', '氐', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7095, '濡', 'rú', '第十一卷', '水部', '濡水。出涿郡 故安，東入淶。从水，需聲。', '水;需', '需', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7096, '灅', 'lěi', '第十一卷', '水部', '灅水。出右北平 俊靡，東南入庚。从水，壘聲。', '水;壘', '壘', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7097, '沽', 'ɡū', '第十一卷', '水部', '沽水。出漁陽塞外，東入海。从水，古聲。', '水;古', '古', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7098, '沛', 'pèi', '第十一卷', '水部', '沛水。出遼東 番汗塞外，西南入海。从水，𣎵聲。', '水;𣎵', '𣎵', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7099, '浿', 'pèi', '第十一卷', '水部', '浿水。出樂浪 鏤方，東入海。从水，貝聲。一曰出浿水縣。', '水;貝', '貝', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7100, '瀤', 'huái', '第十一卷', '水部', '北方水也。从水，褱聲。', '水;褱', '褱', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7101, '㶟', 'lěi', '第十一卷', '水部', '㶟水。出鴈門 陰館 絫頭山，東入海。从水，纍聲。一曰治水也。', '水;纍', '纍', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7102, '𣻐', 'jū', '第十一卷', '水部', '𣻐水。出北地 直路西，東入洛。从水，虘聲。', '水;虘', '虘', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7103, '泒', 'ɡū', '第十一卷', '水部', '泒水。起鴈門 葰人 戍夫山，東北入海。从水，瓜聲。', '水;瓜', '瓜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
