INSERT INTO `shuowen_voice_revel` VALUES (7131, '溥', 'pǔ', '第十一卷', '水部', '大也。从水，尃聲。', '水;尃', '尃', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
