INSERT INTO `shuowen_voice_revel` VALUES (7149, '活;𣴠', 'ɡuō', '第十一卷', '水部', '水流也。从水，𠯑聲。𣴠或从𦕾。', '水;𠯑', '𠯑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '重文应删', 0x31, 'null', 'null', 'null', DEFAULT);
