INSERT INTO `shuowen_voice_revel` VALUES (7151, '泫', 'xuàn', '第十一卷', '水部', '湝流也。从水，玄聲。上黨有泫氏縣。', '水;玄', '玄', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7152, '淲', 'biāo', '第十一卷', '水部', '水流貌。从水，彪省聲。詩曰「淲池北流」。', '水;彪', '彪', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7153, '淢', 'yù', '第十一卷', '水部', '疾流也。从水，或聲。', '水;或', '或', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7154, '瀏', 'liú', '第十一卷', '水部', '流淸貌。从水，劉聲。詩曰「瀏其清矣」。', '水;劉', '劉', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7155, '濊', 'huò', '第十一卷', '水部', '礙流也。从水，歲聲。詩曰「施罟濊濊」。', '水;歲', '歲', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7156, '滂', 'pānɡ', '第十一卷', '水部', '沛也。从水，旁聲。', '水;旁', '旁', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7157, '汪', 'wānɡ', '第十一卷', '水部', '深廣也。从水，㞷聲。一曰汪，池也。', '水;㞷', '㞷', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7158, '漻', 'liáo', '第十一卷', '水部', '淸深也。从水，翏聲。', '水;翏', '翏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7159, '泚', 'cǐ', '第十一卷', '水部', '淸也。从水，此聲。', '水;此', '此', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7160, '況', 'kuànɡ', '第十一卷', '水部', '寒水也。从水，兄聲。', '水;兄', '兄', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7161, '沖', 'chōnɡ', '第十一卷', '水部', '湧繇也。从水，中聲。讀若動。', '水;中;動', '中;動', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7162, '汎', 'fàn', '第十一卷', '水部', '浮皃。从水，凡聲。', '水;凡', '凡', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7163, '沄', 'yún', '第十一卷', '水部', '轉流也。从水，云聲。讀若混。', '水;云;混', '云;混', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7164, '浩', 'hào', '第十一卷', '水部', '澆也。从水，告聲。虞書曰「洪水浩浩」。', '水;告', '告', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7165, '沆', 'hànɡ', '第十一卷', '水部', '莽沆，大水也。从水，亢聲。一曰大澤貌。', '水;亢', '亢', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7166, '泬', 'jué', '第十一卷', '水部', '水從孔穴疾出也。从水穴。穴亦聲。', '水;穴', '穴', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7167, '濞', 'pì', '第十一卷', '水部', '水暴至聲。从水，鼻聲。', '水;鼻', '鼻', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7168, '灂', 'zhuó', '第十一卷', '水部', '水之小聲也。从水，爵聲。', '水;爵', '爵', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7169, '潝', 'xī', '第十一卷', '水部', '水流疾聲。从水，翕聲。', '水;翕', '翕', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7170, '滕', 'ténɡ', '第十一卷', '水部', '水超踊也。从水，朕聲。', '水;朕', '朕', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7171, '潏', 'jué', '第十一卷', '水部', '湧出也。一曰水中坻，人所爲爲潏。一曰潏，水名，在京兆 杜陵。从水，矞聲。', '水;矞', '矞', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7172, '洸', 'ɡuānɡ', '第十一卷', '水部', '水涌光也。从水光。光亦聲。詩曰「有洸有潰」。', '水;光', '光', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7173, '波', 'bō', '第十一卷', '水部', '水涌流也。从水，皮聲。', '水;皮', '皮', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7174, '澐', 'yún', '第十一卷', '水部', '江水大波謂之澐。从水，雲聲。', '水;雲', '雲', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7175, '瀾;漣', 'lán', '第十一卷', '水部', '大波爲瀾。从水，闌聲。瀾或从連。', '水;闌;連', '闌;連', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7176, '淪', 'lún', '第十一卷', '水部', '小波爲淪。从水，侖聲。詩曰「河水淸且淪猗」。一曰沒也。', '水;侖', '侖', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7177, '漂', 'piāo', '第十一卷', '水部', '浮也。从水，票聲。', '水;票', '票', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7178, '浮', 'fú', '第十一卷', '水部', '汎也。从水，孚聲。', '水;孚', '孚', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7179, '濫', 'làn', '第十一卷', '水部', '氾也。从水，監聲。一曰濡上及下也。詩曰「𧥑沸濫泉」。一曰淸也。', '水;監', '監', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7180, '氾', 'fàn', '第十一卷', '水部', '濫也。从水，𢎘聲。一曰淹。', '水;𢎘', '𢎘', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7181, '泓', 'hónɡ', '第十一卷', '水部', '下深貌。从水，弘聲。', '水;弘', '弘', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7182, '湋', 'wéi', '第十一卷', '水部', '回也。从水，韋聲。', '水;韋', '韋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7183, '測', 'cè', '第十一卷', '水部', '深所至也。从水，則聲。', '水;則', '則', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7184, '湍', 'tuān', '第十一卷', '水部', '淺水流沙上曰湍。一曰疾瀨也。从水，耑聲。', '水;耑', '耑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7185, '淙', 'cónɡ', '第十一卷', '水部', '水聲也。从水，宗聲。', '水;宗', '宗', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7186, '激', 'jī', '第十一卷', '水部', '水礙衺疾波也。从水，敫聲。一曰半遮也。', '水;敫', '敫', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7187, '洞', 'dònɡ', '第十一卷', '水部', '疾流也。从水，同聲。', '水;同', '同', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7188, '𤄫', 'fān', '第十一卷', '水部', '大波也。从水，旛聲。', '水;旛', '旛', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7189, '洶', 'xiōnɡ', '第十一卷', '水部', '洶洶涌也。从水，匈聲。', '水;匈', '匈', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7190, '涌;湧', 'yǒnɡ', '第十一卷', '水部', '滕也。从水，甬聲。一曰涌水，在楚國。', '水;甬', '甬', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7191, '湁', 'chì', '第十一卷', '水部', '湁湒，𩰾也。从水，拾聲。', '水;拾', '拾', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7192, '涳', 'kōnɡ', '第十一卷', '水部', '直流也。从水，空聲。', '水;空', '空', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7193, '汋', 'zhuó', '第十一卷', '水部', '激水聲也。从水，勺聲。井一有水、一無水，謂之瀱汋。', '水;勺', '勺', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7194, '瀱', 'jì', '第十一卷', '水部', '井一有水、一無水，謂之瀱汋也。从水，罽聲。', '水;罽', '罽', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7195, '渾', 'hún', '第十一卷', '水部', '溷流聲也。从水，軍聲。一曰洿下也。', '水;軍', '軍', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7196, '洌', 'liè', '第十一卷', '水部', '水淸也。从水，列聲。易曰「井洌寒泉食」。', '水;列', '列', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7197, '淑', 'shū', '第十一卷', '水部', '淸湛也。从水，叔聲。', '水;叔', '叔', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7198, '溶', 'yǒnɡ', '第十一卷', '水部', '水盛也。从水，容聲。', '水;容', '容', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7199, '澂', 'chénɡ', '第十一卷', '水部', '淸也。从水，徵省聲。', '水;徵', '徵', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7200, '清;淸', 'qīnɡ', '第十一卷', '水部', '朖也。澂水之皃。从水，靑聲。', '水;靑', '靑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7201, '湜', 'shí', '第十一卷', '水部', '水淸見底也。从水，是聲。詩曰「湜湜其止」。', '水;是', '是', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7202, '潣', 'mǐn', '第十一卷', '水部', '水流浼浼皃。从水，閔聲。', '水;閔', '閔', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7203, '滲', 'shèn', '第十一卷', '水部', '下漉也。从水，參聲。', '水;參', '參', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7204, '潿', 'wéi', '第十一卷', '水部', '不流濁也。从水，圍聲。', '水;圍', '圍', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7205, '溷', 'hùn', '第十一卷', '水部', '亂也。一曰水濁皃。从水，圂聲。', '水;圂', '圂', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7206, '淈', 'ɡǔ', '第十一卷', '水部', '濁也。从水，屈聲。一曰滒泥。一曰水出皃。', '水;屈', '屈', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7207, '㳬', 'xuán', '第十一卷', '水部', '回淵也。从水，旋省聲。', '水;旋', '旋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7208, '漼', 'cuǐ', '第十一卷', '水部', '深也。从水，崔聲。詩曰「有漼者淵」。', '水;崔', '崔', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7209, '淵;𣶒', 'yuān', '第十一卷', '水部', '回水也。从水。象形。左右，岸也。中象水兒。淵或省水[𣶒]。古文从囗水。', '水;𣶒;囗;水', '𣶒', 'null', NULL, '水;囗;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7210, '濔', 'mǐ', '第十一卷', '水部', '水滿也。从水，爾聲。', '水;爾', '爾', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7211, '澹', 'dàn', '第十一卷', '水部', '澹澹，水繇皃也。从水，詹聲。', '水;詹', '詹', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7212, '潯', 'xún', '第十一卷', '水部', '旁深也。从水，尋聲。', '水;尋', '尋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7213, '泙', 'pínɡ', '第十一卷', '水部', '谷也。从水，平聲。', '水;平', '平', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7214, '泏', 'zhú', '第十一卷', '水部', '水兒。从水，出聲。讀若窋。', '水;出;窋', '出;窋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7215, '瀳', 'jiàn', '第十一卷', '水部', '水至也。从水，薦聲。讀若尊。', '水;薦;尊', '薦;尊', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7216, '𤁰', 'zhí', '第十一卷', '水部', '土得水沮也。从水，𥏼聲。讀若䵂。', '水;𥏼;䵂', '𥏼;䵂', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7217, '滿', 'mǎn', '第十一卷', '水部', '盈溢也。从水，㒼聲。', '水;㒼', '㒼', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7218, '滑', 'huá', '第十一卷', '水部', '利也。从水，骨聲。', '水;骨', '骨', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7219, '濇', 'sè', '第十一卷', '水部', '不滑也。从水，𠾂聲。', '水;𠾂', '𠾂', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7220, '澤', 'zé', '第十一卷', '水部', '光潤也。从水，睪聲。', '水;睪', '睪', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7221, '淫', 'yín', '第十一卷', '水部', '浸淫隨理也。从水，㸒聲。一曰久雨曰淫。', '水;㸒', '㸒', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7222, '瀸', 'jiān', '第十一卷', '水部', '漬也。从水，韱聲。爾雅曰「泉一見一不爲瀸」。', '水;韱', '韱', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7223, '泆', 'yì', '第十一卷', '水部', '水所蕩泆也。从水，失聲。', '水;失', '失', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7224, '潰', 'kuì', '第十一卷', '水部', '漏也。从水，貴聲。', '水;貴', '貴', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7225, '沴', 'lì', '第十一卷', '水部', '水不利也。从水，㐱聲。五行傳曰「若其沴作」。', '水;㐱', '㐱', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7226, '淺', 'qiǎn', '第十一卷', '水部', '不深也。从水，戔聲。', '水;戔', '戔', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7227, '洔', 'zhǐ', '第十一卷', '水部', '水暫益且止未減也。从水，寺聲。', '水;寺', '寺', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7228, '渻', 'shěnɡ', '第十一卷', '水部', '少減也。一曰水門。从水，省聲。又水出丘前謂之渻丘。', '水;省', '省', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7229, '淖', 'nào', '第十一卷', '水部', '泥也。从水，卓聲。', '水;卓', '卓', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7230, '濢', 'zuǐ', '第十一卷', '水部', '小溼也。从水，翠聲。', '水;翠', '翠', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7231, '溽', 'rù', '第十一卷', '水部', '溽暑，溼暑也。从水，辱聲。', '水;辱', '辱', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7232, '涅', 'niè', '第十一卷', '水部', '黑土在水中者也。从水土，日聲。', '水;土;日', '日', 'null', NULL, '水;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7233, '滋', 'zī', '第十一卷', '水部', '益也。从水，茲聲。一曰滋水，出牛㱃山 白陘谷，東入呼沱。', '水;茲', '茲', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7234, '𣷤', 'hū', '第十一卷', '水部', '靑黑皃。从水。曶聲。', '水;曶', '曶', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7235, '浥', 'yì', '第十一卷', '水部', '溼也。从水，邑聲。', '水;邑', '邑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7236, '沙;𣲡', 'shā', '第十一卷', '水部', '水散石也。从水少。水少沙見。楚東有沙水。譚長說沙或从尐。', '水;少;尐', '', 'null', NULL, '水;少;尐', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7237, '瀨', 'lài', '第十一卷', '水部', '水流沙上也。从水，賴聲。', '水;賴', '賴', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7238, '濆', 'fén', '第十一卷', '水部', '水厓也。从水，賁聲。詩曰「敦彼淮濆」。', '水;賁', '賁', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7239, '涘', 'sì', '第十一卷', '水部', '水厓也。从水，矣聲。周書曰「王出涘」。', '水;矣', '矣', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7240, '汻', 'hǔ', '第十一卷', '水部', '水厓也。从水，午聲。', '水;午', '午', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7241, '氿', 'ɡuǐ', '第十一卷', '水部', '水厓枯土也。从水，九聲。爾雅曰「水醮曰氿」。', '水;九', '九', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7242, '漘', 'chún', '第十一卷', '水部', '水厓也。从水，脣聲。詩曰「寘河之漘」。', '水;脣', '脣', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7243, '浦', 'pǔ', '第十一卷', '水部', '水瀕也。从水，甫聲。', '水;甫', '甫', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7244, '沚', 'zhǐ', '第十一卷', '水部', '小渚曰沚。从水，止聲。詩曰「于沼于沚」。', '水;止', '止', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7245, '沸', 'fèi', '第十一卷', '水部', '畢沸，濫泉也。从水，弗聲。', '水;弗', '弗', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7246, '潀;潨', 'cónɡ', '第十一卷', '水部', '小水入大水曰潀。从水，眾聲。詩曰「鳧鷖在潀」。', '水;眾', '眾', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7247, '派', 'pài', '第十一卷', '水部', '別水也。从水𠂢。𠂢亦聲。', '水;𠂢', '𠂢', 'null', NULL, '水', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7248, '汜', 'sì', '第十一卷', '水部', '水別復入水也。从水，巳聲。詩曰「江有汜」。一曰汜，竆瀆也。', '水;巳', '巳', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7249, '湀', 'ɡuǐ', '第十一卷', '水部', '湀辟，流水處也。从水，癸聲。', '水;癸', '癸', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7250, '滎;濙', 'xínɡ', '第十一卷', '水部', '滎濘，絕小水也。从水，熒省聲。濙滎或不省。', '水;熒', '熒', 'null', NULL, '水', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7251, '濘', 'nìnɡ', '第十一卷', '水部', '滎濘也。从水，寧聲。', '水;寧', '寧', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7252, '瀅', 'yíng', '第十一卷', '水部', '淸水也。从水，瑩聲。', '水;瑩', '瑩', 'null', NULL, '水', 'null', 'null', 'null', 'null', '政宇应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7253, '洼;漥;窪', 'wā', '第十一卷', '水部', '深池也。从水，圭聲。洼或从穴。', '水;圭;穴', '圭', 'null', NULL, '水;穴', 'null', 'null', 'null', 'null', '重文并入', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7254, '漥;窪', 'wā', '第十一卷', '水部', '淸水也。从水，窐聲。一曰窊也。', '水;窐', '窐', 'null', NULL, '水', 'null', 'null', 'null', 'null', '政宇应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7255, '潢', 'huánɡ', '第十一卷', '水部', '積水池也。从水，黃聲。', '水;黃', '黃', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7256, '沼', 'zhǎo', '第十一卷', '水部', '池也。从水，召聲。', '水;召', '召', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7257, '池', 'chí', '第十一卷', '水部', '陂也。从水，也聲。', '水;也', '也', 'null', NULL, '水', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7258, '湖', 'hú', '第十一卷', '水部', '大陂也。从水，胡聲。楊州浸有五湖。浸，川澤所仰㠯漑灌者也。', '水;胡', '胡', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7259, '汥', 'zhī', '第十一卷', '水部', '水都也。从水，支聲。', '水;支', '支', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7260, '洫', 'xù', '第十一卷', '水部', '十里爲成，成閒廣八尺、深八尺謂之洫。从水，血聲。論語曰「盡力于溝洫」。', '水;血', '血', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7261, '溝', 'ɡōu', '第十一卷', '水部', '水瀆也。廣四尺，深四尺。从水，冓聲。', '水;冓', '冓', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7262, '瀆', 'dú', '第十一卷', '水部', '溝也。从水，𧷏聲。一曰邑中曰溝。', '水;𧷏', '𧷏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7263, '渠', 'qú', '第十一卷', '水部', '水所居也。从水，柜聲。', '水;柜', '柜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7264, '瀶;𤄈', 'lín', '第十一卷', '水部', '谷也。从水，臨聲。讀若林。一曰寒也。', '水;臨;林', '臨;林', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7265, '湄', 'méi', '第十一卷', '水部', '水艸交爲湄。从水，眉聲。', '水;眉', '眉', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7266, '洐', 'xínɡ', '第十一卷', '水部', '溝行水也。从水行。', '水;行', '行', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7267, '㵎;澗', 'jiàn', '第十一卷', '水部', '山夾水也。从水，閒聲。一曰㵎水，出弘農 新安，東南入雒。', '水;閒', '閒', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7268, '澳', 'yù', '第十一卷', '水部', '隈厓也。其內曰澳，其外曰鞫。从水，奧聲。', '水;奧', '奧', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7269, '澩', 'xué', '第十一卷', '水部', '夏有水、冬無水曰澩。从水，學省聲。讀若學。澩或不省。', '水;學', '學', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7270, '𤅩;灘', 'tān', '第十一卷', '水部', '水濡而乾也。从水，𪄿聲。詩曰「𤅩其乾矣」。俗𤅩从隹。', '水;𪄿;隹', '𪄿', 'null', NULL, '水;隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7271, '汕', 'shàn', '第十一卷', '水部', '魚游水皃。从水，山聲。詩曰「烝然汕汕」。', '水;山', '山', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7272, '決', 'jué', '第十一卷', '水部', '行流也。从水，夬聲。盧江有決水，出大別山。', '水;夬', '夬', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7273, '灓', 'luán', '第十一卷', '水部', '屚流也。从水，䜌聲。', '水;䜌', '䜌', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7274, '滴', 'dī', '第十一卷', '水部', '水注也。从水，啻聲。', '水;啻', '啻', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7275, '注', 'zhù', '第十一卷', '水部', '灌也。从水，主聲。', '水;主', '主', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7276, '𣵽;沃;𦰚', 'wò', '第十一卷', '水部', '灌也。从水，芺聲。', '水;芺', '芺', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7277, '㳻', 'zé', '第十一卷', '水部', '所㠯𢹬水也。从水，昔聲。漢律曰「及其門首洒㳻」。', '水;昔', '昔', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7278, '澨', 'shì', '第十一卷', '水部', '埤增水邊土，人所止者。从水，筮聲。夏書曰「過三澨」。', '水;筮', '筮', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7279, '津', 'jīn', '第十一卷', '水部', '水渡也。从水，𦘔聲。古文津从舟淮[進]。', '水;𦘔;舟;進', '𦘔;進', 'null', NULL, '水;舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7280, '淜', 'pínɡ', '第十一卷', '水部', '無舟渡河也。从水，朋聲。', '水;朋', '朋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7281, '㶇', 'hénɡ', '第十一卷', '水部', '小津也。从水，橫聲。一曰㠯船渡也。', '水;橫', '橫', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7282, '泭', 'fū', '第十一卷', '水部', '編木㠯渡也。从水，付聲。', '水;付', '付', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7283, '渡', 'dù', '第十一卷', '水部', '濟也。从水，度聲。', '水;度', '度', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7284, '沿', 'yán', '第十一卷', '水部', '緣水而下也。从水，㕣聲。春秋傳曰「王沿夏」。', '水;㕣', '㕣', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7285, '㴑;𣶮;𣴔;𣳙;泝;遡;溯', 'sù', '第十一卷', '水部', '逆流而上曰「㴑洄」。㴑向也。水欲下，違之而上也。从水，㡿聲。㴑或从辵朔。', '水;㡿;辵;朔', '㡿;朔', 'null', NULL, '水;辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7286, '洄', 'huí', '第十一卷', '水部', '㴑洄也。从水，回聲。', '水;回', '回', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7287, '泳', 'yǒnɡ', '第十一卷', '水部', '潛行水中也。从水，永聲。', '水;永', '永', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7288, '潛;濳', 'qián', '第十一卷', '水部', '涉水也。一曰藏也。从水，朁聲。一曰漢爲潛。', '水;朁', '朁', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7289, '淦', 'ɡàn', '第十一卷', '水部', '水入船中也。从水，金聲。一曰泥也。淦或从今。', '水;金;今', '金;今', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7290, '泛', 'fàn', '第十一卷', '水部', '浮也。从水，乏聲。', '水;乏', '乏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7291, '汓;泅', 'qiú', '第十一卷', '水部', '浮行水上也。从水子。古文或㠯汓爲[湛]沒字。汓或从囚聲。', '水;子;囚', '湛;囚', 'null', NULL, '水;子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7292, '砅;濿', 'lì', '第十一卷', '水部', '履石渡水也。从水石。詩曰「深則砅[厲]」。砅或从厲。', '水;石;厲', '厲', 'null', NULL, '水;石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7293, '湊', 'còu', '第十一卷', '水部', '水上人所會也。从水，奏聲。', '水;奏', '奏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7294, '湛;㴴', 'chén', '第十一卷', '水部', '沒也。从水，甚聲。一曰湛水，豫州浸。古文[剡]。', '水;甚;剡', '甚;剡', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7295, '湮', 'yīn', '第十一卷', '水部', '沒也。从水，垔聲。', '水;垔', '垔', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7296, '㲻', 'nì', '第十一卷', '水部', '沒也。从水人。讀與溺同。', '溺', '溺', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7297, '沒', 'mò', '第十一卷', '水部', '湛也。从水，𠬛聲。', '水;𠬛', '𠬛', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7298, '渨', 'wēi', '第十一卷', '水部', '沒也。从水，畏聲。', '水;畏', '畏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7299, '滃', 'wěnɡ', '第十一卷', '水部', '雲气起也。从水，翁聲。', '水;翁', '翁', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7300, '泱', 'yānɡ', '第十一卷', '水部', '滃也。从水，央聲。', '水;央', '央', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7301, '淒', 'qī', '第十一卷', '水部', '雨雲起也。从水，妻聲。詩曰「有渰淒淒」。', '水;妻', '妻', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7302, '渰', 'yǎn', '第十一卷', '水部', '雨雲皃。从水，弇聲。', '水;弇', '弇', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7303, '溟', 'mínɡ', '第十一卷', '水部', '小雨溟溟也。从水，冥聲。', '水;冥', '冥', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7304, '洓', 'sè', '第十一卷', '水部', '小雨霝皃。从水，朿聲。', '水;朿', '朿', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7305, '瀑', 'bào', '第十一卷', '水部', '疾雨也。从水，𣉱聲。詩曰「終風且瀑」。一曰沫也。一曰瀑，霣也。', '水;𣉱', '𣉱', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7306, '澍', 'shù', '第十一卷', '水部', '時雨也。所㠯樹生萬物者也。从水，尌聲。', '水;尌', '尌', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7307, '湒', 'jí', '第十一卷', '水部', '雨下皃。从水，咠聲。一曰𩰾湧皃。', '水;咠', '咠', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7308, '澬', 'cí', '第十一卷', '水部', '久雨曰「涔澬」。一曰水名。从水，資聲。', '水;資', '資', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7309, '潦', 'lǎo', '第十一卷', '水部', '雨水也。从水，尞聲。', '水;尞', '尞', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7310, '濩', 'huò', '第十一卷', '水部', '雨流𩅸下皃。从水，蒦聲。', '水;蒦', '蒦', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7311, '涿', 'zhuó', '第十一卷', '水部', '流下滴也。从水，豖聲。上谷有涿鹿縣。奇字涿，从日乙[乁]。', '水;豖;日;乁', '豖', 'null', NULL, '水;日;乁', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7312, '瀧', 'lónɡ', '第十一卷', '水部', '雨瀧瀧也。从水，龍聲。', '水;龍', '龍', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7313, '渿', 'nài', '第十一卷', '水部', '㴎沛也。从水，柰聲。', '水;柰', '柰', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7314, '滈', 'hào', '第十一卷', '水部', '久雨也。从水，高聲。', '水;高', '高', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7315, '漊', 'lǚ', '第十一卷', '水部', '雨漊漊也。一曰汝南人謂㱃酒習之不醉曰漊。从水，婁聲。', '水;婁', '婁', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7316, '溦', 'wēi', '第十一卷', '水部', '小雨也。从水，𢼸聲。', '水;𢼸', '𢼸', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7317, '濛', 'ménɡ', '第十一卷', '水部', '微雨也。从水，蒙聲。', '水;蒙', '蒙', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7318, '沈', 'chén', '第十一卷', '水部', '陵上滈水也。从水，冘聲。一曰濁黕也。', '水;冘', '冘', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7319, '洅', 'zài', '第十一卷', '水部', '靁震洅洅也。从水，再聲。', '水;再', '再', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7320, '淊', 'hàn', '第十一卷', '水部', '泥水淊淊也。从水，臽聲。一曰繅絲湯。', '水;臽', '臽', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7321, '𣹢;涵', 'hán', '第十一卷', '水部', '水澤多也。从水，圅聲。詩曰「僭始旣涵」。', '水;圅', '圅', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7322, '𣹤', 'rù', '第十一卷', '水部', '漸溼也。从水，挐聲。', '水;挐', '挐', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7323, '瀀', 'yōu', '第十一卷', '水部', '澤多也。从水，憂聲。詩曰「旣瀀旣渥」。', '水;憂', '憂', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7324, '涔', 'cén', '第十一卷', '水部', '漬也。从水，岑聲。一曰涔陽渚，在郢。', '水;岑', '岑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7325, '漬', 'zì', '第十一卷', '水部', '漚也。从水，責聲。', '水;責', '責', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7326, '漚', 'òu', '第十一卷', '水部', '久漬也。从水，區聲。', '水;區', '區', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7327, '浞', 'zhuó', '第十一卷', '水部', '水濡皃也。从水，足聲。', '水;足', '足', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7328, '渥', 'wò', '第十一卷', '水部', '霑也。从水，屋聲。', '水;屋', '屋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7329, '㴶', 'què', '第十一卷', '水部', '灌也。从水，隺聲。', '水;隺', '隺', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7330, '洽', 'qià', '第十一卷', '水部', '霑也。从水，合聲。', '水;合', '合', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7331, '濃', 'nónɡ', '第十一卷', '水部', '露多也。从水，農聲。詩曰「零露濃濃」。', '水;農', '農', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7332, '瀌', 'biāo', '第十一卷', '水部', '瀌瀌，雨雪皃。从水，麃聲。', '水;麃', '麃', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7333, '溓', 'lián', '第十一卷', '水部', '溓溓，薄仌也。或曰中絕小水。又曰淹也。从水，兼聲。濂或从廉。', '水;兼;廉', '兼;廉', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7334, '泐', 'lè', '第十一卷', '水部', '水凝合之理也。从水，阞聲。周禮曰「石有時而泐」。', '水;阞', '阞', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7335, '滯', 'zhì', '第十一卷', '水部', '凝也。从水，帶聲。', '水;帶', '帶', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7336, '汦', 'zhǐ', '第十一卷', '水部', '箸止也。从水，氏聲。', '水;氏', '氏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7337, '㶁', 'ɡuó', '第十一卷', '水部', '水裂去也。从水，虢聲。', '水;虢', '虢', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7338, '澌', 'sī', '第十一卷', '水部', '水索也。从水，斯聲。', '水;斯', '斯', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7339, '汽;汔', 'qì', '第十一卷', '水部', '水涸也。从水，气聲。或曰泣下。詩曰「汽可小康」。', '水;气', '气', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7340, '涸', 'hé', '第十一卷', '水部', '渴也。从水，固聲。讀若「狐貈」之貈。涸亦从水鹵舟。', '水;固;貈;水;鹵;舟', '固;貈;舟', 'null', NULL, '水;水;鹵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7341, '消', 'xiāo', '第十一卷', '水部', '盡也。从水，肖聲。', '水;肖', '肖', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7342, '潐', 'jiào', '第十一卷', '水部', '盡也。从水，𤊙聲。', '水;𤊙', '𤊙', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7343, '渴', 'kě', '第十一卷', '水部', '盡也。从水，曷聲。', '水;曷', '曷', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7344, '漮', 'kānɡ', '第十一卷', '水部', '水虛也。从水，康聲。', '水;康', '康', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7345, '溼', 'shī', '第十一卷', '水部', '幽溼也。从一，覆也。覆土而有水，故溼也。从㬎省聲。', '一;土;水;㬎', '㬎', 'null', NULL, '一;土;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7346, '湆', 'qì', '第十一卷', '水部', '幽溼也。从水，音聲。', '水;音', '音', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7347, '洿', 'wū', '第十一卷', '水部', '濁水不流也。一曰窊下也。从水，夸聲。', '水;夸', '夸', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7348, '汙;污', 'wū', '第十一卷', '水部', '薉也。从水，于聲。一曰小池爲汙。一曰涂也。', '水;于', '于', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7349, '湫', 'jiǎo', '第十一卷', '水部', '隘下也。一曰有湫水在周地。春秋傳曰「晏子之宅湫隘」。安定 朝那有湫淵。从水，秋聲。', '水;秋', '秋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7350, '潤', 'rùn', '第十一卷', '水部', '水曰潤下。从水，閏聲。', '水;閏', '閏', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7351, '準;凖', 'zhǔn', '第十一卷', '水部', '平也。从水，隼聲。', '水;隼', '隼', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7352, '汀', 'tīnɡ', '第十一卷', '水部', '平也。从水，丁聲。汀或从平。', '水;丁;平', '丁', 'null', NULL, '水;平', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7353, '沑', 'nǜ', '第十一卷', '水部', '水吏也。又𥁕也。从水，丑聲。', '水;丑', '丑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7354, '瀵', 'fèn', '第十一卷', '水部', '水漫也。从水，糞聲。讀若粉。爾雅曰「瀵大出尾下」。', '水;糞;粉', '糞;粉', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7355, '𣿒', 'cuǐ', '第十一卷', '水部', '新也。从水，辠聲。', '水;辠', '辠', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7356, '瀞', 'jìnɡ', '第十一卷', '水部', '無垢薉也。从水，靜聲。', '水;靜', '靜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7357, '瀎', 'mò', '第十一卷', '水部', '瀎泧，飾滅皃。从水，蔑聲。', '水;蔑', '蔑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7358, '泧', 'sà', '第十一卷', '水部', '瀎泧也。从水，戉聲。讀若「椒樧」之樧。', '水;戉;樧', '戉;樧', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7359, '洎', 'jì', '第十一卷', '水部', '灌釡也。从水，自聲。', '水;自', '自', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7360, '湯', 'tānɡ', '第十一卷', '水部', '熱水也。从水，昜聲。', '水;昜', '昜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7361, '渜', 'nuǎn', '第十一卷', '水部', '湯也。从水，耎聲。', '水;耎', '耎', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7362, '洝', 'àn', '第十一卷', '水部', '煗水也。从水，安聲。', '水;安', '安', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7363, '洏', 'ér', '第十一卷', '水部', '洝也。从水，而聲。一曰煮孰也。', '水;而', '而', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7364, '涗', 'shuì', '第十一卷', '水部', '財𥁕水也。从水，兌聲。周禮曰「㠯涗漚其絲」。', '水;兌', '兌', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7365, '涫', 'ɡuàn', '第十一卷', '水部', '𩰾也。从水，官聲。酒泉有樂涫縣。', '水;官', '官', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7366, '涾', 'tà', '第十一卷', '水部', '涫溢也。今河朔方言謂𩰾溢爲涾。从水，沓聲。', '水;沓', '沓', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7367, '汏', 'tài', '第十一卷', '水部', '淅㵎也。从水，大聲。', '水;大', '大', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7368, '㶕;𤄒', 'jiǎn', '第十一卷', '水部', '淅也。从水，𥳑聲。', '水;𥳑', '𥳑', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7369, '淅', 'xī', '第十一卷', '水部', '汏米也。从水，析聲。', '水;析', '析', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7370, '滰', 'jiànɡ', '第十一卷', '水部', '浚乾漬米也。从水，竟聲。孟子曰「孔子去齊，滰淅而行」。', '水;竟', '竟', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7371, '𣸈;溲', 'sǒu', '第十一卷', '水部', '𣵽汏也。从水，叜聲。', '水;叜', '叜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7372, '浚', 'jùn', '第十一卷', '水部', '抒也。从水，夋聲。', '水;夋', '夋', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7373, '𤃹;瀝', 'lì', '第十一卷', '水部', '浚也。从水，磿聲。一曰水下滴𤃹也。', '水;磿', '磿', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7374, '漉;淥', 'lù', '第十一卷', '水部', '浚也。从水，鹿聲。一曰水下皃也。漉或从录。', '水;鹿;录', '鹿;录', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7375, '潘', 'pān', '第十一卷', '水部', '淅米汁也。从水，番聲。一曰潘水，在河南 熒陽。', '水;番', '番', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7376, '灡', 'lán', '第十一卷', '水部', '潘也。从水，蘭聲。', '水;蘭', '蘭', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7377, '泔', 'ɡān', '第十一卷', '水部', '周謂潘曰泔。从水，甘聲。', '水;甘', '甘', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7378, '滫', 'xiū', '第十一卷', '水部', '久泔也。从水，脩聲。', '水;脩', '脩', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7379, '澱', 'diàn', '第十一卷', '水部', '滓垽也。从水，殿聲。', '水;殿', '殿', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7380, '淤', 'yū', '第十一卷', '水部', '澱滓濁泥也。从水，於聲。', '水;於', '於', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7381, '滓', 'zǐ', '第十一卷', '水部', '澱也。从水，宰聲。', '水;宰', '宰', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7382, '淰', 'niǎn', '第十一卷', '水部', '濁也。从水，念聲。', '水;念', '念', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7383, '瀹', 'yuè', '第十一卷', '水部', '漬也。从水，龠聲。', '水;龠', '龠', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7384, '𤃭', 'jiǎo', '第十一卷', '水部', '釃酒也。一曰浚也。从网水。焦聲。讀若夏書「天用剿絕」。', '网;水;焦;剿', '焦;剿', 'null', NULL, '网;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7385, '漀', 'qǐnɡ', '第十一卷', '水部', '側出酒也。从水，殸聲。殸，籒文磬字。', '水;殸', '殸', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7386, '湑', 'xǔ', '第十一卷', '水部', '莤酒也。一曰浚也。一曰露皃也。从水，胥聲。詩曰「有酒湑我」。又曰「零露湑兮」。', '水;胥', '胥', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7387, '湎', 'miǎn', '第十一卷', '水部', '湛於酒也。从水，面聲。周書曰「罔敢湎于酒」。', '水;面', '面', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7388, '𤖅;浆;漿', 'jiānɡ', '第十一卷', '水部', '酢𤖅也。从水，將省聲。古文漿[爿]。', '水;將;爿', '將;爿', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7389, '涼', 'liánɡ', '第十一卷', '水部', '薄也。从水，京聲。', '水;京', '京', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7390, '淡', 'dàn', '第十一卷', '水部', '薄味也。从水，炎聲。', '水;炎', '炎', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7391, '涒', 'tūn', '第十一卷', '水部', '食巳而復吐之。从水，君聲。爾雅曰「大歲在申曰涒灘」。', '水;君', '君', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7392, '澆', 'jiāo', '第十一卷', '水部', '𣵽也。从水，堯聲。', '水;堯', '堯', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7393, '液', 'yè', '第十一卷', '水部', '𧗃也。从水，夜聲。', '水;夜', '夜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7394, '汁', 'zhī', '第十一卷', '水部', '液也。从水，十聲。', '水;十', '十', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7395, '瀋', 'shěn', '第十一卷', '水部', '汁也。从水，審聲。春秋傳曰「猶拾瀋也」。', '水;審', '審', 'null', NULL, '水', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
