INSERT INTO `shuowen_voice_revel` VALUES (7401, '濈', 'jí', '第十一卷', '水部', '和也。从水，戢聲。', '水;戢', '戢', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
