INSERT INTO `shuowen_voice_revel` VALUES (7408, '淬', 'cuì', '第十一卷', '水部', '滅火器也。从水，卒聲。', '水;卒', '卒', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7409, '沐', 'mù', '第十一卷', '水部', '濯髮也。从水，木聲。', '水;木', '木', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7410, '沬;湏;頮', 'huì', '第十一卷', '水部', '洒面也。从水，未聲。古文沬从廾水，从頁。', '水;未;廾;水;頁', '未', '貴', '亦作靧。', '水;廾;水;頁', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
