INSERT INTO `shuowen_voice_revel` VALUES (7416, '淋', 'lín', '第十一卷', '水部', '㠯水𣵽也。从水，林聲。一曰淋淋，山下水也。', '水;林', '林', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7417, '渫', 'xiè', '第十一卷', '水部', '除去也。从水，枼聲。', '水;枼', '枼', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7418, '澣;浣', 'huàn', '第十一卷', '水部', '濯衣垢也。从水，𧹳聲。今澣从完。', '水;𧹳;完', '𧹳;完', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
