INSERT INTO `shuowen_voice_revel` VALUES (7420, '涑', 'sōu', '第十一卷', '水部', '澣也。从水，束聲。河東有涑水。', '水;束', '束', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7421, '潎', 'pì', '第十一卷', '水部', '於水中擊絮也。从水，敝聲。', '水;敝', '敝', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7422, '㙙', 'lǒnɡ', '第十一卷', '水部', '涂也从水，从土，尨聲。讀若隴。', '土;尨;隴', '尨;隴', 'null', NULL, '土', 'null', 'null', 'null', 'null', '重出应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7423, '灑', 'sǎ', '第十一卷', '水部', '汛也。从水，麗聲。', '水;麗', '麗', 'null', NULL, '水', 'null', 'null', 'null', 'null', '麗轨', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7424, '汛', 'xùn', '第十一卷', '水部', '灑也。从水，卂聲。', '水;卂', '卂', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7425, '染', 'rǎn', '第十一卷', '水部', '㠯繪染爲色。从水，朵聲。', '水;朵', '朵', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7426, '泰', 'tài', '第十一卷', '水部', '滑也。从廾水。大聲。古文泰如此[仌]。', '廾;水;大;仌', '大', 'null', NULL, '廾;水;仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7427, '㶄', 'yán', '第十一卷', '水部', '海岱之閒謂相污曰㶄。从水，閻聲。', '水;閻', '閻', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7428, '浼', 'měi', '第十一卷', '水部', '污也。从水，免聲。詩曰「河水浼浼」。孟子曰「汝安能浼我」。', '水;免', '免', 'null', NULL, '水', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7429, '灒', 'zàn', '第十一卷', '水部', '污灑也。一曰水中人也。从水，贊聲。', '水;贊', '贊', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7430, '㵞', 'chóu', '第十一卷', '水部', '腹中有水气也。从水，愁聲。', '水;愁', '愁', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7431, '湩', 'dònɡ', '第十一卷', '水部', '乳汁也。从水，重聲。', '水;重', '重', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7432, '洟', 'tì', '第十一卷', '水部', '鼻液也。从水，夷聲。', '水;夷', '夷', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7433, '潸', 'shān', '第十一卷', '水部', '涕流皃。从水，散省聲。詩曰「潸焉出涕」。', '水;散', '散', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7434, '汗', 'hàn', '第十一卷', '水部', '身液也。从水，干聲。', '水;干', '干', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7435, '泣', 'qì', '第十一卷', '水部', '無聲出涕者曰泣。从水，立聲。', '水;立', '立', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7436, '涕', 'tì', '第十一卷', '水部', '泣也。从水，弟聲。', '水;弟', '弟', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7437, '湅', 'liàn', '第十一卷', '水部', '𤄒也。从水，柬聲。', '水;柬', '柬', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7438, '𤅊', 'niè', '第十一卷', '水部', '議辠也。从水獻。與法同意。', '水;獻', '獻', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7439, '渝', 'yū', '第十一卷', '水部', '變污也。从水，俞聲。一曰渝水，在遼西 臨渝，東出塞。', '水;俞', '俞', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7440, '減', 'jiǎn', '第十一卷', '水部', '損也。从水，咸聲。', '水;咸', '咸', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7441, '滅', 'miè', '第十一卷', '水部', '盡也。从水，烕聲。', '水;烕', '烕', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7442, '漕', 'cáo', '第十一卷', '水部', '水轉穀也。从水，曹聲。一曰人之所乘及船也。', '水;曹', '曹', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7443, '泮', 'pàn', '第十一卷', '水部', '諸矦饗射之宮。西南爲水，東北爲牆。从水半。半亦聲。', '水;半', '半', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7444, '漏', 'lòu', '第十一卷', '水部', '㠯銅受水，刻節，晝夜百節。从水屚。取屚下之義。屚亦聲。', '水;屚', '屚', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7445, '澒', 'hònɡ', '第十一卷', '水部', '丹沙所化爲水銀也。从水，項聲。', '水;項', '項', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7446, '萍', 'pínɡ', '第十一卷', '水部', '苹也。水艸也。从水苹。苹亦聲。', '水;苹', '苹', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7447, '濊', 'huì', '第十一卷', '水部', '水多皃。从水，歲聲。', '水;歲', '歲', 'null', NULL, '水', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7448, '汩', 'ɡǔ', '第十一卷', '水部', '治水也。从水，曰聲。', '水;曰', '曰', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7449, '瀼', 'ránɡ', '第十一卷', '水部', '露濃皃。从水，襄聲。', '水;襄', '襄', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7450, '漙', 'tuán', '第十一卷', '水部', '露皃。从水，專聲。', '水;專', '專', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7451, '汍', 'wán', '第十一卷', '水部', '泣淚皃。从水，丸聲。', '水;丸', '丸', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7452, '泯', 'mǐn', '第十一卷', '水部', '滅也。从水，民聲。', '水;民', '民', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7453, '瀣', 'xiè', '第十一卷', '水部', '沆瀣，气也。从水，䪥省聲。', '水;䪥', '䪥', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7454, '瀘', 'lú', '第十一卷', '水部', '水名。从水，盧聲。', '水;盧', '盧', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7455, '瀟', 'xiāo', '第十一卷', '水部', '水名。从水，蕭聲。', '水;蕭', '蕭', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7456, '瀛', 'yínɡ', '第十一卷', '水部', '水名。从水，嬴聲。', '水;嬴', '嬴', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7457, '滁', 'chú', '第十一卷', '水部', '水名。从水，除聲。', '水;除', '除', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7458, '洺', 'mínɡ', '第十一卷', '水部', '水名。从水，名聲。', '水;名', '名', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7459, '潺', 'chán', '第十一卷', '水部', '水聲。从水，孱聲。', '水;孱', '孱', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7460, '湲', 'yuán', '第十一卷', '水部', '潺湲，水聲。从水，爰聲。', '水;爰', '爰', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7461, '濤;𣾭', 'tāo', '第十一卷', '水部', '大波也。从水，壽聲。', '水;壽', '壽', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7462, '漵', 'xù', '第十一卷', '水部', '水浦也。从水，敘聲。', '水;敘', '敘', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7463, '港', 'ɡǎnɡ', '第十一卷', '水部', '水派也。从水，䢽聲。', '水;䢽', '䢽', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7464, '瀦', 'zhū', '第十一卷', '水部', '水所亭也。从水，豬聲。', '水;豬', '豬', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7465, '𤅤', 'mí', '第十一卷', '水部', '大水也。从水，镾聲。', '水;镾', '镾', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7466, '淼;渺', 'miǎo', '第十一卷', '水部', '大水也。从三水。[渺]或作渺[眇]。', '水;眇', '眇', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7467, '潔', 'jié', '第十一卷', '水部', '瀞也。从水，絜聲。', '水;絜', '絜', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7468, '浹', 'jiā', '第十一卷', '水部', '洽也，从也。从水，夾聲。', '水;夾', '夾', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7469, '𣹆', 'kè', '第十一卷', '水部', '奄忽也。从水，盇聲。', '水;盇', '盇', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7470, '潠', 'xùn', '第十一卷', '水部', '含水噴也。从水，巽聲。', '水;巽', '巽', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7471, '涯', 'yá', '第十一卷', '水部', '水邊也。从水，从厓。厓亦聲。', '水;厓', '厓', 'null', NULL, '水', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7472, '潑', 'pō', '第十一卷', '水部', '沛也，沈培説。从水，發聲。章炳麟說，讀若𣎵。', '水;發;𣎵', '發;𣎵', 'null', NULL, '水', 'null', 'null', 'null', 'null', '政宇新附 | 𣎵🤰', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7473, '沝', 'zhuǐ', '第十一卷', '沝部', '二水也。闕。凡沝之屬皆从沝。', '水', '', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7474, '𣹳;流', 'liú', '第十一卷', '沝部', '水行也。从沝㐬[𠫓]。㐬，突忽也。篆文从水。', '沝;𠫓;水', '', 'null', NULL, '沝;𠫓;水', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7475, '𣻣;涉', 'shè', '第十一卷', '沝部', '徒行濿水也。从沝步。篆文从水。', '沝;步;水', '', 'null', NULL, '沝;步;水', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7476, '瀕;𩕘;𩕨;頻', 'pín', '第十一卷', '瀕部', '水厓。人所賔附也。顰戚不歬而止。从頁，从涉。凡瀕之屬皆从瀕。', '賔;頁;涉', '賔', 'null', NULL, '頁;涉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7477, '顰', 'pín', '第十一卷', '瀕部', '涉水顰戚也。从瀕，卑聲。', '瀕;卑', '卑', 'null', NULL, '瀕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7478, '𡿨;甽;畎', 'quǎn', '第十一卷', '𡿨部', '水小流也。周禮「匠人爲溝洫，枱廣五寸，二枱爲耦，一耦之伐。廣尺、深尺謂之𡿨」。倍𡿨謂之遂。倍遂曰溝。倍溝曰洫。倍洫曰巜。凡𡿨之屬皆从𡿨。古文𡿨从田川。田之川也。篆文𡿨从田，犬聲。六畎爲一畮。', '田;川;犬', '川;犬', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7479, '巜', 'kuài', '第十一卷', '巜部', '水流澮澮也。方百里爲巜。廣二尋，深二仞。凡巜之屬皆从巜。', '澮', '澮', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7480, '粼', 'lín', '第十一卷', '巜部', '水生厓石閒粼粼也。从巜，粦聲。', '巜;粦', '粦', 'null', NULL, '巜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7481, '川;巛', 'chuān', '第十一卷', '川部', '毌穿通流水也。虞書曰「濬𡿨巜歫川」。言深𡿨巜之水會爲川也。凡川之屬皆从川。', '穿', '穿', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7482, '巠', 'jīnɡ', '第十一卷', '川部', '水𧖴也。从川在一下。一，地也。𡈼省聲。一曰水冥巠也。古文巠不省。', '川;一;𡈼', '𡈼', 'null', NULL, '川;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7483, '巟', 'huānɡ', '第十一卷', '川部', '水廣也。从川，亡聲。易曰「包巟用馮河」。', '川;亡', '亡', 'null', NULL, '川', 'null', 'null', 'null', 'null', '🤰漠', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7484, '𡿿', 'huò', '第十一卷', '川部', '水流皃。从川，或聲。', '川;或', '或', 'null', NULL, '川', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7485, '𡿯', 'yù', '第十一卷', '川部', '水流也。从川，曰聲。', '川;曰', '曰', 'null', NULL, '川', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7486, '𡿪;𡿩', 'liè', '第十一卷', '川部', '水流𡿪𡿪也。从川，列省聲。', '川;列', '列', 'null', NULL, '川', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7487, '邕', 'yōnɡ', '第十一卷', '川部', '邑四方有水，自邕成池者是也。从川邑。讀若雝。籒文邕如此[呂]。', '川;邑;雝;呂', '雝', 'null', NULL, '川;邑;呂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7488, '𡿧', 'zāi', '第十一卷', '川部', '害也。从一雝川。春秋傳曰「川雝爲澤凶」。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7489, '侃', 'kǎn', '第十一卷', '川部', '剛直也。从㐰。㐰，古文信也。从川，取其不舍晝夜。論語曰「子路侃侃如也」。', '㐰;川', '', 'null', NULL, '㐰;川', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7490, '州;洲', 'zhōu', '第十一卷', '川部', '水中可凥者曰州，水𠣘繞其旁。从重川。昔堯遭洪水，民凥水中高土，故曰「九州」。詩曰「在河之州」。一曰州，𤲮也。各𤲮其土而生也。古文州。', '川', '', 'null', NULL, '川', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7491, '泉', 'quán', '第十一卷', '泉部', '水厡也。象水流出成川形。凡泉之屬皆从泉。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7492, '㶗', 'fàn', '第十一卷', '泉部', '泉水也。从泉，䋣聲。讀若飯。', '泉;䋣;飯', '䋣;飯', 'null', NULL, '泉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7493, '灥', 'xún', '第十一卷', '灥部', '三泉也。闕。凡灥之屬皆从灥。', '泉', '', 'null', NULL, '泉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7494, '𠫐;原;厡', 'yuán', '第十一卷', '灥部', '水本也。从灥出厂下。篆文从泉。', '灥;厂;泉', '泉', 'null', NULL, '灥;厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7495, '永', 'yǒnɡ', '第十一卷', '永部', '水長也。象水巠理之長永也。詩曰「江之永矣」。凡永之屬皆从永。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7496, '羕', 'yànɡ', '第十一卷', '永部', '水長也。从永，羊聲。詩曰「江之羕矣」。', '永;羊', '永;羊', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7497, '𠂢', 'pài', '第十一卷', '𠂢部', '水之衺流別也。从反永。凡𠂢之屬皆从𠂢。讀若「稗縣」。', '永;稗', '稗', 'null', NULL, '永', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7498, '𧖴;脈;衇', 'mài', '第十一卷', '𠂢部', '血理分衺行體中者。从𠂢，从血。𧖴或从肉。籒文。', '𠂢;血;肉', '𠂢', 'null', NULL, '血;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7499, '覛;覓;𧵬', 'mì', '第十一卷', '𠂢部', '衺視也。从𠂢，从見。籒文。', '𠂢;見', '𠂢', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7500, '谷', 'ɡǔ', '第十一卷', '谷部', '泉出通川爲谷。从水半見出於口。凡谷之屬皆从谷。', '水;口', '', 'null', NULL, '水;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7501, '谿;豀', 'xī', '第十一卷', '谷部', '山𨽍無所通者。从谷，奚聲。', '谷;奚', '奚', 'null', NULL, '谷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7502, '𧯆;豁', 'huò', '第十一卷', '谷部', '通谷也。从谷，害聲。', '谷;害', '害', 'null', NULL, '谷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7503, '豂', 'liáo', '第十一卷', '谷部', '空谷也。从谷，翏聲。', '谷;翏', '翏', 'null', NULL, '谷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7504, '豅', 'lónɡ', '第十一卷', '谷部', '大長谷也。从谷，龍聲。讀若聾。', '谷;龍;聾', '龍;聾', 'null', NULL, '谷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7505, '𧮯', 'hónɡ', '第十一卷', '谷部', '谷中響也。从谷，厷聲。', '谷;厷', '厷', 'null', NULL, '谷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7506, '谸', 'qiān', '第十一卷', '谷部', '望山谷千千靑也。从谷，千聲。', '谷;千', '千', 'null', NULL, '谷', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7507, '䜭;濬', 'jùn', '第十一卷', '谷部', '深通川也。从𣦵谷。𣦵，殘也。谷，阬坎意也。虞書曰「䜭畎澮歫川」。䜭或从水。古文䜭[睿]。', '𣦵;谷;水;睿', '', 'null', NULL, '𣦵;谷;水;睿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7508, '仌', 'bīnɡ', '第十一卷', '仌部', '凍也。象水冰之形。凡仌之屬皆从仌。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7509, '冰;凝', 'nínɡ', '第十一卷', '仌部', '水堅也。从水仌。俗冰从疑。', '水;仌;疑', '疑', 'null', NULL, '水;仌', 'null', 'null', 'null', 'null', '蒸部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7510, '凍', 'dònɡ', '第十一卷', '仌部', '仌也。从仌，東聲。', '仌;東', '東', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7511, '𠗲;凌;𣎎', 'línɡ', '第十一卷', '仌部', '仌出也。从仌，朕聲。詩曰「納于𣎎陰」。𣎎或从夌。', '仌;朕;夌', '朕;夌', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7512, '凘', 'sī', '第十一卷', '仌部', '流仌也。从仌，斯聲。', '仌;斯', '斯', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7513, '凋', 'diāo', '第十一卷', '仌部', '半傷也。从仌。周聲。', '仌;周', '周', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7514, '冬', 'dōnɡ', '第十一卷', '仌部', '四時盡也。从仌，从𠂂。𠂂，古文終字。古文冬从日。', '仌;𠂂;日', '𠂂', 'null', NULL, '仌;日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7515, '冶', 'yě', '第十一卷', '仌部', '銷也。从仌，台聲。', '仌;台', '台', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7516, '凔', 'cānɡ', '第十一卷', '仌部', '寒也。从仌，倉聲。', '仌;倉', '倉', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7517, '冷', 'lěnɡ', '第十一卷', '仌部', '寒也。从仌，令聲。', '仌;令', '令', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7518, '𠗴;𠗙', 'hán', '第十一卷', '仌部', '寒也。从仌，圅聲。', '仌;圅', '圅', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7519, '𠘡', 'lǐn', '第十一卷', '仌部', '𠘡𠘡，寒也。从仌，廩聲。', '仌;廩', '廩', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7520, '凊', 'qìnɡ', '第十一卷', '仌部', '寒也。从仌，靑聲。', '仌;靑', '靑', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7521, '㓖', 'bì', '第十一卷', '仌部', '㓖冹，風寒也。詩曰「一之日㓖冹」。从仌，畢聲。', '仌;畢', '畢', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7522, '冹', 'fú', '第十一卷', '仌部', '㓖冹也。从仌，犮聲。', '仌;犮', '犮', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7523, '凓', 'lì', '第十一卷', '仌部', '凓冽，寒皃。詩曰「二之日凓冽」。从仌，栗聲。', '仌;栗', '栗', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7524, '冽', 'liè', '第十一卷', '仌部', '凓冽也。从仌，列聲。', '仌;列', '列', 'null', NULL, '仌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7525, '雨', 'yǔ', '第十一卷', '雨部', '水從雲下也。一，象天。冂，象雲，水霝其閒也。凡雨之屬皆从雨。古文。', '一;冂', '', 'null', NULL, '一;冂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7526, '靁;畾;雷', 'léi', '第十一卷', '雨部', '𩃬昜薄動生物者也。从雨。畾，象回轉形。籒文靁閒有回。回，靁聲也。古文靁。古文靁。', '雨;畾;回', '', 'null', NULL, '雨;畾;回', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7527, '霣', 'yǔn', '第十一卷', '雨部', '齊人謂靁爲霣。从雨，員聲。一曰雲轉起也。讀若昆。古文霣如此[鼎]。', '雨;員;昆;鼎', '員;昆', 'null', NULL, '雨;鼎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7528, '霆', 'tínɡ', '第十一卷', '雨部', '靁餘聲鈴鈴。所㠯挺出萬物。从雨，廷聲。', '雨;廷', '廷', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7529, '霅', 'zhá', '第十一卷', '雨部', '霅霅。靁電皃。从雨，譶省聲。一曰霅，衆言也。', '雨;譶', '譶', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7530, '電', 'diàn', '第十一卷', '雨部', '𩃬昜激燿也。从雨，从申。古文電如此。', '雨;申', '申', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7531, '震', 'zhèn', '第十一卷', '雨部', '劈歷振物者。从雨，辰聲。春秋傳曰「震夷伯之廟」。籒文震[云爻鬲火]。', '雨;辰;云;爻;鬲;火', '辰', 'null', NULL, '雨;云;爻;鬲;火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7532, '雪', 'xuě', '第十一卷', '雨部', '冰雨。說物者也。从雨，彗聲。', '雨;彗', '彗', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7533, '霄', 'xiāo', '第十一卷', '雨部', '雨霰爲霄。从雨，肖聲。齊語也。', '雨;肖', '肖', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7534, '霰', 'xiàn', '第十一卷', '雨部', '稷雪也。从雨，散聲。霰或从見。', '雨;散;見', '散;見', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7535, '雹', 'báo', '第十一卷', '雨部', '雨仌也。从雨，包聲。古文雹如此。', '雨;包', '包', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7536, '霝', 'línɡ', '第十一卷', '雨部', '雨𩂣也。从雨。𠱠，象𩂣形。詩曰「霝雨其濛」。', '雨;𠱠', '', 'null', NULL, '雨;𠱠', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7537, '𩂣', 'luò', '第十一卷', '雨部', '雨𩂣也。从雨，各聲。', '雨;各', '各', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7538, '零', 'línɡ', '第十一卷', '雨部', '徐雨也。从雨，令聲。', '雨;令', '令', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7539, '𩆵', 'sī', '第十一卷', '雨部', '小雨財𩂣也。从雨，鮮聲。讀若斯。', '雨;鮮;斯', '鮮;斯', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7540, '霢', 'mài', '第十一卷', '雨部', '霢霂。小雨也。从雨，脈聲。', '雨;脈', '脈', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7541, '霂', 'mù', '第十一卷', '雨部', '霢霂也。从雨，沐聲。', '雨;沐', '沐', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7542, '𩆑', 'suān', '第十一卷', '雨部', '小雨也。从雨，酸聲。', '雨;酸', '酸', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7543, '𩃔', 'jiān', '第十一卷', '雨部', '溦雨也。从雨，㦰聲。又讀若芟。', '雨;㦰;芟', '㦰;芟', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7544, '𩅧', 'zhōnɡ', '第十一卷', '雨部', '小雨也。从雨，衆聲。朙堂月令曰「𩅧雨」。', '雨;衆', '衆', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7545, '霃', 'chén', '第十一卷', '雨部', '久𩃬也。从雨，沈聲。', '雨;沈', '沈', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7546, '𩄡', 'lián', '第十一卷', '雨部', '久雨也。从雨，兼聲。', '雨;兼', '兼', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7547, '𩄙;䨡', 'hán', '第十一卷', '雨部', '久雨也。从雨，圅聲。', '雨;圅', '圅', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7548, '霖', 'lín', '第十一卷', '雨部', '凡雨三日巳往爲霖。从雨，林聲。', '雨;林', '林', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7549, '𩂢', 'yín', '第十一卷', '雨部', '霖雨也。南陽謂霖𩂢。从雨，㐺聲。', '雨;㐺', '㐺', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7550, '𩄚', 'zī', '第十一卷', '雨部', '雨聲。从雨，眞聲。讀若資。', '雨;眞;資', '眞;資', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7551, '䨞', 'yǔ', '第十一卷', '雨部', '雨皃。方語也。从雨，禹聲。讀若禹。', '雨;禹', '禹', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7552, '𩅼', 'jiān', '第十一卷', '雨部', '小雨也。从雨，僉聲。', '雨;僉', '僉', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7553, '霑', 'zhān', '第十一卷', '雨部', '雨𩃵也。从雨，沾聲。', '雨;沾', '沾', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7554, '𩃵', 'rǎn', '第十一卷', '雨部', '濡也。从雨，染聲。', '雨;染', '染', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7555, '𩅸;霤', 'liù', '第十一卷', '雨部', '屋水流也。从雨，畱聲。', '雨;畱', '畱', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7556, '屚', 'lòu', '第十一卷', '雨部', '屋穿水入也。从雨在尸下。尸者，屋也。', '雨;尸', '', 'null', NULL, '雨;尸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7557, '䨣', 'pò', '第十一卷', '雨部', '雨濡革也。从雨革。讀若膊。', '雨;革;膊', '膊', 'null', NULL, '雨;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7558, '霽', 'jì', '第十一卷', '雨部', '雨止也。从雨，齊聲。', '雨;齊', '齊', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7559, '霋', 'qī', '第十一卷', '雨部', '霽謂之霋。从雨，妻聲。', '雨;妻', '妻', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7560, '霩', 'kuò', '第十一卷', '雨部', '雨止雲罷皃。从雨，郭聲。', '雨;郭', '郭', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7561, '露', 'lù', '第十一卷', '雨部', '潤澤也。从雨，路聲。', '雨;路', '路', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7562, '霜', 'shuānɡ', '第十一卷', '雨部', '喪也。成物者。从雨，相聲。', '雨;相', '相', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7563, '霚;雺', 'wù', '第十一卷', '雨部', '地气發、天不應，曰霚。从雨，敄聲。籒文霚省[矛]。', '雨;敄;矛', '敄;矛', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7564, '霾', 'mái', '第十一卷', '雨部', '風而雨土爲霾。从雨，貍聲。詩曰「終風且霾」。', '雨;貍', '貍', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7565, '霿', 'ménɡ', '第十一卷', '雨部', '天氣下、地不應，曰霿。霿，晦也。从雨，瞀聲。', '雨;瞀', '瞀', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7566, '霓', 'ní', '第十一卷', '雨部', '屈虹。靑赤或白色。𩃬气也。从雨，兒聲。', '雨;兒', '兒', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7567, '𩅀', 'diàn', '第十一卷', '雨部', '寒也。从雨，執聲。或曰早霜也。讀若春秋傳「墊阨」。', '雨;執;墊', '執;墊', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7568, '雩', 'yú', '第十一卷', '雨部', '夏祭，樂於赤帝，㠯祈甘雨也。从雨，亏聲。雩或从羽。雩，舞羽也。', '雨;亏;羽', '亏;羽', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7569, '需', 'xū', '第十一卷', '雨部', '䇓也。遇雨不進止䇓也。从雨而。易曰「雲上于天需」。', '雨;而', '', 'null', NULL, '雨;而', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7570, '䨒', 'yù', '第十一卷', '雨部', '水音也。从雨，羽聲。', '雨;羽', '羽', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7571, '霞', 'xiá', '第十一卷', '雨部', '赤雲气也。从雨，叚聲。', '雨;叚', '叚', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7572, '霏', 'fēi', '第十一卷', '雨部', '雨雲皃。从雨，非聲。', '雨;非', '非', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7573, '霎', 'shà', '第十一卷', '雨部', '小雨也。从雨，妾聲。', '雨;妾', '妾', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7574, '䨴', 'duì', '第十一卷', '雨部', '黮䨴。雲黑皃。从雨，對聲。', '雨;對', '對', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7575, '靄', 'ǎi', '第十一卷', '雨部', '雲皃。从雨，藹省聲。', '雨;藹', '藹', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7576, '雲;云', 'yún', '第十一卷', '雲部', '山川气也。从雨。云，象回轉之形。凡雲之屬皆从雲。古文省雨。亦古文雲。', '雨', '', 'null', NULL, '雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7577, '𩃬;侌', 'yīn', '第十一卷', '雲部', '雲覆日也。从雲，今聲。古文𩃬省。亦古文𩃬。', '雲;今', '今', 'null', NULL, '雲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7578, '魚', 'yú', '第十一卷', '魚部', '水蟲也。象形。魚尾與燕尾相佀。凡魚之屬皆从魚。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7579, '䲊', 'duò', '第十一卷', '魚部', '魚子巳生者也。从魚，隋聲。籒文䲊。', '魚;隋', '隋', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7580, '鮞', 'ér', '第十一卷', '魚部', '魚子也。一曰魚之美者，東海之鮞。从魚，而聲。', '魚;而', '而', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7581, '魼', 'qū', '第十一卷', '魚部', '魼魚也。从魚，去聲。[鰈，或从枼。]', '魚;去;枼', '去;枼', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '政宇应补重文', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7582, '𩹾', 'nà', '第十一卷', '魚部', '𩹾魚。佀鼈無甲，有尾無足，口在腹下。从魚，納聲。', '魚;納', '納', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7583, '鰨', 'tǎ', '第十一卷', '魚部', '虛鰨也。从魚，𦐇聲。', '魚;𦐇', '𦐇', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '政宇应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7584, '鱒', 'zùn', '第十一卷', '魚部', '赤目魚也。从魚，尊聲。', '魚;尊', '尊', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7585, '𩻜', 'lín', '第十一卷', '魚部', '𩻜魚也。从魚，猌聲。', '魚;猌', '猌', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7586, '鰫', 'yónɡ', '第十一卷', '魚部', '鰫魚也。从魚，容聲。', '魚;容', '容', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7587, '䱬', 'xū', '第十一卷', '魚部', '䱬魚也。从魚，胥聲。', '魚;胥', '胥', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7588, '鮪', 'wěi', '第十一卷', '魚部', '鮥也。周禮「春獻王鮪」。从魚，有聲。', '魚;有', '有', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7589, '䱭', 'ɡènɡ', '第十一卷', '魚部', '䱭鯍，鮪也。周雒謂之鮪，蜀謂之䱭鯍。从魚，恆聲。', '魚;恆', '恆', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7590, '𩶡;𩶶;𩷮;鯍', 'ménɡ', '第十一卷', '魚部', '䱭鯍也。从魚，巟聲。', '魚;巟', '巟', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7591, '鮥', 'luò', '第十一卷', '魚部', '叔鮪也。从魚，各聲。', '魚;各', '各', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
