INSERT INTO `shuowen_voice_revel` VALUES (7595, '鱣', 'zhān', '第十一卷', '魚部', '鯉也。从魚，亶聲。籒文鱣[蟺]。', '魚;亶;蟺', '亶;蟺', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
