INSERT INTO `shuowen_voice_revel` VALUES (7599, '䱾', 'lóu', '第十一卷', '魚部', '䱾魚也。一名鯉。一名鰜。从魚，婁聲。', '魚;婁', '婁', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
