INSERT INTO `shuowen_voice_revel` VALUES (7603, '鯾;鯿', 'biān', '第十一卷', '魚部', '鯾魚也。从魚，便聲。鯿鯾或从扁。', '魚;便;扁', '便;扁', 'null', NULL, '魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
