INSERT INTO `shuowen_voice_revel` VALUES (7694, '非', 'fēi', '第十一卷', '非部', '韋也。从飛下翄。取其相背也。凡非之屬皆从非。', '飛', '飛', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7695, '䨽', 'fěi', '第十一卷', '非部', '別也。从非己。', '非;己', '非', 'null', NULL, '己', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7696, '靡', 'mǐ', '第十一卷', '非部', '柀靡也。从非，麻聲。', '非;麻', '麻', 'null', NULL, '非', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7697, '靠', 'kào', '第十一卷', '非部', '相韋也。从非，告聲。', '非;告', '告', 'null', NULL, '非', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7698, '𨻼', 'bī', '第十一卷', '非部', '𨻼牢謂之獄。所㠯拘非也。从非，陛省聲。', '非;陛', '陛', 'null', NULL, '非', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7699, '卂', 'xùn', '第十一卷', '卂部', '疾飛也。从飛而羽不見。凡卂之屬皆从卂。', '飛', '', 'null', NULL, '飛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7700, '煢', 'qiónɡ', '第十一卷', '卂部', '回疾也。从卂，營省聲。', '卂;營', '營', 'null', NULL, '卂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7701, '𠃉', 'yǐ', '第十二卷', '𠃉部', '燕燕，玄鳥也。齊、魯謂之𠃉。取其鳴自謼。象形也。凡𠃉之屬皆从𠃉。𠃉或从鳥。', '鳥', '', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7702, '孔', 'kǒnɡ', '第十二卷', '𠃉部', '通也。嘉美之也。从𠃉子。𠃉，請子之𠊱鳥也，𠃉至而得子，嘉美之也。故古人名嘉字子孔。', '𠃉;子', '', 'null', NULL, '𠃉;子', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7703, '乳', 'rǔ', '第十二卷', '𠃉部', '人及鳥生子曰乳。獸曰產。从孚𠃉。𠃉者，𠃉鳥。朙堂月令「𠃉鳥至之日，祠于高禖，㠯請子」，故乳从𠃉。「請子」必㠯「𠃉至之日」者，𠃉，春分來、秋分去，開生之𠊱鳥，帝少昦司分之官也。', '孚;𠃉', '', 'null', NULL, '孚;𠃉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7704, '不', 'fǒu', '第十二卷', '不部', '鳥飛上翔不下來也。从一。一猶天也。象形。凡不之屬皆从不。', '一', '', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7705, '否', 'fǒu', '第十二卷', '不部', '不也。从口不。不亦聲。', '口;不', '不', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7706, '至', 'zhì', '第十二卷', '至部', '鳥飛從高下至地也。从一。一猶地也。象形。不，上去；而至，下來也。凡至之屬皆从至。古文至。', '一', '', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7707, '到', 'dào', '第十二卷', '至部', '至也。从至，刀聲。', '至;刀', '刀', 'null', NULL, '至', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7708, '臻', 'zhēn', '第十二卷', '至部', '至也。从至，秦聲。', '至;秦', '秦', 'null', NULL, '至', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7709, '𦥊', 'chì', '第十二卷', '至部', '忿戾也。从至，至而復孫。孫，遁也。周書曰「有夏氏之民叨𦥊」。𦥊讀若摯。', '至;孫;摯', '摯', 'null', NULL, '至;孫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7710, '臺', 'tái', '第十二卷', '至部', '觀四方而高者也。从至，从高省。與室、屋同意。之聲。', '至;高;之', '之', 'null', NULL, '至;高', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7711, '臸', 'rì', '第十二卷', '至部', '到也。从二至。', '至', '', 'null', NULL, '至', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7712, '西;棲;卤;卥', 'xī', '第十二卷', '西部', '鳥在巢上也。象形。日在西方而鳥西，故因㠯爲「東西」之西。凡西之屬皆从西。西或从木妻。古文西。籒文西。', '木;妻', '妻', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7713, '䙵', 'xī', '第十二卷', '西部', '姓也。从西，圭聲。', '西;圭', '圭', 'null', NULL, '西', 'null', 'null', 'null', 'null', '当删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7714, '鹵', 'lǔ', '第十二卷', '鹵部', '西方鹹地也。从西省。象鹽形。安定有鹵縣。東方謂之㡿，西方謂之鹵。凡鹵之屬皆从鹵。', '西', '', 'null', NULL, '西', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7715, '𪉤;𪉵', 'cuó', '第十二卷', '鹵部', '鹹𪉤也。从鹵，差省聲。河內謂之𪉤，沛人言若虘[䣜]。', '鹵;差;䣜', '差;䣜', 'null', NULL, '鹵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7716, '鹹', 'xián', '第十二卷', '鹵部', '銜也。北方味也。从鹵，咸聲。', '鹵;咸', '咸', 'null', NULL, '鹵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7717, '鹽', 'yán', '第十二卷', '鹽部', '鹵也。天生曰鹵，人生曰鹽。从鹵，監聲。古者夙沙初作䰞海鹽。凡鹽之屬皆从鹽。', '鹵;監', '監', 'null', NULL, '鹵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7718, '盬', 'ɡǔ', '第十二卷', '鹽部', '河東鹽池也。袤五十一里，廣七里，周百十六里。从鹽省，古聲。', '鹽;古', '古', 'null', NULL, '鹽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7719, '鹼', 'jiǎn', '第十二卷', '鹽部', '鹵也。从鹽省，僉聲。', '鹽;僉', '僉', 'null', NULL, '鹽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7720, '戸;戶;户;㦿', 'hù', '第十二卷', '戸部', '護也。半門曰戸。象形。凡戸之屬皆从戸。古文戸从木。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7721, '扉', 'fēi', '第十二卷', '戸部', '戸扇也。从戸，非聲。', '戸;非', '非', 'null', NULL, '戸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7722, '扇', 'shàn', '第十二卷', '戸部', '扉也。从戸羽。', '戸;羽', '', 'null', NULL, '戸;羽', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7723, '房', 'fánɡ', '第十二卷', '戸部', '室在旁也。从戸，方聲。', '戸;方', '方', 'null', NULL, '戸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7724, '戻', 'tì', '第十二卷', '戸部', '輺車旁推戸也。从戸，大聲。讀與釱同。', '戸;大;釱', '大;釱', 'null', NULL, '戸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7725, '戹;厄', 'è', '第十二卷', '戸部', '隘也。从戸，乙聲。', '戸;乙', '', 'null', NULL, '戸;乙', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7726, '肁', 'zhào', '第十二卷', '戸部', '始開也。从戸聿。', '戸;聿', '', 'null', NULL, '戸;聿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7727, '扆', 'yǐ', '第十二卷', '戸部', '戸牖之閒謂之扆。从戸，衣聲。', '戸;衣', '衣', 'null', NULL, '戸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7728, '㧁', 'qù', '第十二卷', '戸部', '閉也。从戸，劫省聲。', '戸;劫', '劫', 'null', NULL, '戸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7729, '扃', 'jiōnɡ', '第十二卷', '戸部', '外閉之關也。从戸，冋聲。', '戸;冋', '冋', 'null', NULL, '戸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7730, '門', 'mén', '第十二卷', '門部', '聞也。从二戸。象形。凡門之屬皆从門。', '戸', '', 'null', NULL, '戸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7731, '閶', 'chānɡ', '第十二卷', '門部', '閶𨵵，天門也。从門，昌聲。楚人名門皆曰閶𨵵。', '門;昌', '昌', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7732, '闈', 'wéi', '第十二卷', '門部', '宮中之門也。从門，韋聲。', '門;韋', '韋', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7733, '䦲', 'yán', '第十二卷', '門部', '䦲謂之樀。樀，廟門也。从門，詹聲。', '門;詹', '詹', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7734, '閎', 'hónɡ', '第十二卷', '門部', '䢽門也。从門，厷聲。', '門;厷', '厷', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7735, '閨', 'ɡuī', '第十二卷', '門部', '特立之戸。上圜下方，有佀圭。从門圭。圭亦聲。', '門;圭', '圭', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7736, '閤', 'ɡé', '第十二卷', '門部', '門旁戸也。从門，合聲。', '門;合', '合', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7737, '闒', 'tà', '第十二卷', '門部', '樓上戸也。从門，𦐇聲。', '門;𦐇', '𦐇', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7738, '閈', 'hàn', '第十二卷', '門部', '閭也。从門，干聲。汝南 平輿里門曰閈。', '門;干', '干', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7739, '閭;櫚', 'lǘ', '第十二卷', '門部', '里門也。从門，呂聲。周禮「五家爲比，五比爲閭」。閭，侶也。二十五家相羣侶也。', '門;呂', '呂', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, '櫚字只见于说解，今暂附识于此', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7740, '閻;壛', 'yán', '第十二卷', '門部', '里中門也。从門，臽聲。閻或从土。', '門;臽;土', '臽', 'null', NULL, '門;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7741, '闠', 'huì', '第十二卷', '門部', '市外門也。从門，貴聲。', '門;貴', '貴', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7742, '闉', 'yīn', '第十二卷', '門部', '闉闍，城曲重門也。从門，垔聲。詩曰「出其闉闍」。', '門;垔', '垔', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7743, '闍', 'dū', '第十二卷', '門部', '闉闍也。从門，者聲。', '門;者', '者', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7744, '闕', 'què', '第十二卷', '門部', '門觀也。从門，欮聲。', '門;欮', '欮', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7745, '閞', 'biàn', '第十二卷', '門部', '門欂櫨也。从門，弁聲。', '門;弁', '弁', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7746, '䦏', 'xiè', '第十二卷', '門部', '門扉也。从門，介聲。', '門;介', '介', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7747, '𨵵;闔', 'hé', '第十二卷', '門部', '門扉也。从門，盇聲。一曰閉也。', '門;盇', '盇', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7748, '闑', 'niè', '第十二卷', '門部', '門梱也。从門，臬聲。', '門;臬', '臬', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7749, '閾', 'yù', '第十二卷', '門部', '門榍也。从門，或聲。論語曰「行不履閾」。古文閾从洫。', '門;或;洫', '或;洫', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7750, '閬', 'lànɡ', '第十二卷', '門部', '門高也。从門，良聲。巴郡有閬中縣。', '門;良', '良', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7751, '闢', 'pì', '第十二卷', '門部', '開也。从門，辟聲。虞書曰「闢四門」。从門，从𡴂。', '門;辟;門;𡴂', '辟', 'null', NULL, '門;門;𡴂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7752, '䦱', 'wěi', '第十二卷', '門部', '闢門也。从門，爲聲。國語曰「䦱門而與之言」。', '門;爲', '爲', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7753, '闡', 'chǎn', '第十二卷', '門部', '開也。从門，單聲。易曰「闡幽」。', '門;單', '單', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7754, '開', 'kāi', '第十二卷', '門部', '張也。从門，开聲。古文[一廾]。', '門;开;一;廾', '开', 'null', NULL, '門;一;廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7755, '闓', 'kǎi', '第十二卷', '門部', '開也。从門，豈聲。', '門;豈', '豈', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7756, '閜', 'xiǎ', '第十二卷', '門部', '大開也。从門，可聲。大桮亦爲閜。', '門;可', '可', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7757, '閘', 'yā', '第十二卷', '門部', '開閉門也。从門，甲聲。', '門;甲', '甲', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7758, '閟', 'bì', '第十二卷', '門部', '閉門也。从門，必聲。春秋傳曰「閟門而與之言」。', '門;必', '必', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7759, '閣', 'ɡé', '第十二卷', '門部', '所㠯止扉者。从門，各聲。', '門;各', '各', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7760, '閒;間', 'jiàn', '第十二卷', '門部', '隙也。从門月。古文閒[外]。', '門;月;外', '月;外', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7761, '𨵌', 'ě', '第十二卷', '門部', '門傾也。从門，阿聲。', '門;阿', '阿', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7762, '閼', 'è', '第十二卷', '門部', '遮𢹬也。从門，於聲。', '門;於', '於', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7763, '𨷱', 'zhuǎn', '第十二卷', '門部', '開閉門戸利也。从門，䌛聲。一曰縷十紘也。', '門;䌛', '䌛', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7764, '䦪', 'yà', '第十二卷', '門部', '門聲也。从門，曷聲。', '門;曷', '曷', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7765, '䦳', 'xiànɡ', '第十二卷', '門部', '門響也。从門，鄉聲。', '門;鄉', '鄉', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7766, '闌', 'lán', '第十二卷', '門部', '門遮也。从門，柬聲。', '門;柬', '柬', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7767, '閑', 'xián', '第十二卷', '門部', '闌也。从門中有木。', '門;木', '', 'null', NULL, '門;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7768, '閉;𨳲;𨳰', 'bì', '第十二卷', '門部', '𨵵門也。从門。才，所㠯歫門也。', '門;才', '', 'null', NULL, '門;才', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7769, '閡', 'ài', '第十二卷', '門部', '外閉也。从門，亥聲。', '門;亥', '亥', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7770, '闇', 'àn', '第十二卷', '門部', '閉門也。从門，音聲。', '門;音', '音', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7771, '關', 'ɡuān', '第十二卷', '門部', '㠯木横持門戸也。从門，𢇅聲。', '門;𢇅', '𢇅', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7772, '𨷲', 'yuè', '第十二卷', '門部', '關下牡也。从門，龠聲。', '門;龠', '龠', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7773, '闐', 'tián', '第十二卷', '門部', '盛皃也。从門，眞聲。', '門;眞', '眞', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7774, '闛', 'tánɡ', '第十二卷', '門部', '闛闛，盛皃也。从門，堂聲。', '門;堂', '堂', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7775, '閹', 'yān', '第十二卷', '門部', '門豎也。宮中奄昏閉門者。从門，奄聲。', '門;奄', '奄', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7776, '閽', 'hūn', '第十二卷', '門部', '常㠯昏𨳲門隸也。从門昏。昏亦聲。', '門;昏', '昏', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7777, '闚', 'kuī', '第十二卷', '門部', '閃也。从門，規聲。', '門;規', '規', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7778, '閃', 'shǎn', '第十二卷', '門部', '闚頭門中也。从人在門中。', '人;門', '', 'null', NULL, '人;門', 'null', 'null', 'null', 'null', '调序', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7779, '𨷻', 'lán', '第十二卷', '門部', '妄入宮亦也。从門，䜌聲。讀若闌。', '門;䜌;闌', '䜌;闌', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7780, '𨳌', 'zhèn', '第十二卷', '門部', '登也。从門𠄟。𠄟，古文下字。讀若「軍敶」之敶。', '門;𠄟;敶', '敶', 'null', NULL, '門;𠄟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7781, '閱;閲', 'yuè', '第十二卷', '門部', '具數於門中也。从門，兌聲。', '門;兌', '兌', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7782, '闋', 'què', '第十二卷', '門部', '事巳𨳲門也。从門，癸聲。', '門;癸', '癸', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7783, '闞', 'kàn', '第十二卷', '門部', '望也。从門，敢聲。', '門;敢', '敢', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7784, '闊', 'kuò', '第十二卷', '門部', '疏也。从門。𣴠聲。', '門;𣴠', '𣴠', 'null', NULL, '門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7785, '閔', 'mǐn', '第十二卷', '門部', '弔者在門也。从門，文聲。古文閔[民思]。', '門;文;民;思', '文;民', 'null', NULL, '門;思', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7786, '闖', 'chèn', '第十二卷', '門部', '馬出門皃。从馬在門中。讀若郴。', '馬;門;郴', '郴', 'null', NULL, '馬;門', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7787, '闤', 'huán', '第十二卷', '門部', '市垣也。从門，瞏聲。', '門;瞏', '瞏', 'null', NULL, '門', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7788, '闥', 'tà', '第十二卷', '門部', '門也。从門，達聲。', '門;達', '達', 'null', NULL, '門', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7789, '閌', 'kānɡ', '第十二卷', '門部', '閌閬，高門也。从門，亢聲。', '門;亢', '亢', 'null', NULL, '門', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7790, '閥', 'fá', '第十二卷', '門部', '閥閱，自序也。从門，伐聲。', '門;伐', '伐', 'null', NULL, '門', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7791, '闃', 'qù', '第十二卷', '門部', '靜也。从門，狊聲。', '門;狊', '狊', 'null', NULL, '門', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7792, '耳', 'ěr', '第十二卷', '耳部', '主聽者也。象形。凡耳之屬皆从耳。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7793, '耴;𦔮', 'zhé', '第十二卷', '耳部', '耳𠂹也。从耳。𠃍，下𠂹，象形。春秋傳曰「秦公子耴」者，其耳𠂹也，故㠯爲名。', '耳', '', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7794, '𦕒', 'diān', '第十二卷', '耳部', '耳小𠂹也。从耳，占聲。', '耳;占', '占', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7795, '耽', 'dān', '第十二卷', '耳部', '耳大𠂹也。从耳，冘聲。詩曰「士之耽兮」。', '耳;冘', '冘', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7796, '聃', 'dān', '第十二卷', '耳部', '耳曼也。从耳，冉聲。聃或从甘。', '耳;冉;甘', '冉;甘', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7797, '聸', 'dān', '第十二卷', '耳部', '𠂹耳也。从耳，詹聲。南方有聸耳國。', '耳;詹', '詹', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7798, '耿', 'ɡěnɡ', '第十二卷', '耳部', '耳箸頰也。从耳，烓省聲。杜林說，耿，光也。从火。聖省聲。凡字，皆𠂇形又聲，杜說非也。', '耳;烓;火;聖', '烓;聖', 'null', NULL, '耳;火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7799, '聯;䏈', 'lián', '第十二卷', '耳部', '連也。从耳，从絲。从耳，耳連於頰。从絲，絲連不絕也。', '耳;絲', '', 'null', NULL, '耳;絲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7800, '𦕼;聊', 'liáo', '第十二卷', '耳部', '耳鳴也。从耳，丣聲。', '耳;丣', '丣', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7801, '聖', 'shènɡ', '第十二卷', '耳部', '通也。从耳。呈聲。', '耳;呈', '呈', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7802, '聰', 'cōnɡ', '第十二卷', '耳部', '察也。从耳，悤聲。', '耳;悤', '悤', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7803, '聽;𦗟', 'tīnɡ', '第十二卷', '耳部', '聆也。从耳𢛳，𡈼聲。', '耳;𢛳;𡈼', '𡈼', 'null', NULL, '耳;𢛳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7804, '聆', 'línɡ', '第十二卷', '耳部', '聽也。从耳，令聲。', '耳;令', '令', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7805, '職', 'zhí', '第十二卷', '耳部', '記𢼸也。从耳，戠聲。', '耳;戠', '戠', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7806, '聒;𦕾', 'ɡuō', '第十二卷', '耳部', '讙語也。从耳，𠯑聲。', '耳;𠯑', '𠯑', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7807, '聥', 'jǔ', '第十二卷', '耳部', '張耳有所聞也。从耳，禹聲。', '耳;禹', '禹', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7808, '聲', 'shēnɡ', '第十二卷', '耳部', '音也。从耳，殸聲。殸，籒文磬。', '耳;殸', '殸', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7809, '聞', 'wén', '第十二卷', '耳部', '知聲也。从耳，門聲。古文从昏。', '耳;門;昏', '門;昏', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7810, '聘', 'pìn', '第十二卷', '耳部', '訪也。从耳，甹聲。', '耳;甹', '甹', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7811, '聾', 'lónɡ', '第十二卷', '耳部', '無聞也。从耳，龍聲。', '耳;龍', '龍', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7812, '𢕈;聳', 'sǒnɡ', '第十二卷', '耳部', '生而聾曰𢕈。从耳，從省聲。', '耳;從', '從', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7813, '䏁', 'zǎi', '第十二卷', '耳部', '益、梁之州謂聾爲䏁。秦、晉聽而不聰、聞而不𨔶謂之䏁。从耳，宰聲。', '耳;宰', '宰', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7814, '聵', 'kuì', '第十二卷', '耳部', '聾也。从耳，貴聲。聵或从㕟。或从𧱏作。', '耳;貴;㕟;𧱏', '貴;㕟', 'null', NULL, '耳;𧱏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7815, '聉', 'wà', '第十二卷', '耳部', '無知意也。从耳，出聲。讀若孼。', '耳;出;孼', '出;孼', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7816, '䎳', 'wà', '第十二卷', '耳部', '𡐦耳也。从耳，月聲。', '耳;月', '月', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7817, '𦘍', 'wà', '第十二卷', '耳部', '吳、楚之外凡無耳者謂之𦘍。言若斷耳爲盟[䎳]。从耳，闋聲。', '耳;䎳;闋', '䎳;闋', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7818, '聅', 'chè', '第十二卷', '耳部', '軍法㠯矢毌耳也。从耳矢。', '耳;矢', '', 'null', NULL, '耳;矢', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7819, '聝;馘', 'ɡuó', '第十二卷', '耳部', '軍戰𣃔耳也。春秋傳曰「㠯爲俘聝」。从耳，或聲。聝或从首。', '耳;或;首', '或', 'null', NULL, '耳;首', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7820, '𦗕', 'mǐ', '第十二卷', '耳部', '乘輿金耳也。从耳，麻聲。讀若「渳水」。一曰若月令「靡艸」之靡。', '耳;麻;渳;靡', '麻;渳;靡', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7821, '耹', 'qín', '第十二卷', '耳部', '國語曰「回祿信於耹遂」。闕。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '应删', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7822, '聑', 'tiē', '第十二卷', '耳部', '安也。从二耳。', '耳', '', '帖', '服帖。', '耳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7823, '聶', 'niè', '第十二卷', '耳部', '駙耳私小語也。从三耳。', '耳', '', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7824, '聱', 'áo', '第十二卷', '耳部', '不聽也。从耳，敖聲。', '耳;敖', '敖', 'null', NULL, '耳', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
