INSERT INTO `shuowen_voice_revel` VALUES (8113, '姞', 'jí', '第十二卷', '女部', '黃帝之後伯䱔姓也。后稷妃家。从女，吉聲。', '女;吉', '吉', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
