INSERT INTO `shuowen_voice_revel` VALUES (8115, '姚', 'yáo', '第十二卷', '女部', '虞舜凥姚虛，因㠯爲姓。从女，兆聲。或爲「姚，嬈也」。史篇㠯爲「姚，易也」。', '女;兆', '兆', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8116, '嬀', 'ɡuī', '第十二卷', '女部', '虞舜凥嬀汭，因㠯爲氏。从女。爲聲。', '女;爲', '爲', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8117, '妘', 'yún', '第十二卷', '女部', '祝融之後姓也。从女，云聲。籒文妘从員。', '女;云;員', '云;員', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8118, '姺', 'shēn', '第十二卷', '女部', '殷諸侯爲亂，疑姓也。从女，先聲。春秋傳曰商有姺、邳。', '女;先', '先', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8119, '㜣', 'niàn', '第十二卷', '女部', '人姓也。从女，然聲。', '女;然', '然', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8120, '𡚽', 'hào', '第十二卷', '女部', '人姓也。从女，丑聲。商書曰「無有作𡚽」。', '女;丑', '丑', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8121, '娸', 'qī', '第十二卷', '女部', '人姓也。从女，其聲。杜林說「娸，醜也」。', '女;其', '其', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8122, '奼', 'chà', '第十二卷', '女部', '少女也。从女，乇聲。', '女;乇', '乇', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8123, '媒', 'méi', '第十二卷', '女部', '謀也。謀合二姓者也。从女，某聲。', '女;某', '某', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8124, '妁', 'shuò', '第十二卷', '女部', '酌也。斟酌二姓者也。从女，勺聲。', '女;勺', '勺', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8125, '嫁', 'jià', '第十二卷', '女部', '女適人也。从女，家聲。', '女;家', '家', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8126, '娶', 'qǔ', '第十二卷', '女部', '取婦也。从女，取聲。', '女;取', '取', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8127, '婚;𤔿;𡖀', 'hūn', '第十二卷', '女部', '婦家也。禮「娶婦㠯昏時，婦人侌也，故曰婚」。从女昏。昏亦聲。籒文婚如此[未详]。', '女;昏', '昏', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8128, '姻', 'yīn', '第十二卷', '女部', '壻家也。女之所因故曰姻。从女因。因亦聲。籒文姻从𣶒。', '女;因;𣶒', '因;𣶒', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8129, '妻', 'qī', '第十二卷', '女部', '婦與己齊者也。从女，从屮，从又。又，持事，妻職也。屮聲。古文妻从𡭙女。𡭙，古文貴字。', '女;屮;又;𡭙', '屮;𡭙', 'null', NULL, '女;又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8130, '婦', 'fù', '第十二卷', '女部', '服也。从女持帚，灑埽也。', '女;帚', '', 'null', NULL, '女;帚', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8131, '妃', 'fēi', '第十二卷', '女部', '匹也。从女己[卪]。', '女;卪', '', 'null', NULL, '女;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8132, '媲', 'pì', '第十二卷', '女部', '妃也。从女，𣬉聲。', '女;𣬉', '𣬉', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8133, '妊', 'rèn', '第十二卷', '女部', '孕也。从女壬。壬亦聲。', '女;壬', '壬', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8134, '娠', 'shēn', '第十二卷', '女部', '女妊身動也。从女，辰聲。春秋傳曰「后緍方娠」。一曰官婢女隷謂之娠。', '女;辰', '辰', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8135, '媰', 'chú', '第十二卷', '女部', '婦人妊娠也。从女，芻聲。周書曰「至于媰婦」。', '女;芻', '芻', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8136, '嬔;嬎', 'fàn', '第十二卷', '女部', '生子齊均也。从女免生。讀若幡。', '女;免;生;幡', '幡', 'null', NULL, '女;免;生', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8137, '嫛', 'yī', '第十二卷', '女部', '嫛婗也。从女，殹聲。', '女;殹', '殹', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8138, '婗', 'ní', '第十二卷', '女部', '嫛婗也。从女，兒聲。一曰婦人惡皃。', '女;兒', '兒', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8139, '母', 'mǔ', '第十二卷', '女部', '牧也。从女。象褱子形。一曰象乳子也。', '女', '', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8140, '嫗', 'yù', '第十二卷', '女部', '母也。从女，區聲。', '女;區', '區', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8141, '媼;媪', 'ǎo', '第十二卷', '女部', '母老偁也。从女，𥁕聲。讀若奥。', '女;𥁕;奥', '𥁕;奥', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8142, '姁', 'xǔ', '第十二卷', '女部', '嫗也。从女，句聲。', '女;句', '句', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8143, '姐', 'jiě', '第十二卷', '女部', '蜀人謂母曰姐。淮南謂之社。从女，且聲。讀若左。', '女;且;左', '且;左', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8144, '姑', 'ɡū', '第十二卷', '女部', '夫母也。从女，古聲。', '女;古', '古', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8145, '威', 'wēi', '第十二卷', '女部', '姑也。从女，戌聲。漢律曰「婦告威姑」。', '女;戌', '戌', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8146, '妣', 'bǐ', '第十二卷', '女部', '殁母也。从女，比聲。籒文妣省[匕]。', '女;比;匕', '比;匕', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8147, '姊', 'zǐ', '第十二卷', '女部', '女兄也。从女，𠂔聲。', '女;𠂔', '𠂔', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8148, '妹', 'mèi', '第十二卷', '女部', '女弟也。从女，未聲。', '女;未', '未', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8149, '娣', 'dì', '第十二卷', '女部', '同夫之女弟也。从女，弟聲。', '女;弟', '弟', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8150, '媦', 'wèi', '第十二卷', '女部', '楚人謂女弟曰媦。从女，胃聲。春秋公羊傳曰「楚王之妻媦」。', '女;胃', '胃', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8151, '㛮;嫂', 'sǎo', '第十二卷', '女部', '兄妻也。从女，叜聲。', '女;叜', '叜', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8152, '姪', 'zhí', '第十二卷', '女部', '女子謂兄弟之子也。从女。至聲。', '女;至', '至', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8153, '姨', 'yí', '第十二卷', '女部', '妻之女弟同出爲姨。从女，夷聲。', '女;夷', '夷', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8154, '妿', 'ē', '第十二卷', '女部', '女師也。从女，加聲。杜林說「加敎於女也」。讀若阿。', '女;加;阿', '加;阿', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8155, '娒', 'mǔ', '第十二卷', '女部', '女師也。从女，每聲。讀若母同。', '女;每;母', '每;母', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8156, '媾', 'ɡòu', '第十二卷', '女部', '重婚也。从女，冓聲。易曰「匪寇婚媾」。', '女;冓', '冓', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8157, '姼', 'chǐ', '第十二卷', '女部', '美女也。从女，多聲。姼或从氏。', '女;多;氏', '多;氏', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8158, '妭', 'bá', '第十二卷', '女部', '美婦也。从女，犮聲。', '女;犮', '犮', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
