INSERT INTO `shuowen_voice_revel` VALUES (8160, '婢', 'bì', '第十二卷', '女部', '女之卑者也。从女卑。卑亦聲。', '女;卑', '卑', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8161, '奴', 'nú', '第十二卷', '女部', '奴婢皆古辠人。周禮曰「其奴男子入于辠隷，女子入于舂稾」。从女又。古文奴[人女]。', '女;又;人', '女', 'null', NULL, '又;人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8162, '㚤', 'yì', '第十二卷', '女部', '婦官也。从女，弋聲。', '女;弋', '弋', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8163, '媊', 'qián', '第十二卷', '女部', '甘氏星經曰「大白號上公，妻曰女媊，凥南斗食厲，天下祭之曰『朙星』」。从女，前聲。', '女;前', '前', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8164, '媧', 'wā', '第十二卷', '女部', '古之神聖女，化萬物者也。从女，咼聲。籒文媧从𩰫。', '女;咼;𩰫', '咼;𩰫', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8165, '娀', 'sōnɡ', '第十二卷', '女部', '帝高辛之妃，偰母號也。从女，戎聲。詩曰「有娀方將」。', '女;戎', '戎', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8166, '娥', 'é', '第十二卷', '女部', '帝堯之女，舜妻娥皇字也。从女，我聲。秦、晉謂好曰「娙娥」。', '女;我', '我', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8167, '嫄', 'yuán', '第十二卷', '女部', '邰國之女，周 棄母字也。从女，原聲。', '女;原', '原', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8168, '嬿', 'yàn', '第十二卷', '女部', '女字也。从女，燕聲。', '女;燕', '燕', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8169, '妸', 'ē', '第十二卷', '女部', '女字也。从女，可聲。讀若阿。', '女;可;阿', '可;阿', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8170, '嬃', 'xū', '第十二卷', '女部', '女字也。从女，須聲。楚䛐曰「女嬃之嬋媛」。賈侍中說，楚人謂姊爲嬃。', '女;須', '須', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8171, '婕', 'jié', '第十二卷', '女部', '女字也。从女，疌聲。', '女;疌', '疌', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
