INSERT INTO `shuowen_voice_revel` VALUES (8322, '娎', 'xiè', '第十二卷', '女部', '娎，㛍也。从女，折聲。', '女;折', '折', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8323, '㛍', 'qiè', '第十二卷', '女部', '得志㛍㛍也。从女，夾聲。一曰㛍，息也。一曰少气也。', '女;夾', '夾', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8324, '嬈', 'niǎo', '第十二卷', '女部', '苛也。一曰𢺕也，戲弄也。从女，堯聲。一曰嬥也。', '女;堯', '堯', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8325, '𡢕', 'huǐ', '第十二卷', '女部', '惡也。一曰人皃也。从女，毀聲。', '女;毀', '毀', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8326, '姍;姗', 'shān', '第十二卷', '女部', '誹也。从女，刪省聲。一曰翼便也。', '女;刪', '刪', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8327, '𡞜', 'cù', '第十二卷', '女部', '醜也。一曰老嫗也。从女，酋聲。讀若蹴。', '女;酋;蹴', '酋;蹴', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8328, '𡠜;嫫', 'mó', '第十二卷', '女部', '𡠜母，古帝妃，都醜也。从女，莫聲。', '女;莫', '莫', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8329, '婓', 'fēi', '第十二卷', '女部', '往來婓婓也。从女，非聲。一曰大醜皃。', '女;非', '非', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8330, '孃', 'ránɡ', '第十二卷', '女部', '煩𢺕也。一曰肥大也。从女，襄聲。', '女;襄', '襄', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8331, '嬒', 'huì', '第十二卷', '女部', '女黑色也。从女，會聲。詩曰「嬒兮蔚兮」。', '女;會', '會', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8332, '媆', 'ruǎn', '第十二卷', '女部', '好皃。从女，耎聲。', '女;耎', '耎', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8333, '㛪', 'yàn', '第十二卷', '女部', '誣挐也。从女，奄聲。', '女;奄', '奄', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8334, '㜮', 'làn', '第十二卷', '女部', '過差也。从女，監聲。論語曰「小人竆斯㜮矣」。', '女;監', '監', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8335, '嫯', 'ào', '第十二卷', '女部', '侮㑥也。从女，敖聲。', '女;敖', '敖', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8336, '婬', 'yín', '第十二卷', '女部', '厶逸也。从女，㸒聲。', '女;㸒', '㸒', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8337, '姘', 'pīn', '第十二卷', '女部', '除也。从女，并聲。漢律「齊民與妻婢姦曰姘」。', '女;并', '并', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8338, '奸', 'jiān', '第十二卷', '女部', '犯婬也。从女，干聲。', '女;干', '干', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8339, '姅', 'bàn', '第十二卷', '女部', '婦人污也。从女，半聲。漢律曰「見姅變不得侍祠」。', '女;半', '半', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8340, '娗', 'tǐnɡ', '第十二卷', '女部', '女出病也。从女，廷聲。', '女;廷', '廷', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8341, '婥', 'nào', '第十二卷', '女部', '女病也。从女，卓聲。', '女;卓', '卓', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8342, '娷', 'zhuì', '第十二卷', '女部', '餧也。从女，垂聲。', '女;垂', '垂', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8343, '㛴', 'nǎo', '第十二卷', '女部', '有所恨痛也。从女，匘省聲。今汝南人有所恨言大㛴。', '女;匘', '匘', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8344, '媿;愧', 'kuì', '第十二卷', '女部', '慙也。从女，鬼聲。媿或从恥省。', '女;鬼;恥', '鬼', 'null', NULL, '女;恥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8345, '奻', 'nuán', '第十二卷', '女部', '訟也。从二女。', '女', '', 'null', NULL, '女', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8346, '姦', 'jiān', '第十二卷', '女部', '厶也。从三女。古文姦从旱心。', '女;旱;心', '旱', 'null', NULL, '女;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8347, '妥', 'tuǒ', '第十二卷', '女部', '安也。从[𢏒[𠂹]]女。妥與安同意。', '𠂹;女', '𠂹', 'null', NULL, '女', 'null', 'null', 'null', 'null', '应补 | 🤰腿', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8348, '嬙', 'qiánɡ', '第十二卷', '女部', '婦官也。从女，牆省聲。', '女;牆', '牆', 'null', NULL, '女', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8349, '妲', 'dá', '第十二卷', '女部', '女字。从女，旦聲。', '女;旦', '旦', 'null', NULL, '女', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8350, '嬌', 'jiāo', '第十二卷', '女部', '姿也。从女，喬聲。', '女;喬', '喬', 'null', NULL, '女', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8351, '嬋', 'chán', '第十二卷', '女部', '嬋娟，態也。从女，單聲。', '女;單', '單', 'null', NULL, '女', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8352, '娟', 'juān', '第十二卷', '女部', '嬋娟也。从女，肙聲。', '女;肙', '肙', 'null', NULL, '女', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8353, '嫠', 'lí', '第十二卷', '女部', '無夫也。从女，𠩺聲。', '女;𠩺', '𠩺', 'null', NULL, '女', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8354, '姤', 'ɡòu', '第十二卷', '女部', '偶也。从女，后聲。', '女;后', '后', 'null', NULL, '女', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8355, '毋', 'wú', '第十二卷', '毋部', '止之䛐也。从女一。女有姦之者，一，禁止之，令勿姦也。凡毋之屬皆从毋。', '女;一', '', 'null', NULL, '女;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8356, '毐', 'ǎi', '第十二卷', '毋部', '士之無行者。从士毋。賈侍中說。秦始皇母與嫪毐婬，坐誅，故世罵婬曰「嫪毐」。讀若娭。', '士;毋;娭', '娭', 'null', NULL, '士;毋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8357, '民', 'mín', '第十二卷', '民部', '眾𦹩也。从古文之象。凡民之屬皆从民。古文民。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8358, '氓', 'ménɡ', '第十二卷', '民部', '民也。从民，亡聲。讀若盲。', '民;亡;盲', '亡;盲', 'null', NULL, '民', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8359, '丿', 'piě', '第十二卷', '丿部', '又戾也。象𠂇引之形。凡丿之屬皆从丿。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8360, '乂;刈', 'yì', '第十二卷', '丿部', '芟艸也。从丿乀相交。乂或从刀。', '刀', '', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8361, '弗', 'fú', '第十二卷', '丿部', '矯也。从丿乀，从韋省。', '丿;乀;韋', '', 'null', NULL, '丿;乀;韋', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8362, '乀', 'fú', '第十二卷', '丿部', '𠂇戾也。从反丿。讀與弗同。', '丿;弗', '弗', 'null', NULL, '丿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8363, '𠂆', 'yì', '第十二卷', '𠂆部', '抴也。朙也。象抴引之形。凡𠂆之屬皆从𠂆。虒字从此。', '虒', '虒', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8364, '弋', 'yì', '第十二卷', '𠂆部', '橜也。象折木衺銳者形。𠂆，象物挂之也。', '𠂆', '', 'null', NULL, '𠂆', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8365, '乁', 'yí', '第十二卷', '乁部', '流也。从反𠂆。讀若移。凡乁之屬皆从乁。', '𠂆;移', '移', 'null', NULL, '𠂆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8366, '也', 'yě', '第十二卷', '乁部', '女侌也。从乁。象形。乁亦聲。秦刻石也字。', '乁', '乁', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8367, '氏', 'shì', '第十二卷', '氏部', '巴蜀名山岸脅之𠂤旁箸欲落𡐦者曰氏。氏崩，聲聞數百里。象形。乁聲。凡氏之屬皆从氏。楊雄賦「響若氏隤」。', '乁', '乁', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8368, '氒', 'jué', '第十二卷', '氏部', '木本也。从氏。下本大於末也。讀若厥。', '氏;厥', '厥', 'null', NULL, '氏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8369, '氐', 'dǐ', '第十二卷', '氐部', '至也，本也。从氏。下箸一。一，地也。凡氐之屬皆从氐。', '氏;一', '', 'null', NULL, '氏;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8370, '𣱐', 'yìn', '第十二卷', '氐部', '臥也。从氐，垔聲。', '氐;垔', '垔', 'null', NULL, '氐', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8371, '㲳', 'dié', '第十二卷', '氐部', '觸也。从氐，失聲。', '氐;失', '失', 'null', NULL, '氐', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8372, '𣱓', 'xiào', '第十二卷', '氐部', '闕。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8373, '戈', 'ɡē', '第十二卷', '戈部', '平頭𢧢也。从弋，一衡之。象形。凡戈之屬皆从戈。', '弋;一', '', 'null', NULL, '弋;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8374, '肈', 'zhào', '第十二卷', '戈部', '上諱。[从戈，肁聲。]', '戈;肁', '肁', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8375, '𢧢;戟;㦸', 'jǐ', '第十二卷', '戈部', '有枝兵也。从戈，榦省。周禮「𢧢長丈六尺」。', '戈;榦', '', 'null', NULL, '戈;榦', 'null', 'null', 'null', 'null', '调序', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8376, '戛', 'jiá', '第十二卷', '戈部', '𢧢也。从戈𦣻。讀若棘。', '戈;𦣻', '', 'null', NULL, '戈;𦣻', 'null', 'null', 'null', 'null', '调序', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8377, '戎', 'rónɡ', '第十二卷', '戈部', '兵也。从戈甲。古文甲字。', '戈;甲', '', 'null', NULL, '戈;甲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8378, '戣', 'kuí', '第十二卷', '戈部', '周制「侍臣執戣，立於東垂」，兵也。从戈，癸聲。', '戈;癸', '癸', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8379, '𢧀', 'ɡān', '第十二卷', '戈部', '盾也。从戈，旱聲。', '戈;旱', '旱', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8380, '賊', 'zéi', '第十二卷', '戈部', '敗也。从戈，則聲。', '戈;則', '則', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8381, '戍', 'shù', '第十二卷', '戈部', '守邊也。从人持戈。', '人;戈', '', 'null', NULL, '人;戈', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8382, '戰', 'zhàn', '第十二卷', '戈部', '鬥也。从戈，單聲。', '戈;單', '單', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8383, '戲', 'xì', '第十二卷', '戈部', '三軍之偏也。一曰兵也。从戈，䖒聲。', '戈;䖒', '䖒', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8384, '𢧄', 'dié', '第十二卷', '戈部', '利也。一曰剔也。从戈，呈聲。', '戈;呈', '呈', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8385, '或;域', 'yù', '第十二卷', '戈部', '邦也。从囗，戈㠯守其一。一，地也。或或从土。', '囗;戈;一;土', '', 'null', NULL, '囗;戈;一;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8386, '𢧵', 'jié', '第十二卷', '戈部', '𣃔也。从戈，雀聲。', '戈;雀', '雀', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8387, '𢦟', 'kān', '第十二卷', '戈部', '殺也。从戈，今聲。商書曰「西伯旣𢦟黎」。', '戈;今', '今', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8388, '戕', 'qiānɡ', '第十二卷', '戈部', '槍也。它國臣來弑君曰戕。从戈，爿聲。', '戈;爿', '爿', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8389, '戮', 'lù', '第十二卷', '戈部', '殺也。从戈，翏聲。', '戈;翏', '翏', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8390, '戡', 'kān', '第十二卷', '戈部', '刺也。从戈，甚聲。', '戈;甚', '甚', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8391, '戭', 'yǎn', '第十二卷', '戈部', '長槍也。从戈，寅聲。春秋傳有「檮戭」。', '戈;寅', '寅', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8392, '𢦒', 'zāi', '第十二卷', '戈部', '傷也。从戈，才聲。', '戈;才', '才', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8393, '戩', 'jiǎn', '第十二卷', '戈部', '滅也。从戈，晉聲。詩曰「實始戩商」。', '戈;晉', '晉', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '△', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8394, '㦰', 'jiān', '第十二卷', '戈部', '絕也。从从，持戈。一曰田器古文。讀若咸。一曰讀若詩「攕攕女手」。', '从;戈;咸;攕', '咸;攕', 'null', NULL, '从;戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8395, '武', 'wǔ', '第十二卷', '戈部', '楚莊王曰「夫武定功戢兵，故止戈爲武」。', '止;戈', '', 'null', NULL, '止;戈', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8396, '戢', 'jí', '第十二卷', '戈部', '臧兵也。从戈，咠聲。詩曰「載戢干戈」。', '戈;咠', '咠', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8397, '戠', 'zhī', '第十二卷', '戈部', '闕。从戈，从音。', '戈;音', '', 'null', NULL, '戈;音', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8398, '戔', 'cán', '第十二卷', '戈部', '賊也。从二戈。周書曰「戔戔」，巧言也。', '戈', '', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8399, '戉', 'yuè', '第十二卷', '戉部', '大斧也。从戈，𠄌聲。司馬法曰「夏執玄戉；殷執白戚；周𠂇杖黃戉，又把白髦」。凡戉之屬皆从戉。', '戈;𠄌', '𠄌', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8400, '戚', 'qī', '第十二卷', '戉部', '戉也。从戉，尗聲。', '戉;尗', '尗', 'null', NULL, '戉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8401, '我', 'wǒ', '第十二卷', '我部', '施身自謂也。或說我，頃頓也。从戈[𢏒省聲]。，古文𠂹也。一曰古文殺字。凡我之屬皆从我。古文我[𢏒省聲]。', '戈;𢏒;殺', '𢏒;殺', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8402, '義;羛', 'yí', '第十二卷', '我部', '己之威義也。从我，从羊。墨翟書「義」从弗。巍郡有羛陽鄉，讀若錡。今屬鄴，本內黃北二十里鄉也。', '我;羊;弗;錡', '義;錡', 'null', NULL, '我;羊;弗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8403, '亅', 'jué', '第十二卷', '亅部', '鉤逆者謂之亅。象形。凡亅之屬皆从亅。讀若橜。', '橜', '橜', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8404, '𠄌', 'jué', '第十二卷', '亅部', '鉤識也。从反亅。讀若窡。', '亅;窡', '窡', 'null', NULL, '亅', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8405, '琴', 'qín', '第十二卷', '琴部', '禁也。神農所作，洞越，練朱五弦，周時加二弦。象形。凡琴之屬皆从琴。古文琴从金。', '禁;金', '禁;金', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8406, '瑟', 'sè', '第十二卷', '琴部', '庖犧所作弦樂也。从琴，必聲。古文瑟。', '琴;必', '必', 'null', NULL, '琴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8407, '琵', 'pí', '第十二卷', '琴部', '琵琶，樂器。从琴，比聲。', '琴;比', '比', 'null', NULL, '琴', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8408, '琶', 'pá', '第十二卷', '琴部', '琵琶也。从琴，巴聲。', '琴;巴', '巴', 'null', NULL, '琴', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8409, '𠃊', 'yǐn', '第十二卷', '𠃊部', '匿也。象𨒅曲隱蔽形。凡𠃊之屬皆从𠃊。讀若隱。', '隱', '隱', 'null', NULL, '', 'null', 'null', 'null', 'null', '⚙️', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8410, '直', 'zhí', '第十二卷', '𠃊部', '正見也。从十目𠃊。古文直或从木如此。', '十;目;𠃊;木', '', 'null', NULL, '十;目;𠃊;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8411, '亡', 'wánɡ', '第十二卷', '亡部', '逃也。从入𠃊。凡亡之屬皆从亡。', '入;𠃊', '', 'null', NULL, '入;𠃊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8412, '乍', 'zhà', '第十二卷', '亡部', '止亡䛐也。从亡一。一，有所礙也。', '亡;一', '', 'null', NULL, '亡;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8413, '望', 'wànɡ', '第十二卷', '亡部', '出亡在外，望其還也。从亡，朢省聲。', '亡;朢', '朢', 'null', NULL, '亡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8414, '無;无', 'wú', '第十二卷', '亡部', '亡也。从亡，𣞤聲。奇字無也。通於元者，虛无道也。王育說，天屈西北爲无。', '亡;𣞤;无', '𣞤;无', 'null', NULL, '亡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8415, '匃;匄', 'ɡài', '第十二卷', '亡部', '气也。亡人爲匄，逯安說。', '亡;人', '', 'null', NULL, '亡;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8416, '匸', 'xì', '第十二卷', '匸部', '衺徯有所㚒臧也。从𠃊，上有一覆之。凡匸之屬皆从匸。讀若徯同。', '𠃊;一;徯', '徯', 'null', NULL, '𠃊;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8417, '區', 'qū', '第十二卷', '匸部', '踦區，臧隱也。从品在匸中。品，衆也。', '品;匸', '', 'null', NULL, '品;匸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8418, '匿', 'nì', '第十二卷', '匸部', '亡也。从匸，若聲。讀若「羊騶箠[笍]」。', '匸;若;笍', '若;笍', 'null', NULL, '匸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8419, '㔷', 'lòu', '第十二卷', '匸部', '側㔷也。从匸，丙聲。一曰箕屬。', '匸;丙', '丙', 'null', NULL, '匸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8420, '匽', 'yǎn', '第十二卷', '匸部', '匿也。从匸，妟聲。', '匸;妟', '妟', 'null', NULL, '匸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8421, '医', 'yì', '第十二卷', '匸部', '臧弓弩矢器也。从匸矢。矢亦聲。春秋國語曰「兵不解医」。', '匸;矢', '矢', 'null', NULL, '匸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8422, '匹', 'pǐ', '第十二卷', '匸部', '四丈也。从匸八。八揲一匹。八亦聲。', '匸;八', '八', 'null', NULL, '匸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8423, '匚', 'fānɡ', '第十二卷', '匚部', '受物之器。象形。凡匚之屬皆从匚。讀若方。', '方', '方', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8424, '匠', 'jiànɡ', '第十二卷', '匚部', '木工也。从匚斤。斤，所㠯作器也。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8425, '匧', 'qiè', '第十二卷', '匚部', '椷。臧也。从匚，夾聲。', '匚;夾', '夾', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '政宇重文应独立', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8426, '匡', 'kuānɡ', '第十二卷', '匚部', '飯器。从匚，㞷聲。', '匚;㞷', '㞷', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '政宇重文应独立', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8427, '匜', 'yí', '第十二卷', '匚部', '佀羹魁。柄中有道，可㠯注水酒。从匚，也聲。', '匚;也', '也', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8428, '匴', 'suǎn', '第十二卷', '匚部', '淥米籔也。从匚，算聲。', '匚;算', '算', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8429, '㔶;㯯', 'ɡònɡ', '第十二卷', '匚部', '小桮也。从匚，贛聲。㔶或从木。', '匚;贛;木', '贛', 'null', NULL, '匚;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8430, '匪', 'fěi', '第十二卷', '匚部', '器佀竹匧。从匚，非聲。逸周書曰「實玄黃于匪」。', '匚;非', '非', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8431, '𠥐', 'cānɡ', '第十二卷', '匚部', '古器也。从匚，倉聲。', '匚;倉', '倉', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8432, '𠤼', 'tiáo', '第十二卷', '匚部', '田器也。从匚，攸聲。', '匚;攸', '攸', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8433, '㔴', 'yì', '第十二卷', '匚部', '田器也。从匚，異聲。', '匚;異', '異', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8434, '匫', 'hū', '第十二卷', '匚部', '古器也。从匚，曶聲。', '匚;曶', '曶', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8435, '匬', 'yǔ', '第十二卷', '匚部', '甌匬器也。从匚，俞聲。', '匚;俞', '俞', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8436, '匱', 'kuì', '第十二卷', '匚部', '匣也。从匚，貴聲。', '匚;貴', '貴', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8437, '匵', 'dú', '第十二卷', '匚部', '匱也。从匚，𧷏聲。', '匚;𧷏', '𧷏', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8438, '匣', 'xiá', '第十二卷', '匚部', '匱也。从匚，甲聲。', '匚;甲', '甲', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8439, '匯', 'huì', '第十二卷', '匚部', '器也。从匚，淮聲。', '匚;淮', '淮', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8440, '匛;柩', 'jiù', '第十二卷', '匚部', '棺也。从匚，久聲。匛或从木。籒文从舊。', '匚;久;木;舊', '久;舊', 'null', NULL, '匚;木', 'null', 'null', 'null', 'null', '篆文应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8441, '匰', 'dān', '第十二卷', '匚部', '宗廟盛宔器也。从匚，單聲。周禮曰「祭祀共匰主」。', '匚;單', '單', 'null', NULL, '匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8442, '曲', 'qū', '第十二卷', '曲部', '象器曲受物之形也。凡曲之屬皆从曲。或說曲，蠶薄也。古文曲。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8443, '㻃', 'qū', '第十二卷', '曲部', '骫曲也。从曲。玉聲。', '曲;玉', '玉', 'null', NULL, '曲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8444, '𤔱', 'tāo', '第十二卷', '曲部', '古器也。从曲，舀聲。', '曲;舀', '舀', 'null', NULL, '曲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8445, '甾', 'zī', '第十二卷', '甾部', '東楚名缶曰甾。象形也。凡甾之屬皆从甾。古文甾。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8446, '疀', 'chā', '第十二卷', '甾部', '𣂁也。古田器也。从甾，疌聲。', '甾;疌', '疌', 'null', NULL, '甾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8447, '𤲙;𠫻;𡘞;畚', 'běn', '第十二卷', '甾部', '蒲器也。𢆟屬。所㠯盛糧。从甾，弁聲。', '甾;弁', '弁', 'null', NULL, '甾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8448, '𤳊;𢆟', 'pínɡ', '第十二卷', '甾部', '㠷也。从甾，并聲。杜林㠯爲竹筥。楊雄㠯爲蒲器。', '甾;并', '并', 'null', NULL, '甾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8449, '𧆭;罏', 'lú', '第十二卷', '甾部', '𦉈也。从甾，虍聲。讀若盧同。籒文𧆭如此[缶盧]。篆文。', '甾;虍;缶;盧', '虍;盧', 'null', NULL, '甾;缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8450, '瓦', 'wǎ', '第十二卷', '瓦部', '土器巳燒之總名。象形也。凡瓦之屬皆从瓦。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8451, '瓬', 'fǎnɡ', '第十二卷', '瓦部', '周家「搏埴之工」也。从瓦，方聲。讀若「㧚破」之㧚。', '瓦;方', '方', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8452, '甄', 'zhēn', '第十二卷', '瓦部', '匋也。从瓦，垔聲。', '瓦;垔', '垔', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8453, '甍', 'ménɡ', '第十二卷', '瓦部', '屋棟也。从瓦，夢省聲。', '瓦;夢', '夢', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8454, '甑', 'zènɡ', '第十二卷', '瓦部', '甗也。从瓦，曾聲。籒文甑从䰜。', '瓦;曾;䰜', '曾', 'null', NULL, '瓦;䰜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8455, '甗', 'yǎn', '第十二卷', '瓦部', '甑也。一穿。从瓦，鬳聲。讀若言。', '瓦;鬳;言', '鬳;言', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8456, '瓵', 'yí', '第十二卷', '瓦部', '甌瓿謂之瓵。从瓦，台聲。', '瓦;台', '台', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8457, '瓽', 'dànɡ', '第十二卷', '瓦部', '大盆也。从瓦，尙聲。', '瓦;尙', '尙', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8458, '甌', 'ōu', '第十二卷', '瓦部', '小盆也。从瓦，區聲。', '瓦;區', '區', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8459, '瓮', 'wènɡ', '第十二卷', '瓦部', '罌也。从瓦，公聲。[讀若翁。]', '瓦;公;翁', '公;翁', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8460, '瓨', 'xiánɡ', '第十二卷', '瓦部', '佀罌。長頸。受十升。从瓦，工聲。讀若洪。', '瓦;工;洪', '工;洪', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8461, '㼝', 'wǎn', '第十二卷', '瓦部', '小盂也。从瓦，夗聲。', '瓦;夗', '夗', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8462, '瓴', 'línɡ', '第十二卷', '瓦部', '𦉥也。佀缾者。从瓦，令聲。', '瓦;令', '令', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8463, '㼰', 'pí', '第十二卷', '瓦部', '罌謂之㼰。从瓦，卑聲。', '瓦;卑', '卑', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8464, '甂', 'biān', '第十二卷', '瓦部', '佀小瓿。大口而卑。用食。从瓦，扁聲。', '瓦;扁', '扁', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8465, '瓿', 'bù', '第十二卷', '瓦部', '甂也。从瓦，咅聲。', '瓦;咅', '咅', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8466, '㼸', 'rónɡ', '第十二卷', '瓦部', '器也。从瓦，容聲。', '瓦;容', '容', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8467, '甓', 'pì', '第十二卷', '瓦部', '令適也。从瓦，辟聲。詩曰「中唐有甓」。', '瓦;辟', '辟', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8468, '甃', 'zhòu', '第十二卷', '瓦部', '井壁也。从瓦，秋聲。', '瓦;秋', '秋', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8469, '甈', 'qì', '第十二卷', '瓦部', '康瓠。破罌也。从瓦，臬聲。甈或从埶。', '瓦;臬;埶', '臬;埶', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8470, '㼽', 'chuǎnɡ', '第十二卷', '瓦部', '瑳垢瓦石也。从瓦，爽聲。', '瓦;爽', '爽', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8471, '㼲', 'liè', '第十二卷', '瓦部', '蹈瓦聲㼲㼲也。从瓦，耎聲。', '瓦;耎', '耎', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8472, '𤬯', 'hán', '第十二卷', '瓦部', '冶槖榦也。从瓦，今聲。', '瓦;今', '今', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8473, '𤭢', 'suì', '第十二卷', '瓦部', '破也。从瓦，卒聲。', '瓦;卒', '卒', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8474, '瓪', 'bǎn', '第十二卷', '瓦部', '敗瓦也。从瓦，反聲。', '瓦;反', '反', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8475, '瓷', 'cí', '第十二卷', '瓦部', '瓦器。从瓦，次聲。', '瓦;次', '次', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8476, '瓻', 'chī', '第十二卷', '瓦部', '酒器。从瓦，稀省聲。', '瓦;稀', '稀', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8477, '弓', 'ɡōnɡ', '第十二卷', '弓部', '竆也。㠯近竆遠者。象形。古者揮作弓。周禮「六弓」——王弓、弧弓㠯射甲革甚質；夾弓、庾弓㠯射干侯鳥獸；唐弓、大弓㠯授學射者。凡弓之屬皆从弓。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8478, '弴', 'dūn', '第十二卷', '弓部', '畫弓也。从弓，𦎫聲。', '弓;𦎫', '𦎫', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8479, '弭', 'mǐ', '第十二卷', '弓部', '弓無緣可㠯解𦆕紛者。从弓，耳聲。弭或从兒。', '弓;耳;兒', '耳;兒', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8480, '弲', 'xuān', '第十二卷', '弓部', '角弓也。雒陽名弩曰弲。从弓，肙聲。', '弓;肙', '肙', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8481, '弧', 'hú', '第十二卷', '弓部', '木弓也。从弓，瓜聲。一曰往體寡、來體多曰「弧弓」。', '弓;瓜', '瓜', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8482, '弨', 'chāo', '第十二卷', '弓部', '弓反也。从弓，召聲。詩曰「彤弓弨兮」。', '弓;召', '召', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8483, '𢑆', 'quán', '第十二卷', '弓部', '弓曲也。从弓，雚聲。', '弓;雚', '雚', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8484, '彄', 'kōu', '第十二卷', '弓部', '弓弩耑。所凥也。从弓，區聲。', '弓;區', '區', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8485, '𢑈', 'yáo', '第十二卷', '弓部', '弓便利也。从弓，䌛聲。讀若燒。', '弓;䌛;燒', '䌛;燒', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8486, '張', 'zhānɡ', '第十二卷', '弓部', '𢻱弓弦也。从弓，長聲。', '弓;長', '長', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8487, '彏', 'jué', '第十二卷', '弓部', '弓急張也。从弓，矍聲。', '弓;矍', '矍', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8488, '弸', 'pénɡ', '第十二卷', '弓部', '弓彊皃。从弓，朋聲。', '弓;朋', '朋', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8489, '彊', 'qiánɡ', '第十二卷', '弓部', '弓有力也。从弓，畺聲。', '弓;畺', '畺', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8490, '彎', 'wān', '第十二卷', '弓部', '持弓關矢也。从弓，䜌聲。', '弓;䜌', '䜌', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8491, '引', 'yǐn', '第十二卷', '弓部', '開弓也。从弓丨。', '弓;丨', '', 'null', NULL, '弓;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8492, '弙', 'wū', '第十二卷', '弓部', '滿弓有所鄉也。从弓，于聲。', '弓;于', '于', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8493, '弘', 'hónɡ', '第十二卷', '弓部', '弓聲也。从弓，厶聲。厶，古文厷字。', '弓;厶', '厶', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8494, '瓕', 'mí', '第十二卷', '弓部', '弛弓也。从弓，璽聲。', '弓;璽', '璽', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8495, '弛', 'chí', '第十二卷', '弓部', '弓解弦也。从弓，也聲。弛或从虒。', '弓;也;虒', '也;虒', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8496, '弢', 'tāo', '第十二卷', '弓部', '弓衣也。从弓𠬢。𠬢，𠂹飾。與𡔷同意。', '弓;𠬢', '𠬢', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8497, '弩', 'nǔ', '第十二卷', '弓部', '弓有臂者。从弓，奴聲。周禮「四弩」——夾弩、庾弩、唐弩、大弩。', '弓;奴', '奴', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8498, '彀', 'ɡòu', '第十二卷', '弓部', '張弩也。从弓，㱿聲。', '弓;㱿', '㱿', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8499, '彉', 'ɡuō', '第十二卷', '弓部', '滿弩也。从弓，黃聲。讀若郭。', '弓;黃;郭', '黃;郭', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8500, '彃', 'bì', '第十二卷', '弓部', '射也。从弓，畢聲。楚辭曰「夫𢏗焉彃曰也」。', '弓;畢', '畢', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8501, '彈', 'dàn', '第十二卷', '弓部', '行丸也。从弓，單聲。或說彈从弓持丸如此。', '弓;單', '單', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8502, '發', 'fā', '第十二卷', '弓部', '射發也。从弓，癹聲。', '弓;癹', '癹', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8503, '𢏗', 'yì', '第十二卷', '弓部', '帝嚳射官。夏 少康滅之。从弓，开聲。論語曰「𢏗善射」。', '弓;开', '开', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8504, '彆', 'biè', '第十二卷', '弓部', '弓戾也。从弓，敝聲。', '弓;敝', '敝', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8505, '弜', 'jiànɡ', '第十二卷', '弜部', '彊也。重也。从二弓。凡弜之屬皆从弜。闕。', '弓', '', 'null', NULL, '弓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8506, '弼;𢐜;𢐑;弻;𢐈;𢐀;㢸', 'bì', '第十二卷', '弜部', '輔也。重也。从弜，㐁聲。古文𢐀如此。亦古文𢐀[攴]。𢐀或如此[弓弗]。', '弜;㐁;攴;弓;弗', '㐁;弗', 'null', NULL, '弜;攴;弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8507, '弦', 'xián', '第十二卷', '弦部', '弓弦也。从弓[玄]。象絲軫之形。凡弦之屬皆从弦。', '弓;玄', '', 'null', NULL, '弓;玄', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8508, '盭', 'lì', '第十二卷', '弦部', '𢐀戾也。从弦省，从盩。盩，了戾之也。讀若戾。', '弦;盩;戾', '戾', 'null', NULL, '弦;盩', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8509, '玅', 'yāo', '第十二卷', '弦部', '急戾也。从弦省，少聲。', '弦;少', '少', 'null', NULL, '弦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8510, '𤣨', 'yì', '第十二卷', '弦部', '不成遂急戾也。从弦省，曷聲。讀若瘞。', '弦;曷;瘞', '曷;瘞', 'null', NULL, '弦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8511, '系;𦃟', 'xì', '第十二卷', '系部', '縣也。从糸。𠂆聲。凡系之屬皆从系。系或从毄處。籒文系从爪絲。', '糸;𠂆;毄;處;爪;絲', '𠂆;毄', 'null', NULL, '糸;處;爪;絲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8512, '孫', 'sūn', '第十二卷', '系部', '子之子曰孫。从系子。系，續也。', '系;子', '', 'null', NULL, '系;子', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8513, '緜', 'mián', '第十二卷', '系部', '聯𢼸也。从系帛。', '系;帛', '', 'null', NULL, '系;帛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8514, '䌛;繇;由', 'yáo', '第十二卷', '系部', '隨從也。从系，䚻聲。或䌛字[由]。', '系;䚻;由', '䚻;由', 'null', NULL, '系', 'null', 'null', 'null', 'null', '重文应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8515, '糸', 'mì', '第十三卷', '糸部', '細絲也。象束絲之形。凡糸之屬皆从糸。讀若覛。古文糸。', '覛', '覛', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8516, '繭', 'jiǎn', '第十三卷', '糸部', '蠶衣也。从糸，从虫，从芇。古文繭从糸見。', '糸;虫;芇;糸;見', '芇;見', 'null', NULL, '糸;虫;糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8517, '繅', 'sāo', '第十三卷', '糸部', '繹繭爲絲也。从糸，巢聲。', '糸;巢', '巢', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8518, '繹', 'yì', '第十三卷', '糸部', '㩅絲也。从糸，睪聲。', '糸;睪', '睪', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8519, '緒;緖', 'xù', '第十三卷', '糸部', '絲耑也。从糸，者聲。', '糸;者', '者', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8520, '緬', 'miǎn', '第十三卷', '糸部', '𢼸絲也。从糸，面聲。', '糸;面', '面', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8521, '純', 'chún', '第十三卷', '糸部', '絲也。从糸，屯聲。論語曰「今也純儉」。', '糸;屯', '屯', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8522, '綃', 'xiāo', '第十三卷', '糸部', '生絲也。从糸，肖聲。', '糸;肖', '肖', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8523, '𦂄', 'kāi', '第十三卷', '糸部', '大絲也。从糸，皆聲。', '糸;皆', '皆', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8524, '𥿼', 'huānɡ', '第十三卷', '糸部', '絲曼延也。从糸，巟聲。', '糸;巟', '巟', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8525, '紇;𥾨', 'hé', '第十三卷', '糸部', '絲下也。从糸，气聲。春秋傳有「臧孫𥾨」。', '糸;气', '气', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8526, '𥿄', 'dī', '第十三卷', '糸部', '絲滓也。从糸，氐聲。', '糸;氐', '氐', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8527, '絓', 'huà', '第十三卷', '糸部', '繭滓絓頭也。从糸，圭聲。一曰㠯囊絮湅也。', '糸;圭', '圭', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8528, '纅', 'yào', '第十三卷', '糸部', '絲色也。从糸，樂聲。', '糸;樂', '樂', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8529, '繀', 'suì', '第十三卷', '糸部', '箸絲於筟車也。从糸，崔聲。', '糸;崔', '崔', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8530, '經', 'jīnɡ', '第十三卷', '糸部', '織從絲也。从糸，巠聲。', '糸;巠', '巠', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8531, '織', 'zhī', '第十三卷', '糸部', '作布帛之總名也。从糸，戠聲。', '糸;戠', '戠', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8532, '𥿮', 'zhì', '第十三卷', '糸部', '樂浪 挈令「織」。从糸，从式。', '糸;式;織', '式;織', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8533, '紝', 'rèn', '第十三卷', '糸部', '機縷也。从糸，壬聲。紝或从任。', '糸;壬;任', '壬;任', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8534, '綜', 'zònɡ', '第十三卷', '糸部', '機縷也。从糸，宗聲。', '糸;宗', '宗', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8535, '綹', 'liǔ', '第十三卷', '糸部', '緯十縷爲綹。从糸，咎聲。讀若桺。', '糸;咎;桺', '咎;桺', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8536, '緯', 'wěi', '第十三卷', '糸部', '織衡絲也。从糸，韋聲。', '糸;韋', '韋', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8537, '緷', 'yùn', '第十三卷', '糸部', '緯也。从糸，軍聲。', '糸;軍', '軍', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8538, '繢', 'huì', '第十三卷', '糸部', '織餘也。一曰畫也。从糸，貴聲。', '糸;貴', '貴', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8539, '統', 'tǒnɡ', '第十三卷', '糸部', '紀也。从糸，充聲。', '糸;充', '充', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8540, '紀', 'jì', '第十三卷', '糸部', '絲別也。从糸，己聲。', '糸;己', '己', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8541, '繈', 'qiǎnɡ', '第十三卷', '糸部', '𧢼纇也。从糸，強聲。', '糸;強', '強', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8542, '纇', 'lèi', '第十三卷', '糸部', '絲節也。从糸，頪聲。', '糸;頪', '頪', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8543, '紿', 'dài', '第十三卷', '糸部', '絲勞卽紿。从絲，台聲。', '絲;台', '台', 'null', NULL, '絲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8544, '納', 'nà', '第十三卷', '糸部', '絲溼納納也。从糸，內聲。', '糸;內', '內', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8545, '紡', 'fǎnɡ', '第十三卷', '糸部', '紡絲也。从糸，方聲。', '糸;方', '方', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8546, '絕;𢇍', 'jué', '第十三卷', '糸部', '𣃔絲也。从刀糸。卪聲。古文絕。象不連體絕二絲。', '刀;糸;卪;絲', '卪', 'null', NULL, '刀;糸;絲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8547, '繼;𦇓;㡭', 'jì', '第十三卷', '糸部', '續也。从糸𢇍。𦇓或作㡭[絕]。反𢇍爲㡭。', '糸;𢇍;絕', '', 'null', NULL, '糸;𢇍;絕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8548, '續;賡', 'xù', '第十三卷', '糸部', '連也。从糸，𧷏聲。古文續从庚貝。', '糸;𧷏;庚;貝', '𧷏', 'null', NULL, '糸;庚;貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8549, '纘', 'zuǎn', '第十三卷', '糸部', '𦇓也。从糸，贊聲。', '糸;贊', '贊', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8550, '紹', 'shào', '第十三卷', '糸部', '𦇓也。从糸，召聲。一曰紹，緊糾也。古文紹从卪。', '糸;召;卪', '召', 'null', NULL, '糸;卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8551, '𦅗;𦆀', 'chǎn', '第十三卷', '糸部', '偏緩也。从糸，羨聲。', '糸;羨', '羨', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8552, '䋼', 'tīnɡ', '第十三卷', '糸部', '緩也。从糸，盈聲。讀與聽同。䋼或从呈。', '糸;盈;聽;呈', '盈;聽;呈', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8553, '縱', 'zònɡ', '第十三卷', '糸部', '緩也。一曰捨也。从糸，從聲。', '糸;從', '從', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8554, '紓', 'shū', '第十三卷', '糸部', '緩也。从糸，予聲。', '糸;予', '予', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8555, '繎', 'rán', '第十三卷', '糸部', '絲勞也。从糸，然聲。', '糸;然', '然', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8556, '紆;䊸', 'yū', '第十三卷', '糸部', '詘也。从糸，于聲。一曰縈也。', '糸;于', '于', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8557, '緈', 'xìnɡ', '第十三卷', '糸部', '直也。从糸，幸聲。讀若陘。', '糸;幸;陘', '幸;陘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8558, '纖', 'xiān', '第十三卷', '糸部', '細也。从糸，韱聲。', '糸;韱', '韱', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8559, '細', 'xì', '第十三卷', '糸部', '𢼸也。从糸，𦥓聲。', '糸;𦥓', '𦥓', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8560, '緢', 'miáo', '第十三卷', '糸部', '氂絲也。从糸，苗聲。周書曰「惟緢有稽」。', '糸;苗', '苗', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8561, '縒', 'cī', '第十三卷', '糸部', '參縒也。从糸，差聲。', '糸;差', '差', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8562, '繙', 'fán', '第十三卷', '糸部', '繙冤也。从糸，番聲。', '糸;番', '番', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8563, '縮', 'suō', '第十三卷', '糸部', '亂也。从糸，宿聲。一曰蹴也。', '糸;宿', '宿', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8564, '紊', 'wèn', '第十三卷', '糸部', '亂也。从糸，文聲。商書曰「有條而不紊」。', '糸;文', '文', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8565, '級', 'jí', '第十三卷', '糸部', '絲次弟也。从糸，及聲。', '糸;及', '及', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8566, '總', 'zǒnɡ', '第十三卷', '糸部', '聚束也。从糸，悤聲。', '糸;悤', '悤', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8567, '䋰', 'jú', '第十三卷', '糸部', '約也。从糸，具聲。', '糸;具', '具', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8568, '約', 'yuē', '第十三卷', '糸部', '纏束也。从糸，勺聲。', '糸;勺', '勺', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8569, '繚', 'liǎo', '第十三卷', '糸部', '纏也。从糸，尞聲。', '糸;尞', '尞', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8570, '纏', 'chán', '第十三卷', '糸部', '繞也。从糸，廛聲。', '糸;廛', '廛', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8571, '繞', 'rǎo', '第十三卷', '糸部', '纏也。从糸，堯聲。', '糸;堯', '堯', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8572, '紾', 'zhěn', '第十三卷', '糸部', '紾轉也。从糸，㐱聲。', '糸;㐱', '㐱', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8573, '繯;𦇏', 'xuàn', '第十三卷', '糸部', '落也。从糸，瞏聲。', '糸;瞏', '瞏', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8574, '辮', 'biàn', '第十三卷', '糸部', '交也。从糸，辡聲。', '糸;辡', '辡', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8575, '結', 'jié', '第十三卷', '糸部', '締也。从糸，吉聲。', '糸;吉', '吉', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8576, '縎', 'ɡǔ', '第十三卷', '糸部', '結也。从糸，骨聲。', '糸;骨', '骨', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8577, '締', 'dì', '第十三卷', '糸部', '結不解也。从糸，帝聲。', '糸;帝', '帝', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8578, '縛', 'fù', '第十三卷', '糸部', '束也。从糸，尃聲。', '糸;尃', '尃', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8579, '繃', 'bēnɡ', '第十三卷', '糸部', '束也。从糸，崩聲。墨子曰「禹葬會稽，桐棺三寸，葛㠯繃[緘]之」。', '糸;崩;緘', '崩;緘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8580, '絿', 'qiú', '第十三卷', '糸部', '急也。从糸，求聲。詩曰「不競不絿」。', '糸;求', '求', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8581, '絅', 'jiōnɡ', '第十三卷', '糸部', '引急也。从糸，冋聲。', '糸;冋', '冋', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8582, '𥿯', 'pài', '第十三卷', '糸部', '𢽳絲也。从糸，𠂢聲。', '糸;𠂢', '𠂢', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8583, '䌴', 'luò', '第十三卷', '糸部', '不均也。从糸，羸聲。', '糸;羸', '羸', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8584, '給', 'jǐ', '第十三卷', '糸部', '相足也。从糸，合聲。', '糸;合', '合', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8585, '綝', 'chēn', '第十三卷', '糸部', '止也。从糸，林聲。讀若郴。', '糸;林;郴', '林;郴', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8586, '縪', 'bì', '第十三卷', '糸部', '止也。从糸，畢聲。', '糸;畢', '畢', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8587, '䊵', 'qiú', '第十三卷', '糸部', '引也。从糸，九聲。', '糸;九', '九', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '政宇补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8588, '終;𠂂', 'zhōnɡ', '第十三卷', '糸部', '䊵絲也。从糸，冬聲。古文終[未详]。', '糸;冬', '冬', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8589, '䌖', 'jié', '第十三卷', '糸部', '合也。从糸集。讀若捷。', '糸;集;捷', '集;捷', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8590, '繒', 'zēnɡ', '第十三卷', '糸部', '帛也。从糸，曾聲。籒文繒从宰省。楊雄㠯爲漢律祠宗廟丹書告也。', '糸;曾;宰', '曾;宰', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8591, '緭', 'wèi', '第十三卷', '糸部', '繒也。从糸，胃聲。', '糸;胃', '胃', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8592, '絩', 'tiào', '第十三卷', '糸部', '綺絲之數也。漢律曰「綺絲數謂之絩，布謂之總，綬組謂之首」。从糸，兆聲。', '糸;兆', '兆', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8593, '綺', 'qǐ', '第十三卷', '糸部', '文繒也。从糸，奇聲。', '糸;奇', '奇', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8594, '縠', 'hú', '第十三卷', '糸部', '細縳也。从糸，㱿聲。', '糸;㱿', '㱿', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8595, '縳', 'juàn', '第十三卷', '糸部', '白鮮𠨗也。从糸，專聲。', '糸;專', '專', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8596, '縑', 'jiān', '第十三卷', '糸部', '并絲繒也。从糸，兼聲。', '糸;兼', '兼', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8597, '綈', 'tí', '第十三卷', '糸部', '㫗繒也。从糸，弟聲。', '糸;弟', '弟', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8598, '練', 'liàn', '第十三卷', '糸部', '湅繒也。从糸，柬聲。', '糸;柬', '柬', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8599, '縞', 'ɡǎo', '第十三卷', '糸部', '鮮𠨗也。从糸，高聲。', '糸;高', '高', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8600, '䌳', 'shī', '第十三卷', '糸部', '粗緒也。从糸，璽聲。', '糸;璽', '璽', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8601, '紬', 'chóu', '第十三卷', '糸部', '大絲繒也。从糸，由聲。', '糸;由', '由', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8602, '綮', 'qǐ', '第十三卷', '糸部', '致繒也。一曰幑識信也，有齒。从糸，啟省聲。', '糸;啟', '啟', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8603, '綾', 'línɡ', '第十三卷', '糸部', '東齊謂布帛之細者曰綾。从糸，夌聲。', '糸;夌', '夌', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8604, '縵', 'màn', '第十三卷', '糸部', '繒無文也。从糸，曼聲。漢律曰「賜衣者縵表白裏」。', '糸;曼', '曼', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8605, '繡', 'xiù', '第十三卷', '糸部', '五采備也。从糸，肅聲。', '糸;肅', '肅', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8606, '絢', 'xuàn', '第十三卷', '糸部', '詩云「素㠯爲絢兮」。从糸，旬聲。', '糸;旬', '旬', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8607, '繪', 'huì', '第十三卷', '糸部', '會五采繡也。虞書曰「山、龍、華蟲作繪」。論語曰「繪事後素」。从糸，會聲。', '糸;會', '會', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8608, '緀', 'qī', '第十三卷', '糸部', '帛文皃。詩曰「緀兮斐兮，成是貝錦」。从糸，妻聲。', '糸;妻', '妻', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8609, '䋛', 'mǐ', '第十三卷', '糸部', '繡文如聚細米也。从糸米。米亦聲。', '糸;米', '米', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8610, '絹', 'juàn', '第十三卷', '糸部', '繒如麥䅌色。从糸，肙聲。', '糸;肙', '肙', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8611, '綠;緑', 'lǜ', '第十三卷', '糸部', '帛靑黃色也。从糸，录聲。', '糸;录', '录', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8612, '縹', 'piǎo', '第十三卷', '糸部', '帛白靑色也。从糸，票聲。', '糸;票', '票', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8613, '䋭', 'yù', '第十三卷', '糸部', '帛靑經縹緯。一曰育陽染也。从糸，育聲。', '糸;育', '育', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8614, '絑', 'zhū', '第十三卷', '糸部', '純赤繒也。虞書「丹朱」如此。从糸，朱聲。', '糸;朱', '朱', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8615, '纁', 'xūn', '第十三卷', '糸部', '淺絳也。从糸，熏聲。', '糸;熏', '熏', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8616, '絀', 'chù', '第十三卷', '糸部', '絳也。从糸，出聲。', '糸;出', '出', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8617, '絳', 'jiànɡ', '第十三卷', '糸部', '大赤繒也。从糸，夅聲。', '糸;夅', '夅', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8618, '綰', 'wǎn', '第十三卷', '糸部', '惡絳也。从糸，官聲。一曰𦌾也。讀若「雞卵」。', '糸;官;卵', '官;卵', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8619, '縉', 'jìn', '第十三卷', '糸部', '帛赤色也。从糸，晉聲。春秋傳曰「縉雲氏」。禮有「縉緣」。', '糸;晉', '晉', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8620, '綪', 'qiàn', '第十三卷', '糸部', '赤繒也。㠯莤染故謂之綪。从糸，靑聲。', '糸;靑', '靑', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8621, '緹;衹', 'tǐ', '第十三卷', '糸部', '帛丹黃色也。从糸，是聲。緹或作衹[氏]。', '糸;是;氏', '是;氏', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8622, '縓', 'quàn', '第十三卷', '糸部', '帛赤黃色也。一染謂之縓，再染謂之赬，三染謂之纁。从糸，原聲。', '糸;原', '原', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8623, '紫', 'zǐ', '第十三卷', '糸部', '帛靑赤色也。从糸，此聲。', '糸;此', '此', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8624, '紅', 'hónɡ', '第十三卷', '糸部', '帛赤白色也。从糸，工聲。', '糸;工', '工', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8625, '𦇎', 'cōnɡ', '第十三卷', '糸部', '帛靑色也。从糸，蔥聲。', '糸;蔥', '蔥', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8626, '紺', 'ɡàn', '第十三卷', '糸部', '帛深靑而揚赤色也。从糸，甘聲。', '糸;甘', '甘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8627, '𦁰;綦', 'qí', '第十三卷', '糸部', '帛蒼艾色也。从糸，𢍁聲。詩曰「縞衣𦁰巾」。未嫁女所服。一曰不借𦁰。𦁰或从其。', '糸;𢍁;其', '𢍁;其', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8628, '繰', 'zǎo', '第十三卷', '糸部', '帛如紺色。或曰深繒。从糸，喿聲。讀若喿。', '糸;喿', '喿', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8629, '緇', 'zī', '第十三卷', '糸部', '帛黑色也。从糸，[葘]聲。', '糸;葘', '葘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8630, '纔', 'shān', '第十三卷', '糸部', '帛雀頭色也。一曰𢼸黑色如紺。纔，淺也。讀若讒。从糸，毚聲。', '讒;糸;毚', '讒;毚', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8631, '𦃖', 'tǎn', '第十三卷', '糸部', '帛騅色也。从糸，剡聲。詩曰「毳衣如𦃖」。', '糸;剡', '剡', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8632, '綟', 'lì', '第十三卷', '糸部', '帛䓞艸染色也。从糸，戾聲。', '糸;戾', '戾', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8633, '紑', 'fóu', '第十三卷', '糸部', '白鱻衣皃。从糸，不聲。詩曰「素衣其紑」。', '糸;不', '不', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8634, '緂', 'tān', '第十三卷', '糸部', '白鱻衣皃。从糸，炎聲。謂衣采色鮮也。', '糸;炎', '炎', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8635, '繻', 'xū', '第十三卷', '糸部', '繒采色也。从糸，需聲。讀若「繻有衣」。', '糸;需', '需', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8636, '縟', 'rù', '第十三卷', '糸部', '緐采飾也。从糸，辱聲。', '糸;辱', '辱', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8637, '纚', 'xǐ', '第十三卷', '糸部', '冠織也。从糸，麗聲。謂㠯緇帛韜髮。', '糸;麗', '麗', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8638, '紘', 'hónɡ', '第十三卷', '糸部', '冠卷維也。从糸，厷聲。紘或从弘。', '糸;厷;弘', '厷;弘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8639, '紞', 'dǎn', '第十三卷', '糸部', '冕冠𡫳耳者。从糸，冘聲。', '糸;冘', '冘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8640, '纓', 'yīnɡ', '第十三卷', '糸部', '冠系也。从糸，嬰聲。', '糸;嬰', '嬰', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8641, '紻', 'yǎnɡ', '第十三卷', '糸部', '纓卷也。从糸，央聲。', '糸;央', '央', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8642, '緌', 'ruí', '第十三卷', '糸部', '系冠纓𠂹者。从糸，委聲。', '糸;委', '委', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8643, '緄', 'ɡǔn', '第十三卷', '糸部', '織成帶也。从糸，昆聲。', '糸;昆', '昆', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8644, '紳', 'shēn', '第十三卷', '糸部', '大帶也。从糸，申聲。', '糸;申', '申', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8645, '繟', 'chǎn', '第十三卷', '糸部', '帶緩也。从糸，單聲。', '糸;單', '單', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8646, '綬', 'shòu', '第十三卷', '糸部', '韍維也。从糸，受聲。', '糸;受', '受', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8647, '組', 'zǔ', '第十三卷', '糸部', '綬屬也。其小者㠯爲冠纓。从糸，且聲。', '糸;且', '且', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8648, '緺', 'ɡuā', '第十三卷', '糸部', '綬紫靑色也。从糸，咼聲。', '糸;咼', '咼', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8649, '縌', 'nì', '第十三卷', '糸部', '綬維也。从糸，逆聲。', '糸;逆', '逆', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8650, '纂', 'zuǎn', '第十三卷', '糸部', '佀組而赤。从糸，算聲。', '糸;算', '算', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8651, '紐', 'niǔ', '第十三卷', '糸部', '系也。一曰結而可解。从糸，丑聲。', '糸;丑', '丑', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8652, '綸', 'lún', '第十三卷', '糸部', '糾靑絲綬也。从糸，侖聲。', '糸;侖', '侖', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8653, '綎', 'tīnɡ', '第十三卷', '糸部', '系綬也。从糸，廷聲。', '糸;廷', '廷', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8654, '絙', 'huán', '第十三卷', '糸部', '緩也。从糸，亘聲。', '糸;亘', '亘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8655, '㬧', 'bó', '第十三卷', '糸部', '頸連也。从糸，㬥省聲。', '糸;㬥', '㬥', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8656, '紟', 'jīn', '第十三卷', '糸部', '衣系也。从糸，今聲。籒文从金。', '糸;今;金', '今;金', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8657, '緣', 'yuàn', '第十三卷', '糸部', '衣純也。从糸，彖聲。', '糸;彖', '彖', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8658, '纀', 'pú', '第十三卷', '糸部', '常削幅謂之纀。从糸，僕聲。', '糸;僕', '僕', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8659, '絝', 'kù', '第十三卷', '糸部', '脛衣也。从糸，夸聲。', '糸;夸', '夸', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8660, '繑', 'qiāo', '第十三卷', '糸部', '絝紐也。从糸，喬聲。', '糸;喬', '喬', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8661, '緥', 'bǎo', '第十三卷', '糸部', '小兒衣也。从糸，保聲。', '糸;保', '保', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8662, '繜', 'zūn', '第十三卷', '糸部', '薉貉中，女子無絝，㠯帛爲脛空，用絮補核，名曰「繜衣」，狀如襜褕。从糸，尊聲。', '糸;尊', '尊', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8663, '紴', 'bō', '第十三卷', '糸部', '絛屬。从糸，皮聲。讀若被。或讀若「水波」之波。', '糸;皮;被;波', '皮;被;波', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8664, '絛', 'tāo', '第十三卷', '糸部', '扁緒也。从糸，攸聲。', '糸;攸', '攸', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '𠧪≈髫≈絛≈條', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8665, '䋐', 'yuè', '第十三卷', '糸部', '采彰也。一曰車馬帬。从糸，戉聲。', '糸;戉', '戉', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8666, '緃', 'zōnɡ', '第十三卷', '糸部', '䋐屬。从糸，從省聲。', '糸;從', '從', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8667, '紃', 'xún', '第十三卷', '糸部', '圜采也。从糸，川聲。', '糸;川', '川', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8668, '緟', 'chónɡ', '第十三卷', '糸部', '增益也。从糸，重聲。', '糸;重', '重', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8669, '纕', 'rǎnɡ', '第十三卷', '糸部', '援臂也。从糸，襄聲。', '糸;襄', '襄', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8670, '纗', 'xī', '第十三卷', '糸部', '維綱中繩也。从糸，巂聲。讀若畫。或讀若維。', '糸;巂;畫;維', '巂;畫;維', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8671, '綱', 'ɡānɡ', '第十三卷', '糸部', '网紘也。从糸，岡聲。古文綱[木]。', '糸;岡;木', '岡', 'null', NULL, '糸;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8672, '縜', 'yún', '第十三卷', '糸部', '綱紐也。从糸，員聲。周禮曰「縜寸」。', '糸;員', '員', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8673, '綅', 'qīn', '第十三卷', '糸部', '綫也。从糸，侵省聲。詩曰「貝冑朱綅」。', '糸;侵', '侵', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8674, '縷', 'lǚ', '第十三卷', '糸部', '綫也。从糸，婁聲。', '糸;婁', '婁', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8675, '綫', 'xiàn', '第十三卷', '糸部', '縷也。从糸，戔聲。古文綫[泉]。', '糸;戔;泉', '戔;泉', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8676, '䋉', 'xué', '第十三卷', '糸部', '縷一枚也。从糸，穴聲。', '糸;穴', '穴', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8677, '縫', 'fénɡ', '第十三卷', '糸部', '㠯鍼紩衣也。从糸，逢聲。', '糸;逢', '逢', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8678, '緁', 'qiè', '第十三卷', '糸部', '緶衣也。从糸，疌聲。緁或从習。', '糸;疌;習', '疌;習', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8679, '紩', 'zhì', '第十三卷', '糸部', '縫也。从糸，失聲。', '糸;失', '失', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8680, '緛', 'ruǎn', '第十三卷', '糸部', '衣戚也。从糸，耎聲。', '糸;耎', '耎', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8681, '䋎', 'zhàn', '第十三卷', '糸部', '補縫也。从糸，旦聲。', '糸;旦', '旦', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8682, '繕', 'shàn', '第十三卷', '糸部', '補也。从糸，善聲。', '糸;善', '善', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8683, '絬', 'xiè', '第十三卷', '糸部', '衣堅也。从糸，舌聲。論語曰「絬衣長，短右袂」。', '糸;舌', '舌', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '月部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8684, '纍', 'léi', '第十三卷', '糸部', '綴得理也。一曰大索也。从糸，畾聲。', '糸;畾', '畾', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8685, '縭', 'lí', '第十三卷', '糸部', '㠯絲介履也。从糸，离聲。', '糸;离', '离', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8686, '緱', 'ɡōu', '第十三卷', '糸部', '刀劒緱也。从糸，矦聲。', '糸;矦', '矦', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8687, '繄', 'yī', '第十三卷', '糸部', '𢧢衣也。从糸，殹聲。一曰赤黑色繒。', '糸;殹', '殹', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8688, '縿', 'shān', '第十三卷', '糸部', '旌旗之游所屬也。从糸，參聲。', '糸;參', '參', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8689, '徽', 'huī', '第十三卷', '糸部', '衺幅也。一曰三糾繩也。从糸，微省聲。', '糸;微', '微', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8690, '䋢', 'biē', '第十三卷', '糸部', '扁緒也。一曰弩要鉤帶。从糸，㪿聲。', '糸;㪿', '㪿', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8691, '紉', 'rèn', '第十三卷', '糸部', '單繩也。从糸，刃聲。', '糸;刃', '刃', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8692, '繩', 'shénɡ', '第十三卷', '糸部', '索也。从糸，蠅省聲。', '糸;蠅', '蠅', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '侵部->蒸部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8693, '䋫', 'zhēnɡ', '第十三卷', '糸部', '䊸未縈繩。一曰急弦之聲。从糸，爭聲。讀若旌。', '糸;爭;旌', '爭;旌', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8694, '縈', 'yínɡ', '第十三卷', '糸部', '收卷也。从糸，熒省聲。', '糸;熒', '熒', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8695, '絇', 'qú', '第十三卷', '糸部', '纑繩絇也。从糸，句聲。讀若鳩。', '糸;句;鳩', '句;鳩', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8696, '縋', 'zhuì', '第十三卷', '糸部', '㠯繩有所縣也。春秋傳曰「夜縋納師」。从糸，追聲。', '糸;追', '追', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8697, '絭', 'quàn', '第十三卷', '糸部', '纕臂繩也。从糸，𢍏聲。', '糸;𢍏', '𢍏', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8698, '緘', 'jiān', '第十三卷', '糸部', '所㠯束匧也。从糸，咸聲。', '糸;咸', '咸', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8699, '縢', 'ténɡ', '第十三卷', '糸部', '緘也。从糸，朕聲。', '糸;朕', '朕', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8700, '編', 'biān', '第十三卷', '糸部', '次𥳑也。从糸，篇聲。', '糸;篇', '篇', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8701, '維', 'wéi', '第十三卷', '糸部', '車葢維也。从糸，隹聲。', '糸;隹', '隹', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8702, '絥;茯;鞴', 'bèi', '第十三卷', '糸部', '車絥也。从糸，伏聲。絥或从艸。絥或从革，𤰈聲。', '糸;伏;艸;革;𤰈', '伏;𤰈', 'null', NULL, '糸;艸;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8703, '䋊', 'zhēnɡ', '第十三卷', '糸部', '䋊綊，乘輿馬飾也。从糸，正聲。', '糸;正', '正', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8704, '綊', 'xié', '第十三卷', '糸部', '䋊綊也。从糸，夾聲。', '糸;夾', '夾', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8705, '䋣;緐', 'fán', '第十三卷', '糸部', '馬髦飾也。从糸每。春秋傳曰「可㠯稱旌緐乎」。䋣或从𢍍。𢍍，籒文弁[㝸]。', '糸;每;𢍍', '𢍍', 'null', NULL, '糸;每', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8706, '繮', 'jiānɡ', '第十三卷', '糸部', '馬紲也。从糸，畺聲。', '糸;畺', '畺', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8707, '紛', 'fēn', '第十三卷', '糸部', '馬尾韜也。从糸，分聲。', '糸;分', '分', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8708, '紂', 'zhòu', '第十三卷', '糸部', '馬緧也。从糸，肘省聲。', '糸;肘', '肘', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8709, '緧', 'qiū', '第十三卷', '糸部', '馬紂也。从糸，酋聲。', '糸;酋', '酋', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8710, '絆', 'bàn', '第十三卷', '糸部', '馬𩡳也。从糸，半聲。', '糸;半', '半', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8711, '𦅨;𦄼', 'xǔ', '第十三卷', '糸部', '絆歬㒳足也。从糸，須聲。漢令「蠻夷卒有𦄼」。', '糸;須', '須', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8712, '紖', 'zhèn', '第十三卷', '糸部', '牛系也。从糸，引聲。讀若矤。', '糸;引;矤', '引;矤', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8713, '縼', 'xuàn', '第十三卷', '糸部', '㠯長繩系牛也。从糸，旋聲。', '糸;旋', '旋', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8714, '縻', 'mí', '第十三卷', '糸部', '牛𦆕也。从糸，麻聲。縻或从多。', '糸;麻;多', '麻;多', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8715, '紲;緤', 'xiè', '第十三卷', '糸部', '犬系也。从糸，世聲。春秋傳曰「臣負羈紲」。紲或从枼。', '糸;世;枼', '世;枼', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8716, '𦄿', 'mò', '第十三卷', '糸部', '索也。从糸，黑聲。', '糸;黑', '黑', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8717, '緪', 'ɡēnɡ', '第十三卷', '糸部', '大索也。一曰急也。从糸，恆聲。', '糸;恆', '恆', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8718, '繘', 'yù', '第十三卷', '糸部', '綆也。从糸，矞聲。古文从絲。籒文繘[系]。', '糸;矞;絲;系', '矞', 'null', NULL, '糸;絲;系', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8719, '綆', 'ɡěnɡ', '第十三卷', '糸部', '汲井綆也。从糸，更聲。', '糸;更', '更', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8720, '絠', 'ǎi', '第十三卷', '糸部', '彈彄也。从糸，有聲。', '糸;有', '有', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8721, '繳;𦅾', 'zhuó', '第十三卷', '糸部', '生絲縷也。謂縷系矰矢而㠯隿射也。从糸，敫聲。', '糸;敫', '敫', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8722, '繴', 'bì', '第十三卷', '糸部', '繴謂之罿，罿謂之罬，罬謂之罦。捕鳥覆車也。从糸，辟聲。', '糸;辟', '辟', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8723, '緍;緡', 'mín', '第十三卷', '糸部', '釣魚繳也。从糸，昏聲。吳人解衣相被謂之緍。', '糸;昏', '昏', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8724, '絮', 'xù', '第十三卷', '糸部', '敝緜也。从糸，如聲。', '糸;如', '如', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8725, '絡', 'luò', '第十三卷', '糸部', '絮也。一曰麻未漚也。从糸，各聲。', '糸;各', '各', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8726, '纊;絖', 'kuànɡ', '第十三卷', '糸部', '絮也。从糸，廣聲。春秋傳曰「皆如挾纊」。纊或从光。', '糸;廣;光', '廣;光', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8727, '紙', 'zhǐ', '第十三卷', '糸部', '絮一𥮒也。从糸，氏聲。', '糸;氏', '氏', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8728, '䋨', 'fǔ', '第十三卷', '糸部', '治敝絮也。从糸，㕻聲。', '糸;㕻', '㕻', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8729, '繫', 'jì', '第十三卷', '糸部', '繫𦃇也。一曰惡絮。从糸，𣪠聲。', '糸;𣪠', '𣪠', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8730, '𦃇', 'lí', '第十三卷', '糸部', '繫𦃇也。从糸，虒聲。一曰維也。', '糸;虒', '虒', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8731, '緝', 'jī', '第十三卷', '糸部', '績也。从糸，咠聲。', '糸;咠', '咠', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8732, '絘', 'cì', '第十三卷', '糸部', '績所未緝者。从糸，次聲。', '糸;次', '次', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8733, '績', 'jī', '第十三卷', '糸部', '緝也。从糸，責聲。', '糸;責', '責', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8734, '纑', 'lú', '第十三卷', '糸部', '布縷也。从糸，盧聲。', '糸;盧', '盧', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8735, '紨', 'fū', '第十三卷', '糸部', '布也。一曰粗紬。从糸，付聲。', '糸;付', '付', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8736, '𦄑', 'suì', '第十三卷', '糸部', '蜀細布也。从糸，彗聲。', '糸;彗', '彗', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8737, '絺', 'chī', '第十三卷', '糸部', '細葛也。从糸，希聲。', '糸;希', '希', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8738, '綌', 'xì', '第十三卷', '糸部', '粗葛也。从糸，𧮫聲。綌或从巾。', '糸;𧮫;巾', '𧮫', 'null', NULL, '糸;巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8739, '縐', 'zhòu', '第十三卷', '糸部', '絺之細者也。詩曰「蒙彼縐絺」。一曰纖也。从糸，芻聲。', '糸;芻', '芻', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8740, '絟', 'quán', '第十三卷', '糸部', '細布也。从糸，全聲。', '糸;全', '全', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8741, '紵', 'zhù', '第十三卷', '糸部', '檾屬。細者爲絟，布白而細曰紵。从糸，宁聲。紵或从緒省。', '糸;宁;緒', '宁', 'null', NULL, '糸;緒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8742, '緦', 'sī', '第十三卷', '糸部', '十五升抽其半布也。一曰㒳麻一絲布也。从糸，思聲。古文緦从思省。', '糸;思', '思', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8743, '緆', 'xī', '第十三卷', '糸部', '細布也。从糸，易聲。緆或从麻。', '糸;易;麻', '易', 'null', NULL, '糸;麻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8744, '繐', 'suì', '第十三卷', '糸部', '細疏布也。从糸，惠聲。', '糸;惠', '惠', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8745, '緰', 'tóu', '第十三卷', '糸部', '緰貲，布也。从糸，俞聲。', '糸;俞', '俞', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8746, '縗', 'cuī', '第十三卷', '糸部', '喪服衣。長六寸，博四寸，直心。从糸，衰聲。', '糸;衰', '衰', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8747, '絰', 'dié', '第十三卷', '糸部', '喪首戴也。从糸，至聲。', '糸;至', '至', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8748, '緶', 'biàn', '第十三卷', '糸部', '交枲也。一曰緁衣也。从糸，便聲。', '糸;便', '便', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8749, '䋀', 'huà', '第十三卷', '糸部', '履也。一曰靑絲頭履也。讀若「阡陌」之陌[佰]。从糸，戸聲。', '佰;糸;戸', '佰;戸', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8750, '䋽', 'běnɡ', '第十三卷', '糸部', '枲履也。从糸，封聲。', '糸;封', '封', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8751, '緉', 'liǎnɡ', '第十三卷', '糸部', '履㒳枚也。一曰絞也。从糸㒳。㒳亦聲。', '糸;㒳', '㒳', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8752, '絜', 'jié', '第十三卷', '糸部', '麻一耑也。从糸，㓞聲。', '糸;㓞', '㓞', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8753, '繆', 'móu', '第十三卷', '糸部', '枲之十絜也。一曰綢繆也。从糸，翏聲。', '糸;翏', '翏', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8754, '綢', 'chóu', '第十三卷', '糸部', '繆也。从糸，周聲。', '糸;周', '周', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8755, '䋈', 'rú', '第十三卷', '糸部', '絜縕也。一曰敝絮也。从糸，奴聲。易曰「需有衣絮」。', '糸;奴', '奴', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8756, '縕;緼', 'yùn', '第十三卷', '糸部', '紼也。从糸，𥁕聲。', '糸;𥁕', '𥁕', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8757, '紼', 'fú', '第十三卷', '糸部', '亂枲也。从糸，弗聲。', '糸;弗', '弗', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8758, '絣', 'bēnɡ', '第十三卷', '糸部', '氐人殊縷布也。从糸，并聲。', '糸;并', '并', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8759, '紕', 'bǐ', '第十三卷', '糸部', '氐人𦇧也。从糸，比聲。讀若禹貢「玭珠」。', '糸;比;玭', '比;玭', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8760, '𦇧', 'jì', '第十三卷', '糸部', '西胡毳布也。从糸，罽聲。', '糸;罽', '罽', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8761, '縊', 'yì', '第十三卷', '糸部', '絞也。从糸，益聲。春秋傳曰「夷姜縊」。', '糸;益', '益', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8762, '綏', 'suī', '第十三卷', '糸部', '車中靶也。从糸，妥聲。', '糸;妥', '妥', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8763, '彝', 'yí', '第十三卷', '糸部', '宗廟常器也。从糸。糸，綦也。廾持之。米，器中實也。从彑。象形。此與爵相佀。周禮「六彝」——雞彝、鳥彝、黃彝、虎彝、蜼彝、斝彝，㠯待祼將之禮。[素]皆古文彝[絲爲]。', '糸;廾;米;彑;素;絲;爲', '彑', 'null', NULL, '糸;廾;米;素;絲;爲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8764, '緻', 'zhì', '第十三卷', '糸部', '密也。从糸，致聲。', '糸;致', '致', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '大徐补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8765, '紈', 'wán', '第十三卷', '糸部', '素也。从糸，丸聲。', '糸;丸', '丸', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '政宇附新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8766, '緗', 'xiānɡ', '第十三卷', '糸部', '帛淺黃色也。从糸，相聲。', '糸;相', '相', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8767, '緋', 'fēi', '第十三卷', '糸部', '帛赤色也。从糸，非聲。', '糸;非', '非', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8768, '緅', 'zōu', '第十三卷', '糸部', '帛靑赤色也。从糸，取聲。', '糸;取', '取', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8769, '繖', 'sǎn', '第十三卷', '糸部', '蓋也。从糸，散聲。', '糸;散', '散', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8770, '綀', 'shū', '第十三卷', '糸部', '布屬。从糸，束聲。', '糸;束', '束', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8771, '縡', 'zǎi', '第十三卷', '糸部', '事也。从糸，宰聲。', '糸;宰', '宰', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8772, '繾', 'qiǎn', '第十三卷', '糸部', '繾綣，不相離也。从糸，遣聲。', '糸;遣', '遣', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8773, '綣', 'quǎn', '第十三卷', '糸部', '繾綣也。从糸，卷聲。', '糸;卷', '卷', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8774, '素', 'sù', '第十三卷', '素部', '白致繒也。从糸𠂹。取其澤也。凡素之屬皆从素。', '糸;𠂹', '', 'null', NULL, '糸;𠂹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8775, '𢍣', 'jú', '第十三卷', '素部', '素屬。从素，廾聲。', '素;廾', '廾', 'null', NULL, '素', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8776, '䋤', 'yuè', '第十三卷', '素部', '白䋤，縞也。从素，勺聲。', '素;勺', '勺', 'null', NULL, '素', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8777, '𦆾', 'lǜ', '第十三卷', '素部', '素屬。从素，率聲。', '素;率', '率', 'null', NULL, '素', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8778, '繛;綽', 'chuò', '第十三卷', '素部', '𦅻也。从素，卓聲。繛或省[糸]。', '素;卓;糸', '卓', 'null', NULL, '素;糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8779, '𦅻;緩', 'huǎn', '第十三卷', '素部', '繛也。从素，爰聲。𦅻或省[糸]。', '素;爰;糸', '爰', 'null', NULL, '素;糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8780, '絲', 'sī', '第十三卷', '絲部', '蠶所吐也。从二糸。凡絲之屬皆从絲。', '糸', '', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8781, '𦆕', 'pèi', '第十三卷', '絲部', '馬𦆕也。从絲車。與連同意。詩曰「六𦆕如絲」。', '絲;車', '', 'null', NULL, '絲;車', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8782, '𢇅', 'ɡuān', '第十三卷', '絲部', '織㠯絲毌杼也。从絲省，卝聲。卝，古文卵字。', '絲;卝', '卝', 'null', NULL, '絲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8783, '率', 'shuài', '第十三卷', '率部', '捕鳥畢也。象絲网，上下其竿柄也。凡率之屬皆从率。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8784, '虫', 'huǐ', '第十三卷', '虫部', '一名蝮，博三寸，首大如擘指。象其臥形。物之𢼸細，或行或飛，或毛或𧝹，或介或鱗，㠯虫爲象。凡虫之屬皆从虫。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8785, '蝮', 'fù', '第十三卷', '虫部', '虫也。从虫，复聲。', '虫;复', '复', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8786, '螣', 'ténɡ', '第十三卷', '虫部', '神它也。从虫，朕聲。', '虫;朕', '朕', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8787, '蚺', 'rán', '第十三卷', '虫部', '大它。可食。从虫，冉聲。', '虫;冉', '冉', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8788, '螼', 'qǐn', '第十三卷', '虫部', '螾也。从虫，堇聲。', '虫;堇', '堇', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8789, '螾;蚓', 'yǐn', '第十三卷', '虫部', '側行者。从虫，寅聲。', '虫;寅', '寅', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8790, '螉', 'wēnɡ', '第十三卷', '虫部', '螉䗥。蟲在牛、馬皮者。从虫，翁聲。', '虫;翁', '翁', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8791, '䗥', 'zōnɡ', '第十三卷', '虫部', '螉䗥也。从虫，從聲。', '虫;從', '從', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
