INSERT INTO `shuowen_voice_revel` VALUES (8857, '蚩', 'chī', '第十三卷', '虫部', '蚩蟲也。从虫，之聲。', '虫;之', '之', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8858, '螌', 'bān', '第十三卷', '虫部', '螌蝥，毒蟲也。从虫，般聲。', '虫;般', '般', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8859, '蝥', 'máo', '第十三卷', '虫部', '螌蝥也。从虫，敄聲。', '虫;敄', '敄', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8860, '蟠', 'fán', '第十三卷', '虫部', '鼠婦也。从虫，番聲。', '虫;番', '番', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8861, '𧉅', 'yī', '第十三卷', '虫部', '𧉅威，委黍。委黍，鼠婦也。从虫，伊省聲。', '虫;伊', '伊', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8862, '蜙;蚣;𧌻', 'sōnɡ', '第十三卷', '虫部', '蜙蝑，春黍也。㠯股鳴者。从虫，松聲。蜙或省[公]。', '虫;松;公', '松;公', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8863, '蝑', 'xū', '第十三卷', '虫部', '蜙蝑也。从虫，胥聲。', '虫;胥', '胥', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8864, '蟅', 'zhè', '第十三卷', '虫部', '螽也。从虫，庶聲。', '虫;庶', '庶', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8865, '蝗', 'huánɡ', '第十三卷', '虫部', '螽也。从虫，皇聲。', '虫;皇', '皇', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8866, '蜩', 'tiáo', '第十三卷', '虫部', '蟬也。从虫，周聲。詩曰「五月鳴蜩」。蜩或从舟。', '虫;周;舟', '周;舟', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8867, '蟬', 'chán', '第十三卷', '虫部', '㠯旁鳴者。从虫，單聲。', '虫;單', '單', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8868, '蜺', 'ní', '第十三卷', '虫部', '寒蜩也。从虫，兒聲。', '虫;兒', '兒', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8869, '螇', 'xī', '第十三卷', '虫部', '螇鹿，蛁尞也。从虫，奚聲。', '虫;奚', '奚', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8870, '蚗', 'jué', '第十三卷', '虫部', '𧉅蚗，蛁尞也。从虫，夬聲。', '虫;夬', '夬', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8871, '𧉄', 'mián', '第十三卷', '虫部', '𧉄蚗，蟬屬。讀若「周天子 赧」。从虫，丏聲。', '赧;虫;丏', '赧;丏', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8872, '蛚;𧊿', 'liè', '第十三卷', '虫部', '蜻蛚也。从虫，列聲。', '虫;列', '列', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8873, '蜻', 'jīnɡ', '第十三卷', '虫部', '蜻蛚也。从虫，靑聲。', '虫;靑', '靑', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8874, '蛉', 'línɡ', '第十三卷', '虫部', '蜻蛉也。从虫，令聲。一曰桑根。', '虫;令', '令', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8875, '蠓', 'měnɡ', '第十三卷', '虫部', '蔑蠓也。从虫，蒙聲。', '虫;蒙', '蒙', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8876, '𧎾', 'lüè', '第十三卷', '虫部', '螶𧎾。一曰浮游。朝生莫死者。从虫，𡭴聲。', '虫;𡭴', '𡭴', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8877, '蜹', 'ruì', '第十三卷', '虫部', '秦、晉謂之蜹。楚謂之蟁。从虫，芮聲。', '虫;芮', '芮', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8878, '蟰', 'xiāo', '第十三卷', '虫部', '蟰蛸，長股者。从虫，肅聲。', '虫;肅', '肅', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8879, '𧍖', 'shěnɡ', '第十三卷', '虫部', '𧍖蟲也。从虫，省聲。', '虫;省', '省', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8880, '蛶', 'liè', '第十三卷', '虫部', '商何也。从虫，寽聲。', '虫;寽', '寽', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8881, '蜡', 'qù', '第十三卷', '虫部', '蠅䏣也。周禮「蜡氏掌徐骴」。从虫，昔聲。', '虫;昔', '昔', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8882, '蝡', 'ruǎn', '第十三卷', '虫部', '動也。从虫，耎聲。', '虫;耎', '耎', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8883, '蚑', 'qí', '第十三卷', '虫部', '行也。从虫，支聲。', '虫;支', '支', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8884, '蠉', 'xuān', '第十三卷', '虫部', '蟲行也。从虫，瞏聲。', '虫;瞏', '瞏', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8885, '𧈪', 'chǎn', '第十三卷', '虫部', '蟲申行也。从虫，屮聲。讀若騁。', '虫;屮;騁', '屮;騁', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8886, '螸', 'yú', '第十三卷', '虫部', '螽醜螸。𠂹腴也。从虫，欲聲。', '虫;欲', '欲', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8887, '𧎥', 'shàn', '第十三卷', '虫部', '蠅醜𧎥。摇翼也。从虫，扇聲。', '虫;扇', '扇', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8888, '蛻', 'tuì', '第十三卷', '虫部', '它、蟬所解皮也。从虫，兌聲。', '虫;兌', '兌', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8889, '𧊝', 'hē', '第十三卷', '虫部', '螫也。从虫，若省聲。', '虫', '', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8890, '螫', 'shì', '第十三卷', '虫部', '蟲行毒也。从虫，赦聲。', '虫;赦', '赦', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8891, '蝁', 'è', '第十三卷', '虫部', '镻也。从虫，亞聲。', '虫;亞', '亞', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8892, '蛘', 'yǎnɡ', '第十三卷', '虫部', '騷蛘也。从虫，羊聲。', '虫;羊', '羊', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8893, '𧐂;蝕', 'shí', '第十三卷', '虫部', '敗創也。从虫人食。食亦聲。', '虫;人;食', '食', 'null', NULL, '虫;人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8894, '蛟', 'jiāo', '第十三卷', '虫部', '龍屬。無角曰蛟。从虫，交聲。池魚滿三千六百，蛟來爲之長，能䢦魚而飛，置笱水中卽蛟去。', '虫;交', '交', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8895, '螭', 'chī', '第十三卷', '虫部', '若龍而黃。北方謂之地螻。从虫，离聲。或云無角曰螭。', '虫;离', '离', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8896, '虯', 'qiú', '第十三卷', '虫部', '龍無角者。从虫，丩聲。', '虫;丩', '丩', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8897, '蜦', 'lún', '第十三卷', '虫部', '它屬也。黑色，濳於神淵之中，能興雲致雨。从虫，侖聲。讀若「䓞艸」。蜦或从戾。', '虫;侖;䓞;戾', '侖;䓞;戾', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8898, '螊', 'lián', '第十三卷', '虫部', '海蟲也。長寸而白，可食。从虫，兼聲。讀若嗛。', '虫;兼;嗛', '兼;嗛', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8899, '蜃', 'shèn', '第十三卷', '虫部', '大蛤。雉入水所𠤎。从虫，辰聲。', '虫;辰', '辰', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8900, '蛤;𧊧', 'ɡé', '第十三卷', '虫部', '蜃屬。有三，皆生於海。厲，千歲雀所𠤎，秦人謂之「牡厲」。海蛤者，百歲燕所𠤎也。魁蛤，一名復絫，老服翼所𠤎也。从虫，合聲。', '虫;合', '合', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8901, '螷', 'pí', '第十三卷', '虫部', '陛也。脩爲螷。圓爲蠇。从虫，庳聲。', '虫;庳', '庳', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8902, '蚌', 'bànɡ', '第十三卷', '虫部', '蜃屬。从虫，丰聲。', '虫;丰', '丰', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8903, '蠇', 'lì', '第十三卷', '虫部', '蚌屬。佀螊𢼸大，出海中，今民食之。从虫，萬聲。讀若賴。', '虫;萬;賴', '萬;賴', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8904, '蝸', 'wō', '第十三卷', '虫部', '蝸，蠃也。从虫，咼聲。', '虫;咼', '咼', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8905, '蝓', 'yú', '第十三卷', '虫部', '虒蝓也。从虫，俞聲。', '虫;俞', '俞', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8906, '蜎', 'yuān', '第十三卷', '虫部', '肙也。从虫，肙聲。', '虫;肙', '肙', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8907, '蟺', 'shàn', '第十三卷', '虫部', '夗蟺也。从虫，亶聲。', '虫;亶', '亶', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8908, '𧍘', 'yōu', '第十三卷', '虫部', '𧍘蟉也。从虫，幽聲。', '虫;幽', '幽', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8909, '蟉', 'liú', '第十三卷', '虫部', '𧍘蟉也。从虫，翏聲。', '虫;翏', '翏', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8910, '蟄', 'zhé', '第十三卷', '虫部', '臧也。从虫，執聲。', '虫;執', '執', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8911, '蚨', 'fú', '第十三卷', '虫部', '靑蚨，水蟲，可還錢。从虫，夫聲。', '虫;夫', '夫', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8912, '䗇', 'jú', '第十三卷', '虫部', '䗇鼀，詹諸。㠯脰鳴者。从虫，匊聲。', '虫;匊', '匊', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8913, '蝦', 'há', '第十三卷', '虫部', '蝦蟆也。从虫，叚聲。', '虫;叚', '叚', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8914, '蟆', 'má', '第十三卷', '虫部', '蝦蟆也。从虫，莫聲。', '虫;莫', '莫', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8915, '蠵', 'xī', '第十三卷', '虫部', '觜蠵。大龜也。㠯胃鳴者。从虫，巂聲。司馬相如說蠵从敻。', '虫;巂;敻', '巂;敻', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8916, '螹', 'jiàn', '第十三卷', '虫部', '螹離也。从虫，斬聲。', '虫;斬', '斬', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8917, '蠏;蟹', 'xiè', '第十三卷', '虫部', '有二敖八足。旁行。非它鮮之穴，無所庇。从虫，解聲。蠏或从魚。', '虫;解;魚', '解', 'null', NULL, '虫;魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8918, '蛫', 'ɡuǐ', '第十三卷', '虫部', '蠏也。从虫，危聲。', '虫;危', '危', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8919, '蜮;蟈', 'yù', '第十三卷', '虫部', '短弧也。佀鼈三足，㠯气射害人。从虫，或聲。蜮又从國。', '虫;或;國', '或;國', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8920, '𧊜', 'è', '第十三卷', '虫部', '佀蜥易長一丈。水潛，吞人卽浮，出日南也。从虫，屰聲。', '虫;屰', '屰', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8921, '蛧', 'wǎnɡ', '第十三卷', '虫部', '蛧蜽，山川之精物也。淮南王說「蛧蜽，狀如三歲小兒，赤黑色，赤目，長耳，美髮」。从虫，网聲。國語曰「木石之怪——夔、蛧蜽」。', '虫;网', '网', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8922, '蜽', 'liǎnɡ', '第十三卷', '虫部', '蛧蜽也。从虫，兩聲。', '虫;兩', '兩', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8923, '蝯', 'yuán', '第十三卷', '虫部', '善援。禺屬。从虫，爰聲。', '虫;爰', '爰', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8924, '蠗', 'zhuó', '第十三卷', '虫部', '禺屬。从虫，翟聲。', '虫;翟', '翟', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8925, '蜼', 'wèi', '第十三卷', '虫部', '如母猴。卬鼻長尾。从虫，隹聲。', '虫;隹', '隹', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8926, '蚼', 'ɡǒu', '第十三卷', '虫部', '北方有蚼犬，食人。从虫，句聲。', '虫;句', '句', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8927, '蛩', 'qiónɡ', '第十三卷', '虫部', '蛩蛩，獸也。一曰秦謂蟬蛻曰蛩。从虫，巩聲。', '虫;巩', '巩', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8928, '蟨', 'jué', '第十三卷', '虫部', '鼠也。一曰西方有獸，前足短，與蛩蛩、巨虛比，其名曰蟨。从虫，厥聲。', '虫;厥', '厥', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8929, '蝙', 'biān', '第十三卷', '虫部', '蝙蝠，服翼也。从虫，扁聲。', '虫;扁', '扁', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8930, '蝠', 'fú', '第十三卷', '虫部', '蝙蝠也。从虫，畐聲。', '虫;畐', '畐', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8931, '蠻', 'mán', '第十三卷', '虫部', '南蠻。它穜。从虫，䜌聲。', '虫;䜌', '䜌', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8932, '閩', 'mǐn', '第十三卷', '虫部', '東南越。它穜。从虫，門聲。', '虫;門', '門', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8933, '虹', 'hónɡ', '第十三卷', '虫部', '螮蝀也。狀佀虫。从虫，工聲。朙堂月令曰「虹始見」。籒文虹从申。申，電也。', '虫;工;申', '工', 'null', NULL, '虫;申', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8934, '螮', 'dì', '第十三卷', '虫部', '螮蝀，虹也。从虫，帶聲。', '虫;帶', '帶', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8935, '蝀', 'dònɡ', '第十三卷', '虫部', '螮蝀也。从虫，東聲。', '虫;東', '東', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8936, '蠥', 'niè', '第十三卷', '虫部', '衣服、歌䚻、艸木之怪謂之䄏。禽獸、蟲蝗之怪謂之蠥。从虫，辥聲。', '虫;辥', '辥', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8937, '蜑', 'dàn', '第十三卷', '虫部', '南方夷也。从虫，延聲。', '虫;延', '延', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8938, '蛋', 'dàn', '第十三卷', '虫部', '俗卵字。从虫，闕。讀與彈同。', '彈', '彈', 'null', NULL, '', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8939, '蟪', 'huì', '第十三卷', '虫部', '蟪蛄，蟬也。从虫，惠聲。', '虫;惠', '惠', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8940, '蠛', 'miè', '第十三卷', '虫部', '蠛蠓，細蟲也。从虫，蔑聲。', '虫;蔑', '蔑', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8941, '虴', 'zhé', '第十三卷', '虫部', '虴蜢，艸上蟲也。从虫，乇聲。', '虫;乇', '乇', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8942, '蜢', 'měnɡ', '第十三卷', '虫部', '虴蜢也。从虫，孟聲。', '虫;孟', '孟', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8943, '蟋', 'xī', '第十三卷', '虫部', '蟋𧍓也。从虫，悉聲。', '虫;悉', '悉', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8944, '螳', 'tánɡ', '第十三卷', '虫部', '螳蜋也。从虫，堂聲。', '虫;堂', '堂', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8945, '䖵', 'kūn', '第十三卷', '䖵部', '蟲之總名也。从二虫。凡䖵之屬皆从䖵。讀若昆。', '虫;昆', '昆', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8946, '蠶', 'cán', '第十三卷', '䖵部', '任絲蟲也。从䖵，朁聲。', '䖵;朁', '朁', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8947, '𧒎', 'é', '第十三卷', '䖵部', '蠶𠤎飛𧒎。从䖵，我聲。或从虫。', '䖵;我;虫', '我', 'null', NULL, '䖵;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8948, '𧎮;蚤', 'zǎo', '第十三卷', '䖵部', '齧人跳蟲也。从䖵，㕚聲。㕚，古爪字。蚤𧎮或从虫。', '䖵;㕚;虫', '㕚', 'null', NULL, '䖵;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8949, '蝨', 'shī', '第十三卷', '䖵部', '齧人蟲。从䖵，卂聲。', '䖵;卂', '卂', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8950, '𧍸;螽', 'zhōnɡ', '第十三卷', '䖵部', '蝗也。从䖵，𠂂聲。𠂂，古文終字。螽或从虫，眾聲。', '䖵;𠂂;虫;眾', '𠂂;眾', 'null', NULL, '䖵;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8951, '𧒝;𧖉;𧔡;𧎰', 'zhǎn', '第十三卷', '䖵部', '𧖉蟲也。从䖵，㞡省聲。', '䖵;㞡', '㞡', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8952, '蠽', 'jié', '第十三卷', '䖵部', '小蟬蜩也。从䖵，𢧵聲。', '䖵;𢧵', '𢧵', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8953, '蠿', 'zhuō', '第十三卷', '䖵部', '蠿蟊，作网鼄蟊也。从䖵，𢇍聲。𢇍，古文絕字。', '䖵;𢇍', '𢇍', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8954, '蟊', 'máo', '第十三卷', '䖵部', '蠿蟊也。从䖵，矛聲。', '䖵;矛', '矛', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8955, '𧕝', 'nínɡ', '第十三卷', '䖵部', '𧕝蟲也。从䖵，寍聲。', '䖵;寍', '寍', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8956, '𧕐;𧖤;螬', 'cáo', '第十三卷', '䖵部', '𧓉𧖤也。从䖵，曹聲。', '䖵;曹', '曹', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8957, '𧕱', 'xiá', '第十三卷', '䖵部', '螻蛄也。从䖵，舝聲。', '䖵;舝', '舝', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8958, '𧓎;蜱', 'pí', '第十三卷', '䖵部', '𧓎蛸也。从䖵，卑聲。𧓎或从虫。', '䖵;卑;虫', '卑', 'null', NULL, '䖵;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8959, '蠭;𧒒', 'fēnɡ', '第十三卷', '䖵部', '飛蟲螫人者。从䖵，逢聲。古文省[夆]。', '䖵;逢;夆', '逢;夆', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8960, '𧖅;蜜', 'mì', '第十三卷', '䖵部', '蠭甘飴也。一曰螟子。从虫，鼏聲。𧖅或从宓。', '虫;鼏;宓', '鼏;宓', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8961, '螶', 'qú', '第十三卷', '䖵部', '螶𧎾也。从䖵，巨聲。', '䖵;巨', '巨', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8962, '蟁;蚊', 'wén', '第十三卷', '䖵部', '齧人飛蟲。从䖵，民聲。蟁或从昏。㠯昏時出也。俗蟁。从虫，从文。', '䖵;民;昏;虫;文', '民;昏;文', 'null', NULL, '䖵;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8963, '蝱;䖟', 'ménɡ', '第十三卷', '䖵部', '齧人飛蟲。从䖵，亡聲。', '䖵;亡', '亡', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8964, '蠹;蠧', 'dù', '第十三卷', '䖵部', '木中蟲。从䖵，槖聲。蠹或从木。象蟲在木中形，譚長說。', '䖵;槖;木', '槖', 'null', NULL, '䖵;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8965, '蠡', 'lǐ', '第十三卷', '䖵部', '蟲齧木中也。从䖵，𧰲聲。古文[未详]。', '䖵;𧰲', '𧰲', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8966, '𧒔;蛷', 'qiú', '第十三卷', '䖵部', '多足蟲也。从䖵，求聲。', '䖵;求', '求', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8967, '𧖚;蜉;𧕰', 'fú', '第十三卷', '䖵部', '𧖕𧕰也。从䖵，㯱聲。𧕰或从虫，从孚。', '䖵;㯱;虫;孚', '㯱;孚', 'null', NULL, '䖵;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8968, '𧕲', 'juǎn', '第十三卷', '䖵部', '蟲食也。从䖵，雋聲。', '䖵;雋', '雋', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8969, '蠢;𢧸', 'chǔn', '第十三卷', '䖵部', '蟲動也。从䖵，春聲。古文蠢从𢦒。周書曰「我有𢧸于西」。', '䖵;春;𢦒', '春', 'null', NULL, '䖵;𢦒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8970, '𧕈', 'wèi', '第十三卷', '䖵部', '飛螘。从䖵，㷉聲。', '䖵;㷉', '㷉', 'null', NULL, '䖵', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8971, '蟲', 'chónɡ', '第十三卷', '蟲部', '有足謂之蟲，無足謂之豸。从三虫。凡蟲之屬皆从蟲。', '虫', '', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8972, '𧔨;蝥;蛑', 'máo', '第十三卷', '蟲部', '蟲食艸𣒨者。从蟲。弔，象形。吏抵冒取民財則生。𧔨或从敄。古文𧔨从虫，从牟。', '蟲;弔;敄;虫;牟', '敄;牟', 'null', NULL, '蟲;弔;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8973, '𧖕;𧖈;蚍', 'pí', '第十三卷', '蟲部', '𧖈𧖚，大螘也。从蟲，毗聲。𧖈或从虫，比聲。', '蟲;毗;虫;比', '毗;比', 'null', NULL, '蟲;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8974, '𧖔', 'lìn', '第十三卷', '蟲部', '蟁也。从蟲，𨳌聲。', '蟲;𨳌', '𨳌', 'null', NULL, '蟲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8975, '𧕿;蜚', 'fěi', '第十三卷', '蟲部', '臭蟲。負蠜也。从蟲，非聲。𧕿或从虫。', '蟲;非;虫', '非', 'null', NULL, '蟲;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8976, '蠱', 'ɡǔ', '第十三卷', '蟲部', '腹中蟲也。春秋傳曰「皿蟲爲蠱」。晦淫之所生也。梟磔死之鬼亦爲蠱。从蟲，从皿。皿，物之用也。', '蟲;皿', '', 'null', NULL, '蟲;皿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8977, '風', 'fēnɡ', '第十三卷', '風部', '八風也。東方曰朙「庶風」。東南曰淸「朙風」。南方曰「景風」。西南曰「涼風」。西方曰「閶𨵵風」。西北曰「不周風」。北方曰「廣莫風」。東北曰「融風」。从虫，凡聲。風動蟲生，故蟲八日而𠤎。凡風之屬皆从風。古文風[未详]。', '虫;凡', '凡', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8978, '𩘁;𩗬', 'liánɡ', '第十三卷', '風部', '北風謂之𩘁。从風，京聲。', '風;京', '京', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8979, '𩖶', 'xuè', '第十三卷', '風部', '小風也。从風，朮聲。', '風;朮', '朮', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8980, '飆', 'biāo', '第十三卷', '風部', '扶搖風也。从風，猋聲。古文飆[包]。', '風;猋;包', '猋;包', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8981, '飄', 'piāo', '第十三卷', '風部', '回風也。从風，票聲。', '風;票', '票', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8982, '颯;䬃', 'sà', '第十三卷', '風部', '風聲也。从風，立聲。', '風;立', '立', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8983, '飂', 'liú', '第十三卷', '風部', '高風也。从風，翏聲。', '風;翏', '翏', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8984, '䬍', 'hū', '第十三卷', '風部', '疾風也。从風忽。忽亦聲。', '風;忽', '忽', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8985, '䬑', 'wèi', '第十三卷', '風部', '大風也。从風，胃聲。', '風;胃', '胃', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8986, '䫻', 'yù', '第十三卷', '風部', '大風也。从風，曰聲。', '風;曰', '曰', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8987, '颺', 'yánɡ', '第十三卷', '風部', '風所飛揚也。从風，昜聲。', '風;昜', '昜', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8988, '䬆', 'lì', '第十三卷', '風部', '䬆颲，風雨𣉱疾也。从風，利聲。讀若栗。', '風;利;栗', '利;栗', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8989, '颲', 'liè', '第十三卷', '風部', '䬆颲也。从風，列聲。讀若烈。', '風;列;烈', '列;烈', 'null', NULL, '風', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8990, '颸', 'sī', '第十三卷', '風部', '涼風也。从風，思聲。', '風;思', '思', 'null', NULL, '風', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8991, '䬒;颼', 'sōu', '第十三卷', '風部', '颼飂也。从風，叜聲。', '風;叜', '叜', 'null', NULL, '風', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8992, '颭', 'zhǎn', '第十三卷', '風部', '風吹浪動也。从風，占聲。', '風;占', '占', 'null', NULL, '風', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8993, '它;蛇;虵', 'tā', '第十三卷', '它部', '虫也。从虫而長。象冤曲𠂹尾形。上古艸凥，患它，故相問「無它乎？」凡它之屬皆从它。它或从虫。', '虫', '', 'null', NULL, '虫', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8994, '龜', 'ɡuī', '第十三卷', '龜部', '舊也。外骨內肉者也。从它。龜頭與它頭同。天地之性，廣肩無雄，龜鼈之類，㠯它爲雄。象足甲尾之形。凡龜之屬皆从龜。古文龜。', '它', '', 'null', NULL, '它', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8995, '䶱', 'tónɡ', '第十三卷', '龜部', '龜名。从龜，𠂂聲。𠂂，古文終字。', '龜;𠂂', '𠂂', 'null', NULL, '龜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8996, '䶲', 'rán', '第十三卷', '龜部', '龜甲邊也。从龜，冉聲。天子巨䶲，尺有二寸，諸矦尺，大夫八寸，士六寸。', '龜;冉', '冉', 'null', NULL, '龜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8997, '黽', 'měnɡ', '第十三卷', '黽部', '鼃黽也。从它。象形。黽頭與它頭同。凡黽之屬皆从黽。籒文黽。', '它', '', 'null', NULL, '它', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8998, '鼃;蛙', 'wā', '第十三卷', '黽部', '蝦蟆屬。从黽，圭聲。', '黽;圭', '圭', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8999, '鼀;𪓰', 'cù', '第十三卷', '黽部', '𡴆鼀，詹諸也。其鳴詹諸，其皮鼀鼀，其行𡴆𡴆。从黽𡴆。𡴆亦聲。鼀或从酋。', '黽;𡴆;酋;戚', '𡴆;酋;戚', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9000, '𪓿', 'shī', '第十三卷', '黽部', '𪓰𪓿，詹諸也。詩曰「得此𪓰𪓿」。言其行𪓿𪓿。从黽，爾聲。', '黽;爾;施', '爾;施', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9001, '鼈', 'biē', '第十三卷', '黽部', '甲蟲也。从黽，敝聲。', '黽;敝', '敝', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9002, '黿', 'yuán', '第十三卷', '黽部', '大鼈也。从黽，元聲。', '黽;元', '元', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9003, '鼉', 'tuó', '第十三卷', '黽部', '水蟲。佀蜥易。長丈所。皮可爲𡔷。从黽，單聲。', '黽;單', '單', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9004, '𪓷', 'xí', '第十三卷', '黽部', '水蟲也。薉貉之民食之。从黽，奚聲。', '黽;奚', '奚', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9005, '䵶', 'qú', '第十三卷', '黽部', '𪓷屬。頭有㒳角。出遼東。从黽，句聲。', '黽;句', '句', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9006, '蠅', 'yínɡ', '第十三卷', '黽部', '營營靑蠅。蟲之大腹者。从黽虫。', '黽;虫', '', 'null', NULL, '黽;虫', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9007, '䵹;蜘', 'zhī', '第十三卷', '黽部', '䵹鼄，鼄蟊也。从黽。𣉻省聲。或从虫。', '黽;𣉻;虫', '𣉻', 'null', NULL, '黽;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9008, '鼄;蛛', 'zhū', '第十三卷', '黽部', '䵹鼄也。从黽，朱聲。鼄或从虫。', '黽;朱;虫', '朱', 'null', NULL, '黽;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9009, '鼂', 'cháo', '第十三卷', '黽部', '匽鼂也。讀若朝。楊雄說，匽鼂，蟲名。杜林㠯爲「朝旦」，非是。从黽，从旦。古文从㫐。', '朝;黽;旦;㫐', '朝;㫐', 'null', NULL, '黽;旦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9010, '鼇', 'áo', '第十三卷', '黽部', '海大鼈也。从黽，敖聲。', '黽;敖', '敖', 'null', NULL, '黽', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9011, '卵;卝', 'luǎn', '第十三卷', '卵部', '凡物無乳者卵生。象形。凡卵之屬皆从卵。古文卵。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9012, '毈', 'duàn', '第十三卷', '卵部', '卵不孚也。从卵，段聲。', '卵;段', '段', 'null', NULL, '卵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9013, '二;弍', 'èr', '第十三卷', '二部', '地之數也。从耦一。凡二之屬皆从二。古文二[弋]。', '一;弋', '', 'null', NULL, '一;弋', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9014, '亟', 'jí', '第十三卷', '二部', '敏疾也。从人口又二。二，天地也。', '人;口;又;二', '', 'null', NULL, '人;口;又;二', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9015, '恆;恒', 'hénɡ', '第十三卷', '二部', '常也。从心舟在二之閒上下。心㠯舟施，恆也。古文恆从月。詩曰「如月之恆」。', '心;舟;二;月', '', 'null', NULL, '心;舟;二;月', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9016, '亘', 'ɡèn', '第十三卷', '二部', '求回也。从二，从囘。囘，古文回，象𠄢回之形。上下所求物也。', '二;囘', '', 'null', NULL, '二;囘', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9017, '竺', 'dǔ', '第十三卷', '二部', '㫗也。从二，竹聲。', '二;竹', '竹', 'null', NULL, '二', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9018, '凡', 'fán', '第十三卷', '二部', '冣括而言也。从二。二，耦也。从𢎜。𢎜，古文及字。', '二;𢎜', '', 'null', NULL, '二;𢎜', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9019, '土', 'tǔ', '第十三卷', '土部', '地之吐生萬物者也。𠄞，象地之上、地之中。丨，物出形也。凡土之屬皆从土。', '𠄞;丨', '', 'null', NULL, '𠄞;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9020, '地', 'dì', '第十三卷', '土部', '元气初分。輕淸昜爲天。重濁侌爲地。萬物所敶列也。从土，也聲。籒文地从𨸏土，𧰲聲。', '土;也;𨸏;土;𧰲', '也;𧰲', 'null', NULL, '土;𨸏;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9021, '坤', 'kūn', '第十三卷', '土部', '地也。易之卦也。从土申。土位在申也。', '土;申', '', 'null', NULL, '土;申', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9022, '垓', 'ɡāi', '第十三卷', '土部', '兼晐八極地也。从土，亥聲。國語曰「天子凥九垓之田」。', '土;亥', '亥', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9023, '墺', 'ào', '第十三卷', '土部', '四方之土可定凥者也。从土，奥聲。古文墺[釆]。', '土;奥;釆', '奥;釆', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9024, '堣', 'yú', '第十三卷', '土部', '堣夷，在冀州 暘谷。立春日。日值之而出。从土，禺聲。尙書曰「宅堣夷」。', '土;禺', '禺', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9025, '坶', 'mù', '第十三卷', '土部', '朝歌南七十里地。周書曰「武王與紂戰于坶野」。从土，母聲。', '土;母', '母', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9026, '坡', 'pō', '第十三卷', '土部', '阪也。从土，皮聲。', '土;皮', '皮', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9027, '坪', 'pínɡ', '第十三卷', '土部', '地平也。从土平。平亦聲。', '土;平', '平', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9028, '均', 'jūn', '第十三卷', '土部', '平徧也。从土匀。匀亦聲。', '土;匀', '匀', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9029, '壤', 'rǎnɡ', '第十三卷', '土部', '柔土也。从土，襄聲。', '土;襄', '襄', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9030, '塙', 'què', '第十三卷', '土部', '堅不可拔也。从土，高聲。', '土;高', '高', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9031, '墽', 'qiāo', '第十三卷', '土部', '磽也。从土，敫聲。', '土;敫', '敫', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9032, '壚', 'lú', '第十三卷', '土部', '黑剛土也。从土，盧聲。', '土;盧', '盧', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9033, '㙚', 'xīnɡ', '第十三卷', '土部', '赤剛土也。从土，觲省聲。', '土;觲', '觲', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9034, '埴', 'zhí', '第十三卷', '土部', '黏土也。从土，直聲。', '土;直', '直', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9035, '坴', 'lù', '第十三卷', '土部', '土凷坴坴也。从土，𡴆聲。讀若速。一曰坴梁地。', '土;𡴆;速', '𡴆;速', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9036, '𡍦', 'hún', '第十三卷', '土部', '土也。从土，軍聲。雒陽有大𡍦里。', '土;軍', '軍', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9037, '墣', 'pú', '第十三卷', '土部', '凷也。从土，菐聲。墣或从卜。', '土;菐;卜', '菐;卜', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9038, '凷;塊', 'kuài', '第十三卷', '土部', '墣也。从土凵。凵屈象形。俗凷字[鬼]。', '土;凵;鬼', '鬼', 'null', NULL, '土;凵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9039, '堛', 'bì', '第十三卷', '土部', '凷也。从土，畐聲。', '土;畐', '畐', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9040, '堫', 'zōnɡ', '第十三卷', '土部', '穜也。从土，㚇聲。一曰內其中。', '土;㚇', '㚇', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9041, '塍', 'chénɡ', '第十三卷', '土部', '稻田中畦埒也。从土，朕聲。', '土;朕', '朕', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9042, '坺', 'bá', '第十三卷', '土部', '坺土也。一臿土謂之坺。从土，犮聲。詩曰「武王載坺」。一曰塵皃。', '土;犮', '犮', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9043, '坄', 'yì', '第十三卷', '土部', '匋竈窗也。从土，役省聲。', '土;役', '役', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9044, '基', 'jī', '第十三卷', '土部', '牆始也。从土，其聲。', '土;其', '其', 'null', NULL, '土', 'null', 'null', 'null', 'null', '基≌根', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9045, '垣', 'yuán', '第十三卷', '土部', '牆也。从土，亘聲。籒文垣从𩫖。', '土;亘;𩫖', '亘', 'null', NULL, '土;𩫖', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9046, '圪', 'yì', '第十三卷', '土部', '牆高皃也。詩曰「崇墉𡉛𡉛」。从土，气聲。', '土;气', '气', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9047, '堵', 'dǔ', '第十三卷', '土部', '垣也。五版爲堵。从土，者聲。籒文从𩫖。', '土;者;𩫖', '者', 'null', NULL, '土;𩫖', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9048, '壁', 'bì', '第十三卷', '土部', '垣也。从土，辟聲。', '土;辟', '辟', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9049, '㙩', 'liáo', '第十三卷', '土部', '𠣘垣也。从土，尞聲。', '土;尞', '尞', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9050, '堨', 'yè', '第十三卷', '土部', '壁閒隙也。从土，曷聲。讀若謁。', '土;曷;謁', '曷;謁', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9051, '埒;埓', 'liè', '第十三卷', '土部', '庳垣也。从土，寽聲。', '土;寽', '寽', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9052, '堪', 'kān', '第十三卷', '土部', '地突也。从土，甚聲。', '土;甚', '甚', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9053, '𡒈;堀', 'kū', '第十三卷', '土部', '突也。从土，屈聲。詩曰「蜉游𡒈閱」。', '土;屈', '屈', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9054, '堂;㙶', 'tánɡ', '第十三卷', '土部', '殿也。从土，尙聲。古文堂如此。籒文堂从尙，京省聲。', '土;尙;京', '尙;京', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9055, '垛', 'duǒ', '第十三卷', '土部', '門堂孰也。从土，朵聲。', '土;朵', '朵', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9056, '坫', 'diàn', '第十三卷', '土部', '屏也。从土，占聲。', '土;占', '占', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9057, '㙙', 'lǒnɡ', '第十三卷', '土部', '涂也。从土，浝聲。', '土;浝', '浝', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9058, '垷', 'xiàn', '第十三卷', '土部', '涂也。从土，見聲。', '土;見', '見', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9059, '墐', 'jìn', '第十三卷', '土部', '涂也。从土，堇聲。', '土;堇', '堇', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9060, '𡏲;塈', 'xì', '第十三卷', '土部', '卬涂也。从土，旣聲。', '土;旣', '旣', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9061, '堊', 'è', '第十三卷', '土部', '白涂也。从土，亞聲。', '土;亞', '亞', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9062, '墀;𡎰', 'chí', '第十三卷', '土部', '涂地也。从土，犀聲。禮「天子赤墀」。', '土;犀', '犀', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9063, '墼', 'jī', '第十三卷', '土部', '令適也。一曰未燒者。从土，毄聲。', '土;毄', '毄', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9064, '𡊄;𡊅', 'fèn', '第十三卷', '土部', '埽除也。从土，弁聲。讀若糞。', '土;弁;糞', '弁;糞', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9065, '埽', 'sǎo', '第十三卷', '土部', '𡊅也。从土帚。', '土;帚', '帚', 'null', NULL, '土', 'null', 'null', 'null', 'null', '幽部 | 㑴≌埽', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9066, '在', 'zài', '第十三卷', '土部', '存也。从土，才聲。', '土;才', '才', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9067, '𡋲;坐', 'zuò', '第十三卷', '土部', '止也。从畱省，从土。土，所止也。此與畱同意。古文𡋲[二人]。', '畱;土;人', '', 'null', NULL, '畱;土;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9068, '坁', 'zhǐ', '第十三卷', '土部', '箸也。从土，氏聲。', '土;氏', '氏', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9069, '塡;填', 'tián', '第十三卷', '土部', '𡫳也。从土，眞聲。', '土;眞', '眞', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9070, '坦', 'tǎn', '第十三卷', '土部', '安也。从土，旦聲。', '土;旦', '旦', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9071, '坒', 'bì', '第十三卷', '土部', '地相次坒也。衛大夫貞子名坒。从土，比聲。', '土;比', '比', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9072, '堤', 'dǐ', '第十三卷', '土部', '滯也。从土，是聲。', '土;是', '是', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9073, '壎', 'xūn', '第十三卷', '土部', '樂器也。㠯土作。六空。从土，熏聲。', '土;熏', '熏', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9074, '封', 'fēnɡ', '第十三卷', '土部', '爵諸侯之土也。从之土，从寸。寸，守其制度也。公侯百里，伯七十里，子男五十里。籒文封从丰土。古文封省。', '之;土;寸;丰;土', '丰', 'null', NULL, '之;土;寸;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9075, '壐;璽', 'xǐ', '第十三卷', '土部', '王者之印也。㠯主土。从土，爾聲。籒文从玉。', '土;爾;玉', '爾', 'null', NULL, '土;玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9076, '墨', 'mò', '第十三卷', '土部', '書墨也。从土黑。', '土;黑', '黑', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9077, '垸', 'huán', '第十三卷', '土部', '㠯桼龢灰丸而䰍也。从土，完聲。一曰補垣也。', '土;完', '完', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9078, '型;𡌒', 'xínɡ', '第十三卷', '土部', '鑄器之法也。从土，㓝聲。', '土;㓝', '㓝', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9079, '埻', 'zhǔn', '第十三卷', '土部', '埻旳，射臬也。从土，𦎫聲。讀若準。', '土;𦎫;準', '𦎫;準', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9080, '塒', 'shí', '第十三卷', '土部', '雞棲於垣爲塒。从土，時聲。', '土;時', '時', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9081, '城', 'chénɡ', '第十三卷', '土部', '㠯盛民也。从土成。成亦聲。籒文城从𩫏。', '土;成;𩫏', '成', 'null', NULL, '土;𩫏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9082, '墉', 'yōnɡ', '第十三卷', '土部', '城垣也。从土，庸聲。古文墉[𩫖省、𦤘省聲]。', '土;庸;𩫖;𦤘', '庸;𦤘', 'null', NULL, '土;𩫖', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9083, '𡑢', 'dié', '第十三卷', '土部', '城上女垣也。从土，葉聲。', '土;葉', '葉', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9084, '坎', 'kǎn', '第十三卷', '土部', '陷也。从土，欠聲。', '土;欠', '欠', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9085, '墊', 'diàn', '第十三卷', '土部', '下也。从土，執聲。春秋傳曰「墊隘」。', '土;執', '執', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9086, '坻;𣹡;𣲋', 'chí', '第十三卷', '土部', '小渚也。从土，氐聲。詩曰「宛在水中坻」。坻或从水，从夊。坻或从水耆。', '土;氐;水;夊;水;耆', '氐;夊;耆', 'null', NULL, '土;水;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9087, '㙷', 'zhí', '第十三卷', '土部', '下入也。从土，㬎聲。', '土;㬎', '㬎', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9088, '垎', 'hè', '第十三卷', '土部', '水乾也。从土，各聲。一曰堅也。', '土;各', '各', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9089, '垐;堲', 'cí', '第十三卷', '土部', '㠯土增大道上。从土，次聲。古文垐。从土卽。虞書曰「龍，朕堲讒說殄行」。堲，疾惡也。', '土;次;卽', '次;卽', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9090, '增', 'zēnɡ', '第十三卷', '土部', '益也。从土，曾聲。', '土;曾', '曾', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9091, '埤', 'pí', '第十三卷', '土部', '增也。从土，卑聲。', '土;卑', '卑', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9092, '坿', 'fù', '第十三卷', '土部', '益也。从土，付聲。', '土;付', '付', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9093, '塞', 'sài', '第十三卷', '土部', '隔也。从土，𡫳聲。', '土;𡫳', '𡫳', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9094, '圣', 'kū', '第十三卷', '土部', '汝、潁之閒謂致力於地曰圣。从又土。讀若「兔鹿窟[𡒈]」。', '又;土;𡒈', '𡒈', 'null', NULL, '又;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9095, '垍', 'jì', '第十三卷', '土部', '堅土也。讀若冀。从土，自聲。', '冀;土;自', '冀;自', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9096, '埱', 'chù', '第十三卷', '土部', '气出土也。一曰始也。从土，叔聲。', '土;叔', '叔', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9097, '埵', 'duǒ', '第十三卷', '土部', '堅土也。讀若朵。从土，垂聲。', '朵;土;垂', '朵;垂', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9098, '埐', 'jīn', '第十三卷', '土部', '地也。从土，侵省聲。', '土;侵', '侵', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9099, '埾', 'jù', '第十三卷', '土部', '積土也。从土，聚省聲。', '土;聚', '聚', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9100, '壔;𡑏', 'dǎo', '第十三卷', '土部', '保也。一曰高土也。讀若毒。从土，𠷎聲。', '毒;土;𠷎', '毒;𠷎', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9101, '培', 'péi', '第十三卷', '土部', '培敦，土田山川也。从土，㕻聲。', '土;㕻', '㕻', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9102, '埩', 'zhēnɡ', '第十三卷', '土部', '治也。从土，爭聲。', '土;爭', '爭', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9103, '墇', 'zhànɡ', '第十三卷', '土部', '𢹬也。从土，章聲。', '土;章', '章', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9104, '𡍫', 'cè', '第十三卷', '土部', '遏遮也。从土，則聲。', '土;則', '則', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9105, '垠;圻', 'yín', '第十三卷', '土部', '地垠㖾也。从土，艮聲。一曰岸也。垠或从斤。', '土;艮;斤', '艮;斤', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9106, '墠', 'shàn', '第十三卷', '土部', '野土也。从土，單聲。', '土;單', '單', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9107, '垑', 'chǐ', '第十三卷', '土部', '恃也。从土，多聲。', '土;多', '多', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9108, '壘', 'lěi', '第十三卷', '土部', '軍壁也。从土，靁省聲。', '土;靁', '靁', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9109, '垝', 'ɡuǐ', '第十三卷', '土部', '毀垣也。从土，危聲。詩曰「乘彼垝垣」。垝或从𨸏。', '土;危;𨸏', '危', 'null', NULL, '土;𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9110, '圮', 'pǐ', '第十三卷', '土部', '毀也。从土，己聲。虞書曰「方命圮族」。圮或从手，配省，非聲。', '土;己;手;配;非', '己;配;非', 'null', NULL, '土;手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9111, '垔', 'yīn', '第十三卷', '土部', '𡫳也。从土，西聲。商書曰「鯀垔洪水」。古文垔如此。', '土;西', '西', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9112, '塹', 'qiàn', '第十三卷', '土部', '阬也。从土，斬聲。一曰大也。', '土;斬', '斬', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9113, '埂', 'ɡěnɡ', '第十三卷', '土部', '秦謂阬爲埂。从土，更聲。讀若「井汲綆」。', '土;更;綆', '更;綆', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9114, '壙', 'kuànɡ', '第十三卷', '土部', '塹穴也。从土，廣聲。一曰大也。', '土;廣', '廣', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9115, '塏', 'kǎi', '第十三卷', '土部', '高燥也。从土，豈聲。', '土;豈', '豈', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9116, '毀;毁', 'huǐ', '第十三卷', '土部', '缺也。从土，毇省聲。', '土;毇', '毇', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9117, '壓', 'yā', '第十三卷', '土部', '壞也。一曰塞補也。从土，厭聲。', '土;厭', '厭', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9118, '壞;𣀩', 'huài', '第十三卷', '土部', '敗也。从土，褱聲。籒文壞从攴。古文壞省[眔土]。', '土;褱;攴;眔;土', '褱;眔', 'null', NULL, '土;攴;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9119, '坷', 'kě', '第十三卷', '土部', '坎坷也。从土，可聲。梁國 寧陵有坷亭。', '土;可', '可', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9120, '㙤', 'xià', '第十三卷', '土部', '𡍩也。从土，虖聲。㙤或从𨸏。', '土;虖;𨸏', '虖', 'null', NULL, '土;𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9121, '坼;𡍩', 'chè', '第十三卷', '土部', '裂也。从土，㡿聲。詩曰「不𡍩不疈」。', '土;㡿', '㡿', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9122, '坱', 'yǎnɡ', '第十三卷', '土部', '𪋻埃也。从土，央聲。', '土;央', '央', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9123, '塺', 'méi', '第十三卷', '土部', '𪋻也。从土，麻聲。', '土;麻', '麻', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9124, '塿', 'lǒu', '第十三卷', '土部', '塺土也。从土，婁聲。', '土;婁', '婁', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9125, '坋;坌', 'fèn', '第十三卷', '土部', '𪋻也。从土，分聲。一曰坋，大防也。', '土;分', '分', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9126, '𡌦', 'fèi', '第十三卷', '土部', '𪋻也。从土，非聲。', '土;非', '非', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9127, '埃', 'āi', '第十三卷', '土部', '𪋻也。从土，矣聲。', '土;矣', '矣', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9128, '㙠', 'yī', '第十三卷', '土部', '𪋻埃也。从土，殹聲。', '土;殹', '殹', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9129, '垽', 'yìn', '第十三卷', '土部', '澱也。从土，沂聲。', '土;沂', '沂', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9130, '垢', 'ɡòu', '第十三卷', '土部', '濁也。从土，后聲。', '土;后', '后', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9131, '㙪', 'yì', '第十三卷', '土部', '天侌𪋻起也。詩曰「㙪㙪其陰」。从土，壹聲。', '土;壹', '壹', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9132, '坏', 'pī', '第十三卷', '土部', '丘一成者也。一曰瓦未燒。从土，不聲。', '土;不', '不', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9133, '垤', 'dié', '第十三卷', '土部', '螘封也。从土，至聲。詩曰「雚鳴于垤」。', '土;至', '至', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9134, '坥', 'qū', '第十三卷', '土部', '益州部謂螾場曰坥。从土，且聲。', '土;且', '且', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9135, '埍', 'juǎn', '第十三卷', '土部', '徒隷所凥也。一曰女牢。一曰亭部。从土，肙聲。', '土;肙', '肙', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9136, '𧷎', 'kū', '第十三卷', '土部', '囚突出也。从土，㕢聲。', '土;㕢', '㕢', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9137, '瘞', 'yì', '第十三卷', '土部', '幽薶也。从土，㾜聲。', '土;㾜', '㾜', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9138, '堋', 'bènɡ', '第十三卷', '土部', '喪葬下土也。从土，朋聲。春秋傳曰「朝而堋」。禮謂之「封」。周官謂之「窆」。虞書曰「堋淫于家」亦如是。', '土;朋', '朋', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9139, '塋', 'yínɡ', '第十三卷', '土部', '墓地。从土，營省。亦聲。', '土;營', '營', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9140, '墓', 'mù', '第十三卷', '土部', '丘墓也。从土，莫聲。', '土;莫', '莫', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9141, '墳', 'fén', '第十三卷', '土部', '墓也。从土，賁聲。', '土;賁', '賁', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9142, '壟;壠', 'lǒnɡ', '第十三卷', '土部', '丘壠也。从土，龍聲。', '土;龍', '龍', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9143, '垗', 'zhào', '第十三卷', '土部', '畔也。爲四畔畍祭其中。从土，兆聲。周禮曰「垗五帝於四郊」。', '土;兆', '兆', 'null', NULL, '土', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9144, '壇', 'tán', '第十三卷', '土部', '祭壇場也。从土，亶聲。', '土;亶', '亶', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9145, '場', 'chánɡ', '第十三卷', '土部', '祭神道也。一曰山田不耕者。一曰治榖田也。从土，昜聲。', '土;昜', '昜', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9146, '圯', 'yí', '第十三卷', '土部', '東楚謂橋。从土，巳聲。', '土;巳', '巳', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9147, '垂', 'chuí', '第十三卷', '土部', '遠邊也。从土，𠂹聲。', '土;𠂹', '𠂹', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9148, '𡒈', 'kū', '第十三卷', '土部', '兔堀也。从土，屈聲。', '土;屈', '屈', 'null', NULL, '土', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9149, '圭;珪', 'ɡuī', '第十三卷', '土部', '瑞玉也。上圜下方。公執「桓圭」，九寸。侯執「信圭」、伯執「躳圭」，皆七寸。子執「榖璧」、男執「蒲璧」，皆五寸。㠯封諸侯。从重土。楚爵有「執圭」。古文圭从玉。', '土;玉', '', 'null', NULL, '土;玉', 'null', 'null', 'null', 'null', '调序', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9150, '塗', 'tú', '第十三卷', '土部', '泥也。从土，涂聲。', '土;涂', '涂', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9151, '塓', 'mì', '第十三卷', '土部', '塗也。从土，冥聲。', '土;冥', '冥', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9152, '埏', 'yán', '第十三卷', '土部', '八方之地也。从土，延聲。', '土;延', '延', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9153, '埸', 'yì', '第十三卷', '土部', '疆也。从土，易聲。', '土;易', '易', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9154, '境', 'jìnɡ', '第十三卷', '土部', '疆也。从土，竟聲。', '土;竟', '竟', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9155, '塾', 'shú', '第十三卷', '土部', '門側堂也。从土，孰聲。', '土;孰', '孰', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9156, '墾', 'kěn', '第十三卷', '土部', '耕也。从土，豤聲。', '土;豤', '豤', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9157, '塘', 'tánɡ', '第十三卷', '土部', '隄也。从土，唐聲。', '土;唐', '唐', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9158, '坳', 'āo', '第十三卷', '土部', '地不平也。从土，幼聲。', '土;幼', '幼', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9159, '壒', 'ài', '第十三卷', '土部', '塵也。从土，蓋聲。', '土;蓋', '蓋', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9160, '墜', 'zhuì', '第十三卷', '土部', '陊也。从土，隊聲。', '土;隊', '隊', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9161, '塔', 'tǎ', '第十三卷', '土部', '西域 浮屠也。从土，荅聲。', '土;荅', '荅', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9162, '坊', 'fānɡ', '第十三卷', '土部', '邑里之名。从土，方聲。', '土;方', '方', 'null', NULL, '土', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9163, '垚', 'yáo', '第十三卷', '垚部', '土高皃。从三土。凡垚之屬皆从垚。', '土', '', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9164, '堯;尭', 'yáo', '第十三卷', '垚部', '高也。从垚在兀上。高遠也。古文堯[二土，二人]。', '垚;兀;土;人', '', 'null', NULL, '垚;兀;土;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9165, '堇;𡎸', 'qín', '第十三卷', '堇部', '黏土也。从黃省，从土。凡堇之屬皆从堇。古文堇。亦古文。', '黃;土', '', 'null', NULL, '黃;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9166, '艱', 'jiān', '第十三卷', '堇部', '土難治也。从堇，艮聲。籒文艱从喜。', '堇;艮;喜', '堇;艮', 'null', NULL, '喜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9167, '里', 'lǐ', '第十三卷', '里部', '凥也。从田，从土。一曰士聲也。凡里之屬皆从里。', '田;土;士', '士', 'null', NULL, '田;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9168, '釐', 'lí', '第十三卷', '里部', '家福也。从里，𠩺聲。', '里;𠩺', '𠩺', 'null', NULL, '里', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9169, '野', 'yě', '第十三卷', '里部', '郊外也。从里，予聲。古文野从里省，从林。', '里;予;林', '予', 'null', NULL, '里;林', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9170, '田', 'tián', '第十三卷', '田部', '敶也。樹榖曰田。象形。囗十，千百之制也。凡田之屬皆从田。', '囗;十', '', 'null', NULL, '囗;十', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9171, '町', 'tīnɡ', '第十三卷', '田部', '田踐處曰町。从田，丁聲。', '田;丁', '丁', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9172, '𤲬', 'ruán', '第十三卷', '田部', '城下田也。一曰𤲬，郤地。从田，耎聲。', '田;耎', '耎', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9173, '𤲮;𢏚;疇;𠃬', 'chóu', '第十三卷', '田部', '耕治之田也。从田。𢏚，象耕田溝詰詘也。𤲮或省。', '田;𢏚', '𢏚', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9174, '疁', 'liú', '第十三卷', '田部', '燒穜也。从田，翏聲。漢律曰「疁田茠艸」。', '田;翏', '翏', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9175, '畬', 'yú', '第十三卷', '田部', '二歲治田也。从田，余聲。易曰「不葘畬田」。', '田;余', '余', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9176, '㽥', 'róu', '第十三卷', '田部', '龢田也。从田柔。柔亦聲。鄭有㽥，地名也。', '田;柔', '柔', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9177, '畸', 'jī', '第十三卷', '田部', '𣧻田也。从田，奇聲。', '田;奇', '奇', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9178, '㽨', 'cuó', '第十三卷', '田部', '𣧻薉田也。从田，差聲。詩曰「天方薦㽨」。', '田;差', '差', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9179, '畮;畝;畞;𠭇', 'mǔ', '第十三卷', '田部', '六尺爲步，步百爲畮，秦田二百四十步爲畮。从田，每聲。畮或从十久。', '田;每;十;久', '每', 'null', NULL, '田;十;久', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9180, '甸', 'diàn', '第十三卷', '田部', '天子五百里內田。从勹田。', '勹;田', '田', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9181, '畿', 'jī', '第十三卷', '田部', '天子千里地。㠯逮近言之則言畿。从田，幾省聲。', '田;幾', '幾', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9182, '畦', 'qí', '第十三卷', '田部', '田五十畮曰畦。从田，圭聲。', '田;圭', '圭', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9183, '畹', 'wǎn', '第十三卷', '田部', '田三十畮曰畹。从田，宛聲。', '田;宛', '宛', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9184, '畔', 'pàn', '第十三卷', '田部', '田界也。从田，半聲。', '田;半', '半', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9185, '界;畍', 'jiè', '第十三卷', '田部', '竟也。从田，介聲。', '田;介', '介', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9186, '㽘', 'ɡǎnɡ', '第十三卷', '田部', '竟也。一曰百也。趙、魏謂百爲㽘。从田，亢聲。', '田;亢', '亢', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9187, '畷', 'zhuì', '第十三卷', '田部', '㒳百閒道也。百廣六尺。从田，叕聲。', '田;叕', '叕', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9188, '畛', 'zhěn', '第十三卷', '田部', '井田閒陌也。从田，㐱聲。', '田;㐱', '㐱', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9189, '畤', 'zhì', '第十三卷', '田部', '天地五帝所基止祭地也。从田，寺聲。右扶風 雝有「五畤」。好畤、鄜畤皆黃帝時築。或云秦文公立。', '田;寺', '寺', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9190, '略', 'lüè', '第十三卷', '田部', '經略土地也。从田，各聲。', '田;各', '各', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9191, '當', 'dānɡ', '第十三卷', '田部', '田相值也。从田，尙聲。', '田;尙', '尙', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9192, '畯', 'jùn', '第十三卷', '田部', '農夫也。从田，夋聲。', '田;夋', '夋', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9193, '甿', 'ménɡ', '第十三卷', '田部', '田民也。从田，亡聲。', '田;亡', '亡', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9194, '疄', 'lìn', '第十三卷', '田部', '轢田也。从田，粦聲。', '田;粦', '粦', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9195, '畱;留', 'liú', '第十三卷', '田部', '止也。从田，丣聲。', '田;丣', '丣', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9196, '畜', 'chù', '第十三卷', '田部', '田畜也。淮南王曰「玄田爲畜」。魯郊禮畜从田，从茲。茲，益也。', '玄;田;茲', '', 'null', NULL, '玄;田;茲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9197, '疃', 'tuǎn', '第十三卷', '田部', '禽獸所踐處也。詩曰「町疃鹿場」。从田，童聲。', '田;童', '童', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9198, '畼', 'chànɡ', '第十三卷', '田部', '不生也。从田，昜聲。', '田;昜', '昜', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9199, '畕', 'jiānɡ', '第十三卷', '畕部', '比田也。从二田。凡畕之屬皆从畕。闕。', '田', '', 'null', NULL, '田', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9200, '畺;疆', 'jiānɡ', '第十三卷', '畕部', '界也。从畕。三，其介畫也。畺或从土，彊聲。', '畕;三;土;彊', '彊', 'null', NULL, '畕;三;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9201, '黃', 'huánɡ', '第十三卷', '黃部', '地之色也。从田，炗聲。炗，古文光。凡黃之屬皆从黃。古文黃[夂]。', '田;炗;夂', '炗', 'null', NULL, '田;夂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9202, '䵌', 'xiān', '第十三卷', '黃部', '赤黃色也。一曰輕㑥人䵌姁也。从黃，夾聲。', '黃;夾', '夾', 'null', NULL, '黃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9203, '䵎', 'tuān', '第十三卷', '黃部', '黑黃色也。从黃，耑聲。', '黃;耑', '耑', 'null', NULL, '黃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9204, '䵋', 'wěi', '第十三卷', '黃部', '靑黃色也。从黃，有聲。', '黃;有', '有', 'null', NULL, '黃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9205, '黇', 'tiān', '第十三卷', '黃部', '白黃色也。从黃，占聲。', '黃;占', '占', 'null', NULL, '黃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9206, '黊', 'huà', '第十三卷', '黃部', '鱻朙黃色也。从黃，圭聲。', '黃;圭', '圭', 'null', NULL, '黃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9207, '男', 'nán', '第十三卷', '男部', '丈夫也。从田力。言男子力於田也。凡男之屬皆从男。', '田;力', '', 'null', NULL, '田;力', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9208, '舅', 'jiù', '第十三卷', '男部', '母之兄弟爲舅。妻之父爲外舅。从男，臼聲。', '男;臼', '臼', 'null', NULL, '男', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9209, '甥', 'shēnɡ', '第十三卷', '男部', '謂我舅者，吾謂之甥。从男，生聲。', '男;生', '生', 'null', NULL, '男', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9210, '力', 'lì', '第十三卷', '力部', '筋也。象人筋之形。治功曰力。能禦大災。凡力之屬皆从力。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9211, '勳;勛', 'xūn', '第十三卷', '力部', '能成王功也。从力，熏聲。古文勳从員。', '力;熏;員', '熏;員', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9212, '功', 'ɡōnɡ', '第十三卷', '力部', '㠯勞定國也。从力，工聲。', '力;工', '工', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9213, '助', 'zhù', '第十三卷', '力部', '左也。从力，且聲。', '力;且', '且', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9214, '𠣊', 'lǜ', '第十三卷', '力部', '助也。从力非。慮聲。', '力;非;慮', '慮', 'null', NULL, '力;非', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9215, '勑', 'lài', '第十三卷', '力部', '勞也。从力，來聲。', '力;來', '來', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9216, '劼', 'jié', '第十三卷', '力部', '愼也。从力，吉聲。周書曰「劼毖殷獻臣」。讀若葛覃。', '力;吉;葛', '吉;葛', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9217, '務', 'wù', '第十三卷', '力部', '趣也。从力，敄聲。', '力;敄', '敄', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9218, '勥', 'qiǎnɡ', '第十三卷', '力部', '迫也。从力，強聲。古文从彊。', '力;強;彊', '強;彊', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9219, '勱', 'mài', '第十三卷', '力部', '勉力也。从力，萬聲。周書曰「用勱相我邦家」。讀與厲同。', '力;萬;厲', '萬;厲', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9220, '勉', 'miǎn', '第十三卷', '力部', '勥也。从力，免聲。', '力;免', '免', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9221, '劭', 'shào', '第十三卷', '力部', '勉也。从力，召聲。讀若「舜樂韶」。', '力;召;韶', '召;韶', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9222, '勖', 'xù', '第十三卷', '力部', '勉也。从力，冒聲。周書曰「勖哉夫子」。', '力;冒', '冒', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9223, '勸', 'quàn', '第十三卷', '力部', '勉也。从力，雚聲。', '力;雚', '雚', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9224, '𠢤', 'jué', '第十三卷', '力部', '彊力也。从力，厥聲。', '力;厥', '厥', 'null', NULL, '力', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9225, '勍', 'qínɡ', '第十三卷', '力部', '彊也。春秋傳曰「勍敵之人」。从力，京聲。', '力;京', '京', 'null', NULL, '力', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9226, '勁', 'jìnɡ', '第十三卷', '力部', '彊也。从力，巠聲。', '力;巠', '巠', 'null', NULL, '力', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9227, '勝', 'shēnɡ', '第十三卷', '力部', '任也。从力，朕聲。', '力;朕', '朕', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9228, '勶', 'chè', '第十三卷', '力部', '發也。从力徹。徹亦聲。', '力;徹', '徹', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9229, '勠', 'lù', '第十三卷', '力部', '勠力，并力也。从力，翏聲。', '力;翏', '翏', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9230, '勨', 'xiànɡ', '第十三卷', '力部', '䌛緩也。从力，象聲。', '力;象', '象', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9231, '動', 'dònɡ', '第十三卷', '力部', '作也。从力，重聲。', '力;重', '重', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9232, '㔣', 'lèi', '第十三卷', '力部', '推也。从力，靁省聲。', '力;靁', '靁', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9233, '劣', 'liè', '第十三卷', '力部', '弱也。从力少。', '力;少', '', 'null', NULL, '力;少', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9234, '勞', 'láo', '第十三卷', '力部', '勮也。从力，熒省。焱火燒冂，用力者勞。古文如此[未详]。', '力;熒', '', 'null', NULL, '力;熒', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9235, '勮', 'jù', '第十三卷', '力部', '務也。从力，豦聲。', '力;豦', '豦', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9236, '勀', 'kè', '第十三卷', '力部', '尤勮也。从力，克聲。', '力;克', '克', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9237, '勩', 'yì', '第十三卷', '力部', '勞也。从力，貰聲。詩曰「莫知我勩」。', '力;貰', '貰', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9238, '勦', 'jiǎo', '第十三卷', '力部', '勞也。从力，巢聲。春秋傳曰「安用勦民」。', '力;巢', '巢', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9239, '劵', 'juàn', '第十三卷', '力部', '勞也。从力，𢍏聲。', '力;𢍏', '𢍏', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9240, '勤', 'qín', '第十三卷', '力部', '勞也。从力，堇聲。', '力;堇', '堇', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9241, '加', 'jiā', '第十三卷', '力部', '語相譄加也。从力口。', '力;口', '', 'null', NULL, '力;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9242, '𠢕', 'háo', '第十三卷', '力部', '健也。从力，敖聲。讀若豪。', '力;敖;豪', '敖;豪', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9243, '勇;恿', 'yǒnɡ', '第十三卷', '力部', '气也。从力，甬聲。勇或从戈用。古文勇从心。', '力;甬;戈;用;心', '甬;用', 'null', NULL, '力;戈;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9244, '勃', 'bó', '第十三卷', '力部', '排也。从力，孛聲。', '力;孛', '孛', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9245, '勡', 'piào', '第十三卷', '力部', '劫也。从力，票聲。', '力;票', '票', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9246, '劫', 'jié', '第十三卷', '力部', '人欲去㠯力脅止曰劫。或曰㠯力去曰劫。从力去。', '力;去', '去', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9247, '飭', 'chì', '第十三卷', '力部', '致臤也。从人力，食聲。讀若敕。', '人;力;食;敕', '食;敕', 'null', NULL, '人;力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9248, '劾', 'hé', '第十三卷', '力部', '法有辠也。从力，亥聲。', '力;亥', '亥', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9249, '募', 'mù', '第十三卷', '力部', '廣求之也。从力，莫聲。', '力;莫', '莫', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9250, '劬', 'qú', '第十三卷', '力部', '勞也。从力，句聲。', '力;句', '句', 'null', NULL, '力', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9251, '勢', 'shì', '第十三卷', '力部', '盛力權也。从力，埶聲。', '力;埶', '埶', 'null', NULL, '力', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9252, '勘', 'kàn', '第十三卷', '力部', '校也。从力，甚聲。', '力;甚', '甚', 'null', NULL, '力', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9253, '辦', 'bàn', '第十三卷', '力部', '致力也。从力，辡聲。', '力;辡', '辡', 'null', NULL, '力', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9254, '努', 'nǔ', '第十三卷', '力部', '勉也。从力，奴聲。', '力;奴', '奴', 'null', NULL, '力', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9255, '劦', 'xié', '第十三卷', '劦部', '同力也。从三力。山海經曰「惟號之山，其風若劦」。凡劦之屬皆从劦。', '力', '', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9256, '恊', 'xié', '第十三卷', '劦部', '同心之龢也。从劦心。', '劦;心', '', 'null', NULL, '劦;心', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9257, '𢣢', 'xié', '第十三卷', '劦部', '同思之龢也。从劦思。', '劦;思', '', 'null', NULL, '劦;思', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9258, '協;叶', 'xié', '第十三卷', '劦部', '同眾之龢也。从劦十。叶古文協从口十。叶或从曰。', '劦;十;口;十;曰', '', 'null', NULL, '劦;十;口;十;曰', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9259, '金', 'jīn', '第十四卷', '金部', '五色金也。黃爲之長，久薶不生衣，百鍊不輕，從革不韋。西方之行，生於土。从土。𠂇又注，象金在土中形。今聲。凡金之屬皆从金。古文金。', '土;今', '今', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9260, '銀', 'yín', '第十四卷', '金部', '白金也。从金，艮聲。', '金;艮', '艮', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9261, '鐐', 'liáo', '第十四卷', '金部', '白金也。从金，尞聲。', '金;尞', '尞', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9262, '鋈', 'wù', '第十四卷', '金部', '白金也。从金，𣵽省聲。', '金;𣵽', '𣵽', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
