INSERT INTO `shuowen_voice_revel` VALUES (9264, '錫', 'xī', '第十四卷', '金部', '銀鉛之閒也。从金，易聲。', '金;易', '易', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9265, '鈏', 'yǐn', '第十四卷', '金部', '錫也。从金，引聲。', '金;引', '引', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9266, '銅', 'tónɡ', '第十四卷', '金部', '赤金也。从金，同聲。', '金;同', '同', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9267, '鏈', 'lián', '第十四卷', '金部', '銅屬。从金，連聲。', '金;連', '連', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9268, '鐵;銕;𨮯', 'tiě', '第十四卷', '金部', '黑金也。从金，𢧤聲。鐵或省[𢧄]。古文鐵从夷。', '金;𢧤;𢧄;夷', '𢧤;𢧄;夷', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
