INSERT INTO `shuowen_voice_revel` VALUES (9271, '鏤', 'lòu', '第十四卷', '金部', '剛鐵也。可㠯刻鏤。从金，婁聲。夏書曰「梁州貢鏤」。一曰鏤，釜也。', '金;婁', '婁', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9272, '鐼', 'fén', '第十四卷', '金部', '鐵屬。从金，賁聲。讀若熏。', '金;賁;熏', '賁;熏', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9273, '銑', 'xiǎn', '第十四卷', '金部', '金之澤者。从金，先聲。一曰小鑿。一曰鐘下㒳角謂之銑。', '金;先', '先', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
