INSERT INTO `shuowen_voice_revel` VALUES (9275, '鑗', 'lí', '第十四卷', '金部', '金屬也。一曰剝也。从金，黎聲。', '金;黎', '黎', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
