INSERT INTO `shuowen_voice_revel` VALUES (9296, '鋞', 'xínɡ', '第十四卷', '金部', '𥁕器也。圜而直上。从金，巠聲。', '金;巠', '巠', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9297, '鑴', 'xī', '第十四卷', '金部', '瓽也。从金，巂聲。', '金;巂', '巂', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
