INSERT INTO `shuowen_voice_revel` VALUES (9305, '鎬', 'hào', '第十四卷', '金部', '𥁕器也。从金，高聲。武王所都，在長安西上林苑中。字亦如此。', '金;高', '高', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9306, '䥝', 'āo', '第十四卷', '金部', '𥁕器也。一曰金器。从金，麀聲。讀若奥。', '金;麀;奥', '麀;奥', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
