INSERT INTO `shuowen_voice_revel` VALUES (9310, '鋗', 'xuān', '第十四卷', '金部', '小盆也。从金，肙聲。', '金;肙', '肙', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9311, '鏏', 'wèi', '第十四卷', '金部', '鼎也。从金，彗聲。讀若彗。', '金;彗', '彗', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9312, '鍵', 'jiàn', '第十四卷', '金部', '鉉也。从金，建聲。一曰車舝。', '金;建', '建', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9313, '鉉', 'xuàn', '第十四卷', '金部', '所㠯舉鼎也。从金，玄聲。易謂之「鉉」，禮謂之「𪔃」。', '金;玄', '玄', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
