INSERT INTO `shuowen_voice_revel` VALUES (9384, '鑮', 'bó', '第十四卷', '金部', '大鐘。淳于之屬，所㠯應鐘磬也。堵㠯二，金樂則𡔷鑮應之。从金，薄聲。', '金;薄', '薄', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
