INSERT INTO `shuowen_voice_revel` VALUES (9386, '鐘', 'zhōnɡ', '第十四卷', '金部', '樂鐘也。秋分之音，萬物穜成，故謂之鐘。从金，童聲。古者垂作鐘。鐘或从甬。', '金;童;甬', '童;甬', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9387, '鈁', 'fānɡ', '第十四卷', '金部', '方鐘也。从金，方聲。', '金;方', '方', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9388, '鎛', 'bó', '第十四卷', '金部', '鎛鱗也。鐘上橫木上金𠌶也。从金，尃聲。一曰田器。詩曰「庤乃錢鎛」。', '金;尃', '尃', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9389, '鍠;𨪽', 'huánɡ', '第十四卷', '金部', '鐘聲也。从金，𦤃聲。詩曰「鐘𡔷𨪽𨪽」。', '金;𦤃', '𦤃', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9390, '鎗', 'chēnɡ', '第十四卷', '金部', '鎗鏓。鐘聲也。从金，倉聲。', '金;倉', '倉', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9391, '鏓', 'cōnɡ', '第十四卷', '金部', '鎗鏓也。从金，悤聲。一曰大鑿中木也。', '金;悤', '悤', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9392, '錚', 'zhēnɡ', '第十四卷', '金部', '金聲也。从金，爭聲。', '金;爭', '爭', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9393, '鏜', 'tānɡ', '第十四卷', '金部', '鐘𡔷之聲也。从金，堂聲。詩曰「擊𡔷其鏜」。', '金;堂', '堂', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
