INSERT INTO `shuowen_voice_revel` VALUES (9398, '鏢', 'biāo', '第十四卷', '金部', '刀削末銅也。从金，票聲。', '金;票', '票', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9399, '鈒', 'sà', '第十四卷', '金部', '鋋也。从金，及聲。', '金;及', '及', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9400, '鋋', 'chán', '第十四卷', '金部', '小矛也。从金，延聲。', '金;延', '延', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9401, '鈗', 'yǔn', '第十四卷', '金部', '侍臣所執兵也。从金，允聲。周書曰「一人冕執鈗」。讀若允。', '金;允', '允', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9402, '鉈', 'shī', '第十四卷', '金部', '短矛也。从金，它聲。', '金;它', '它', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
