INSERT INTO `shuowen_voice_revel` VALUES (9405, '鏠', 'fēnɡ', '第十四卷', '金部', '兵耑也。从金，逢聲。', '金;逢', '逢', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
