INSERT INTO `shuowen_voice_revel` VALUES (9454, '𨥦', 'qí', '第十四卷', '金部', '利也。从金，𠂔聲。讀若齊。', '金;𠂔;齊', '𠂔;齊', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9455, '錗', 'nèi', '第十四卷', '金部', '側意。从金，委聲。', '金;委', '委', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9456, '鑺', 'qú', '第十四卷', '金部', '兵器也。从金，瞿聲。', '金;瞿', '瞿', 'null', NULL, '金', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9457, '銘', 'mínɡ', '第十四卷', '金部', '記也。从金，名聲。', '金;名', '名', 'null', NULL, '金', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9458, '鎖', 'suǒ', '第十四卷', '金部', '𨮯鎖，門鍵也。从金，𧴪聲。', '金;𧴪', '𧴪', 'null', NULL, '金', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9459, '鈿', 'tián', '第十四卷', '金部', '金華也。从金，田聲。', '金;田', '田', 'null', NULL, '金', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9460, '釧', 'chuàn', '第十四卷', '金部', '臂環也。从金，川聲。', '金;川', '川', 'null', NULL, '金', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9461, '釵', 'chāi', '第十四卷', '金部', '笄屬。从金，叉聲。', '金;叉', '叉', 'null', NULL, '金', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9462, '釽', 'pī', '第十四卷', '金部', '裂也。从金爪。', '金;爪', '', 'null', NULL, '金;爪', 'null', 'null', 'null', 'null', '新', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9463, '鋥', 'zèng', '第十四卷', '金部', '相摩見光也。从金，呈聲。', '金;呈', '呈', 'null', NULL, '金', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9464, '开;𭑈', 'jiān', '第十四卷', '开部', '平也。象二干對冓。上平也。凡开之屬皆从开。', '干', '', 'null', NULL, '干', 'null', 'null', 'null', 'null', '⚙️', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9465, '勺', 'zhuó', '第十四卷', '勺部', '枓也。所㠯挹取也。象形。中有實。與包同意。凡勺之屬皆从勺。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9466, '与', 'yǔ', '第十四卷', '勺部', '賜予也。一勺爲与。此與予同意。', '一;勺;予', '予', 'null', NULL, '一;勺', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9467, '几', 'jǐ', '第十四卷', '几部', '凥几也。象形。周禮「五几」——玉几、彫几、彤几、䰍几、素几。凡几之屬皆从几。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9468, '凭', 'pínɡ', '第十四卷', '几部', '依几也。从任几。周書曰「凭玉几」。讀若馮。', '任;几;馮', '馮', 'null', NULL, '任;几', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9469, '凥', 'jū', '第十四卷', '几部', '処也。从尸几。尸得几而止也。孝經曰「仲尼凥」。凥，謂閒凥如此。', '尸;几', '', 'null', NULL, '尸;几', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9470, '処;處', 'chǔ', '第十四卷', '几部', '止也。从夂几。夂得几而止也。處或从虍聲。', '夂;几;虍', '虍', 'null', NULL, '夂;几', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9471, '且', 'jū', '第十四卷', '且部', '所㠯薦也。从几。足有二橫。一，其下地也。凡且之屬皆从且。古文㠯爲且，又㠯爲几字。', '几;一', '', 'null', NULL, '几;一', 'null', 'null', 'null', 'null', '重文大徐无、小徐有', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9472, '俎', 'zǔ', '第十四卷', '且部', '禮俎也。从半肉在且上。', '肉;且', '', 'null', NULL, '肉;且', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9473, '𧇿', 'zù', '第十四卷', '且部', '且往也。从且，豦聲。', '且;豦', '且;豦', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9474, '斤', 'jīn', '第十四卷', '斤部', '斫木斧也。象形。凡斤之屬皆从斤。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9475, '斧', 'fǔ', '第十四卷', '斤部', '所㠯斫也。从斤，父聲。', '斤;父', '父', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9476, '斨', 'qiānɡ', '第十四卷', '斤部', '方銎。斧也。从斤，爿聲。詩曰「又缺我斨」。', '斤;爿', '爿', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9477, '斫', 'zhuó', '第十四卷', '斤部', '擊也。从斤，石聲。', '斤;石', '石', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9478, '斪', 'qú', '第十四卷', '斤部', '斪斸，所㠯斫也。从斤，句聲。', '斤;句', '句', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9479, '斸', 'zhú', '第十四卷', '斤部', '斪斸也。从斤，屬聲。', '斤;屬', '屬', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9480, '斲', 'zhuó', '第十四卷', '斤部', '斫也。从斤，𠁁聲[𨪐]。斲或从丮，晝聲。', '斤;𨪐;丮;晝', '𨪐;晝', 'null', NULL, '斤;丮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9481, '釿', 'yǐn', '第十四卷', '斤部', '劑𣃔也。从斤，金聲。', '斤;金', '金', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9482, '所', 'suǒ', '第十四卷', '斤部', '伐木聲也。从斤，戸聲。詩曰「伐木所所」。', '斤;戸', '戸', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9483, '斯', 'sī', '第十四卷', '斤部', '析也。从斤，其聲。', '斤;其', '其', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9484, '斮', 'zhuó', '第十四卷', '斤部', '斬也。从斤，昔聲。', '斤;昔', '昔', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9485, '𣃔;𠸿;斷', 'duàn', '第十四卷', '斤部', '𢧵也。从斤𢇍。𢇍，古文絕。古文𣃔从[𠧢召]。𠧢，古文叀字。周書曰「𠸿𠸿猗，無它技」。亦古文𣃔[刀]。', '斤;𢇍;召;𠧢;刀', '𠧢', 'null', NULL, '斤;𢇍;召;刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9486, '𣂞', 'luǒ', '第十四卷', '斤部', '柯擊也。从斤，良聲。', '斤;良', '良', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9487, '新', 'xīn', '第十四卷', '斤部', '取木也。从斤，𣓀聲。', '斤;𣓀', '𣓀', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9488, '斦', 'yín', '第十四卷', '斤部', '二斤也。闕。', '斤', '', 'null', NULL, '斤', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9489, '斗', 'dǒu', '第十四卷', '斗部', '十升也。象形。有柄。凡斗之屬皆从斗。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9490, '斛', 'hú', '第十四卷', '斗部', '十斗也。从斗，角聲。', '斗;角', '角', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9491, '斝', 'jiǎ', '第十四卷', '斗部', '玉爵也。夏曰醆。殷曰斝。周曰爵。从斗。[吅冂]象形。與爵同意。或說斝受六升。', '斗;吅;冂', '', 'null', NULL, '斗;吅;冂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9492, '料', 'liào', '第十四卷', '斗部', '量也。从米在斗中。讀若遼。', '米;斗;遼', '遼', 'null', NULL, '米;斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9493, '斞', 'yǔ', '第十四卷', '斗部', '量也。从斗，臾聲。周禮曰「桼三斞」。', '斗;臾', '臾', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9494, '斡', 'wò', '第十四卷', '斗部', '蠡柄也。从斗。倝聲。楊雄、杜林說，皆㠯爲軺車輪斡也。', '斗;倝', '倝', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9495, '魁', 'kuí', '第十四卷', '斗部', '羮斗也。从斗，鬼聲。', '斗;鬼', '鬼', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9496, '斠', 'jiào', '第十四卷', '斗部', '平斗斛量也。从斗，冓聲。', '斗;冓', '冓', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9497, '斟', 'zhēn', '第十四卷', '斗部', '勺也。从斗，甚聲。', '斗;甚', '甚', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9498, '斜', 'xié', '第十四卷', '斗部', '抒也。从斗，余聲。讀若荼。', '斗;余;荼', '余;荼', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9499, '㪺', 'jū', '第十四卷', '斗部', '挹也。从斗，𥇛聲。', '斗;𥇛', '𥇛', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9500, '㪵', 'bàn', '第十四卷', '斗部', '量物分半也。从斗半。半亦聲。', '斗;半', '半', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9501, '𣂆', 'pānɡ', '第十四卷', '斗部', '量旁溢也。从斗，旁聲。', '斗;旁', '旁', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9502, '㪻', 'juàn', '第十四卷', '斗部', '抒屚也。从斗，䜌聲。', '斗;䜌', '䜌', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9503, '斣', 'dòu', '第十四卷', '斗部', '相易物俱等爲斣。从斗，蜀聲。', '斗;蜀', '蜀', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9504, '𣂁;𣂀', 'tiāo', '第十四卷', '斗部', '斛旁有庣也。从斗，庣聲。一曰突也。一曰𣂁，利也。爾雅曰「𣂁謂之疀」，古田器也。', '斗;庣', '庣', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9505, '升', 'shēnɡ', '第十四卷', '斗部', '十合也。从斗。象形。合龠爲合。龠容千二百黍。', '斗', '', 'null', NULL, '斗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9506, '矛', 'máo', '第十四卷', '矛部', '酋矛也。建於兵車。長二丈。象形。凡矛之屬皆从矛。古文矛从戈。', '戈', '', 'null', NULL, '戈', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9507, '𥍫', 'lánɡ', '第十四卷', '矛部', '矛屬。从矛，良聲。', '矛;良', '良', 'null', NULL, '矛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9508, '𥎆', 'kài', '第十四卷', '矛部', '矛屬。从矛，害聲。', '矛;害', '害', 'null', NULL, '矛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9509, '矠', 'zé', '第十四卷', '矛部', '矛屬。从矛，昔聲。讀若笮。', '矛;昔;笮', '昔;笮', 'null', NULL, '矛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9510, '矝;矜', 'jīn', '第十四卷', '矛部', '矛柄也。从矛，令聲。', '矛;令', '令', 'null', NULL, '矛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9511, '䂇', 'niǔ', '第十四卷', '矛部', '刺也。从矛，丑聲。', '矛;丑', '丑', 'null', NULL, '矛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9512, '車', 'chē', '第十四卷', '車部', '輿輪之總名也。夏后時奚仲所造。象形。凡車之屬皆从車。籒文車[戔]。', '戔', '', 'null', NULL, '戔', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9513, '軒', 'xuān', '第十四卷', '車部', '曲輈藩車也。从車，干聲。', '車;干', '干', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9514, '輺;輜', 'zī', '第十四卷', '車部', '輺軿，衣車也。軿，車歬衣也。車後爲輺。从車，[葘]聲。', '車;葘', '葘', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9515, '軿;輧', 'pínɡ', '第十四卷', '車部', '輺輧也。从車，并聲。', '車;并', '并', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9516, '轀;輼', 'wēn', '第十四卷', '車部', '臥車也。从車，𥁕聲。', '車;𥁕', '𥁕', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9517, '輬', 'liánɡ', '第十四卷', '車部', '臥車也。从車，京聲。', '車;京', '京', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9518, '軺', 'yáo', '第十四卷', '車部', '小車也。从車，召聲。', '車;召', '召', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9519, '輕', 'qīnɡ', '第十四卷', '車部', '輕車也。从車，巠聲。', '車;巠', '巠', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9520, '輶', 'yóu', '第十四卷', '車部', '輕車也。从車，酋聲。詩曰「輶車鑾鑣」。', '車;酋', '酋', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9521, '輣', 'pénɡ', '第十四卷', '車部', '樓車也。从車，朋聲。', '車;朋', '朋', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9522, '軘', 'tún', '第十四卷', '車部', '兵車也。从車，屯聲。', '車;屯', '屯', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9523, '䡴', 'chōnɡ', '第十四卷', '車部', '陷敶車也。从車，童聲。', '車;童', '童', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9524, '轈', 'cháo', '第十四卷', '車部', '兵車。高如巢，㠯望敵也。从車，巢聲。春秋傳曰「楚子乘轈車」。', '車;巢', '巢', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9525, '輯', 'jí', '第十四卷', '車部', '車輿也。从車，咠聲。', '車;咠', '咠', 'null', NULL, '車', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9526, '輿', 'yú', '第十四卷', '車部', '車輿也。从車，舁聲。', '車;舁', '舁', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9527, '䡬', 'màn', '第十四卷', '車部', '衣車葢也。从車，曼聲。', '車;曼', '曼', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9528, '軓', 'fàn', '第十四卷', '車部', '車軾歬也。从車，凡聲。周禮曰「立當前軓」。', '車;凡', '凡', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9529, '軾', 'shì', '第十四卷', '車部', '車歬也。从車，式聲。', '車;式', '式', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9530, '輅', 'lù', '第十四卷', '車部', '車軨歬橫木也。从車，各聲。', '車;各', '各', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9531, '䡈;較', 'jué', '第十四卷', '車部', '車輢上曲鉤也。从車，爻聲。', '車;爻', '爻', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9532, '䡊', 'fǎn', '第十四卷', '車部', '車耳反出也。从車反。反亦聲。', '車;反', '反', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9533, '轛', 'zhuì', '第十四卷', '車部', '車橫軨也。从車，𡭊聲。周禮曰「參分軹圍，去一㠯爲轛圍」。', '車;𡭊', '𡭊', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9534, '輒', 'zhé', '第十四卷', '車部', '車㒳輢也。从車，耴聲。', '車;耴', '耴', 'null', NULL, '車', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9535, '輢', 'yǐ', '第十四卷', '車部', '車旁也。从車，奇聲。', '車;奇', '奇', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9536, '䡅', 'chūn', '第十四卷', '車部', '車約䡅也。从車，川聲。周禮曰「孤乘夏䡅」。一曰下棺車曰䡅。', '車;川', '川', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9537, '轖', 'sè', '第十四卷', '車部', '車箱交革也。从車，嗇聲。', '車;嗇', '嗇', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9538, '軨', 'línɡ', '第十四卷', '車部', '車轖閒橫木。从車，令聲。軨，司馬相如說軨从霝。', '車;令;霝', '令;霝', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9539, '輑', 'yǐn', '第十四卷', '車部', '軺車歬橫木也。从車，君聲。讀若羣。一曰讀若褌。', '車;君;羣;褌', '君;羣;褌', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9540, '軫', 'zhěn', '第十四卷', '車部', '車後橫木也。从車，㐱聲。', '車;㐱', '㐱', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9541, '轐', 'bú', '第十四卷', '車部', '車伏兔也。从車，菐聲。周禮曰「加軫與轐焉」。', '車;菐', '菐', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9542, '𨏵', 'mǐn', '第十四卷', '車部', '車伏兔下革也。从車，𤔿聲。𤔿，古文婚字。讀若閔。', '車;𤔿;閔', '𤔿;閔', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9543, '軸', 'zhóu', '第十四卷', '車部', '所㠯持輪者也。从車，由聲。', '車;由', '由', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9544, '輹', 'fù', '第十四卷', '車部', '車軸縛也。从車，复聲。易曰「輿說輹」。', '車;复', '复', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9545, '輮', 'róu', '第十四卷', '車部', '車网也。从車，柔聲。', '車;柔', '柔', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9546, '𨍶', 'qiónɡ', '第十四卷', '車部', '車輮規也。一曰一輪車。从車，熒省聲。讀若煢。', '車;熒;煢', '熒;煢', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9547, '輪', 'lún', '第十四卷', '車部', '有輻曰輪。無輻曰輇。从車，侖聲。', '車;侖', '侖', 'null', NULL, '車', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9548, '轂', 'ɡǔ', '第十四卷', '車部', '輻所湊也。从車，㱿聲。', '車;㱿', '㱿', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9549, '輥', 'ɡǔn', '第十四卷', '車部', '轂𪗍等皃也。从車，昆聲。周禮曰「望其轂欲其輥」。', '車;昆', '昆', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9550, '軝', 'qí', '第十四卷', '車部', '長轂之軝也。㠯朱約之。从車，氏聲。詩曰「約軝䥘衡」。軝或从革。', '車;氏;革', '氏', 'null', NULL, '車;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9551, '軹', 'zhǐ', '第十四卷', '車部', '車輪小穿也。从車，只聲。', '車;只', '只', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9552, '軎;轊', 'wèi', '第十四卷', '車部', '車軸耑也。从車。象形。杜林說。軎或从彗。', '車;彗', '彗', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9553, '輻', 'fú', '第十四卷', '車部', '輪轑也。从車，畐聲。', '車;畐', '畐', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9554, '軑', 'dì', '第十四卷', '車部', '車輨也。从車，大聲。', '車;大', '大', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9555, '輨', 'ɡuǎn', '第十四卷', '車部', '轂耑錔也。从車，官聲。', '車;官', '官', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9556, '轅', 'yuán', '第十四卷', '車部', '輈也。从車，袁聲。', '車;袁', '袁', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9557, '輈', 'zhōu', '第十四卷', '車部', '轅也。从車，舟聲。籒文輈。', '車;舟', '舟', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9558, '䡞', 'jú', '第十四卷', '車部', '直轅𩎈也。从車，具聲。', '車;具', '具', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9559, '䡇', 'yuè', '第十四卷', '車部', '車轅耑持衡者。从車，元聲。', '車;元', '元', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9560, '軶;軛', 'è', '第十四卷', '車部', '轅歬也。从車，戹聲。', '車;戹', '戹', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9561, '䡣', 'hún', '第十四卷', '車部', '軶軥也。从車，軍聲。', '車;軍', '軍', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9562, '軥', 'qú', '第十四卷', '車部', '軶下曲者。从車，句聲。', '車;句', '句', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9563, '轙', 'yǐ', '第十四卷', '車部', '車衡載𦆕者。从車，義聲。轙或从金獻。', '車;義;金;獻', '義;獻', 'null', NULL, '車;金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9564, '軜', 'nà', '第十四卷', '車部', '驂馬內𦆕系軾歬者。从車，內聲。詩曰「𣵽㠯觼軜」。', '車;內', '內', 'null', NULL, '車', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9565, '輔', 'fǔ', '第十四卷', '車部', '春秋傳曰「輔車相依」。从車，甫聲。人頰車也。', '車;甫', '甫', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9566, '轑', 'lǎo', '第十四卷', '車部', '車葢弓也。从車，尞聲。一曰輻也。', '車;尞', '尞', 'null', NULL, '車', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9567, '䡓', 'juàn', '第十四卷', '車部', '車搖也。从車行。一曰衍省聲。', '車;行;衍', '衍', 'null', NULL, '車;行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9568, '𨋬', 'chénɡ', '第十四卷', '車部', '軺車後登也。从車，丞聲。讀若易「拯馬」之拯。', '車;丞;拯', '丞;拯', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9569, '載', 'zài', '第十四卷', '車部', '乘也。从車，𢦒聲。', '車;𢦒', '𢦒', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9570, '軍', 'jūn', '第十四卷', '車部', '圜圍也。四千人爲軍。从包省，从車。車，兵車也。', '包;車', '', 'null', NULL, '包;車', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9571, '軷', 'bá', '第十四卷', '車部', '出，將有事於道，必先告其神，立壇四通，尌茅㠯依神，爲「軷」。旣祭犯軷，轢牲而行，爲「範軷」。从車，犮聲。詩曰「取羝㠯軷」。', '車;犮', '犮', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9572, '範', 'fàn', '第十四卷', '車部', '範軷也。从車，笵省聲。讀與犯同。', '車;笵;犯', '笵;犯', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9573, '䡾', 'niè', '第十四卷', '車部', '載高貌。从車，獻聲。', '車;獻', '獻', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9574, '轄', 'xiá', '第十四卷', '車部', '車聲也。从車，害聲。一曰轄，鍵也。', '車;害', '害', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9575, '轉', 'zhuǎn', '第十四卷', '車部', '還也。从車，專聲。', '車;專', '專', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9576, '輸', 'shū', '第十四卷', '車部', '委輸也。从車，俞聲。', '車;俞', '俞', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9577, '輖', 'zhōu', '第十四卷', '車部', '重也。从車，周聲。', '車;周', '周', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9578, '輩', 'bèi', '第十四卷', '車部', '若軍發車百㒳爲輩。从車，非聲。', '車;非', '非', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9579, '軋', 'yà', '第十四卷', '車部', '𨋁也。从車，乙聲。', '車;乙', '乙', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9580, '𨋁', 'niǎn', '第十四卷', '車部', '轢也。从車，㞋聲。', '車;㞋', '㞋', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9581, '轢', 'lì', '第十四卷', '車部', '車所踐也。从車，樂聲。', '車;樂', '樂', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9582, '軌', 'ɡuǐ', '第十四卷', '車部', '車徹也。从車，九聲。', '車;九', '九', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9583, '𨌰;䡮', 'zōnɡ', '第十四卷', '車部', '車迹也。从車，從省。', '車;從', '從', '宗', '𨌰、踪古今字', '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9584, '軼', 'yì', '第十四卷', '車部', '車相出也。从車，失聲。', '車;失', '失', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9585, '䡩', 'kēnɡ', '第十四卷', '車部', '車䡩弘聲也。从車，眞聲。讀若論語「鏗尒，舍琴而作」。一曰讀若掔。', '車;眞;鏗;掔', '眞;鏗;掔', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9586, '𨎌', 'zhì', '第十四卷', '車部', '抵也。从車，埶聲。', '車;埶', '埶', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9587, '軭', 'kuánɡ', '第十四卷', '車部', '車戾也。从車，匡聲。', '車;匡', '匡', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9588, '軔', 'rèn', '第十四卷', '車部', '所㠯礙車也。从車，刃聲。', '車;刃', '刃', 'null', NULL, '車', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9589, '輟', 'chuò', '第十四卷', '車部', '車小缺復合者也。从車，叕聲。', '車;叕', '叕', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9590, '䡔', 'qǐ', '第十四卷', '車部', '礙也。从車，多聲。', '車;多', '多', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9591, '轚', 'jí', '第十四卷', '車部', '車轄相擊也。从車𣪠。𣪠亦聲。周禮曰「舟輿轚互者」。', '車;𣪠', '𣪠', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9592, '𨏉', 'shuàn', '第十四卷', '車部', '治車軸也。从車，算聲。', '車;算', '算', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9593, '軻', 'kě', '第十四卷', '車部', '椄軸車也。从車，可聲。', '車;可', '可', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9594, '䡰', 'kēnɡ', '第十四卷', '車部', '車堅也。从車，殸聲。', '車;殸', '殸', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9595, '軵', 'rǒnɡ', '第十四卷', '車部', '反推車令有所付也。从車付。讀若茸。', '車;付;茸', '茸', 'null', NULL, '車;付', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9596, '輇', 'quán', '第十四卷', '車部', '蕃車下庳輪也。从車，全聲。讀若饌。一曰無輻也。', '車;全;饌', '全;饌', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9597, '輗', 'ní', '第十四卷', '車部', '大車轅耑持衡者也。从車，兒聲。輗或从宜。輗又从木。', '車;兒;宜;木', '兒;宜', 'null', NULL, '車;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9598, '軧', 'dǐ', '第十四卷', '車部', '大車後也。从車，氐聲。', '車;氐', '氐', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9599, '轃', 'zhēn', '第十四卷', '車部', '大車簀也。从車，秦聲。讀若臻。', '車;秦;臻', '秦;臻', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9600, '轒', 'fén', '第十四卷', '車部', '淮陽名車穹隆轒。从車，賁聲。', '車;賁', '賁', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9601, '䡝', 'yuān', '第十四卷', '車部', '大車後壓也。从車，宛聲。', '車;宛', '宛', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9602, '輂', 'jú', '第十四卷', '車部', '大車駕馬者也。从車，共聲。', '車;共', '共', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9603, '𨌅', 'chái', '第十四卷', '車部', '連車也。一曰卻車抵堂爲𨌅。从車，差省聲。讀若遲。', '車;差;遲', '差;遲', 'null', NULL, '車', 'null', 'null', 'null', 'null', '歌部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9604, '輦', 'niǎn', '第十四卷', '車部', '輓車也。从車。㚘，在車歬引之也。', '車;㚘', '', 'null', NULL, '車;㚘', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9605, '輓', 'wǎn', '第十四卷', '車部', '引車也。从車，免聲。', '車;免', '免', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9606, '軠;軖', 'kuánɡ', '第十四卷', '車部', '紡車也。从車，㞷聲。讀若狂。一曰一輪車。', '車;㞷;狂', '㞷;狂', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9607, '轘', 'huàn', '第十四卷', '車部', '車裂人也。从車，瞏聲。春秋傳曰「轘諸栗門」。', '車;瞏', '瞏', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9608, '斬', 'zhǎn', '第十四卷', '車部', '𢧵也。从車斤。斬法車裂也。', '車;斤', '', 'null', NULL, '車;斤', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9609, '𨎪;輀', 'ér', '第十四卷', '車部', '喪車也。从車，重而。而亦聲。', '車;而', '而', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9610, '轟', 'hōnɡ', '第十四卷', '車部', '轟轟，羣車聲也。从三車。', '車', '', 'null', NULL, '車', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9611, '轏', 'zhàn', '第十四卷', '車部', '車名。从車，孱聲。', '車;孱', '孱', 'null', NULL, '車', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9612, '轔', 'lín', '第十四卷', '車部', '車聲。从車，粦聲。', '車;粦', '粦', 'null', NULL, '車', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9613, '轍', 'zhé', '第十四卷', '車部', '車迹也。从車，徹省聲。', '車;徹', '徹', 'null', NULL, '車', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9614, '𠂤', 'duī', '第十四卷', '𠂤部', '小𨸏也。象形。凡𠂤之屬皆从𠂤。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9615, '𡴎', 'niè', '第十四卷', '𠂤部', '危高也。从𠂤，屮聲。讀若臬。', '𠂤;屮;臬', '屮;臬', 'null', NULL, '𠂤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9616, '官', 'ɡuān', '第十四卷', '𠂤部', '吏事君也。从宀𠂤。𠂤猶眾也。此與師同意。', '宀;𠂤', '', 'null', NULL, '宀;𠂤', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9617, '𨸏;阜', 'fù', '第十四卷', '𨸏部', '大陸也。山無石者。象形。凡𨸏之屬皆从𨸏。古文[𠱠]。', '𠱠', '', 'null', NULL, '𠱠', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9618, '陵', 'línɡ', '第十四卷', '𨸏部', '大𨸏也。从𨸏，夌聲。', '𨸏;夌', '夌', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9619, '䧰', 'hùn', '第十四卷', '𨸏部', '大𨸏也。从𨸏，鯀聲。', '𨸏;鯀', '鯀', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9620, '阞', 'lè', '第十四卷', '𨸏部', '地理也。从𨸏，力聲。', '𨸏;力', '力', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9621, '陰', 'yīn', '第十四卷', '𨸏部', '闇也。水之南，山之北也。从𨸏，侌聲。', '𨸏;侌', '侌', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9622, '陽', 'yánɡ', '第十四卷', '𨸏部', '高朙也。从𨸏。昜聲。', '𨸏;昜', '昜', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9623, '陸', 'lù', '第十四卷', '𨸏部', '高平地。从𨸏，坴聲。籒文陸[三𡴆]。', '𨸏;坴;𡴆', '坴;𡴆', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9624, '阿', 'ē', '第十四卷', '𨸏部', '大陵曰阿。从𨸏，可聲。一曰阿，曲𨸏也。', '𨸏;可', '可', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9625, '陂', 'bēi', '第十四卷', '𨸏部', '阪也。从𨸏，皮聲。一曰池也。', '𨸏;皮', '皮', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9626, '阪', 'bǎn', '第十四卷', '𨸏部', '坡者曰阪。从𨸏，反聲。一曰澤障也。一曰山脅也。', '𨸏;反', '反', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9627, '陬', 'zōu', '第十四卷', '𨸏部', '阪隅也。从𨸏，取聲。', '𨸏;取', '取', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9628, '隅', 'yú', '第十四卷', '𨸏部', '陬也。从𨸏，禺聲。', '𨸏;禺', '禺', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9629, '險', 'xiǎn', '第十四卷', '𨸏部', '阻難也。从𨸏，僉聲。', '𨸏;僉', '僉', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9630, '限', 'xiàn', '第十四卷', '𨸏部', '阻也。从𨸏，艮聲。一曰門榍也。', '𨸏;艮', '艮', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9631, '阻', 'zǔ', '第十四卷', '𨸏部', '險也。从𨸏，且聲。', '𨸏;且', '且', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9632, '陮', 'duì', '第十四卷', '𨸏部', '陮隗，高也。从𨸏，隹聲。', '𨸏;隹', '隹', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9633, '隗', 'wěi', '第十四卷', '𨸏部', '陮隗也。从𨸏，鬼聲。', '𨸏;鬼', '鬼', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9634, '阭', 'yǔn', '第十四卷', '𨸏部', '高也。从𨸏，允聲。一曰地名也。', '𨸏;允', '允', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9635, '𨻌', 'lěi', '第十四卷', '𨸏部', '磊𨻌也。从𨸏，𠂹聲。', '𨸏;𠂹', '𠂹', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9636, '陗', 'qiào', '第十四卷', '𨸏部', '陖也。从𨸏，肖聲。', '𨸏;肖', '肖', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9637, '陖', 'jùn', '第十四卷', '𨸏部', '陗高也。从𨸏，夋聲。', '𨸏;夋', '夋', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9638, '隥', 'dènɡ', '第十四卷', '𨸏部', '仰也。从𨸏，登聲。', '𨸏;登', '登', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9639, '陋', 'lòu', '第十四卷', '𨸏部', '阸陜也。从𨸏，㔷聲。', '𨸏;㔷', '㔷', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9640, '陜', 'xiá', '第十四卷', '𨸏部', '隘也。从𨸏，夾聲。', '𨸏;夾', '夾', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9641, '陟', 'zhì', '第十四卷', '𨸏部', '登也。从𨸏步。古文陟[人]。', '𨸏;步;人', '', 'null', NULL, '𨸏;步;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9642, '陷', 'xiàn', '第十四卷', '𨸏部', '高下也。从𨸏，臽聲。', '𨸏;臽', '臽', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9643, '隰', 'xí', '第十四卷', '𨸏部', '阪下溼也。从𨸏，㬎聲。', '𨸏;㬎', '㬎', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9644, '䧢', 'qū', '第十四卷', '𨸏部', '㩻䧢也。从𨸏，區聲。', '𨸏;區', '區', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9645, '隤', 'tuí', '第十四卷', '𨸏部', '下隊也。从𨸏，貴聲。', '𨸏;貴', '貴', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9646, '隊', 'zhuì', '第十四卷', '𨸏部', '從高隊也。从𨸏，㒸聲。', '𨸏;㒸', '㒸', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9647, '降', 'jiànɡ', '第十四卷', '𨸏部', '下也。从𨸏，夅聲。', '𨸏;夅', '夅', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9648, '隕', 'yǔn', '第十四卷', '𨸏部', '從高下也。从𨸏，員聲。易曰「有隕自天」。', '𨸏;員', '員', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9649, '隉', 'niè', '第十四卷', '𨸏部', '危也。从𨸏，从毀省。徐巡㠯爲隉，凶也。賈侍中說，隉，法度也。班固說，不安也。周書曰「邦之阢隉」。讀若「虹蜺」之蜺。', '𨸏;毀;蜺', '蜺', 'null', NULL, '𨸏;毀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9650, '阤', 'zhì', '第十四卷', '𨸏部', '小崩也。从𨸏，也聲。', '𨸏;也', '也', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9651, '隓;墮;𡐦', 'huī', '第十四卷', '𨸏部', '敗城𨸏曰隓。从𨸏，𢀡聲。篆文[土隋]。', '𨸏;𢀡;土;隋', '𢀡;隋', 'null', NULL, '𨸏;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9652, '𨻺', 'qīnɡ', '第十四卷', '𨸏部', '仄也。从𨸏，頃聲。', '𨸏;頃', '頃', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9653, '陊', 'duò', '第十四卷', '𨸏部', '落也。从𨸏，多聲。', '𨸏;多', '多', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9654, '阬', 'kēnɡ', '第十四卷', '𨸏部', '閬也。从𨸏，亢聲。', '𨸏;亢', '亢', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9655, '𨽍', 'dú', '第十四卷', '𨸏部', '通溝。㠯防水者也。从𨸏，𧷏聲。讀若洞。古文𨽍从谷。', '𨸏;𧷏;洞;谷', '𧷏;洞', 'null', NULL, '𨸏;谷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9656, '防', 'fánɡ', '第十四卷', '𨸏部', '隄也。从𨸏，方聲。防或从土。', '𨸏;方;土', '方', 'null', NULL, '𨸏;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9657, '隄', 'dī', '第十四卷', '𨸏部', '唐也。从𨸏，是聲。', '𨸏;是', '是', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9658, '阯;址', 'zhǐ', '第十四卷', '𨸏部', '基也。从𨸏，止聲。阯或从土。', '𨸏;止;土', '止', 'null', NULL, '𨸏;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9659, '陘', 'xínɡ', '第十四卷', '𨸏部', '山絕坎也。从𨸏，巠聲。', '𨸏;巠', '巠', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9660, '附', 'bù', '第十四卷', '𨸏部', '附婁，小土山也。从𨸏，付聲。春秋傳曰「附婁無松柏」。', '𨸏;付', '付', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9661, '阺', 'dǐ', '第十四卷', '𨸏部', '秦謂陵阪曰阺。从𨸏，氐聲。', '𨸏;氐', '氐', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9662, '阢', 'wù', '第十四卷', '𨸏部', '石山戴土也。从𨸏，兀聲。', '𨸏;兀', '兀', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9663, '隒', 'yǎn', '第十四卷', '𨸏部', '崖也。从𨸏，兼聲。讀若儼。', '𨸏;兼;儼', '兼;儼', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9664, '阸;阨', 'è', '第十四卷', '𨸏部', '塞也。从𨸏，戹聲。', '𨸏;戹', '戹', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9665, '隔', 'ɡé', '第十四卷', '𨸏部', '塞也。从𨸏，鬲聲。', '𨸏;鬲', '鬲', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9666, '障', 'zhànɡ', '第十四卷', '𨸏部', '隔也。从𨸏，章聲。', '𨸏;章', '章', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9667, '隱', 'yǐn', '第十四卷', '𨸏部', '蔽也。从𨸏，㥯聲。', '𨸏;㥯', '㥯', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9668, '隩', 'ào', '第十四卷', '𨸏部', '水隈厓也。从𨸏，奧聲。', '𨸏;奧', '奧', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9669, '隈', 'wēi', '第十四卷', '𨸏部', '水曲也。从𨸏，畏聲。', '𨸏;畏', '畏', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9670, '𨺫', 'qiǎn', '第十四卷', '𨸏部', '𨺫商，小塊也。从𨸏，从臾。臾，古文蕢字。', '𨸏;臾', '臾', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9671, '𨼬', 'xiè', '第十四卷', '𨸏部', '水衡官谷也。从𨸏，解聲。一曰小谿。', '𨸏;解', '解', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9672, '隴', 'lǒnɡ', '第十四卷', '𨸏部', '天水大阪也。从𨸏，龍聲。', '𨸏;龍', '龍', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9673, '䧇', 'yī', '第十四卷', '𨸏部', '酒泉 天䧇阪也。从𨸏，衣聲。', '𨸏;衣', '衣', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9674, '陝', 'shǎn', '第十四卷', '𨸏部', '弘農 陝也。古虢國，王季之子所封也。从𨸏，㚒聲。', '𨸏;㚒', '㚒', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9675, '𨼊', 'wú', '第十四卷', '𨸏部', '弘農 陝東陬也。从𨸏，𣞤聲。', '𨸏;𣞤', '𣞤', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9676, '𨹵', 'juǎn', '第十四卷', '𨸏部', '河東 安邑陬也。从𨸏，卷聲。', '𨸏;卷', '卷', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9677, '陭', 'yì', '第十四卷', '𨸏部', '上黨 陭氏阪也。从𨸏，奇聲。', '𨸏;奇', '奇', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9678, '隃', 'shù', '第十四卷', '𨸏部', '北陵西隃，鴈門是也。从𨸏，俞聲。', '𨸏;俞', '俞', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9679, '阮', 'yuán', '第十四卷', '𨸏部', '代郡 五阮關也。从𨸏，元聲。', '𨸏;元', '元', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9680, '䧊', 'kū', '第十四卷', '𨸏部', '大𨸏也。一曰右扶風 郿有䧊𨸏。从𨸏，告聲。', '𨸏;告', '告', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9681, '陚', 'wù', '第十四卷', '𨸏部', '丘名。从𨸏，武聲。', '𨸏;武', '武', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9682, '𨺟', 'zhēnɡ', '第十四卷', '𨸏部', '丘名。从𨸏，貞聲。', '𨸏;貞', '貞', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9683, '䦺', 'dīnɡ', '第十四卷', '𨸏部', '丘名。从𨸏，丁聲。讀若丁。', '𨸏;丁', '丁', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9684, '䧦', 'huī', '第十四卷', '𨸏部', '鄭地阪。从𨸏，爲聲。春秋傳曰「將會鄭伯於䧦」。', '𨸏;爲', '爲', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9685, '陼', 'zhǔ', '第十四卷', '𨸏部', '如渚者陼丘。水中高者也。从𨸏，者聲。', '𨸏;者', '者', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9686, '陳', 'chén', '第十四卷', '𨸏部', '宛丘也。舜後嬀滿之所封。从𨸏，从木。申聲。古文陳。', '𨸏;木;申', '申', 'null', NULL, '𨸏;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9687, '陶', 'táo', '第十四卷', '𨸏部', '再成丘也。在濟陰。从𨸏，匋聲。夏書曰「東至于陶丘」。陶丘有堯城，堯嘗所凥，故堯號陶唐氏。', '𨸏;匋', '匋', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9688, '𨹸', 'zhào', '第十四卷', '𨸏部', '耕㠯臿浚出下壚土也。一曰耕休田也。从𨸏。从土，召聲。', '𨸏;土;召', '召', 'null', NULL, '𨸏;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9689, '阽', 'yán', '第十四卷', '𨸏部', '壁危也。从𨸏。占聲。', '𨸏;占', '占', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9690, '除', 'chú', '第十四卷', '𨸏部', '殿陛也。从𨸏，余聲。', '𨸏;余', '余', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9691, '階', 'jiē', '第十四卷', '𨸏部', '陛也。从𨸏，皆聲。', '𨸏;皆', '皆', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9692, '阼', 'zuò', '第十四卷', '𨸏部', '主階也。从𨸏，乍聲。', '𨸏;乍', '乍', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9693, '陛', 'bì', '第十四卷', '𨸏部', '升高陛也。从𨸏，坒聲。', '𨸏;坒', '坒', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9694, '陔', 'ɡāi', '第十四卷', '𨸏部', '階次也。从𨸏，亥聲。', '𨸏;亥', '亥', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9695, '際', 'jì', '第十四卷', '𨸏部', '壁會也。从𨸏，祭聲。', '𨸏;祭', '祭', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9696, '隙', 'xì', '第十四卷', '𨸏部', '壁際也。从𨸏𡭴。𡭴亦聲。', '𨸏;𡭴', '𡭴', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9697, '陪', 'péi', '第十四卷', '𨸏部', '重土也。一曰滿也。从𨸏，咅聲。一曰陪臣。陪，備也。', '𨸏;咅', '咅', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9698, '䧘', 'zhuàn', '第十四卷', '𨸏部', '道邊庳垣也。从𨸏，彖聲。', '𨸏;彖', '彖', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9699, '陾', 'rénɡ', '第十四卷', '𨸏部', '築牆聲也。从𨸏，耎聲。詩曰「捄之陾陾」。', '𨸏;耎', '耎', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9700, '陴', 'pí', '第十四卷', '𨸏部', '城上女牆。俾倪也。从𨸏，卑聲。籒文陴从𩫖。', '𨸏;卑;𩫖', '卑', 'null', NULL, '𨸏;𩫖', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9701, '隍', 'huánɡ', '第十四卷', '𨸏部', '城池也。有水曰池。無水曰隍矣。从𨸏，皇聲。易曰「城復于隍」。', '𨸏;皇', '皇', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9702, '阹', 'qū', '第十四卷', '𨸏部', '依山谷爲午馬圈也。从𨸏，去聲。', '𨸏;去', '去', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9703, '陲', 'chuí', '第十四卷', '𨸏部', '危也。从𨸏，垂聲。', '𨸏;垂', '垂', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9704, '隖', 'wǔ', '第十四卷', '𨸏部', '小障也。一曰庳城也。从𨸏，烏聲。', '𨸏;烏', '烏', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9705, '院', 'yuàn', '第十四卷', '𨸏部', '堅也。从𨸏，完聲。', '𨸏;完', '完', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9706, '陯', 'lún', '第十四卷', '𨸏部', '山𨸏陷也。从𨸏，侖聲。', '𨸏;侖', '侖', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9707, '陙', 'chún', '第十四卷', '𨸏部', '小𨸏也。从𨸏，辰聲。', '𨸏;辰', '辰', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9708, '䧖', 'jiàn', '第十四卷', '𨸏部', '小𨸏也。从𨸏，戔聲。', '𨸏;戔', '戔', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9709, '阠', 'shēn', '第十四卷', '𨸏部', '陵名。从𨸏，卂聲。', '𨸏;卂', '卂', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9710, '阡', 'qiān', '第十四卷', '𨸏部', '路東西爲陌，南北爲阡。从𨸏，千聲。', '𨸏;千', '千', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9711, '𨺅', 'fù', '第十四卷', '𨺅部', '㒳𨸏之閒也。从二𨸏。凡𨺅之屬皆从𨺅。', '𨸏', '', 'null', NULL, '𨸏', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9712, '𨼱', 'jué', '第十四卷', '𨺅部', '𨸏䆕也。从𨺅，夬聲。', '𨺅;夬', '夬', 'null', NULL, '𨺅', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9713, '𨽝;隘', 'ài', '第十四卷', '𨺅部', '陋也。从𨺅，𠍳聲。𠍳，籒文嗌字。篆文𨽝从𨸏益。', '𨺅;𠍳;𨸏;益', '𠍳;益', 'null', NULL, '𨺅;𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9714, '𨽵;燧;𤎩', 'suì', '第十四卷', '𨺅部', '塞上亭，守㷭火者也。从𨺅，从火，遂聲。篆文𨽵省[𨸏火㒸]。', '𨺅;火;遂;𨸏;火;㒸', '遂;㒸', 'null', NULL, '𨺅;火;𨸏;火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9715, '厽', 'lěi', '第十四卷', '厽部', '絫坺土爲牆壁。象形。凡厽之屬皆从厽。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9716, '絫', 'lěi', '第十四卷', '厽部', '增也。从厽糸。厽亦聲。一曰絫，十黍之重也。', '厽;糸', '厽', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9717, '垒', 'lěi', '第十四卷', '厽部', '絫墼也。从厽土。厽亦聲。', '厽;土', '土', 'null', NULL, '厽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9718, '四', 'sì', '第十四卷', '四部', '侌數也。象四分之形。凡四之屬皆从四。古文四如此。籒文四。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9719, '宁', 'zhù', '第十四卷', '宁部', '辨積物也。象形。凡宁之屬皆从宁。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9720, '𤲑', 'zhǔ', '第十四卷', '宁部', '㡒也。所㠯盛米也。从宁甾。甾，缶也。宁亦聲。', '宁;甾', '宁', 'null', NULL, '甾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9721, '叕', 'zhuó', '第十四卷', '叕部', '綴聯也。[四又]象形。凡叕之屬皆从叕。', '又', '', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9722, '綴', 'zhuì', '第十四卷', '叕部', '合箸也。从叕糸。叕亦聲。', '叕;糸', '叕', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9723, '亞', 'yà', '第十四卷', '亞部', '醜也。象人局背之形。賈侍中說㠯爲次弟也。凡亞之屬皆从亞。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9724, '𣉩', 'yà', '第十四卷', '亞部', '闕。[亞日]', '亞;日', '亞', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9725, '五', 'wǔ', '第十四卷', '五部', '五行也。从二。侌昜在天地閒交午也。凡五之屬皆从五。古文五如此。', '二;午', '午', 'null', NULL, '二', 'null', 'null', 'null', 'null', '🔴', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9726, '六', 'liù', '第十四卷', '六部', '易之數。侌變於六，正於八。从入八。凡六之屬皆从六。', '入;八', '', 'null', NULL, '入;八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9727, '七', 'qī', '第十四卷', '七部', '昜之正也。从一。微侌從中衺出也。凡七之屬皆从七。', '一', '', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9728, '九', 'jiǔ', '第十四卷', '九部', '昜之變也。象其屈曲究盡之形。凡九之屬皆从九。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9729, '馗;逵', 'kuí', '第十四卷', '九部', '九𨔶道也。佀龜背，故謂之馗。从九首。馗或从辵坴。馗，高也，故从坴。', '九;首;辵;坴', '九', 'null', NULL, '首;辵;坴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9730, '禸;蹂;厹;𠘯', 'róu', '第十四卷', '禸部', '獸足蹂地也。𠄞，象形。九聲。尒疋曰「狐貍貛貉醜，其足𨆌，其迹禸」。凡禸之屬皆从禸。篆文禸。从足，柔聲。', '𠄞;九;足;柔', '九;柔', 'null', NULL, '𠄞;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9731, '禽', 'qín', '第十四卷', '禸部', '走獸總名。从禸。象形。今聲。禽、离、兕，頭相佀。', '禸;今', '今', 'null', NULL, '禸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9732, '离', 'chī', '第十四卷', '禸部', '山神也。獸形。从禽頭。从禸。从屮。歐陽喬說，离，猛獸也。', '禸;屮', '', 'null', NULL, '禸;屮', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9733, '萬', 'wàn', '第十四卷', '禸部', '蟲也。从禸。象形。', '禸', '', 'null', NULL, '禸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9734, '禹', 'yǔ', '第十四卷', '禸部', '蟲也。从禸。象形。古文禹。', '禸', '', 'null', NULL, '禸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9735, '𥝋', 'fèi', '第十四卷', '禸部', '周成王時，州靡國獻𥝋𥝋，人身反歱，自𥬇，𥬇卽上脣弇其目，食人。北方謂之土螻。爾雅曰「𥝋𥝋，如人，被髮」。讀若費。一名梟陽。从禸。象形。', '費;禸', '費', 'null', NULL, '禸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9736, '禼;𥝁', 'xiè', '第十四卷', '禸部', '蟲也。从禸。象形。讀與偰同。古文禼。', '禸;偰', '偰', 'null', NULL, '禸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9737, '嘼', 'chù', '第十四卷', '嘼部', '獸牲也。象耳、頭、足禸地之形。古文嘼下从禸。凡嘼之屬皆从嘼。', '禸', '', 'null', NULL, '禸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9738, '獸', 'shòu', '第十四卷', '嘼部', '守備者也。一曰㒳足曰禽。四足曰獸。从嘼，从犬。', '嘼;犬', '嘼', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9739, '甲', 'jiǎ', '第十四卷', '甲部', '東方之孟，昜气𦹩動。从木戴孚甲之象。大一經曰「人頭空爲甲」。凡甲之屬皆从甲。古文甲。始於一，見於十，歲成於木之象。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9740, '乙', 'yǐ', '第十四卷', '乙部', '象春艸木冤曲而出。侌气尙彊，其出乙乙也。與丨同意。乙承甲，象人頸。凡乙之屬皆从乙。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9741, '乾', 'qián', '第十四卷', '乙部', '上出也。从乙。乙，物之𨔶也。倝聲。籒文乾[𠐱]。', '乙;倝;𠐱', '倝;𠐱', 'null', NULL, '乙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9742, '亂', 'luàn', '第十四卷', '乙部', '不治也。从乙𤔔。乙，治之也。', '乙;𤔔', '𤔔', 'null', NULL, '乙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9743, '尤', 'yóu', '第十四卷', '乙部', '異也。从乙，又聲。', '乙;又', '又', 'null', NULL, '乙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9744, '丙', 'bǐnɡ', '第十四卷', '丙部', '位南方。萬物成炳然，侌气初起，昜气將𧇾。从一入冂。一者，昜也。丙承乙，象人肩。凡丙之屬皆从丙。', '一;入;冂', '', 'null', NULL, '一;入;冂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9745, '丁', 'dīnɡ', '第十四卷', '丁部', '夏時萬物皆丁實。象形。丁承丙，象人心。凡丁之屬皆从丁。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9746, '戊', 'wù', '第十四卷', '戊部', '中宮也。象六甲五龍相拘絞也。戊承丁，象人脅。凡戊之屬皆从戊。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9747, '成', 'chénɡ', '第十四卷', '戊部', '就也。从戊，丁聲。古文成从午。', '戊;丁;午', '丁', 'null', NULL, '戊;午', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9748, '己', 'jǐ', '第十四卷', '己部', '中宮也。象萬物辟藏詘形也。己承戊，象人腹。凡己之屬皆从己。古文己。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9749, '巹', 'jǐn', '第十四卷', '己部', '謹身有所承也。从己丞。讀若詩云「赤舄几几」。', '己;丞;几', '几', 'null', NULL, '己;丞', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9750, '㠱', 'jì', '第十四卷', '己部', '長居也。从己，其聲。讀若杞。', '己;其;杞', '其;杞', 'null', NULL, '己', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9751, '巴', 'bā', '第十四卷', '巴部', '蟲也。或曰食象它。象形。凡巴之屬皆从巴。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9752, '𢃳', 'bǎ', '第十四卷', '巴部', '𢱧擊也。从巴帚。闕。', '巴;帚', '巴', 'null', NULL, '帚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9753, '庚', 'ɡēnɡ', '第十四卷', '庚部', '位西方。象秋時萬物庚庚有實也。庚承己，象人䐡。凡庚之屬皆从庚。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9754, '辛', 'xīn', '第十四卷', '辛部', '秋時萬物成而孰。金剛味辛，辛痛卽泣出。从一䇂。䇂，辠也。辛承庚，象人股。凡辛之屬皆从辛。', '一;䇂', '', 'null', NULL, '一;䇂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9755, '辠', 'zuì', '第十四卷', '辛部', '犯法也。从辛自。言辠人戚鼻苦辛之𢝊。秦㠯辠佀𦤃字，改爲罪。', '辛;自', '', 'null', NULL, '辛;自', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9756, '辜', 'ɡū', '第十四卷', '辛部', '辠也。从辛，古聲。古文辜从死。', '辛;古;死', '古', 'null', NULL, '辛;死', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9757, '辥', 'xuē', '第十四卷', '辛部', '辠也。从辛，𡴎聲。', '辛;𡴎', '𡴎', 'null', NULL, '辛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9758, '辤;辝', 'cí', '第十四卷', '辛部', '不受也。从受辛。受辛宜辤之也。籒文辤[台]。', '受;辛;台', '台', 'null', NULL, '受;辛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9759, '辭', 'cí', '第十四卷', '辛部', '說也。从𤔔辛。𤔔辛猶理辜也。籒文辭从司。', '𤔔;辛;司', '司', 'null', NULL, '𤔔;辛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9760, '辡', 'biǎn', '第十四卷', '辡部', '辠人相與訟也。从二辛。凡辡之屬皆从辡。', '辛', '', 'null', NULL, '辛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9761, '辯', 'biàn', '第十四卷', '辡部', '治也。从言在辡之閒。', '言;辡', '辡', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9762, '壬', 'rén', '第十四卷', '壬部', '位北方也。侌極昜生，故易曰「龍戰于野」。戰者，接也。象人褢妊之形。承亥壬㠯子，生之敘也。壬與巫同意。壬承辛，象人脛，脛任體也。凡壬之屬皆从壬。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9763, '癸', 'ɡuǐ', '第十四卷', '癸部', '冬時水土平，可揆度也。象水從四方流入地中之形。癸承壬，象人足。凡癸之屬皆从癸。籒文从癶，从矢。', '揆;癶;矢', '揆', 'null', NULL, '癶;矢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9764, '子', 'zǐ', '第十四卷', '子部', '十一月昜气動，萬物滋，人㠯爲偁。象形。凡子之屬皆从子。古文子从川，象髮也。籒文子，𦥓有髮。臂、脛，在几上也。', '川;几', '', 'null', NULL, '川;几', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9765, '孕', 'yùn', '第十四卷', '子部', '褢子也。从子，乃聲。', '子;乃', '乃', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9766, '㝃', 'miǎn', '第十四卷', '子部', '生子免身也。从子免。', '子;免', '免', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9767, '字', 'zì', '第十四卷', '子部', '乳也。愛也。从子在宀下。子亦聲。', '子;宀', '子', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9768, '㝅;𣫌', 'ɡòu', '第十四卷', '子部', '乳也。从子，㱿聲。一曰㝅瞀也。', '子;㱿', '㱿', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9769, '孿', 'luán', '第十四卷', '子部', '一乳㒳子也。从子，䜌聲。', '子;䜌', '䜌', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9770, '孺', 'rú', '第十四卷', '子部', '乳子也。一曰輸孺也。輸孺尙小也。从子，需聲。', '子;需', '需', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9771, '季', 'jì', '第十四卷', '子部', '少偁也。从子，稚省。稚亦聲。', '子;稚', '稚', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9772, '孟', 'mènɡ', '第十四卷', '子部', '長也。从子，皿聲。', '子;皿', '皿', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9773, '孼', 'niè', '第十四卷', '子部', '庶子也。从子，辥聲。', '子;辥', '辥', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9774, '孳;孶', 'zī', '第十四卷', '子部', '孶孶，彶彶生也。从子，茲聲。籒文孶从絲。', '子;茲;絲', '茲', 'null', NULL, '子;絲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9775, '孤', 'ɡū', '第十四卷', '子部', '無父也。从子，瓜聲。', '子;瓜', '瓜', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9776, '存', 'cún', '第十四卷', '子部', '恤問也。从子，在省。', '子;在', '', 'null', NULL, '子;在', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9777, '𡥈', 'jiào', '第十四卷', '子部', '放也。从子，爻聲。', '子;爻', '爻', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9778, '疑', 'yí', '第十四卷', '子部', '惑也。从子止匕。矢聲。', '子;止;匕;矢', '矢', 'null', NULL, '子;止;匕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9779, '了', 'liǎo', '第十四卷', '了部', '尥也。从子無臂。象形。凡了之屬皆从了。', '子', '', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9780, '孑', 'jié', '第十四卷', '了部', '無又臂也。从了。𠃊，象形。', '了;𠃊', '', 'null', NULL, '了;𠃊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9781, '孓', 'jué', '第十四卷', '了部', '無𠂇臂也。从了。𠃎，象形。', '了;𠃎', '', 'null', NULL, '了;𠃎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9782, '孨', 'zhuǎn', '第十四卷', '孨部', '謹也。从三子。凡孨之屬皆从孨。讀若翦。', '子;翦', '翦', 'null', NULL, '子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9783, '孱', 'chán', '第十四卷', '孨部', '迮也。从孨在尸下。一曰呻吟也。', '孨;尸', '孨', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9784, '孴;𡥨', 'nǐ', '第十四卷', '孨部', '盛皃。从孨，从日。讀若「薿薿」。一曰若「存」。籒文孴从二子。一曰𡥨卽奇字「晉」。', '孨;日;薿;存;子;晉', '薿;存', 'null', NULL, '孨;日;子;晉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9785, '𠫓;㐬', 'tū', '第十四卷', '𠫓部', '不順忽出也。从到子。易曰「突如其來如」。不孝子突出，不容於內也。𠫓即易突字也。凡𠫓之屬皆从𠫓。或从到古文子[川]。', '子;突;川', '突', 'null', NULL, '子;川', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9786, '育;毓', 'yù', '第十四卷', '𠫓部', '養子使作善也。从𠫓。肉聲。虞書曰「敎育子」。育或从每。', '𠫓;肉;每', '肉', 'null', NULL, '𠫓;每', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9787, '疏', 'shū', '第十四卷', '𠫓部', '通也。从㐬[𠫓]，从疋。疋亦聲。', '㐬;𠫓;疋', '疋', 'null', NULL, '㐬;𠫓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9788, '丑', 'chǒu', '第十四卷', '丑部', '紐也。十二月萬物動用事。象手之形。日加丑。亦舉手時也。凡丑之屬皆从丑。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9789, '䏔', 'niǔ', '第十四卷', '丑部', '食肉也。从丑肉。丑亦聲。', '丑;肉', '肉', 'null', NULL, '丑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9790, '羞', 'xiū', '第十四卷', '丑部', '進獻也。从羊丑。羊所進也。丑亦聲。', '羊;丑', '丑', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9791, '寅', 'yín', '第十四卷', '寅部', '髕也。正月昜气動，去黃泉欲上出，侌尙強也。象宀不𨔶髕寅於下也。凡寅之屬皆从寅。古文寅[土]。', '宀;土', '', 'null', NULL, '宀;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9792, '卯', 'mǎo', '第十四卷', '卯部', '冒也。二月萬物冒地而出。象開門之形。故二月爲天門。凡卯之屬皆从卯。古文卯。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '📖', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9793, '辰', 'chén', '第十四卷', '辰部', '震也。三月昜气動，靁電振，民農時也，物皆生。从乙𠤎。𠤎，象芒達。厂聲。辰，房星，天時也，𠄞故从上，从𠄞。𠄞，古文上字。凡辰之屬皆从辰。古文辰。', '乙;𠤎;厂;𠄞', '厂', 'null', NULL, '乙;𠤎;𠄞', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9794, '辱', 'rǔ', '第十四卷', '辰部', '恥也。从寸在辰下。失耕時，於封畺上戮之也。辰者，農之時也，故房星爲辰。田候也。', '寸;辰', '', 'null', NULL, '寸;辰', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9795, '巳;已', 'sì', '第十四卷', '巳部', '巳也。四月昜气巳出，陰气巳臧，萬物見，成彣彰，故巳爲它。象形。凡巳之屬皆从巳。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9796, '㠯;以', 'yǐ', '第十四卷', '巳部', '用也。从反巳。賈侍中說，巳，意巳實也。象形。', '巳', '', 'null', NULL, '巳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9797, '午', 'wǔ', '第十四卷', '午部', '啎也。五月侌气啎屰昜，冒地而出也。象形。此與矢同意。凡午之屬皆从午。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9798, '啎;牾', 'wǔ', '第十四卷', '午部', '屰也。从午，吾聲。', '午;吾', '吾', 'null', NULL, '午', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9799, '未', 'wèi', '第十四卷', '未部', '味也。六月滋味也。五行木老於未。象木重枝葉也。凡未之屬皆从未。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9800, '申;𢑚', 'shēn', '第十四卷', '申部', '神也。七月侌气成體自申束。从𦥑自持也。吏㠯餔時聽事，申旦政也。凡申之屬皆从申。古文申。籒文申。', '𦥑', '', 'null', NULL, '臼', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9801, '𣍃;朄', 'yìn', '第十四卷', '申部', '擊小𡔷引樂聲也。从申，柬聲。', '申;柬', '柬', 'null', NULL, '申', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9802, '臾', 'yú', '第十四卷', '申部', '束縛捽抴爲臾曳。从申，从乙。', '申;乙', '', 'null', NULL, '申;乙', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9803, '曳', 'yè', '第十四卷', '申部', '臾曳也。从申，𠂆聲。', '申;𠂆', '𠂆', 'null', NULL, '申', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9804, '酉;丣', 'yǒu', '第十四卷', '酉部', '就也。八月黍成，可爲酎酒。象古文酉之形也。凡酉之屬皆从酉。古文酉从卯。卯爲春門，萬物巳出。丣爲秋門，萬物巳入。一，𨳰門象也。', '卯;一', '', 'null', NULL, '卯;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9805, '酒', 'jiǔ', '第十四卷', '酉部', '就也，所㠯就人性之善惡。从水酉。酉亦聲。一曰造也。吉凶所造起也。古者儀狄作酒醪，禹嘗之而美，遂疏儀狄。杜康作秫酒。', '水;酉', '酉', 'null', NULL, '水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9806, '𨢊', 'ménɡ', '第十四卷', '酉部', '𥶶生衣也。从酉。冡聲。', '酉;冡', '冡', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9807, '䤁', 'yín', '第十四卷', '酉部', '孰𥶶也。从酉，甚聲。', '酉;甚', '甚', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9808, '釀', 'niànɡ', '第十四卷', '酉部', '醞也。作酒曰釀。从酉，襄聲。', '酉;襄', '襄', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9809, '醞;醖', 'yùn', '第十四卷', '酉部', '釀也。从酉，𥁕聲。', '酉;𥁕', '𥁕', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9810, '𨠒', 'fàn', '第十四卷', '酉部', '酒疾孰也。从酉，弁聲。', '酉;弁', '弁', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9811, '酴', 'tú', '第十四卷', '酉部', '酒母也。从酉，余聲。讀若廬。', '酉;余;廬', '余;廬', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9812, '釃', 'shī', '第十四卷', '酉部', '下酒也。从酉，麗聲。一曰醇也。', '酉;麗', '麗', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '麗轨', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9813, '䣺', 'juān', '第十四卷', '酉部', '𨢌酒也。从酉，肙聲。', '酉;肙', '肙', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9814, '𨢌', 'lì', '第十四卷', '酉部', '䣺也。从酉，鬲聲。', '酉;鬲', '鬲', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9815, '醴', 'lǐ', '第十四卷', '酉部', '酒一宿孰也。从酉，豊聲。', '酉;豊', '豊', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9816, '醪', 'láo', '第十四卷', '酉部', '汁滓酒也。从酉，翏聲。', '酉;翏', '翏', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9817, '醇', 'chún', '第十四卷', '酉部', '不澆酒也。从酉，𦎫聲。', '酉;𦎫', '𦎫', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9818, '醹', 'rǔ', '第十四卷', '酉部', '厚酒也。从酉，需聲。詩曰「酒醴維醹」。', '酉;需', '需', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9819, '酎', 'zhòu', '第十四卷', '酉部', '三重醇酒也。从酉，肘省聲。朙堂月令曰「孟秋天子㱃酎」。', '酉;肘', '肘', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9820, '醠', 'ànɡ', '第十四卷', '酉部', '濁酒也。从酉，盎聲。', '酉;盎', '盎', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9821, '醲', 'nónɡ', '第十四卷', '酉部', '厚酒也。从酉，農聲。', '酉;農', '農', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9822, '䣵', 'rónɡ', '第十四卷', '酉部', '重釀酒也。从酉，耳聲。', '酉;耳', '耳', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9823, '酤', 'ɡū', '第十四卷', '酉部', '一宿酒也。一曰買酒也。从酉，古聲。', '酉;古', '古', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9824, '𨢮', 'zhī', '第十四卷', '酉部', '酒也。从酉，𥏼省聲。', '酉;𥏼', '𥏼', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9825, '𨣨', 'làn', '第十四卷', '酉部', '泛齊。行酒也。从酉，監聲。', '酉;監', '監', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9826, '䤗', 'ɡǎn', '第十四卷', '酉部', '酒味淫也。从西，竷省聲。讀若春秋傳曰「美而豓」。', '西;竷;豓', '竷;豓', 'null', NULL, '西', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9827, '酷', 'kù', '第十四卷', '酉部', '酒味厚也。从酉，告聲。', '酉;告', '告', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9828, '酓', 'yǎn', '第十四卷', '酉部', '酒味苦也。从酉，今聲。', '酉;今', '今', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9829, '醰;㽑', 'dàn', '第十四卷', '酉部', '酒味長也。从酉，覃聲。', '酉;覃', '覃', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9830, '䣪', 'pò', '第十四卷', '酉部', '酒色也。从酉，𣎵聲。', '酉;𣎵', '𣎵', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9831, '配', 'pèi', '第十四卷', '酉部', '酒色也。从酉，妃省聲。', '酉;妃', '妃', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9832, '䣧', 'yì', '第十四卷', '酉部', '酒色也。从酉，弋聲。', '酉;弋', '弋', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9833, '醆', 'zhǎn', '第十四卷', '酉部', '爵也。一曰酒濁而微清也。从酉，戔聲。', '酉;戔', '戔', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9834, '酌', 'zhuó', '第十四卷', '酉部', '盛酒行觴也。从酉，勺聲。', '酉;勺', '勺', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9835, '醮', 'jiào', '第十四卷', '酉部', '冠娶禮祭也。从酉，焦聲。醮或从示。', '酉;焦;示', '焦', 'null', NULL, '酉;示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9836, '䤐', 'jǐn', '第十四卷', '酉部', '歃酒也。从酉，朁聲。', '酉;朁', '朁', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9837, '𨟴', 'yìn', '第十四卷', '酉部', '少少㱃也。从酉，匀聲。', '酉;匀', '匀', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9838, '醻;酬', 'chóu', '第十四卷', '酉部', '獻醻，主人進客也。从酉，𠷎聲。醻或从州。', '酉;𠷎;州', '𠷎;州', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9839, '醋', 'zuó', '第十四卷', '酉部', '客酌主人也。从酉，昔聲。', '酉;昔', '昔', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9840, '䤉', 'mì', '第十四卷', '酉部', '㱃酒俱盡也。从酉，𥁑聲。', '酉;𥁑', '𥁑', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9841, '釂', 'jiào', '第十四卷', '酉部', '㱃酒盡也。从酉，爵聲。', '酉;爵', '爵', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9842, '酣', 'hān', '第十四卷', '酉部', '酒樂也。从酉，甘聲。', '酉;甘', '甘', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9843, '酖', 'dān', '第十四卷', '酉部', '樂酒也。从酉，冘聲。', '酉;冘', '冘', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9844, '醧', 'yù', '第十四卷', '酉部', '宴厶㱃也。从酉，區聲。', '酉;區', '區', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9845, '醵', 'jù', '第十四卷', '酉部', '會㱃酒也。从酉，豦聲。醵或从巨。', '酉;豦;巨', '豦;巨', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9846, '酺', 'pú', '第十四卷', '酉部', '王德布，大㱃酒也。从酉，甫聲。', '酉;甫', '甫', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9847, '醅', 'pēi', '第十四卷', '酉部', '醉飽也。从酉，咅聲。讀若僰。', '酉;咅;僰', '咅;僰', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9848, '醉', 'zuì', '第十四卷', '酉部', '卒也。卒其度量，不至於亂也。从酉卒。一曰酒潰也。', '酉;卒', '卒', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9849, '醺', 'xūn', '第十四卷', '酉部', '醉也。从酉，熏聲。詩曰「公尸來燕醺醺」。', '酉;熏', '熏', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9850, '醟', 'yònɡ', '第十四卷', '酉部', '䣱酒也。从酉，熒省聲。', '酉;熒', '熒', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9851, '䣱', 'xù', '第十四卷', '酉部', '酒醟也。从酉，句聲。', '酉;句', '句', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9852, '酲', 'chénɡ', '第十四卷', '酉部', '病酒也。一曰醉而覺也。从酉，呈聲。', '酉;呈', '呈', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9853, '醫', 'yī', '第十四卷', '酉部', '治病工也。从殹，从酉。殹，惡姿也。醫之性然，得酒而使，故从酉。王育說。一曰殹，病聲。酒，所㠯治病也。周禮有「醫酒」。古者巫彭初作醫。', '殹;酉', '', 'null', NULL, '殹;酉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9854, '莤', 'sù', '第十四卷', '酉部', '禮「祭束茅，加於祼圭，而灌鬯酒」是爲莤，像神㱃之也。从酉艸。春秋傳曰「爾貢苞茅不入，王祭不供，無㠯莤酒」。一曰莤，𣖁上𡫳也。', '酉;艸', '', 'null', NULL, '酉;艸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9855, '醨', 'lí', '第十四卷', '酉部', '薄酒也。从酉，离聲。讀若離。', '酉;离;離', '离;離', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9856, '䤘', 'chǎn', '第十四卷', '酉部', '酢也。从酉，韱聲。', '酉;韱', '韱', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9857, '酸', 'suān', '第十四卷', '酉部', '酢也。从酉，夋聲。籒文酸从畯。', '酉;夋;畯', '夋;畯', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9858, '酨', 'zài', '第十四卷', '酉部', '酢浆也。从酉，𢦒聲。', '酉;𢦒', '𢦒', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9859, '醶', 'yàn', '第十四卷', '酉部', '酢浆也。从酉，僉聲。', '酉;僉', '僉', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9860, '酢', 'cù', '第十四卷', '酉部', '醶也。从酉，乍聲。', '酉;乍', '乍', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9861, '酏', 'yǐ', '第十四卷', '酉部', '黍酒也。从酉，也聲。一曰甜也。賈侍中說，酏爲鬻淸。', '酉;也', '也', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9862, '𨡓;𤖕;醬;酱', 'jiànɡ', '第十四卷', '酉部', '醢也。从肉酉。酒㠯龢酱也。爿聲。古文𤖕如此。籒文[皿]。', '肉;酉;爿;皿', '爿', 'null', NULL, '肉;酉;皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9863, '醢', 'hǎi', '第十四卷', '酉部', '肉𤖕也。从酉，𥁓聲。籒文[艸鹵䀁]。', '酉;𥁓;艸;鹵;䀁', '𥁓;䀁', 'null', NULL, '酉;艸;鹵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9864, '𨡭', 'mú', '第十四卷', '酉部', '𨡭䤅，榆𤖕也。从酉，敄聲。', '酉;敄', '敄', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9865, '䤅', 'tú', '第十四卷', '酉部', '𨡭䤅也。从酉，俞聲。', '酉;俞', '俞', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9866, '𨢡', 'bì', '第十四卷', '酉部', '𢶈榆𤖕也。从酉，畢聲。', '酉;畢', '畢', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9867, '䤎', 'jú', '第十四卷', '酉部', '𤖕也。从酉，矞聲。', '酉;矞', '矞', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9868, '䣼', 'liánɡ', '第十四卷', '酉部', '襍味也。从酉，京聲。', '酉;京', '京', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9869, '䤔', 'jiàn', '第十四卷', '酉部', '䤔䣸，味薄也。从酉，漸聲。', '酉;漸', '漸', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9870, '䣸', 'rǎn', '第十四卷', '酉部', '䤔䣸也。从酉，任聲。', '酉;任', '任', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9871, '酹', 'lèi', '第十四卷', '酉部', '餟祭也。从酉，寽聲。', '酉;寽', '寽', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9872, '酪', 'lào', '第十四卷', '酉部', '乳𤖅也。从酉，各聲。', '酉;各', '各', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9873, '醐', 'hú', '第十四卷', '酉部', '醍醐，酪之精者也。从酉，胡聲。', '酉;胡', '胡', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9874, '酩', 'mǐnɡ', '第十四卷', '酉部', '酩酊，醉也。从酉，名聲。', '酉;名', '名', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9875, '酊', 'dǐnɡ', '第十四卷', '酉部', '酩酊也。从酉，丁聲。', '酉;丁', '丁', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9876, '醒', 'xǐnɡ', '第十四卷', '酉部', '醉解也。从酉，星聲。', '酉;星', '星', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9877, '醍', 'tǐ', '第十四卷', '酉部', '清酒也。从酉，是聲。', '酉;是', '是', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9878, '酋', 'qiú', '第十四卷', '酋部', '繹酒也。从酉，水半見於上。禮有「大酋」，掌酒官也。凡酋之屬皆从酋。', '酉', '', 'null', NULL, '酉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9879, '𢍜;尊', 'zūn', '第十四卷', '酋部', '酒器也。从酋。廾㠯奉之。周禮「六𢍜」——犧𢍜、象𢍜、箸𢍜、壺𢍜、大𢍜、山𢍜，㠯待祭祀賓客之禮。𢍜或从寸。', '酋;廾;寸', '', 'null', NULL, '酋;廾;寸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9880, '戌', 'xū', '第十四卷', '戌部', '烕也。九月昜气微，萬物畢成，昜下入地也。五行土生於戊，盛於戌。从戊一。一亦聲。凡戌之屬皆从戌。', '戊;一', '一', 'null', NULL, '戊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9881, '亥', 'hài', '第十四卷', '亥部', '荄也。十月微昜起接盛侌。从𠄞。𠄞，古文上字也。一人男，一人女也。从𠃋，象褱子咳咳之形也。春秋傳曰「亥有二首六身」。凡亥之屬皆从亥。古文亥。亥爲豕，與豕同。亥而生子，復從一起。', '𠄞;人;𠃋;豕', '', 'null', NULL, '𠄞;人;𠃋;豕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
