INSERT INTO `t_han_nga` VALUES (1, '天;蒼天;昊天;旻天;上天;乾;玄', '顯/坦;晧;閔;健;縣', '天，豫 司 兖 冀以舌腹言之，天，顯也，在上高顯也。靑 徐以舌頭言之，天，坦也，坦然高而遠也。春曰蒼天，陽氣始發，色蒼蒼也。夏曰昊天，其氣布散晧晧也。秋曰旻天，旻，閔也，物就枯落可閔傷也。冬曰上天，其氣上騰，與地絕也，故月令曰：「天氣上騰，地氣下降。」易謂之乾，乾，健也，健行不息也。又謂之玄，玄，縣也，如縣物在上也。', '', '釋天第一', '釋名', '1;3;4;6;7', DEFAULT, DEFAULT, '3;538/3;6664/3;4188/3;6/3;9741;2506', '5690/0/9070;4212;7785;4997;5705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2, '日', '實', '日，實也，光明盛實也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4187', '4585', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3, '月', '闕', '月，闕也，滿則闕也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4273', '7744', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4, '光', '晃/廣', '光，晃也，晃晃然也。亦言廣也，所照廣遠也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '6509', '4200/0/5938', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5, '景', '境', '景，境也，所照處有境限也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4211', '9154', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6, '晷', '規', '晷，規也，如規畫也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4218', '6675', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (7, '燿', '', '{燿}曜，耀也，光明照耀也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '6501', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (8, '星', '散', '星，散也，列位布散也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4314', '2720', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (9, '宿', '', '宿，宿也，星各止宿其處也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '4600', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (10, '氣/气', '愾', '氣[气]，愾也，愾然有聲而無形也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4374/0/224', '6895', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (11, '風', '氾/放', '風，豫 司 兖 冀橫口合脣言之，風，氾也，其氣博氾而動物也。靑 徐言風，踧口開脣推氣言之，風，放也，氣放散也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '8977', '7180/0/2512', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (12, '陰', '蔭', '陰，蔭也，氣在內奧蔭也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9621', '524', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (13, '陽', '揚', '陽，揚也，氣在外發揚也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9622', '7956', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (14, '寒', '扞', '寒，捍{扞}也，捍格也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4613', '8065', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (15, '暑', '煮', '暑，煮也，熱如煮物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4243', '1877', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (16, '熱', '爇', '熱，爇也，如火所燒爇也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '6510', '6422', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (17, '雨', '羽/輔', '雨，羽也，〔雨水從雲下也〕，如鳥羽動則散也。〔亦言輔也，言輔時生養也〕。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7525', '2241/0/9565', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (18, '春', '蠢', '春，蠢也，物蠢動而生也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '683', '8969', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (19, '夏', '假/叚', '夏，假[叚]也，寬假萬物，使生長也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '3371', '5074/0/1927', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (20, '秋', '緧/逎', '秋，緧[逎]也，緧迫品物，使時成也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4491', '8709/0/1179', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (21, '冬', '終', '冬，終也，物終成也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7514', '8588', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (22, '時', '期/稘/司', '四時，四方各一時也。時，期[稘]也，物之生死，各應節期而止也。〔又曰時，司空也。司空，主也，各主一方之生死。〕', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4189', '4280/0/4500/0/5771', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (23, '歲;年;載;祀', '越;進;巳', '歲，越也，越故限也。年，進也，進而前也。唐 虞曰載，載生物也。殷曰祀，祀，巳也，新氣升故氣巳也。', '', '釋天第一', '釋名', '1;2;4', DEFAULT, DEFAULT, '1082;4482;9569;31', '990;1113;9795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (24, '五行', '', '五行者，五氣也，於其方各施行也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '9725/1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (25, '金', '禁', '金，禁也，其氣剛巖能禁制物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9259', '71', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (26, '木', '冒', '木，冒也，華葉自覆冒也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '3411', '4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (27, '水', '凖', '水，凖也，凖平物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '6982', '7351', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (28, '火', '化/𠤎/毀/燬', '火，化[𠤎]也，消化物也。亦言毀[燬]也，物入中皆毀壞也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '6414', '5214/0/5211/0/9116/0/6417', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (29, '土', '吐', '土，吐也，能吐生萬物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9019', '884', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (30, '子;坎', '孳;險', '子，孳也，陽氣始萌，孳生於下也。於易爲坎，坎，險也。', '', '釋天第一', '釋名', '1;2', DEFAULT, DEFAULT, '9764;9084', '9774;9629', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (31, '丑;艮', '紐;限', '丑，紐也，寒氣自屈紐也。於易爲艮，艮，限也，時未可聽物生，限止之也。', '', '釋天第一', '釋名', '1;2', DEFAULT, DEFAULT, '9788;5223', '8651;9630', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (32, '寅', '演', '寅，演也，演生物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9791', '7146', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (33, '卯;震', '冒', '卯，冒也，載冒土而出也。於易爲震，二月之時，雷始震也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9792;7531', '4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (34, '辰', '伸', '辰，伸也，物皆伸舒而出也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9793', '5110', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (35, '巳;巽', '散', '巳，巳也，陽氣畢布巳也。於易爲巽，巽，散也，物皆生布散也。', '', '釋天第一', '釋名', '2', DEFAULT, DEFAULT, '9795;3024', '2720', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (36, '午;離', '啎;麗', '午，仵{啎}也，陰氣從下上，與陽相仵逆也。於易爲離，離，麗也，物皆附麗陽氣以茂也。', '', '釋天第一', '釋名', '1;2', DEFAULT, DEFAULT, '9797;2293', '9798;6286', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (37, '未', '昧', '未，昧也，日中則昃，向幽昧也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9799', '4192', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (38, '申', '身', '申，身也，物皆成其身體也，亦言各申束之，使備成也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9800', '5248', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (39, '酉;兌', '秀;說', '酉，秀也，秀者物皆成也。於易爲兌，兌，悦{說}也，物得備足，皆喜悦也。', '', '釋天第一', '釋名', '1;2', DEFAULT, DEFAULT, '9804;5459', '4415;1545', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (40, '戌', '恤/脫/挩', '戌，恤也，物當收斂，矜恤之也。亦言脫[挩]也，物脫落也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9880', '6799/0/2652/0/7977', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (41, '亥', '核', '亥，核也，收藏百物，核取其好惡、眞僞也。亦言物成皆堅核也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9881', '3728', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (42, '甲', '', '甲，孚甲也，萬物解孚甲而生也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '9739', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (43, '乙', '軋', '乙，軋也，自抽軋而出也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9740', '9579', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (44, '丙', '炳', '丙，炳也，物生炳然，皆著見也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9744', '6494', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (45, '丁', '壯', '丁，壯也，物體皆丁壯也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9745', '228', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (46, '戊', '茂', '戊，茂也，物皆茂盛也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9746', '522', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (47, '己', '紀', '己，紀也，皆有定形，可紀識也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9748', '8540', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (48, '庚', '更', '庚猶更也。更，堅強貌也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9753', '2020', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (49, '辛', '新', '辛，新也，物初新者，皆收成也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9754', '9487', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (50, '壬', '妊', '壬，妊也，陰陽交，物懷妊，至子而萌也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9762', '8133', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (51, '癸', '揆', '癸，揆也，揆度而生，乃出之也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '9763', '7973', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (52, '霜', '喪', '霜，喪也，其氣慘毒，物皆喪也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7562', '978', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (53, '露', '慮', '露，慮也，覆慮物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7561', '6702', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (54, '雪', '綏', '雪，綏也，水下遇寒氣而凝，綏綏然也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7532', '8762', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (55, '霰', '星', '霰，星也，水雪相搏，如星而散也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7534', '4314', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (56, '霢霂', '沐', '霢霂，小雨也，言裁霢歷霑漬，如人沐頭，惟及其上枝，而根不濡也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7540/7541', '7409', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (57, '雲', '云/運', '雲猶云，云，眾盛意也。又言運也，若運行也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7576', '7576/0/1138', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (58, '霞', '假', '〔霞，白雲映日光而成赤色，假日之赤光而成也，故字从雨，叚聲。〕', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7571', '5074', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (59, '雷', '', '雷，硍也，如轉物有所硍雷之聲也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '7526', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (60, '電', '殄', '電，殄也，乍見則殄滅也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7530', '2551', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (61, '震;辟歷', '戰', '震，戰也，所擊輒破，若攻戰也。又曰辟歷，辟，析也，所歷皆破析也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7531;5796/1070', '8382', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (62, '雹', '包/儦', '雹，跑{包}[儦]也，其所中物皆摧折，如人所蹴跑也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7535', '5814/0/4986', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (63, '虹;叕蝀/螮蝀;升朝;美人', '攻;掇/歠/東', '〔虹，陽氣之動也。〕虹，攻也，純陽攻陰氣也。又曰蝃蝀{叕蝀}[螮蝀]，其見每於日在西而見於東，掇[歠]飲東方之水氣也。見於西方曰升朝，日始升而岀見也。又曰美人，陰陽不和，婚姻錯亂，淫風流行，男美於女，女美於男，互相奔隨之時，則此氣盛，故以其盛時名之也。', '', '釋天第一', '釋名', '1;2', DEFAULT, DEFAULT, '8933;9721/8935/0/8934/8935;9505/4287;2355/4948', '2051;7987/0/5590/0/3844', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (64, '霓', '齧', '霓，齧也，其體斷絕，見於非時，此災氣也，傷害於物，如有所蝕齧也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7566', '1313', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (65, '暈', '捲', '暈，捲也，氣在外捲結之也，日月俱然。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4215', '8045', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (66, '曀', '翳', '陰而風曰曀，曀，翳也，言雲氣掩翳日光，使不明也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4227', '2273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (67, '霾', '晦', '風而雨土曰霾，霾，晦也，言如物塵晦之色也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7564', '4225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (68, '珥', '耳', '珥，氣在日兩旁之名也。珥，耳也，言似人耳之在面旁也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '125', '7792', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (69, '𧐂', '食', '日月虧曰蝕{𧐂}，稍稍侵虧，如蟲蝕{食}草木葉也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '8893', '3204', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (70, '朏', '', '〔朏，月未成明也。〕', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '4275', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (71, '霸', '', '〔霸，月始生霸然也。〕', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '4276', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (72, '晦', '灰', '〔晦，月盡之名也。〕晦，灰也，火死爲灰，月光盡似之也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4225', '6448', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (73, '朔', '蘇', '〔朔，月始之名也。〕朔，蘇也，月死復蘇生也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4274', '256', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (74, '弦', '', '弦，月半之名也。其形一旁曲、一旁直，若張弓施弦也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '8507', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (75, '望/朢', '', '望[朢]，月滿之名也。月大十六日、小十五日，日在東、月在西，遙相望也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '8413/0/5240', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (76, '昏', '損', '昏，損也，陽精損滅也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4221', '7975', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (77, '晨', '伸', '晨，伸也，旦而日光復伸見也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4316', '5110', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (78, '祲', '侵', '祲，侵也，赤黑之氣相侵也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '66', '5076', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (79, '氛', '粉', '氛，粉也，潤氣著草木，因寒凍凝，色白若粉之形也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '225', '4376', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (80, '霚', '冒', '霚，冒也，氣蒙亂、覆冒物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7563', '4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (81, '霿', '蒙/冡', '霿，蒙[冡]也，日光不明蒙蒙然也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '7565', '649/0/4803', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (82, '彗星', '', '彗星，星光梢似彗也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '1926/4314', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (83, '孛星', '', '孛星，星旁氣孛孛然也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '3870/4314', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (84, '筆星', '', '筆星，星氣有一枝，末銳似筆也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '1940/4314', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (85, '流星;枉矢', '光', '流星，星轉行如流水也。又曰枉矢，齊 魯謂光景爲枉矢，言其光行若射矢之所至也；亦言其氣枉暴，有所灾害也。', '', '釋天第一', '釋名', '2', DEFAULT, DEFAULT, '7474/4314;3590/3307', '6509', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (86, '癘', '厲', '癘，厲也，疾氣流行中人，如磨厲傷物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4745', '5983', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (87, '疫', '役', '疫，役也，言有鬼行役也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4783', '1976', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (88, '札', '𢧵', '札，𢧵也，氣傷人如有所斷𢧵也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '3768', '8386', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (89, '灾/𡿧', '烖', '灾[𡿧]，烖也，火所燒滅之餘曰烖，言其於物如是也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '6487/0/7488', '6487', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (90, '害', '割', '害，割也，如割削物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '4614', '2777', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (91, '異', '', '異，言異於常也。', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '1777', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (92, '眚', '省', '眚，省也，如病者省瘦也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '2182', '2219', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (93, '匿/𢘋', '態', '慝{匿}[𢘋]，態也，有姦態也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '8418/0/6819', '6833', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (94, '𡝩/䄏', '夭', '妖{𡝩}[䄏]，殀{夭}也，殀害物也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '8274/0/69', '6621', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (95, '蠥', '櫱/隉', '蠥，蘖也，遇之如物見髡蘖{髡櫱}[阢隉]也。', '', '釋天第一', '釋名', '1', DEFAULT, DEFAULT, '8936', '3802/0/9649', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (96, '伏', '', '〔伏者何？金氣伏藏之日。金畏火，故三伏皆庚日。〕', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '5161', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (97, '霄', '', '〔霄，青天也，無雲氣而青碧者也。又曰近天氣也。〕', '', '釋天第一', '釋名', '', DEFAULT, DEFAULT, '7533', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (98, '地;坤', '底/諦;順', '地，底也，其體底下，載萬物也。亦言諦也，五土所生，莫不信諦也。易謂之坤。坤，順也，上順乾也。', '', '釋地第二', '釋名', '1;2', DEFAULT, DEFAULT, '9020;9021', '5950/0/1516;5652', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (99, '土', '吐', '土，吐也，吐生萬物也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '9019', '884', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (100, '田', '塡', '巳耕者曰田。田，塡也，五稼塡滿其中也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '9170', '9069', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (101, '壤', '瀼/䑋', '壤，瀼[䑋]也，瀼瀼，肥濡意也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '9029', '7449/0/2648', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (102, '原/邍', '元', '廣平曰原。原[邍]，元也，如元氣廣大也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '7494/0/1204', '2', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (103, '陸', '漉', '高平曰陸。陸，漉也，水流漉而去也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '9623', '7374', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (104, '衍', '', '下平曰衍，言漫衍也。', '', '釋地第二', '釋名', '', DEFAULT, DEFAULT, '7136', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (105, '隰', '墊', '下濕曰隰。隰，墊也；墊濕意也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '9643', '9085', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (106, '澤', '', '下而有水曰澤，言潤澤也。', '', '釋地第二', '釋名', '', DEFAULT, DEFAULT, '7220', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (107, '鹵', '鑪', '地不生物曰鹵。鹵，爐{鑪}也，如爐火處也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '7714', '9322', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (108, '黎', '藜', '徐州贡土五色，有青、黃、赤、白、黑也。土青曰黎，似藜草色也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '4513', '672', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (109, '埴', '戠/殖', '土黃而細密曰埴。埴，膱{戠}[殖]也，黏胒如脂之膱也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '9034', '8397/0/2558', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (110, '鼠肝', '', '土赤曰鼠肝，似鼠肝色也。', '', '釋地第二', '釋名', '', DEFAULT, DEFAULT, '6391/2607', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (111, '漂/縹', '僄', '土白曰漂[縹]，漂[僄]，輕飛散也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '7177/0/8612', '5137', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (112, '盧/黸', '壚', '土黑曰盧[黸]，盧[壚]然解散也。', '', '釋地第二', '釋名', '1', DEFAULT, DEFAULT, '3143/0/6541', '9032', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (113, '山', '產', '山，產也，產生物也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5848', '3876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (114, '𨸏;陵', '隆', '土山曰阜。阜{𨸏}，厚也，言高厚也。大阜曰陵。陵，隆也，體隆高也。', '', '釋山第三', '釋名', '2', DEFAULT, DEFAULT, '9617;9618', '3877', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (115, '冢', '腫', '山頂曰冢。冢，腫也，言腫起也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5813', '2662', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (116, '陂', '', '山旁曰陂，言陂陁也。', '', '釋山第三', '釋名', '', DEFAULT, DEFAULT, '9625', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (117, '岡', '亢', '山脊曰岡。岡，亢也，在上之言也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5869', '6653', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (118, '涌', '桶', '山旁隴閒曰涌。涌猶桶，桶狹而長也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '7190', '3760', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (119, '嵩/崇', '竦', '山大而高曰嵩。嵩[崇]，竦也，亦高稱也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5909/0/5880', '6682', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (120, '岑', '磛', '山小而高曰岑。岑，嶃{磛}也，嶃嶃然也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5870', '6032', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (121, '嶠', '橋', '山銳而長曰嶠，形似橋也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5904', '3784', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (122, '甗', '', '小山別大山曰甗。甗，甑一孔者，甗形孤出處似之也。', '', '釋山第三', '釋名', '', DEFAULT, DEFAULT, '8455', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (123, '嶅', '堯', '山多小石曰磝。磝{嶅}，堯也，每石堯堯，獨處而出見也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5867', '9164', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (124, '礐/嶨', '學', '山多大石曰礐。礐[嶨]，學也，大石之形，學學然也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '6027/0/5866', '2072', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (125, '岵', '怙', '山有草木曰岵。岵，怙也，人所怙取以爲事用也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5864', '6773', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (126, '屺', '圮', '山無草木曰屺。屺，圮也，圮壞無所岀生也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5865', '9110', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (127, '埒', '脫/捝', '山上有水曰埒。埒，脫[捝]也，脫而下流也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '9051', '2652/0/7977', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (128, '岨', '臚', '石載土曰岨。岨，臚然也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '5868', '2598', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (129, '崔嵬', '', '土載石曰崔嵬，因形名之也。', '', '釋山第三', '釋名', '', DEFAULT, DEFAULT, '5900/5846', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (130, '朝陽;夕陽', '照', '山東曰朝陽，山西曰夕陽。隨日所照而名之也。', '', '釋山第三', '釋名', '2', DEFAULT, DEFAULT, '4287/9622;4325/9622', '6496', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (131, '甽', '吮', '山下根之受霤處曰甽。甽，吮也，吮得山之肥潤也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '7478', '808', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (132, '林', '森', '山中叢木曰林。林，森也，森森然也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '3846', '3854', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (133, '麓', '陸', '山足曰麓。麓，陸也，言水流順陸燥也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '3852', '9623', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (134, '石', '格', '山體曰石。石，格也，堅捍格也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '6009', '3601', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (135, '礫', '料', '小石曰礫。礫，料也，小石相枝柱其閒，料料然岀內氣也。', '', '釋山第三', '釋名', '1', DEFAULT, DEFAULT, '6018', '9492', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (136, '瀆', '獨', '天下大水四，謂之四瀆，江 河 淮 濟是也。瀆，獨也，各獨出其所而入海也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7262', '6355', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (137, '江', '公', '江，公也，諸水流入其中，出物不私，所公共也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '6989', '716', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (138, '淮', '圍', '淮，圍也，圍繞揚州北界，東至海也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7051', '3928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (139, '河', '下', '河，下也，隨地下處而通流也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '6984', '9', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (140, '濟', '', '濟，濟也，言源岀河北濟河而南也。', '', '釋水第四', '釋名', '', DEFAULT, DEFAULT, '7093', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (141, '川', '穿', '川，穿也，穿地而流也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7481', '4640', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (142, '㵎', '閒', '山夾水曰㵎。㵎，閒也，言在兩山之閒也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7267', '7760', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (143, '濫', '銜', '水正出曰濫泉。濫，銜也，如人口有所銜，口闓則見也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7179', '9422', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (144, '沃泉', '', '縣岀曰沃泉，水從上下，有所灌沃也。', '', '釋水第四', '釋名', '', DEFAULT, DEFAULT, '7276/7491', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (145, '氿泉', '軌', '側出曰氿泉。氿，軌也，流狹而長，如車軌也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7241/7491', '9582', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (146, '肥泉', '', '所出同、所歸異曰肥泉，本同出時所浸潤少，所歸各枝散而多，似肥者也。', '', '釋水第四', '釋名', '', DEFAULT, DEFAULT, '2649/7491', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (147, '雍沛;涌泉;濆泉', '', '水從河出曰雍沛，言在河岸限內，時見雍岀，則沛然也。水上出曰涌泉、濆泉并是也。', '', '釋水第四', '釋名', '', DEFAULT, DEFAULT, '2299/7098;7190/7491;7238/7491', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (148, '唐', '掌', '水泆出所爲澤曰掌，水停處如手掌中也。今兖州人謂(澤)[唐]曰掌也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '878', '7828', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (149, '汜', '巳', '水決復入爲汜。汜，巳也，如出有所爲，畢巳而還入也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7248', '9795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (150, '瀾', '連', '風吹水波成文曰瀾。瀾，連也，波體轉流相及連也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7175', '1168', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (151, '淪', '倫/侖', '水小波曰淪。淪，倫[侖]也，水文相次有倫理也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7176', '5036/0/3271', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (152, '涇', '徑', '水直波曰涇。涇，徑也，言如道徑也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7004', '1225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (153, '湄', '眉', '水草交曰湄。湄，眉也，臨水如眉臨目也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7265', '2218', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (154, '海', '晦', '水經川所歸之處曰海。海，晦也，主承穢濁，其水黑而晦也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7129', '4225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (155, '溝', '冓', '水注谷曰溝。田閒之水亦曰溝。溝，搆{冓}也，從橫相交搆也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7261', '2494', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (156, '澮', '會', '注溝曰澮。澮，會也，小溝之所聚會也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '7020', '3274', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (157, '洲/州;渚;沚;坻;潏;魚梁;水碓', '聚;遮;止;遲;術', '水中可居者曰洲。洲[州]，聚也，人及鳥物所聚息之處也。小洲曰渚。渚，遮也，體高能遮水，使從旁回也。小渚曰沚。沚，止也，小可以止息其上也。小沚曰坻。坻，遲也，能遏水使流遲也。人所爲之曰潏。潏，術也，偃水使鬱術也，魚梁、水碓之謂也。', '', '釋水第四', '釋名', '1;2;3;4;5', DEFAULT, DEFAULT, '7490/0/7490;7091;7244;9086;7171;7578/3785;6982/6049', '5236;1186;1064;1148;1267', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (158, '㠀', '到/鳥', '海中可居者曰㠀。㠀，到也，人所奔到也。亦言鳥也，人物所趣如鳥之下也。', '', '釋水第四', '釋名', '1', DEFAULT, DEFAULT, '5851', '7707/0/2368', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (159, '頓丘', '', '丘一成曰頓丘，一頓而成，無上下大小之殺也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '5658/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (160, '陶丘', '', '再成曰陶丘，於高山上一重作之，如陶竈然也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '9687/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (161, '崑崙丘', '', '三成曰崑崙丘，如崑崙之高而積重也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '5910/5911/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (162, '髦丘/嵍丘', '', '前高曰髦丘[嵍丘]，如馬舉頭垂髦也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '5734/5231/0/5896/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (163, '宛丘;陘', '', '中央下曰宛丘，有丘宛宛如偃器也。陘上有一泉水亦是也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '4607/5231;9659', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (164, '阿丘', '何', '偏高曰阿丘，阿，何也，如人儋何物，一邊偏高也。', '', '釋丘第五', '釋名', '1', DEFAULT, DEFAULT, '9624/5231', '5023', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (165, '畝丘', '', '畝丘，丘體滿一畝之地也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '9179/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (166, '圜丘;方丘', '', '圜丘、方丘，就其方圜名之也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '3907/5231;5453/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (167, '融丘', '', '銳上曰融丘。融，明也；明，陽也，凡上銳皆高而近陽者也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '1862/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (168, '乘丘', '', '如乘者曰乘丘。四馬曰乘，一基在後似車，四列在前似駕馬之形也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '3410/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (169, '陼丘', '', '如陼者曰陼丘，形似水中之高地，隆高而廣也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '9685/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (170, '泥丘/屔丘', '', '水潦所止曰泥丘[屔丘]，其止汙水，留不去成泥也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '7106/5231/0/5233/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (171, '都丘', '', '澤中有丘曰都丘，言蟲鳥往所都聚也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '4005/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (172, '梧丘', '啎', '當途曰梧丘。梧，忤{啎}也，與人相當忤也。', '', '釋丘第五', '釋名', '1', DEFAULT, DEFAULT, '3535/5231', '9798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (173, '畫丘', '', '道岀其右曰畫丘，人尙右，凡有指畫皆用右也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '1943/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (174, '載丘', '', '道出其前曰載丘，在前故言載也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '9569/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (175, '昌丘', '', '道岀其後曰昌丘。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '4237/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (176, '渻丘', '此', '水岀其前曰阯丘{渻丘}，阯，此也，言所出然。', '', '釋丘第五', '釋名', '1', DEFAULT, DEFAULT, '7228/5231', '1083', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (177, '阻丘', '', '水岀其後曰阻丘，北阻水以爲險也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '9631/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (178, '沚丘', '止', '水岀其右曰沚丘。沚，止也，西方義氣有所制止也。', '', '釋丘第五', '釋名', '1', DEFAULT, DEFAULT, '7244/5231', '1064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (179, '營丘', '', '水出其左曰營丘。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '4629/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (180, '陽丘', '', '丘高曰陽丘，體高近陽也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '9622/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (181, '宗丘', '', '宗丘，邑中所宗也。', '', '釋丘第五', '釋名', '', DEFAULT, DEFAULT, '4622/5231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (182, '道路', '蹈/露', '道一達曰道路。道，蹈也。路，露也，言人所踐蹈而露見也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '1205/1410', '1355/0/7561', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (183, '岐旁', '', '二達曰岐旁。物兩爲岐，在邊曰旁，此道並通出似之也。', '', '釋道第六', '釋名', '', DEFAULT, DEFAULT, '4018/8', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (184, '劇旁', '', '三達曰劇旁。古者列樹以表道，道有夾溝，以通水潦，恒見修治，此道旁轉多，用功稍劇也。', '', '釋道第六', '釋名', '', DEFAULT, DEFAULT, '2809/8', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (185, '衢', '𣚏', '四達曰衢。齊 魯謂四齒杷爲欋{𣚏}，欋杷地則有四處，此道似之也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '1269', '3685', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (186, '康', '昌', '五達曰康。康，昌也，昌盛也。車步併列並用之，言充盛也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '4457', '4237', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (187, '莊', '裝', '六達曰莊。莊，裝也，裝其上使高也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '241', '5351', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (188, '劇驂', '', '七達曰劇驂。驂馬有四耳，今此道有七，比於劇也。', '', '釋道第六', '釋名', '', DEFAULT, DEFAULT, '2809/6189', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (189, '崇期', '充', '八達曰崇期。崇，充也，道多所通，人充滿其上，如共期也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '5880/4280', '5460', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (190, '逵', '', '九達曰逵。齊 魯謂道多爲逵師，此形然也。', '', '釋道第六', '釋名', '', DEFAULT, DEFAULT, '9729', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (191, '豪', '翱', '城下道曰䧫，䧫{豪}，翱也，言都邑之內翱翔，祖駕之處也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '6100', '2266', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (192, '蹊', '係', '步所用道曰蹊。蹊，係也，言射疾則用之，故還係於正道也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '1244', '5164', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (193, '徑', '經', '徑，經也，人所經由也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '1225', '8530', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (194, '亢/迒', '㽘', '鹿兔之道曰亢[迒]，行不由正，如亢陌[㽘佰]山谷草野而過也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '6653/0/1207', '9186', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (195, '涂', '度', '涂，度也，人所由得通度也。', '', '釋道第六', '釋名', '1', DEFAULT, DEFAULT, '6999', '1928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (196, '州', '注', '州，注也，郡國所注仰也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '7490', '7275', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (197, '靑州', '', '靑州在東，取物生而靑也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '3188/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (198, '徐州', '舒', '徐州，徐，舒也，土氣舒緩也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '1238/7490', '2510', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (199, '楊州', '揚', '楊州，州界多水，水波揚也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '3510/7490', '7956', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (200, '荆州', '警', '荆州，取名於荆山也。必取荆爲名者，荆，警也，南蠻數爲寇逆，其民有道後服，無道先彊，常警備之也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '488/7490', '1553', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (201, '豫州', '', '豫州，地在九州之中，京師東都所在，常安豫也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '6134/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (202, '涼州;雍州', '翳', '涼州，西方所在，寒涼也。古曰雍州，在四山之內，雍，翳也。', '', '釋州國第七', '釋名', '2', DEFAULT, DEFAULT, '7389/7490;2299/7490', '2273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (203, '并州', '', '并州，并，兼并也。其州或并或設，故因以爲名也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '5226/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (204, '幽州', '', '幽州，在北幽昧之地也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '2501/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (205, '冀州', '易', '冀州，亦取地以爲名也。冀，易也。其地有險有易也，又帝王所都，亂則冀治，弱則冀彊，荒則冀豐也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '5230/7490', '6132', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (206, '兖州', '', '兖州，取兖水以爲名也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7027/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (207, '司州', '', '司州，司隸校尉所主也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '5771/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (208, '益州', '阨', '益州，益，阨也，所在之地險阨也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '3154/7490', '9664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (209, '營州', '', '古有營州，齊 衞之地於天文屬營室，取其名也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '4629/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (210, '燕', '宛', '燕，宛也，北方沙漠平廣，此地在涿鹿山南，宛宛然以爲國都也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '7686', '4607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (211, '宋', '送', '宋，送也，地接淮 泗而東南傾，以爲殷後，若云滓穢所在，送使隨流東入海也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4620', '1144', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (212, '鄭', '町/汀', '鄭，町[汀]也，其地多平，町町然也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4028', '9171/0/7352', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (213, '楚', '齭', '楚，辛楚[齭]也，其地蠻多而人性急，數有戰爭，相爭相害，辛楚之禍也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '3849', '1314', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (214, '周', '', '周，周也，地在岐山之南，其山四周也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (215, '秦', '津/𧗁', '秦，津[𧗁]也，其地沃衍，有津潤也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4492', '7279/0/3170', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (216, '晉', '進', '晉，進也，其地在北，有事於中國，則進而南也；又取晉水以爲名，其水迅進也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4203', '1113', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (217, '趙', '朝/𣶃', '趙，朝[𣶃]也，本小邑，朝事於大國也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '1030', '4287/0/7137', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (218, '魯', '', '魯，魯鈍也，國多山水，民性樸魯也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '2227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (219, '衞', '', '衞，衞也，旣滅殷，立武庚爲殷後，三監以守衞之也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '1277', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (220, '齊', '臍', '齊，齊[臍]也，地在勃海之南，如齊之中也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4393', '2629', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (221, '吳;越', '虞', '吳，虞也，太伯讓位而不歸，就封之於此，虞其志也。越，夷蠻之國也，度越禮義，無所拘也。此十二國，上應列宿，各以其地及於事宜，制此名也。至秦改諸侯、置郡縣，随其所在山川土形而立其名，漢就而因之也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '6619;990', '3109', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (222, '河南', '', '河南，在河之南也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '6984/3873', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (223, '河內', '', '河內，河水從岐山而南，從雷首而東，從譚首而北，郡在其內也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '6984/3280', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (224, '河東', '', '河東，在河水東也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '6984/3844', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (225, '河西', '', '河西，在河水西也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '6984/7712', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (226, '上黨', '', '上黨，黨，所也，在山上，其所最高，故曰上黨也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '6/6562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (227, '潁川', '', '潁川，因潁水爲名也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7059/7481', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (228, '汝南', '', '汝南，在汝水南也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7017/3873', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (229, '汝陰', '', '汝陰，在汝水陰也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7017/9621', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (230, '東郡;南郡', '', '東郡、南郡，皆以京師方面言之也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '3844/4004;3873/4004', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (231, '北海', '', '北海，海在其北也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '5229/7129', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (232, '西海', '', '西海，海在其西也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7712/7129', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (233, '南海', '', '南海，在海南也，宜言海南，欲同四海名，故言南海也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '3873/7129', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (234, '東海', '', '東海，海在其東也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '3844/7129', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (235, '濟南', '', '濟南，濟水在其南也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7093/3873', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (236, '濟北', '', '濟北，濟水在其北也，義亦如南海也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7093/5229', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (237, '濟陰', '', '濟陰，在濟水之陰也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '7093/9621', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (238, '南陽', '', '南陽，在中國之南，而居陽地，故以爲名也。凡若此類郡國之名，取號於此，則其餘可知也。縣邑之名亦如之。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '3873/9622', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (239, '邦', '封', '大邑曰邦，邦，封也，封有功於是也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4003', '9074', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (240, '都', '', '國城曰都，都者，國君所居，人所都會也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '4005', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (241, '井;邑;丘;甸', '浥/合;乘', '周制：九夫爲井，其制似井字也；四井爲邑，邑猶俋{浥}[合]也，邑人聚會之稱也；四邑爲丘，丘，聚也；四丘爲甸，甸，乘也，出兵車一乘也。', '', '釋州國第七', '釋名', '2;4', DEFAULT, DEFAULT, '3190;4002;5231;9180', '7235/0/3269;3410', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (242, '鄙', '否', '鄙，否也，小邑不能遠通也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4008', '934', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (243, '縣', '', '縣，縣也，縣係於郡也。', '', '釋州國第七', '釋名', '', DEFAULT, DEFAULT, '5705', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (244, '郡', '羣', '郡，羣也，人所羣聚也。', '', '釋州國第七', '釋名', '1', DEFAULT, DEFAULT, '4004', '2352', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (245, '伍;鄰;比;里;黨;鄉', '五;連;長;向', '周制：五家爲伍，以五爲名也；又謂之鄰，鄰，連也，相接連也；又曰比，比，相親比也；五鄰爲里，居方一里之中也。五百家爲黨，黨，長也，一聚之所尊長也；萬二千五百家爲鄉，鄉，向也，衆所向也。', '', '釋州國第七', '釋名', '1;2;5;6', DEFAULT, DEFAULT, '5066;4006;5227;9167;6562;4185', '9725;1168;6067;4559', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (246, '人', '仁', '人，仁也，仁生物也，故易曰：「立人之道曰仁與義。」', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '4948', '4951', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (247, '體', '第', '體，第也，骨肉、毛血、表裏、大小相次第也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2586', '3011', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (248, '軀', '區', '軀，區也，是衆名之大總，若區域也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5249', '8417', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (249, '形', '', '形，有形象之異也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '5712', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (250, '身', '伸', '身，伸也，可屈伸也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5248', '5110', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (251, '毛', '貌/冒', '毛，貌也、冒也，在表所以別形貌，且以自覆冒也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5385', '5466/0/4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (252, '皮', '被', '皮，被也，被覆體也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1987', '5323', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (253, '膚', '布', '膚，布也，布在表也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2598', '4910', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (254, '肌', '冀/垍', '肌，懻{冀}[垍]也，膚幕堅懻也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2597', '5230/0/9095', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (255, '骨', '滑', '骨，滑也，骨堅而滑也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2568', '7218', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (256, '胑', '枝', '胑，枝也，似木之枝格也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2641', '3569', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (257, '肉', '柔', '肉，柔也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2593', '3607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (258, '筋', '靳', '筋，靳也，肉中之力，氣之元，靳固於身形也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2740', '1823', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (259, '膜', '幕', '膜，幕也，幕絡一體也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2709', '4881', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (260, '血', '濊', '血，濊也，岀於肉，流而濊濊也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '3167', '7155', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (261, '膿', '醲', '膿，醲也，汁醲厚也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '3173', '9821', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (262, '汁', '涕', '汁，涕也，汁涕涕而出也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '7394', '7436', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (263, '津/𧗁', '進', '津[𧗁]，進也，汁進出也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '7279/0/3170', '1113', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (264, '汋', '澤', '汋，澤也，有潤澤也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '7193', '7220', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (265, '汗', '扞', '汗，𣵡{扞}也，岀在於表，𣵡𣵡然也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '7434', '8065', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (266, '髓', '殨', '髓，瀢{殨}也，瀢瀢然也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2584', '2546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (267, '髮', '拔', '髮，拔也，拔擢而出也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5728', '7995', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (268, '𦥓', '峻', '𦥓，峻也，所生高峻也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '6698', '5876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (269, '髦', '冒', '髦，冒也，覆冒頭頸也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5734', '4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (270, '眉', '媚', '眉，媚也，有嫵媚也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2218', '8183', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (271, '頭', '獨', '頭，獨也，於體高而獨也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5599', '6355', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (272, '首', '', '首，始也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '5701', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (273, '面', '漫', '面，漫也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5695', '1910', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (274, '頟', '鄂/㖾', '額{頟}，鄂[㖾]也，有垠鄂也，故幽州人謂之鄂也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5610', '4096/0/974', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (275, '角', '', '角，生於額角也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '2826', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (276, '頞', '鞍', '頞，鞍也，偃折如鞍也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5611', '1832', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (277, '目', '默', '目，默也，默而內識也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2095', '6315', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (278, '眼', '限', '眼，限也，瞳子限限而出也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2096', '9630', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (279, '疌/䀹', '插/接', '睫{疌}[䀹]，插也、接也，插於眼匡而相接也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1074/0/2100', '7895/0/7917', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (280, '童子;眸子', '重;冒', '瞳子{童子}。瞳，重也，膚幕相裹重也。子，小稱也，主謂其精明者也。或曰眸子。眸，冒也，相裹冒也。', '', '釋形體第八', '釋名', '1;2', DEFAULT, DEFAULT, '1746/9764;2212/9764', '5242;4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (281, '鼻/自', '嘒', '鼻[自]，嘒也，岀氣嘒嘒也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2232/0/2223', '860', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (282, '口', '空', '口，空也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '780', '4646', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (283, '頰', '夾/挾', '頰，夾也，面兩旁稱也；亦取挾斂食物也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5613', '6598/0/7869', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (284, '舌', '泄/渫', '舌，泄[渫]也，舒泄所當言也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1444', '7063/0/7417', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (285, '齒', '始', '齒，始也，少長之別始乎此也，以齒食多者長也，食少者幼也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1278', '8182', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (286, '牙', '樝/䶥', '牙，樝[䶥]牙也，隨形言之也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1323', '3415/0/1289', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (287, '頤;輔車;牙車;頷車/顄車;頰車;鼸車', '宧;含', '頤，頤[宧]也。頤，養也，動於下、止於上，上下咀物以養人也。或曰輔車，言其骨強，所以輔持口也。或曰牙車，牙所載也；或曰頷車[顄車]，頷，含也，口含物之車也；或曰頰車，亦所以載物也；或曰鼸車，鼸鼠之食積於頰，人食似之，故取名也。凡繫於車，皆取在下載上物也。', '', '釋形體第八', '釋名', '1;4', DEFAULT, DEFAULT, '7825;9565/9512;1323/9512;5647/9512/0/5616/9512;5613/9512;6403/9512', '4560;815', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (288, '耳', '耏/而', '耳，耏[而]也，耳有一體，屬著兩邊，耏耏然也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '7792', '6075/0/6074', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (289, '脣', '緣', '脣，緣也，口之緣也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2601', '8657', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (290, '吻;口卷', '免/文/捪', '吻，免也，入之則碎，出則免也。又取抆{文}[捪]也，漱唾所出，恆加抆拭，因以爲名也。或曰口卷也，可以卷制食物使不落也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '784;780/5784', '6298/0/5723/0/7921', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (291, '立人', '', '鼻下曰立人，取立於鼻下，狹而長，似人立也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '6677/4948', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (292, '頿', '姿', '口上曰髭，髭{頿}，姿也，爲姿容之美也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5707', '8279', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (293, '承𤖅', '', '口下曰承𤖅，承水𤖅也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '7912/7388', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (294, '須', '秀/䇓', '頤下曰鬚。鬚{須}，秀也，物成乃秀，人成而鬚生也；亦取須[䇓]體幹長而後生也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5706', '4415/0/6689', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (295, '𩓾;鬢', '冉;瀕', '在頰耳旁曰髯{𩓾}，隨口動搖，髯髯{冉冉}然也。其上連髮曰鬢。鬢，濱{瀕}也；濱，崖也，爲面額之崖岸也。', '', '釋形體第八', '釋名', '1;2', DEFAULT, DEFAULT, '5708;5729', '6073;7476', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (296, '距', '矩', '鬢曲頭曰距。距，矩也，言其曲似矩也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1402', '3032', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (297, '項', '确', '項，确也，堅确受枕之處也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5619', '6035', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (298, '頸', '徑', '頸，徑也，徑挺而長也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5617', '1225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (299, '咽;嬰;脰;嗌', '燕;纓;投;阨', '咽，嚥{燕}也，言嚥物也。或謂之䑍{嬰}，在頤下纓理之中也。靑 徐謂之脰，物投其中，受而下之也。又謂之嗌，氣所流通，阨要之處也。', '', '釋形體第八', '釋名', '1;2;3;4', DEFAULT, DEFAULT, '789;8259;2602;790', '7686;8640;7925;9664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (300, '胡', '互', '胡，互也，在咽下垂，能歛互物也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2677', '2958', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (301, '胸', '空', '胸猶啌也。啌{空}，氣所衝也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5808', '4646', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (302, '臆', '抑', '臆猶抑，抑氣所塞也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2615', '5790', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (303, '膺', '邕', '膺，壅{邕}也，氣所壅塞也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2614', '7487', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (304, '腹', '複/富', '腹，複也、富也，腸胃之屬以自裹盛，復於外複之，其中多品，似富者也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2630', '5305/0/4584', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (305, '心', '纖', '心，纖也，所識纖微，無物不貫也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '6703', '8558', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (306, '肝', '榦', '肝，幹{榦}也，於五行屬木，故其體狀有枝幹也。凡物以木爲幹也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2607', '3618', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (307, '肺', '勃/孛', '肺，勃[孛]也，言其氣勃鬱也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2605', '9244/0/3870', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (308, '脾', '裨', '脾，裨也，在胃下，裨助胃氣，主化穀也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2606', '5331', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (309, '腎', '引', '腎，引也。腎屬水，主引水氣，灌注諸脈也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2604', '8491', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (310, '胃', '圍', '胃，圍也，圍受食物也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2609', '3928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (311, '腸', '𧀄', '腸，暢{𧀄}也，通暢胃氣，去滓穢也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2611', '523', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (312, '臍', '劑', '臍，劑也，腸端之所限劑也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2629', '2782', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (313, '水腹;少腹', '小', '自臍以下曰水腹，水汋所聚也。又曰少腹，少，小也，比於臍以上爲小也。', '', '釋形體第八', '釋名', '2', DEFAULT, DEFAULT, '6982/2630;705/2630', '704', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (314, '脬;膀光/旁光', '鞄;橫廣', '脬，鞄也。鞄，空虛之言也，主以虛承水汋也。或曰膀胱{膀光}[旁光]，言其體短而橫廣也。', '', '釋形體第八', '釋名', '1;2', DEFAULT, DEFAULT, '2610;2618/6509/0/8/6509', '1794;3794/5938', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (315, '陰', '蔭', '陰，蔭也，言所在蔭翳也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '9621', '524', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (316, '脇', '挾', '脇，挾也，在兩旁，臂所挾也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2617', '7869', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (317, '肋', '勒', '肋，勒也，所以檢勒五臓也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2620', '1836', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (318, '隔', '', '{隔}膈，隔也，隔塞上下，使氣與穀不相亂也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '9665', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (319, '亦', '繹', '腋{亦}，繹也，言可張翕尋繹也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '6614', '8518', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (320, '肩甲', '堅/闔', '髆曰肩甲。肩，堅也。甲，闔也，與胸脅背相會闔也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2623/9739', '1950/0/7747', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (321, '臂', '裨', '臂，裨也，在旁曰裨也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2626', '5331', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (322, '肘', '注', '肘，注也，可隠注也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2628', '7275', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (323, '𢯲', '宛', '{𢯲}腕，宛也，言可宛屈也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '7832', '4607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (324, '掌', '', '掌，言可以排掌也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '7828', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (325, '手', '須/䇓', '手，須[䇓]也，事業之所須也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '7827', '5706/0/6689', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (326, '節', '', '節，有限節也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '2876', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (327, '爪', '紹', '爪，紹也，筋極爲爪，紹續指端也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1879', '8550', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (328, '背', '倍', '背，倍也，在後稱也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2616', '5114', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (329, '脊', '積', '脊，積也，積續骨節，終上下也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '8108', '4465', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (330, '尾', '微/𢼸', '尾，微[𢼸]也，承脊之末，稍微殺也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5426', '1236/0/5071', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (331, '要', '約', '要，約也，在體之中，約結而小也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1784', '8568', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (332, '髖', '緩', '髖，緩也，其腋皮厚而緩也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2577', '8779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (333, '臀', '殿', '臀，殿也，高厚有殿遻也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5408', '1969', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (334, '尻', '廖/廫', '尻，廖[廫]也，所在廖牢深也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5407', '5975/0/5969', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (335, '樞', '', '樞，機也，要髀股動搖如樞機也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '3649', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (336, '髀', '卑', '髀，卑也，在下稱也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2574', '1931', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (337, '股', '固', '股，固也，爲強固也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2635', '3927', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (338, '厀', '伸', '膝{厀}，伸也，可屈伸也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '5783', '5110', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (339, '腳', '卻', '腳，卻也，以其坐時卻在後也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2636', '5785', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (340, '脛', '莖', '脛，莖也，直而長似物莖也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '2637', '493', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (341, '欂;枅', '肩', '{欂}膝頭曰膞。膞，圍也，因形團圜而名之也。{枅}或曰蹁。蹁，扁{肩}也，亦因形而名之也。', '', '釋形體第八', '釋名', '2', DEFAULT, DEFAULT, '3631;3633', '2623', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (342, '足', '續', '足，續也，言續脛也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1326', '8548', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (343, '止', '', '{止}趾，止也，言行一進一止也。', '', '釋形體第八', '釋名', '', DEFAULT, DEFAULT, '1064', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (344, '蹏', '底', '蹏，底也，在足底也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1327', '5950', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (345, '踝', '确', '踝，确也，居足兩旁，磽确然也，亦因其形踝踝然也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1329', '6035', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (346, '跟', '根', '足後曰跟，在下旁著地，一體任之，象木根也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1328', '3562', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (347, '踵', '鍾', '踵，鍾也，鍾聚也，上體之所鍾聚也。', '', '釋形體第八', '釋名', '1', DEFAULT, DEFAULT, '1358', '9292', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (348, '姿', '資', '姿，資也。資，取也，形貌之稟，取爲資本也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8279', '3940', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (349, '容', '用', '容，用也，合事宜之用也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '4587', '2081', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (350, '妍', '研', '妍，研也，研精於事宜，則無蚩繆也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8297', '6046', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (351, '蚩', '癡', '蚩，癡也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8857', '4795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (352, '行', '抗', '兩腳進曰行。行，抗也，抗足而前也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1266', '8066', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (353, '步', '捕', '徐行曰步。步，捕也，如有所伺捕，務安詳也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1081', '8067', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (354, '趨', '赴', '疾行曰趨。趨，赴也，赴所至也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '980', '981', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (355, '走', '奏', '疾趨曰走。走，奏也，促有所奏至也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '979', '6659', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (356, '奔', '變', '〔疾走曰奔〕。奔，變也，有急變，奔赴之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '6624', '2019', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (357, '仆', '踣', '仆，踣也，頓踣而前也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5155', '1392', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (358, '超', '卓/趠', '超，卓[趠]也，舉腳有所卓越也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '983', '5222/0/1033', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (359, '跳', '條', '跳，條也，如草木枝條，務上行也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1370', '3570', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (360, '立', '林', '立，林也，如林木森然，各駐其所也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '6677', '3846', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (361, '騎', '岐', '騎，歧{岐}也，兩腳歧別也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '6185', '4018', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (362, '乘;登', '升', '乘，陞{升}也。登亦如之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '3410;1079', '9505', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (363, '載', '戴', '載，戴也，戴在其上也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '9569', '1778', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (364, '儋', '任', '擔{儋}，任也，任力所勝也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5024', '5087', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (365, '負', '背', '負，背也，置項背也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '3963', '2616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (366, '駐', '株', '駐，株也，如株木不動也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '6221', '3563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (367, '坐', '挫', '坐，挫也，骨節挫屈也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '9067', '7853', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (368, '伏', '覆', '伏，覆也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5161', '4852', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (369, '偃', '安', '偃，安也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5154', '4576', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (370, '僵', '畺', '僵，正直畺然也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5153', '9200', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (371, '側', '逼', '側，逼也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5053', '1213', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (372, '據', '居', '據，居也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7865', '5402', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (373, '企', '啟', '〔企，啟也。〕啟，開也，言自延竦之時，樞機皆開張也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '4952', '1995', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (374, '竦', '從', '竦，從也，體支皆從引也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '6682', '5225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (375, '視', '是', '視，是也，察其是非也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5475', '1089', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (376, '聽', '靜', '聽，靜也，靜，然後所聞審也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7803', '3189', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (377, '觀', '翰/厂', '觀，翰[厂]也，望之延頸翰翰也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5484', '2243/0/5976', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (378, '望', '朚', '望，茫{朚}也，遠視茫茫也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8413', '4322', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (379, '跪', '危', '跪，危也，兩膝隠地，體危陧也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1332', '6007', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (380, '跽', '忌', '跽，忌也，見所敬忌，不敢自安也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1333', '6871', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (381, '跌;扶', '', '拜於丈夫爲跌，跌然屈折，下就地也。於婦人爲扶，自抽扶而上下也。', '', '釋姿容第九', '釋名', '', DEFAULT, DEFAULT, '1386;7854', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (382, '攀', '翻', '攀，翻也，連翻上及之言也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1772', '2275', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (383, '𤸪', '制', '{𤸪}掣，制也，制頓之使順己也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7946', '2795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (384, '牽', '弦', '牽，弦也，使弦急也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '755', '8507', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (385, '引', '演', '引，演也，使演廣也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8491', '7146', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (386, '匊', '局', '掬{匊}，局也，使相局近也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5803', '958', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (387, '撮', '捽', '撮，捽也，暫捽取之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7906', '7905', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (388, '𠭯', '叉', '摣{𠭯}，叉也，五指俱往叉取也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1914', '1905', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (389, '捉', '促', '捉，促也，使相促及也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7898', '5162', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (390, '執', '攝/懾', '執，攝[懾]也，使畏攝己也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '6646', '7866/0/6939', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (391, '拈', '黏', '拈，黏也，兩指翕之，黏著不放也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7881', '4510', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (392, '抶', '鐵', '抶，鐵也，其處皮薰黑，色如鐵也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8052', '9268', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (393, '蹋', '榻', '蹋，榻也，榻著地也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1351', '3837', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (394, '比/捭', '裨', '批{比}[捭]，裨也，兩指裨助，共擊之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5227/0/8056', '5331', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (395, '挾', '夾', '挾，夾也，夾在旁也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7869', '6598', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (396, '奉', '逢', '捧{奉}，逢也，兩手相逢以執之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1756', '1130', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (397, '懷', '回/歸', '懷，回也，本有去意，回來就己也。亦言歸也，來歸己也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '6762', '3912/0/1073', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (398, '抱/袌', '保', '抱[袌]，保也，相親保也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7909/0/5288', '4950', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (399, '戴', '載', '戴，載也，載之於頭也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1778', '9569', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (400, '提', '地', '提，地也，臂垂所持近地也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7879', '9020', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (401, '挈', '結', '挈，結也，結束持之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7857', '8575', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (402, '持', '偫', '持，跱{偫}也，跱之於手中也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7856', '5026', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (403, '操', '鈔', '操，抄{鈔}也，手出其下之言也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7861', '9436', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (404, '擥', '斂', '攬{擥}，斂也，斂置手中也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7871', '2023', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (405, '擁', '翁', '擁，翁也，翁撫之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7966', '2248', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (406, '撫', '敷/尃', '撫，敷[尃]也，敷手以拍之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7920', '2007/0/1985', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (407, '白/𢫦', '搏', '拍{白}[𢫦]，搏也，以手搏其上也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '4929/0/7889', '7864', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (408, '搏', '博', '搏，博也，四指廣博，亦以擊之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '7864', '1468', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (409, '摩娑', '末殺', '摩娑猶末殺也，手上下之言也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8009/8254', '3564/1978', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (410, '蹙', '遒', '蹙，遒也，遒迫之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1417', '1179', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (411, '踐', '殘', '踐，殘也，使殘壊也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1357', '2550', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (412, '踖', '藉', '踖，藉也，以足藉也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1336', '581', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (413, '履', '', '履，以足履之，因以名之也。', '', '釋姿容第九', '釋名', '', DEFAULT, DEFAULT, '5430', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (414, '蹈', '道', '蹈，道也，以足踐之，如道路也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1355', '1205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (415, '此', '弭', '跐{此}，弭也，足踐之，使弭服也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1083', '8479', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (416, '躡/𤴘', '懾', '躡[𤴘]，懾也，登其上使懾服也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '1352/0/1075', '6939', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (417, '匍匐', '捕/伏', '匍匐，小兒時也。匍猶捕也，藉索可執取之言也；匐，伏也，伏地行也。人雖長大，及其求事，盡力之勤，猶亦稱之。詩曰「凡民有喪，匍匐救之」是也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5801/5802', '8067/0/5161', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (418, '偃蹇/㫃蹇', '', '偃蹇[㫃蹇]。偃，偃息而臥，不執事也。蹇，跛蹇也，病不能作事，今託病似此，而不宜執事役也。', '', '釋姿容第九', '釋名', '', DEFAULT, DEFAULT, '5154/1395/0/4288/1395', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (419, '望羊', '陽', '望羊，羊，陽也，言陽氣在上，舉頭高，似若望之然也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8413/2332', '9622', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (420, '沐秃', '', '沐秃，沐者髮下垂，秃者無髮，皆無上貌之稱也。', '', '釋姿容第九', '釋名', '', DEFAULT, DEFAULT, '7409/5472', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (421, '卦賣', '挂', '卦賣。卦，掛{挂}也，自掛於市而自賣邊，自可無慙色，言此似之也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '2074/3865', '8070', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (422, '倚簁/倚籭', '技', '倚簁[倚籭]。倚，技也；簁，作清簁也。言人多技巧，尙輕細如簁也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5045/2925/0/5045/2914', '8023', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (423, '寠數', '局縮', '寠數猶局縮，皆小意也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '4611/2010', '958/8563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (424, '齧制/齧𤸪', '', '齧掣{齧制}[齧𤸪]。掣，卷掣也；齧，噬齧也。語說卷掣，與人相持，如噬齧也。', '', '釋姿容第九', '釋名', '', DEFAULT, DEFAULT, '1313/2795/0/1313/7946', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (425, '衇摘/衇謫', '', '衇摘[衇謫]猶譎摘，如醫別人衇，知疾之意，見事者之稱也。', '', '釋姿容第九', '釋名', '', DEFAULT, DEFAULT, '7498/7937/0/7498/1690', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (426, '貸騃/貸𥏳', '', '貸騃[貸𥏳]，貸者，言以物貸予。騃者，言必棄之不復得也。不相量事者之稱也。此皆見於形貌者也。', '', '釋姿容第九', '釋名', '', DEFAULT, DEFAULT, '3950/6203/0/3950/3347', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (427, '臥', '化/𠤎', '臥，化[𠤎]也，精氣變化，不與覺時同也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5244', '5214/0/5211', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (428, '寐', '謐', '寐，謐也，靜謐無聲也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '4685', '1554', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (429, '寢', '侵', '寢，權假臥之名也。寢，侵也，侵損事功也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '4684', '5076', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (430, '民/瞑', '泯', '眠{民}[瞑]，泯也，無知泯泯也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '8357/0/2181', '7452', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (431, '覺', '告', '覺，告也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5509', '778', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (432, '寤', '啎', '寤，忤{啎}也，能與物相接忤也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '4686', '9798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (433, '欠', '欽', '欠，欽也，開張其口作聲，脣欽欽然也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '5523', '5524', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (434, '嚏', '疐', '嚏，疐也，聲作疐而出也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '832', '2505', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (435, '𥬇', '鈔', '𥬇，鈔也，頰皮上鈔者也。', '', '釋姿容第九', '釋名', '1', DEFAULT, DEFAULT, '3010', '9436', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (436, '嬰兒;嫛婗', '是', '人始生曰嬰兒，胸前曰嬰，抱之嬰前，乳養之也。或曰嫛婗，嫛，是也，言是人也。婗，其啼聲也，故因以名之也。', '', '釋長幼第十', '釋名', '2', DEFAULT, DEFAULT, '8259/5457;8137/8138', '1089', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (437, '男', '任', '男，任也，典任事也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '9207', '5087', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (438, '女;吾', '如;啎', '女，如也，婦人外成，如人也，故三從之義，少如父教，嫁如夫命，老如子言。靑 徐人曰娪，娪{吾}，忤{啎}也，始生時，人意不喜，忤忤然也。', '', '釋長幼第十', '釋名', '1;2', DEFAULT, DEFAULT, '8109;837', '8243;9798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (439, '孺', '濡', '兒始能行曰孺子。孺，濡也，言濡弱也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '9770', '7095', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (440, '悼', '逃', '七年曰悼。悼，逃也，知有廉耻，隠逃其情也。亦言是時而死，可傷悼也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '6941', '1176', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (441, '齔', '洗', '毀齒曰齔。齔，洗也，毁洗故齒，更生新也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '1280', '7413', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (442, '長', '萇', '長，萇也，言體萇也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '6067', '307', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (443, '幼', '少', '幼，少也，言生日少也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '2498', '705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (444, '童', '', '十五曰童，故禮有「陽童」，牛羊之無角者曰童，山無草木亦曰童，言未巾冠似之也。女子之未笄者亦稱之也。', '', '釋長幼第十', '釋名', '', DEFAULT, DEFAULT, '1746', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (445, '弱', '', '二十曰弱，言柔弱也。', '', '釋長幼第十', '釋名', '', DEFAULT, DEFAULT, '5719', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (446, '壯', '', '三十曰壯，言丁壯也。', '', '釋長幼第十', '釋名', '', DEFAULT, DEFAULT, '228', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (447, '強', '', '四十曰強，言堅強也。', '', '釋長幼第十', '釋名', '', DEFAULT, DEFAULT, '8824', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (448, '艾', '乂/䢃', '五十曰艾。艾，乂[䢃]也。乂，治也，治事能斷割芟刈，無所疑也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '389', '8360/0/5798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (449, '耆', '指', '六十曰耆。耆，指也，不從力役，指事使人也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '5378', '7830', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (450, '毛/𦽡', '', '七十曰耄{毛}[𦽡]。頭髮白，耄耄然也。', '', '釋長幼第十', '釋名', '', DEFAULT, DEFAULT, '5385/0/5377', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (451, '耋', '鐵', '八十曰耋。耋，鐵也，皮膚變黑，色如鐵也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '5376', '9268', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (452, '鮐背;黃耇;胡耇;凍棃;齯齒', '垢;兒', '九十曰鮐背，背有鮐文也。或曰黃耇。黃，鬢髮變黃也；耇，垢也，皮色驪顇，恆如有垢者也。或曰胡耇，咽皮如雞胡也。或曰凍棃，皮有斑點，如凍棃色也。或曰齯齒，大齒落盡，更生細者如小兒齒也。', '', '釋長幼第十', '釋名', '2;5', DEFAULT, DEFAULT, '7650/2616;9201/5379;2677/5379;7510/3416;1297/1278', '9130;5457', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (453, '期頤', '宧', '百年曰期頤。頤[宧]，養也，老昏不復知服味善惡，孝子期於盡養道而巳也。', '', '釋長幼第十', '釋名', '1', DEFAULT, DEFAULT, '4280/7825', '4560', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (454, '老;仙/僊', '朽;遷', '老，朽也。老而不死曰仙。仙[僊]，遷也，遷入山也，故其制字人旁作山也。', '', '釋長幼第十', '釋名', '1;2', DEFAULT, DEFAULT, '5375;5187/0/5186', '2547;1137', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (455, '親', '櫬', '親，襯{櫬}也，言相隱襯也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '5512', '3828', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (456, '屬', '續', '屬，續也，恩相連續也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '5427', '8548', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (457, '父', '甫', '父，甫也，甫，始也，始生己也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '1907', '2082', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (458, '母', '冒', '母，冒也，冒，含也，含生己也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8139', '4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (459, '祖;王父;王母', '祚/胙;暀/往', '祖，祚[胙]也，祚，物先也。又謂之王父，王，暀[往]也，家中所歸暀也。王母亦如之。', '', '釋親屬第十一', '釋名', '1;2', DEFAULT, DEFAULT, '36;78/1907;78/8139', '76/0/2669;4238/0/1229', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (460, '曾祖;曾孫', '增', '曾祖，從下推上，祖位轉增益也。曾孫亦如之。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '710/36;710/8512', '9090', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (461, '高祖', '皋', '高祖，高，皋也，最在上，皋韜諸下也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '3318/36', '6660', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (462, '兄', '荒', '兄，荒也；荒，大也，故靑 徐人謂兄爲荒也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '5462', '546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (463, '弟', '第', '弟，第也，相次第而生也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '3399', '3011', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (464, '子', '孳', '子，孳也，相生蕃孳也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '9764', '9774', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (465, '孫', '遜', '孫，遜也，遜遁在後生也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8512', '1140', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (466, '玄孫', '縣', '玄孫，玄，縣也，上縣於高祖，最在下也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '2506/8512', '5705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (467, '來孫', '', '玄孫之子曰來孫，此在無服之外，其意疏遠，呼之乃來也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '3346/8512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (468, '昆孫/𥊽孫', '貫', '來孫之子曰昆孫[𥊽孫]。昆，貫也，恩情轉遠，以禮貫連之耳。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '4254/8512/0/3400/8512', '4339', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (469, '仍孫', '', '昆孫之子曰仍孫，以禮仍有之耳，恩意實遠也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '5047/8512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (470, '雲孫', '', '仍孫之子曰雲孫，言去己遠，如浮雲也。皆爲早娶晚死壽考者言也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '7576/8512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (471, '世父;伯父', '把', '父之兄曰世父，言爲嫡統繼世也。又曰伯父，伯，把也，把持家政也。', '', '釋親屬第十一', '釋名', '2', DEFAULT, DEFAULT, '1473/1907;4965/1907', '7875', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (472, '仲父', '中', '父之弟曰仲父。仲，中也，位在中也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '4966/1907', '231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (473, '叔父', '少', '仲父之弟曰叔父。叔，少也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '1923/1907', '705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (474, '季父', '癸', '叔父之弟曰季父。季，癸也。甲乙之次，癸最在下，季亦然也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '9771/1907', '9763', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (475, '從祖父母', '', '父之世叔父母曰從祖父母，言從己親祖別而下也。亦言隨從己祖以爲名也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '5225/36/1907/8139', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (476, '姑', '故', '父之姊妹曰姑。姑，故也，言於己爲久故之人也。夫之母曰姑，亦如之也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8144', '2004', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (477, '姊', '積', '姊，積也，猶日始岀，積時多而明也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8147', '4465', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (478, '妹', '昧', '妹，昧也，猶日始出，歷時少，尙昧也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8148', '4192', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (479, '姪', '迭', '姑謂兄弟之女爲姪。姪，迭也，共行事夫，更迭進御也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8152', '1166', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (480, '舅', '久', '夫之父曰舅。舅，久也，久老稱也。母之兄弟曰舅，亦如之也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '9208', '3407', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (481, '外舅;外姑', '', '妻之父曰外舅，母曰外姑，言妻從外來，至己家爲婦，故反以此稱之，夫妻匹敵之義也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '4331/9208;4331/8144', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (482, '外甥', '生', '妻之𥊽弟曰外甥，其姊妹女也，來歸己，內爲妻，故其男爲外姓之甥。甥，生也，他姓子，本生於外，不得如其女來在己內也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '4331/9209', '3874', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (483, '出;離孫', '', '姊妹之子曰出，出嫁於異姓而生之也。岀之子曰離孫，言遠離己也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '3863;2293/8512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (484, '歸孫', '', '姪之子曰歸孫。婦人謂嫁曰歸，姪子列，故其所生爲孫也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '1073/8512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (485, '姨;從母', '弟;娣', '妻之姊妹曰姨。姨，弟也，言與己妻相長弟也。母之姊妹曰姨，亦如之。禮謂之從母，爲娣而來，則從母列也。故雖不來，猶以此名之也。', '', '釋親屬第十一', '釋名', '1;2', DEFAULT, DEFAULT, '8153;5225/8139', '3399;8149', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (486, '私/厶;甥', '生', '姊妹互相謂夫曰私[厶]，言於其夫兄弟之中，此人與己姊妹有恩私也。謂姊妹之子曰甥。甥亦生也，出配他男而生，故其制字—男旁作生也。', '', '釋親屬第十一', '釋名', '2', DEFAULT, DEFAULT, '4429/0/5843;9209', '3874', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (487, '女君;男君', '', '妾謂夫之嫡妻曰女君。夫爲男君，故名其妻曰女君也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '8109/839;9207/839', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (488, '嫂', '叟/縮', '嫂，叟也，老者稱也；叟，縮也，人及物老，皆縮小於舊也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8151', '1908/0/8563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (489, '叔', '少/俶/倏', '叔，少也，幼者稱也。亦言俶[倏]也，見嫂俶然卻退也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '1923', '705/0/5015/0/6348', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (490, '兄公;兄章;兄伀', '君;灼', '夫之兄曰兄公。公，君也；君，尊稱也。俗閒曰兄章。章，灼也，章灼、敬奉之也。又曰兄伀，言是己所敬，見之怔忪，自肅齊也。俗或謂舅曰章、又曰忪，亦如之也。', '', '釋親屬第十一', '釋名', '1;2', DEFAULT, DEFAULT, '5462/716;5462/1742;5462/4971', '839;6473', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (491, '㠯/始', '似', '少婦謂長婦曰姒{㠯}[始]，言其先來，己所當法似也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '9796/0/8182', '5084', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (492, '娣', '弟', '長婦謂少婦曰娣。娣，弟也，次第己後來也。或曰先後，以來先後言之也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8149', '3399', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (493, '稙;孰', '祝/柷', '靑 徐人謂長婦曰稙，禾苗先生者曰稙，取名於此也。荆 豫人謂長婦曰熟。熟{孰}，祝[柷]也；祝，始也。', '', '釋親屬第十一', '釋名', '2', DEFAULT, DEFAULT, '4419;1885', '47/0/3766', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (494, '亞;友壻', '', '兩壻相謂曰亞，言一人取姊，一人取妹，相亞次也。又並來至女氏門，姊夫在前，妹夫在後，亦相亞而相倚共成其禮也。又曰友壻，言相親友也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '9723;1929/227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (495, '婚', '昏', '婦之父曰婚，言壻親迎用昏，女恆以昏夜成禮也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8127', '4221', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (496, '姻', '因', '壻之父曰姻。姻，因也，女往因媒也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8128', '3922', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (497, '后', '後', '天子之妃曰后。后，後也，言在後，不敢以副言也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '5769', '1250', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (498, '夫人', '扶', '諸侯之妃曰夫人。夫，扶也，扶助其君也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '6674/4948', '7854', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (499, '內子', '', '卿之妃曰內子。子，女子也，在閨門之內治家也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '3280/9764', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (500, '命婦', '服', '大夫之妃曰命婦。婦，服也，服家事也。夫受命於朝，妻受命於家也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '840/8130', '5447', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (501, '妻', '齊', '士、庶人曰妻。妻，齊也。夫賤不足以尊稱，故齊等言也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8129', '4393', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (502, '嬪', '賓', '天子妾有嬪。嬪，賓也，諸妾之中見賓敬也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8247', '3966', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (503, '妾', '接', '妾，接也，以賤見接幸也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '1747', '7917', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (504, '𠈪', '承', '姪娣曰𡟒。𡟒{𠈪}，承也，承事嫡也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '5107', '7912', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (505, '妃', '輩', '妃，輩也，一人獨處，一人往輩耦之也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8131', '9578', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (506, '匹', '辟/僻', '匹，辟[僻]也，往相辟耦也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8422', '5796/0/5128', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (507, '耦', '遇', '耦，遇也，二人相對遇也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '2821', '1127', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (508, '嫡', '敵', '嫡，敵也，與匹相敵也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8237', '2028', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (509, '庶', '摭', '庶，摭也，拾摭之也。謂拾摭微陋，待遇之也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '5956', '7984', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (510, '鰥/矝', '昆/煇', '無妻曰鰥。鰥[矝]，昆[煇]也；昆，明也。愁悒不寐，目恆鰥鰥然也，故其字從魚，魚目恆不閉者也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '7593/0/9510', '4254/0/6502', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (511, '寡', '踝/顆', '無夫曰寡。寡，踝[顆]也。踝踝，單獨之言也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '4606', '1329/0/5643', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (512, '孤', '顧', '無父曰孤。孤，顧也，顧望無所瞻見也。', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '9775', '5651', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (513, '獨', '', '老而無子曰獨。獨，隻獨也，言無所依也。', '', '釋親屬第十一', '釋名', '', DEFAULT, DEFAULT, '6355', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (514, '嬖', '卑', '〔嬖，卑賤婢妾，媚以色事人得幸者也。〕', '', '釋親屬第十一', '釋名', '1', DEFAULT, DEFAULT, '8269', '1931', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (515, '道', '導', '道，導也，所以通導萬物也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1205', '1986', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (516, '德/悳', '得', '德[悳]，得也，得事宜也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1224/0/6710', '1254', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (517, '文', '彣', '文，彣也，會集衆綵以成錦繍，會集衆字以成辭義，如彣繍然也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5723', '5721', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (518, '武', '舞', '武，舞也，征伐動行，如物鼓舞也。故樂記曰：「發揚蹈厲，太公之志也。」', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8395', '3378', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (519, '仁', '忍', '仁，忍也，好生惡殺，善含忍也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4951', '6962', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (520, '義/誼', '宜', '義[誼]，宜也，裁制事物，使合宜也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8402/0/1556', '4597', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (521, '禮', '體', '禮，體也，得事體也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '12', '2586', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (522, '智', '知', '智，知也，無所不知也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2230', '3315', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (523, '信', '申', '信，申也，言以相申束，使不相違也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1523', '9800', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (524, '孝', '好', '孝，好也，愛好父母，如所說好也。孝經說曰：「孝，畜也，畜養也。」', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5384', '8189', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (525, '慈', '字', '慈，字也，字，愛物也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6745', '9767', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (526, '友', '有', '友，有也，相保有也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1929', '4318', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (527, '恭', '拱/供', '恭，拱也，自拱持也。亦言供給事人也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6741', '7840/0/5025', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (528, '悌', '弟', '悌，弟也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6977', '3399', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (529, '敬', '警', '敬，警也，恆自肅警也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5818', '1553', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (530, '慢', '曼', '慢，漫{曼}也，漫漫，心無所限忌也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6836', '1910', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (531, '通', '洞/迵', '通，洞[迵]也，無所不貫洞也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1134', '7187/0/1165', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (532, '達', '徹', '達，徹也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1163', '1996', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (533, '敏', '閔/潣', '敏，閔[潣]也，進取無否滯之言也。故汝 潁言敏如閔也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1998', '7785/0/7202', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (534, '篤/䈞', '築', '篤[䈞]，築也，築，堅實稱也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6196/0/3333', '3617', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (535, '厚/㫗', '後', '厚[㫗]，後也，有終後也。故靑 徐人言厚如後也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3337/0/3335', '1250', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (536, '薄/洦', '迫', '薄[洦]，迫也，單薄相偪迫也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '561/0/7125', '1182', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (537, '懿', '僾/𥴨', '懿，僾[𥴨]也，言奥僾也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6643', '5017/0/3005', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (538, '良', '量', '良，量也，量力而動，不敢越限也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3339', '5243', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (539, '言', '宣', '言，宣也，宣彼此之意也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1474', '4558', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (540, '語', '敘', '語，敘也，敘己所欲說也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1477', '2064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (541, '說', '述', '說，述也，宣述人意也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1545', '1107', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (542, '序', '抒', '序，抒也，抴抒其實也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5936', '7980', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (543, '抴', '泄/渫', '抴，泄[渫]也，發泄出之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8073', '7063/0/7417', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (544, '發', '撥/播', '發，撥也，撥使開也。亦言播也，播使移散也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8502', '7978/0/8036', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (545, '導', '陶/匋', '導，陶[匋]也，陶演己意也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1986', '9687/0/3287', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (546, '演', '延', '演，延也，言蔓延而廣也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7146', '1265', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (547, '頌', '容', '頌，容也，敘說其成功之形容也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5602', '4587', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (548, '贊', '録/慮', '讚{贊}，録[慮]也，省録之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3947', '9276/0/6702', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (549, '名', '', '{名}銘，名也，記名其功也。', '', '釋言語第十二', '釋名', '', DEFAULT, DEFAULT, '836', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (550, '勒/革', '刻', '勒[革]，刻也，刻識之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1836/0/1790', '2764', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (551, '紀', '記', '紀，記也，記識之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8540', '1567', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (552, '識', '幟/是', '識，幟也，有章幟可按視也。亦言是也，察其是非也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1517', '4917/0/1089', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (553, '是', '嗜', '是，嗜也，人嗜樂之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1089', '889', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (554, '非', '排', '非，排也，人所惡，排去也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7694', '7848', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (555, '基', '據', '基，據也，在下，物所依據也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9044', '7865', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (556, '業', '捷', '業，捷也，事捷乃有功業也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1749', '8087', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (557, '事', '敕', '事，倳{敕}也；倳，立也，凡所立之功也。故靑 徐人言立曰倳也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1933', '2021', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (558, '功', '攻', '功，攻也，攻治之乃成也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9212', '2051', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (559, '取', '趣', '取，趣也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1925', '982', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (560, '名', '明', '名，明也，名實事使分明也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '836', '4321', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (561, '號', '呼', '號，呼也，以其善惡呼名之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3068', '826', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (562, '善', '演', '善，演也，演盡物理也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1735', '7146', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (563, '惡', '㧖', '惡，㧖也，㧖困物也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6880', '7876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (564, '好', '巧', '好，巧也，如巧者之造物，無不皆善，人好之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8189', '3031', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (565, '醜', '臭/殠', '醜，臭[殠]也，如臭穢也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5834', '6361/0/2545', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (566, '遟', '穨/隤', '遟，頽{穨}[隤]也，不進之言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1148', '5473/0/9645', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (567, '疾', '𢧵', '疾，𢧵也，有所越𢧵也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4694', '8386', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (568, '緩', '浣/刓', '緩，浣[刓]也。浣，斷也，持之不急，則動搖浣斷，自放縱也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8779', '7418/0/2793', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (569, '急', '及', '急，及也，操切之，使相逮及也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6809', '1917', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (570, '巧', '考/攷', '巧，考[攷]也，考合異類，共成一體也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3031', '5383/0/2049', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (571, '拙', '屈/詘', '拙，屈[詘]也，使物否屈不爲用也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8025', '5428/0/1700', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (572, '燥', '焦', '燥，焦也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6517', '6486', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (573, '濕/溼', '浥', '濕[溼]，浥也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7071/0/7345', '7235', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (574, '彊', '畺/僵', '彊，畺[僵]也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8489', '9200/0/5153', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (575, '弱', '衄', '弱，衂{衄}也，言委衂也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5719', '3172', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (576, '能', '該/晐', '能，該[晐]也，無物不兼該也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6411', '1721/0/4255', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (577, '否', '鄙/啚', '否，鄙[啚]也，鄙劣不能有所堪成也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '934', '4008/0/3343', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (578, '喿', '燥', '躁{喿}，燥也，物燥乃動而飛揚也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1426', '6517', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (579, '靜', '整', '靜，整也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3189', '2002', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (580, '逆/屰', '遻', '逆[屰]，遻也。言不從其理，則生殿遻不順也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1124/0/1443', '1131', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (581, '順', '循', '順，循也，循其理也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5652', '1233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (582, '淸', '靑', '淸，靑也，去濁遠穢，色如靑也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7200', '3188', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (583, '濁', '瀆/黷', '濁，瀆[黷]也，汁滓演瀆也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7082', '7262/0/6563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (584, '貴', '歸', '貴，歸也，物所歸仰也。汝 潁言貴，聲如「歸暀」之歸也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3979', '1073', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (585, '賤', '踐', '賤，踐也，卑下見踐履也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3980', '1357', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (586, '榮', '熒', '榮猶熒熒，照明貌也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3536', '6580', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (587, '辱', '衄', '辱，亦衂{衄}也，言折衂也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9794', '3172', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (588, '禍', '毀', '禍，毀也，言毀滅也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '67', '9116', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (589, '福', '富', '福，富也，其中多品，如富者也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '20', '4584', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (590, '進', '引', '進，引也，引而前也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1113', '8491', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (591, '退', '墜', '退，墜也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1249', '9160', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (592, '羸', '絫', '羸，累{絫}也，恆累於人也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2349', '9716', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (593, '健', '建', '健，建也，能有所建爲也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4997', '1263', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (594, '哀', '愛/㤅', '哀，愛[㤅]也，愛乃思念之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '936', '3367/0/6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (595, '樂', '', '樂，樂也，使人好樂之也。', '', '釋言語第十二', '釋名', '', DEFAULT, DEFAULT, '3762', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (596, '委', '萎', '委，萎也，萎蕤就之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8213', '616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (597, '曲', '局', '曲，局也，相局近也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8442', '958', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (598, '𨌰', '從', '蹤{𨌰}，從也，人形從之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9583', '5225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (599, '迹', '積', '跡{迹}，積也，積累而前也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1093', '4465', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (600, '扶', '傅/搏', '扶，傅[搏]也，傅近之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7854', '5042/0/7864', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (601, '將/𢪇', '', '將[𢪇]，將救護之也。', '', '釋言語第十二', '釋名', '', DEFAULT, DEFAULT, '1982/0/7855', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (602, '縛', '薄', '縛，薄也，使相薄著也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8578', '561', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (603, '束', '促', '束，促也，相促近也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3897', '5162', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (604, '覆', '孚', '覆，孚也，如孚甲之在物外也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4852', '1880', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (605, '葢/盇', '加', '葢[盇]，加也，加物上也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '586/0/3180', '9241', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (606, '威', '畏', '威，畏也，可畏懼也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8145', '5841', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (607, '嚴', '儼', '嚴，儼也，儼然人憚之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '973', '5002', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (608, '政', '正', '政，正也，下所取正也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2005', '1087', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (609, '教', '效', '教，效也，下所法效也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2071', '2003', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (610, '侍', '時', '侍，時也，尊者不言，常於時供所當進者也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5051', '4189', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (611, '御', '語', '御，語也，尊者將有所欲，先語之也。亦言職卑，尊者所勒御，如御牛馬然也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1258', '1477', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (612, '雅', '雒', '雅，雒也，爲之難，人將爲之，雒雒然憚之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2279', '2281', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (613, '俗', '欲', '俗，欲也，俗人所欲也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5095', '5540', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (614, '艱', '根', '艱，根也，如物根也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9166', '3562', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (615, '難', '憚', '難，憚也，人所忌憚也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2401', '6940', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (616, '吉', '實', '吉，實也，有善實也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '876', '4585', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (617, '凶', '空', '凶，空也，就空亡也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4527', '4646', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (618, '停', '定', '停，定也，定於所在也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5205', '4574', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (619, '起', '啟/企', '起，啟[企]也，啟舉一體也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1017', '1995/0/4952', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (620, '翱', '敖', '翱，敖也，言敖遊也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2266', '2513', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (621, '翔', '羊', '翔，佯{羊}也，言仿佯也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2267', '2332', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (622, '出', '推', '出，推也，推而前也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3863', '7846', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (623, '入', '納/內', '入，納[內]也，納使還也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3279', '8544/0/3280', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (624, '候', '護', '候，護也，司護諸事也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5078', '1562', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (625, '望', '罔', '望，惘{罔}也，視遠惘惘也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8413', '4813', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (626, '狡/佼', '交', '狡[佼]，交也，與物交錯也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6305/0/4955', '6625', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (627, '夬', '決', '夬，決也，有所破壊決裂也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1912', '7272', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (628, '終', '充', '〔終，充也，充之於始也。〕', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8588', '5460', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (629, '始', '息', '始，息也，言滋息也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8182', '6704', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (630, '消', '削', '消，削也，言減削也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7341', '2746', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (631, '息', '塞', '息，塞也，言物滋息塞滿也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6704', '9093', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (632, '姦', '奸', '姦，奸也，言奸正法也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8346', '8338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (633, '宄', '恑', '宄，佹{恑}也，佹易常正也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4617', '6853', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (634, '誰', '推', '誰，推也，有推擇，言不能一也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1705', '7846', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (635, '往', '暀', '往，暀也，歸暀於彼也，故其言之，卬頭以指遠也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1229', '4238', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (636, '來', '哀/依', '來，哀[依]也，使來入己哀之，故其言之，低頭以招之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3346', '936/0/5046', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (637, '麤', '錯/逪', '麤，錯[逪]也，相遠之言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6287', '9327/0/1119', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (638, '細', '弭/佴', '細，弭[佴]也，弭弭兩致之言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8559', '8479/0/5049', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (639, '疏', '索', '疏，索也，獲索相遠也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9787', '3871', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (640, '密', '蜜', '密，蜜也，如蜜所塗無不滿也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5874', '8960', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (641, '甘', '含', '甘，含也，人所含也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3037', '815', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (642, '苦', '吐', '苦，吐也，人所吐也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '323', '884', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (643, '安', '晏/妟', '安，晏[妟]也，晏晏然和喜，無動懼也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4576', '4209/0/8250', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (644, '危', '阢/𦨉', '危，阢[𦨉]也。阢阢，不固之言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6007', '9662/0/5442', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (645, '成', '盛', '成，盛也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9747', '3140', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (646, '敗', '潰', '敗，潰也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2040', '7224', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (647, '亂', '渾/溷', '亂，渾[溷]也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9742', '7195/0/7205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (648, '治', '值', '治，值也，物皆值其所也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7087', '5177', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (649, '煩', '䋣', '煩，繁{䋣}也，物繁則相雜撓也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5681', '8705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (650, '省', '𤸃', '省，𤸃也，臞𤸃約少之言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2219', '4768', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (651, '閒', '𥳑', '閒，𥳑也，事功𥳑省也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7760', '2892', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (652, '劇', '巨/鉅', '劇，巨[鉅]也，事功巨也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2809', '3032/0/9446', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (653, '貞', '定', '貞，定也，精定不動惑也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2076', '4574', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (654, '淫', '浸', '淫，浸也，浸淫旁人之言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7221', '7088', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (655, '沈/湛', '澹', '沈[湛]，澹也，澹然安著之言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7318/0/7294', '7211', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (656, '浮', '孚', '浮，孚也，孚甲在上稱也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7178', '1880', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (657, '貪', '探', '貪，探也，探取入他分也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3982', '8001', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (658, '廉', '斂', '廉，斂也，自檢斂也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '5947', '2023', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (659, '潔', '隺', '潔，確{隺}也，確然不羣貌也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7467', '3326', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (660, '污', '洿', '污，洿也，如洿泥也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7348', '7347', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (661, '公', '廣', '公，廣也，可廣施也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '716', '5938', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (662, '私/厶', '恤', '私[厶]，恤也，所恤念也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4429/0/5843', '6799', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (663, '勇', '踊', '勇，踴{踊}也，遇敵踴躍，欲擊之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9243', '1346', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (664, '怯', '脅', '怯，脅也，見敵恐脅也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6345', '2617', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (665, '斷', '段/𥠄', '斷，段[𥠄]也，分爲異段也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9485', '1971/0/4450', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (666, '絕', '𢧵', '絕，𢧵也，如割𢧵也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '8546', '8386', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (667, '罵', '迫', '罵，迫也，以惡言被迫人也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4845', '1182', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (668, '詈', '歷/離', '詈，歷也，以惡言相彌歷也。亦言離也，以此挂離之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4844', '1070/0/2293', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (669, '祝', '屬', '祝，屬也，以善惡之詞相屬著也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '47', '5427', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (670, '詛', '阻', '詛，阻也，使人行事阻限於言也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1614', '9631', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (671, '盟', '明', '盟，明也，告其事於神明也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4324', '4321', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (672, '誓', '制', '誓，制也，以拘制之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1530', '2795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (673, '左', '𠂇', '佐{左}，左[𠂇]也，在左右也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '3027', '1930', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (674, '助', '乍', '助，乍也，乍往相助，非長久也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '9213', '8412', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (675, '飾', '式', '飾，拭{式}也，物穢者，拭其上使明。由他物而後明，猶加文於質上也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4894', '3030', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (676, '蕩/踼', '盪', '蕩[踼]，盪也，排盪去穢垢也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '7026/0/1387', '3161', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (677, '咄', '惙', '咄，惙也，心有所念，惙然發此聲也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '854', '6922', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (678, '𧪰', '左', '嗟{𧪰}，佐{左}也，言之不足以盡意，故發此聲以自佐也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '1671', '3027', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (679, '噫', '意', '噫，憶{意}也，憶念之，故發此聲也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '820', '6708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (680, '烏', '舒', '嗚{烏}，舒也，氣憤懣，故發此聲以舒寫之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '2487', '2510', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (681, '念', '黏', '念，黏也，意相親愛，心黏著不能忘也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6719', '4510', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (682, '意', '', '{意}憶，意也，恆在意中也。', '', '釋言語第十二', '釋名', '', DEFAULT, DEFAULT, '6708', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (683, '思;忖', '司', '思，司也，凡有所司捕，必靜思。忖亦然也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6701;6972', '5771', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (684, '克', '刻', '克，刻也。刻物有定處，人所克念，有常心也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '4412', '2764', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (685, '慮', '旅', '慮，旅也；旅，衆也。易曰：「一致百慮」。慮及衆物，以一定之也。', '', '釋言語第十二', '釋名', '1', DEFAULT, DEFAULT, '6702', '4309', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (686, '飲', '奄', '飲，奄也，以口奄而引咽之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '5589', '6599', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (687, '食', '殖/蒔', '食，殖[蒔]也，所以自生殖也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3204', '2558/0/541', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (688, '啜', '絕', '啜，絕也，乍啜而絕於口也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '804', '8546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (689, '湌', '乾', '飡{湌}，乾也，乾入口也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3227', '9741', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (690, '吮', '循', '吮，循也，不絕口，稍引滋汋，循咽而下也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '808', '1233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (691, '欶', '促', '嗽{欶}，促也，用口急促也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '5570', '5162', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (692, '含;銜', '合', '含，合也。合口停之也。銜亦然也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '815;9422', '3269', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (693, '咀', '藉', '咀，藉也，以藉齒牙也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '803', '581', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (694, '嚼', '削', '嚼，削也，稍削之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '807', '2746', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (695, '啄', '琢', '鳥曰啄，如琢物上復下也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '953', '145', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (696, '齧', '齾', '獸曰囓。囓{齧}，齾也，所臨則秃齾也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '1313', '1295', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (697, '餅;胡餅;蒸餅;湯餅;蝎餅;髓餅;金餅;索餅', '并;污/杇', '餅，并也，溲麫使合并也。胡餅，作之大漫污[槾杇]也。亦言以胡麻著上也。蒸餅、湯餅、蝎餅、髓餅、金餅、索餅之屬，皆隨形而名之也。', '', '釋飲食第十三', '釋名', '1;2', DEFAULT, DEFAULT, '3212;2677/3212;623/3212;7360/3212;8823/3212;2584/3212;9259/3212;3871/3212', '5226;7348/0/3657', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (698, '糝', '黏', '糝，黏也，相黏𪏮也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4358', '4510', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (699, '黍', '𡫽', '〔黍，𪏮{𡫽}也，相黏𪏮也。〕', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4507', '4687', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (700, '餌;餹䬾', '而', '餌，而也，相黏而也。兖 豫曰餹䬾，就形名之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '1874;0/0', '6074', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (701, '餈', '漬', '餈，漬也，烝燥屑，使相潤漬餅之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3213', '7325', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (702, '饙', '分', '饙，分也，衆粒各自分也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3205', '708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (703, '飧', '散', '飧，散也，投水於中解散也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3225', '2720', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (704, '羹', '汪郎/暀朗', '羹，汪也，汁汪郎[暀朗]也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '1870', '7157/4137/0/4238/4277', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (705, '𦞦', '蒿', '𦞦，蒿也，香氣蒿蒿也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '2711', '670', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (706, '糜', '爢', '糜，煮米使糜[爢]爛也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4360', '6469', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (707, '鬻', '濯/淖', '粥{鬻}，濯[淖]於糜，粥粥然也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '1868', '7419/0/7229', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (708, '𤖅', '將/𢪇', '𤖅，將[𢪇]也，飲之，寒溫多少與體相將順也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '7388', '1982/0/7855', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (709, '湯', '', '湯，熱湯湯也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '7360', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (710, '酪/露', '澤', '酪[露]，澤也，乳汁所作，使人肥澤也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '9872/0/7561', '7220', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (711, '韲', '濟', '虀{韲}，濟也，與諸味相濟成也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4541', '7093', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (712, '菹', '阻', '菹，阻也，生釀之，遂使阻於寒温之閒，不得爛也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '591', '9631', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (713, '䤅', '投', '䤅，投也，味相投成也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '9865', '7925', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (714, '醢;𧖺/䏙;臡', '晦;瀋;昵', '醢，晦也。晦，冥也，封塗使密冥乃成也。醢多汁者曰𧖺。𧖺[䏙]，瀋也，宋 魯人皆謂汁爲瀋。醢有骨者曰臡。臡，昵也，骨肉相搏昵無汁也。', '', '釋飲食第十三', '釋名', '1;2;3', DEFAULT, DEFAULT, '9863;3174/0/2727;2695', '4225;7395;4251', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (715, '豉', '嗜/旨', '豉，嗜[旨]也，五味調和，須之而成，乃可甘嗜也，故齊人謂豉、聲如嗜也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4506', '889/0/3042', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (716, '𩍔', '朽', '麴{𩍔}，朽也，鬱之使生衣朽敗也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4363', '2547', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (717, '蘖/糱', '缺', '蘖[糱]，缺也，漬麥覆之，使生牙開缺也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '0/0/4355', '3301', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (718, '乍/鮺', '菹', '鮓{乍}[鮺]，菹也，以鹽米釀魚以爲菹，熟而食之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '8412/0/7660', '591', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (719, '腊', '昔', '腊，言乾昔也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4250', '4250', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (720, '脯;脩', '搏;縮', '脯，搏也，乾燥相搏著也。又曰脩，脩，縮也，乾燥而縮也。', '', '釋飲食第十三', '釋名', '1;2', DEFAULT, DEFAULT, '2684;2685', '7864;8563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (721, '膊', '迫', '膊，迫也，薄椓肉，迫著物使燥也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '2688', '1182', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (722, '膾', '會', '膾，會也，細切肉，令散分其赤白異切之，巳乃會合和之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '2716', '3274', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (723, '炙', '', '炙，炙也，炙於火上也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '6582', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (724, '脯炙', '', '脯炙，以餳蜜豉汁淹而炙之，脯脯然也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '2684/6582', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (725, '釜炙', '', '釜炙，於釜汁中和熟之也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '1860/6582', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (726, '䐄炙', '銜', '䐄炙，䐄，銜也。䐄炙，細切肉，和以薑、椒、鹽、豉，巳乃以肉銜，裹其表而炙之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '2724/6582', '9422', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (727, '貊炙', '', '貊炙，全體炙之，各自以刀割，出於胡貊之爲也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '0/6582', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (728, '膾炙', '', '膾炙，細切豬、羊、馬肉，使如膾而炙之也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '2716/6582', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (729, '生脠', '挻', '生脠，以一分膾，二分細切，合和挻攪之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3874/2696', '7900', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (730, '血䐄/血䘓', '甚', '血䐄[血䘓]，以血作之，增其酢豉之味，使甚苦，以消酒也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3167/2724/0/3167/3179', '3041', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (731, '膏饡', '', '膏饡，消膏而加菹其中，亦以消酒也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '2612/3223', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (732, '𩐌;兌/挩', '', '生瀹蔥薤曰兌[𩐌]，言其柔滑兌兌[挩]然也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '4540;5459/0/7977', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (733, '韓羊;韓兔;韓雞', '', '韓羊、韓兔、韓雞，本法出韓國所爲也，猶酒言「宐成醪、蒼梧淸」之屬也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '3397/2332;3397/6293;3397/2290', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (734, '奥', '', '{奥}䐿，奥也，藏肉於奥內，稍岀用之也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '4562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (735, '脬', '赴', '脬，赴也，夏月赴疾作之，久則臭也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '2610', '981', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (736, '分乾', '', '分乾，切豬肉以梧，分乾其中而和之也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '708/9741', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (737, '肺䐣;膏饡', '饡', '肺䐣，䐣，饡也，以米糝之，如膏饡也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '2605/2700;2612/3223', '3223', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (738, '雞纖;兔纖', '瀸', '雞纖，細擗其腊，令纖[瀸]，然後漬以酢也。兔纖亦如之。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '2290/8558;6293/8558', '7222', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (739, '餳;飴', '洋/羕;怡', '餳，洋[羕]也，煮米消爛，洋洋然也。飴，小弱於餳，形怡怡然也。', '', '釋飲食第十三', '釋名', '1;2', DEFAULT, DEFAULT, '3210;3209', '7081/0/7496;6744', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (740, '餔', '哺', '餔，哺也，如餳而濁，可哺也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3226', '816', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (741, '酒', '酉/久/踧', '酒，酉也，釀之米麴酉澤[繹]，久而味美也。亦言踧也，能否皆彊相踧，持飲之也。又入口咽之，皆踧其面也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '9805', '9804/0/3407/0/1334', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (742, '緹齊', '', '緹齊，色赤如緹也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '8621/4393', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (743, '盎齊', '滃', '盎齊，盎，滃也，滃滃然濁色也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3146/4393', '7299', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (744, '汎齊', '', '汎齊，浮蟻在上，汎汎然也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '7162/4393', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (745, '沈齊', '', '沈齊，濁滓沈下，汁淸在上也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '7318/4393', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (746, '醴齊', '體', '醴齊，醴，體也，釀之一宿而成體，有酒味而巳也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '9815/4393', '2586', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (747, '繹酒', '', '{繹酒}醳酒，久釀酋繹也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '8518/9805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (748, '事酒', '', '事酒，有事而釀之酒也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '1933/9805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (749, '苦酒', '', '苦酒，淳毒甚者，酢苦也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '323/9805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (750, '寒鬻;育/𩱱', '', '寒粥{寒鬻}，末稻米投寒水中，育育[𩱱]然也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '4613/1868;9786/0/1872', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (751, '干飯', '乾', '干飯，飯而暴乾之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '1441/3220', '9741', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (752, '糗', '齲', '糗，齲也，飯而磨散之，使齲碎也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '4366', '1325', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (753, '餱', '候', '餱，候也，候人饑者以食之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3215', '5078', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (754, '麮', '齲', '煮麥曰麮，麮亦齲也，煮熟亦齲壞也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3357', '1325', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (755, '柰油;杏油', '', '柰油，搗柰實，和以塗繒上，燥而發之，形似油也。杏油亦如之。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '3422/7038;3421/7038', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (756, '桃濫/桃蘫', '', '桃濫[桃蘫]，水漬而藏之，其味濫濫然酢也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '3424/7179/0/3424/594', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (757, '柰脯', '', '柰脯，切柰暴乾之，如脯也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '3422/2684', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (758, '鮑魚', '腐', '鮑魚，鮑，腐也，埋藏奄之，使腐臭也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '7662/7578', '2732', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (759, '蟹胥', '湑', '蟹胥，取蟹藏之，使骨肉解之，胥胥[湑]然也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '8917/2698', '7386', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (760, '蟹虀', '', '蟹虀，去其匡虀，熟搗之，令如虀也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '8917/0', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (761, '桃諸', '儲', '桃諸，藏桃也。諸，儲也，藏以爲儲，待給冬月用之也。', '', '釋飲食第十三', '釋名', '1', DEFAULT, DEFAULT, '3424/1488', '5027', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (762, '瓠蓄', '', '瓠蓄，皮瓠以爲脯，蓄積以待冬月時用之也。', '', '釋飲食第十三', '釋名', '', DEFAULT, DEFAULT, '4552/682', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (763, '靑', '生', '靑，生也，象物生時色也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '3188', '3874', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (764, '赤', '赫', '赤，赫也，赫赫，太陽之色也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '6585', '6593', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (765, '黃', '晃', '黃猶晃晃，象日光色也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '9201', '4200', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (766, '白', '', '白，啓也，如冰啟時色也。', '', '釋采帛第十四', '釋名', '', DEFAULT, DEFAULT, '4929', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (767, '黑', '晦', '黑，晦也，如晦冥時色也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '6540', '4225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (768, '絳', '工', '絳，工也，染之難得色，以得色爲工也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8617', '3029', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (769, '紫', '疵', '紫，疵也，非正色。五色之疵瑕，以惑人者也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8623', '4708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (770, '紅', '絳', '紅，絳也。白色之似絳者也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8624', '8617', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (771, '緗', '桑', '緗，桑也，如桑葉初生之色也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8766', '3858', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (772, '綠', '瀏', '綠，瀏也，荆泉之水，於上視之，瀏然綠色，此似之也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8611', '7154', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (773, '縹;碧縹;天縹;骨縹', '漂', '縹猶漂也，漂漂淺靑色也。有碧縹，有天縹，有骨縹，各以其色所象言之也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8612;185/8612;3/8612;2568/8612', '7177', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (774, '緇', '滓', '緇，滓也。泥之黑者曰滓，此色然也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8629', '7381', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (775, '皁', '早', '皁，早也，日未出時，早起視物皆黑，此色如之也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '680', '4190', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (776, '布;綀', '疏', '布，布也，布列衆縷爲經，以緯橫成之也。又太古衣皮，女工之始始於是，施布其法，使民盡用之也。亦謂之疏[綀]，言其經緯疏也。', '', '釋采帛第十四', '釋名', '2', DEFAULT, DEFAULT, '4910;8770', '9787', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (777, '絹', '緊', '絹，䋗{緊}也，其絲䋗厚而疏也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8610', '1949', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (778, '縑', '兼', '縑，兼也，其絲細緻，數兼於絹。染兼五色，細緻不漏水也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8596', '4504', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (779, '練', '爤', '練，爛{爤}也，煮使委爛也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8598', '6468', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (780, '素', '', '素，言朴素也。巳織則供用，不復加功飾也。又物不加飾，皆目之爲素，此色然也。', '', '釋采帛第十四', '釋名', '', DEFAULT, DEFAULT, '8774', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (781, '綈', '', '綈，似𧋘蟲之色，綠而澤也。', '', '釋采帛第十四', '釋名', '', DEFAULT, DEFAULT, '8597', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (782, '錦', '金', '錦，金也，作之用功重，其價如金，故其制字從帛與金也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '4928', '9259', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (783, '綺;杯文;長命;棊文', '攲/㩻', '綺，攲[㩻]也，其文攲邪，不順經緯之縱橫也。有杯文，形似杯也；有長命，其綵色相閒，皆橫終幅，此之謂也。言長命者，服之使人命長，本造者之意也。有棊文，方文如棊也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8593;0/5723;6067/840;3754/5723', '1935/0/6008', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (784, '綾', '凌', '綾，凌也，其文望之如冰凌之理也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8603', '7511', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (785, '繡', '修', '繡，修也，文修修然也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8605', '5714', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (786, '羅', '', '羅，文羅羅疏也。', '', '釋采帛第十四', '釋名', '', DEFAULT, DEFAULT, '4831', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (787, '纚/籭', '簁', '纚[籭]，簁也，麤可以簁物也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8637/0/2914', '2925', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (788, '笭辟', '', '笭辟，經絲貫杼中，一閒并、一閒疏，疏者笭笭然，并者歷辟而密也。', '', '釋采帛第十四', '釋名', '', DEFAULT, DEFAULT, '2970/5796', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (789, '綀', '疏', '紡麤絲織之曰疏。疏[綀]，料也，料料然疏也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8770', '9787', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (790, '縠;沙', '粟/踧', '縠，粟也，其形踧踧，視之如粟也。又謂之沙，亦取踧踧如沙也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8594;7236', '4392/0/1334', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (791, '繐', '慧', '繐，慧也，齊人謂涼爲慧，言服之輕細涼慧也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8744', '6733', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (792, '紈', '渙', '紈，渙也，細澤有光，渙渙然也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8765', '7147', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (793, '蒸栗', '', '蒸栗，染紺使黃，色如蒸栗然也。', '', '釋采帛第十四', '釋名', '', DEFAULT, DEFAULT, '623/4391', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (794, '紺', '含', '紺，含也，靑而含赤色也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8626', '815', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (795, '綸', '倫/侖', '綸，倫[侖]也，作之有倫理也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '8652', '5036/0/3271', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (796, '綿絮', '湎/胥', '綿絮。綿猶湎，湎，柔而無文也。絮，胥也，胥久故解落也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '0/8724', '7387/0/2698', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (797, '紬;絓', '抽;挂', '紬，抽也，抽引絲端岀細緒也。又謂之絓，絓，挂也，挂於杖端，振舉之也。', '', '釋采帛第十四', '釋名', '1;2', DEFAULT, DEFAULT, '8601;8527', '7993;8070', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (798, '莫;繫𦃇', '幕', '煮繭曰莫。莫，幕也，貧者著衣可以幕絡絮也。[繫𦃇]或謂之牽離，煮熟爛，牽引使離散如緜然也。', '', '釋采帛第十四', '釋名', '1', DEFAULT, DEFAULT, '701;8729/8730', '4881', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (799, '潢/獲', '', '〔潢[獲]，染書也。〕', '', '釋采帛第十四', '釋名', '', DEFAULT, DEFAULT, '7255/0/6362', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (800, '冠', '貫', '冠，貫也，所以貫韜髮也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '4797', '4339', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (801, '纓', '頸', '纓，頸也，自上而繫於頸也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '8640', '5617', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (802, '笄', '係', '笄，係也，所以係冠，使不墜也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '2900', '5164', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (803, '冕;衮冕;鷩冕;毳冕;黹冕', '俛/潣/文;卷;蔽;芮;紩', '祭服曰冕。冕猶俛[潣]。俛，平直貌也。亦言文也，玄上纁下，前後垂珠，有文飾也。有衮冕。衮，卷也，畫卷龍於衣也。有鷩冕。鷩雉，山雞也。鷩，憋{蔽}也，性急憋，不可生服，必自殺，故畫其形於衣，以象人執耿介之節也。毳冕。毳，芮也，畫藻文於衣，象水草之毳芮，温暖而潔也。黹冕。黹，紩也，畫黻紩文綵於衣也。此皆隨衣而名之也，所垂前後珠轉減耳。', '', '釋首飾第十五', '釋名', '1;2;3;4;5', DEFAULT, DEFAULT, '4805;5254/4805;2465/4805;5398/4805;4942/4805', '5659/0/7202/0/5723;5784;550;533;8679', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (804, '章甫', '彰/夫', '章甫。殷冠名也。甫，丈夫也，服之所以表章[彰]丈夫也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '1742/2082', '5715/0/6674', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (805, '吁', '幠/覆', '〔冔，亦殷冠名也。冔{吁}，幠也，幠之言覆，言以覆首也。〕', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '913', '4898/0/4852', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (806, '牟追', '冒/追/𠂤', '牟追。牟，冒也，言其形冒髮，追[𠂤]追然也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '751/1177', '4807/0/1177/0/9614', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (807, '收', '', '收，夏后氏冠名也，言收斂髮也。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '2047', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (808, '委貌', '', '委貌，冠形有委曲之貌，上小下大也。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '8213/5466', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (809, '弁;爵弁;皮弁;韋弁', '拚', '弁，如兩手相合抃{拚}時也。以爵韋爲之，謂之爵弁；以鹿皮爲之，謂之皮弁；以韎韋爲之，謂之韋弁也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '5467;3201/5467;1987/5467;3382/5467', '7971', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (810, '纚', '', '纚，以韜髮者也。以纚爲之，因以爲名也。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '8637', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (811, '總', '', '總，以束髮者也，總而束之也。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '8566', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (812, '幘;兌;夾;兌幘;兌髮;牛心', '蹟;銳', '幘，蹟也，下齊眉蹟然也。兌，上小下大，兌兌[銳]然也。或曰𢂿{夾}，𢂿折其後也。或曰兌幘，形似幘也。賤者所著曰兌髮，作之裁裹髮也。或曰牛心，形似之也。', '', '釋首飾第十五', '釋名', '1;2', DEFAULT, DEFAULT, '4866;5459;6598;5459/4866;5459/5728;728/6703', '1093;9367', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (813, '冃', '冒', '帽{冃}，冒也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '4804', '4807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (814, '巾', '謹', '巾，謹也。二十成人，士冠，庶人巾，當自謹修於四敎也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '4854', '1520', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (815, '簪', '兓', '簪，兓也，以兓連冠於髮也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '5464', '5465', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (816, '帝', '摘/擿', '揥{帝}，摘[擿]也，所以摘髮也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '7', '7937/0/7926', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (817, '導;櫟鬢;篦', '', '導，所以導櫟鬢髮，使入巾幘之裏也。或曰櫟鬢，以事名之也。{篦}', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '1986;3528/5729;3015', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (818, '鏡', '景', '鏡，景也，言有光景也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '9289', '4211', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (819, '梳;比', '疏', '梳，言其齒疏也。數者曰比，比於梳，其齒差數也。亦言細相比也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '3682;5227', '9787', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (820, '㕞', '帥/瑟', '㕞，帥也。帥，髮長短皆令上從也。亦言瑟也，㕞髮令上瑟然也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '1916', '4856/0/8406', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (821, '籋', '攝', '{籋}鑷，攝也，攝取髮也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '2965', '7866', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (822, '綃頭;佰頭;裛', '鈔;掩', '綃頭，綃，鈔也，鈔髮使上從也。或曰陌頭{佰頭}，言其從後橫陌而前也。齊人謂之㡋{裛}，言㡋{掩}斂髮使上從也。', '', '釋首飾第十五', '釋名', '1;3', DEFAULT, DEFAULT, '8522/5599;5068/5599;5353', '9436;8033', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (823, '副;編;次;髲;鬄;步搖', '覆;被;剔/𩮜', '王后首飾曰副。副，覆也，以覆首也；亦言副貳也，兼用衆物，成其飾也。編，編髮爲之也。次，次第髮也。髲，被也，髮少者得以被助其髮也。鬄，剔[𩮜]也，剔刑人之髮爲之也。步搖，上有垂珠，步則搖也。', '', '釋首飾第十五', '釋名', '1;4;5', DEFAULT, DEFAULT, '2765;8700;5584;5744;5743;1081/7949', '4852;5323;2806/0/5759', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (824, '槶;頍;皃', '恢;傾', '簂{槶}，恢也，恢廓覆髮上也。魯人曰頍，頍，傾也，著之傾近前也。齊人曰𢂹{皃}，飾形貌也。', '', '釋首飾第十五', '釋名', '1;2', DEFAULT, DEFAULT, '3732;5649;5466', '6740;5052', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (825, '華勝', '', '華勝。華，象草木華也。勝，言人形容正等，一人著之則勝也。蔽髮前爲飾也。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '3884/9227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (826, '釵;爵釵', '叉', '釵，叉也，象枝之形，因形名之也。爵釵，釵頭施爵也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '9461;3201/9461', '1905', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (827, '瑱;充耳', '鎭', '瑱，鎭也，縣當耳旁，不欲使人妄聽，自鎭重也。或曰充耳，充塞其耳，亦所以止聽也。故里語曰：「不瘖不聾，不成姑公。」', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '126;5460/7792', '9358', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (828, '璫', '', '穿耳施珠曰璫，此本出於蠻夷所爲也，蠻夷婦女輕淫好走，故以此璫錘之也。今中國人效之耳。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '211', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (829, '脂', '砥', '脂，砥也，著面柔滑，如砥石也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '2705', '5982', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (830, '粉', '分', '粉，分也，研米使分散也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '4376', '708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (831, '䞓粉', '赤', '䞓粉。䞓，赤也，染粉使赤，以著頰上也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '6589/4376', '6585', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (832, '胡粉', '餬/𩱍', '胡粉。胡，餬[𩱍]也，脂和之如餬，以塗面也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '2677/4376', '3239/0/1869', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (833, '黱', '代', '黛{黱}，代也，滅眉毛去之，以此畫代其處也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '6568', '5081', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (834, '脣脂', '', '脣脂，以丹作之，象脣赤也。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '2601/2705', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (835, '香澤', '', '香澤，人髮恆枯顇，以此濡澤之也。', '', '釋首飾第十五', '釋名', '', DEFAULT, DEFAULT, '4516/7220', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (836, '彊', '強', '彊，其性凝強，以制服亂髮也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '8489', '8824', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (837, '旳', '灼', '以丹注面曰旳。旳，灼也，此本天子諸矦羣妾當以次進御。其有月事者，止而不御，重以口說，故注此丹於面，灼然爲識。女史見之，則不書其名於第録也。', '', '釋首飾第十五', '釋名', '1', DEFAULT, DEFAULT, '4199', '6473', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (838, '衣;裳', '依;障', '凡服，上曰衣，衣，依也，人所依以芘寒暑也。下曰裳，裳，障也，所以自障蔽也。', '', '釋衣服第十六', '釋名', '1;2', DEFAULT, DEFAULT, '5252;4870', '5046;9666', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (839, '領', '頸', '領，頸也，以壅頸也。亦言緫領，衣體爲端首也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5618', '5617', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (840, '䘳', '禁', '襟{䘳}，禁也，交於前，所以禁禦風寒也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5269', '71', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (841, '袂', '制/𤸪', '袂，掣{制}[𤸪]也；掣，開也，開張之，以受臂屈伸也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5286', '2795/0/7946', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (842, '袪', '虛', '袪，虛也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5284', '5232', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (843, '袖', '由/受', '袖，由也，手所由出入也。亦言受也，以受手也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5285', '8514/0/2518', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (844, '紟', '禁', '衿{紟}亦禁也，禁使不得解散也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '8656', '71', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (845, '帶', '蔕', '帶，蔕也，著於衣，如物之繫蔕也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '4865', '514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (846, '系', '繫', '系，繫也，相聯繫也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '8511', '8729', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (847, '衽', '襜/澹', '衽，襜[澹]也，在旁襜襜然也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5266', '5290/0/7211', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (848, '裾', '倨/踞', '裾，倨也，倨倨然直也。亦言在後常見踞也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5294', '5001/0/1389', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (849, '玄端', '', '玄端，玄衣也。其幅下正直端方，與要接也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '2506/6680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (850, '素積', '', '素積，素裳也，辟積其要中使踧，因以名之也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '8774/4465', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (851, '褘衣;翬;鷂翟;闕翟;鞠衣;亶衣/𧝑衣;彖衣', '菊/䕮;坦', '王后之六服有褘衣，畫翬雉之文於衣也。伊 洛而南，雉素質五色皆備成章曰翬。鷂翟，畫鷂雉之文於衣也。江 淮而南，雉靑質五采皆備成章曰鷂。闕翟，翦闕繒爲翟雉形以綴衣也。鞠衣，黃如菊[䕮]華色也。襢衣{亶衣}[𧝑衣]，襢，坦也，坦然正白無文采也。褖衣{彖衣}，褖然黑色也。', '', '釋衣服第十六', '釋名', '5;6', DEFAULT, DEFAULT, '5270/5252;2259;2454/2244;7744/2244;1809/5252;3342/5252/0/5255/5252;6107/5252', '273/0/416;9070', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (852, '韍;巨巾;跪襜', '韠/蔽;澹', '韍，韠也。韠，蔽膝也，所以蔽膝前也。婦人蔽膝亦如之。齊人謂之巨巾。田家婦女出至田野，以覆其頭，故因以爲名也。又曰跪襜，跪時襜襜[澹]然張也。', '', '釋衣服第十六', '釋名', '1;3', DEFAULT, DEFAULT, '4925;3032/4854;1332/5290', '3383/0/550;7211', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (853, '佩', '倍', '佩，倍也，言其非一物，有倍貳也：有珠、有玉、有容刀、有帨巾、有觽之屬也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '4958', '5114', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (854, '襦', '煗', '襦，煗也，言温煗也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5318', '6514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (855, '絝', '跨', '袴{絝}，跨也，兩股各跨別也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '8659', '1353', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (856, '習', '褺/襲', '褶{習}[褺]，襲也，覆上之言也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '2239', '5315/0/5272', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (857, '複;襌', '', '襌衣，言無裏也。有裏曰複，無裏曰襌。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '5305;5321', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (858, '襡;布䙱', '屬', '䙱{襡}，屬也，衣裳上下相聯屬也。荆州謂襌衣曰布䙱，亦是也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5316;4910/0', '5427', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (859, '襜褕', '澹/裕', '襜褕，言其襜襜[澹]宏裕也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5290/5256', '7211/0/5335', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (860, '韝', '溝', '褠{韝}，襌衣之無胡者也，言袖夾直，形如溝也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '3387', '7261', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (861, '中衣', '', '中衣，言在小衣之外，大衣之中也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '231/5252', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (862, '兩當', '', '裲襠{兩當}，其一當胸，其一當背，因以名之也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '4811/9191', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (863, '白腹/鬕腹', '佰', '帕腹{白腹}[鬕腹]，橫帕[佰]其腹也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '4929/2630/0/5749/2630', '5068', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (864, '抱腹/勹腹', '', '抱腹[勹腹]，上下有帶，抱裹其腹上，無襠者也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '7909/2630/0/5799/2630', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (865, '心衣', '', '心衣，抱腹而施鉤肩，鉤肩之閒施一襠，以奄心也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '6703/5252', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (866, '衫', '芟', '衫，芟也，芟末無袖端也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5370', '579', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (867, '反閉', '', '反閉，襦之小者也。卻向著之，領含於項，反於背後，閉其襟也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '1919/7768', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (868, '圭', '', '{圭}婦人上服曰袿，其下垂者上廣下狹，如刀圭也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '9149', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (869, '巽', '緣', '襈{巽}，緣也，靑絳爲之緣也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '3024', '8657', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (870, '裙', '羣', '裙，下裳也。裙，羣也，聯接羣幅也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '4869', '2352', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (871, '緝下', '', '緝下，橫縫緝其下也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '8731/9', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (872, '緣裙', '', '緣裙，裙施緣也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '8657/4869', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (873, '緣襡', '', '緣䙱{緣襡}，䙱施緣也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '8657/5316', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (874, '帔', '披', '帔，披也，披之肩背，不及下也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '4868', '7945', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (875, '直領', '', '直領，領邪直而交下，亦如丈夫服袍方也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '8410/5618', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (876, '交領', '', '交領，就形名之也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '6625/5618', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (877, '曲領', '', '曲領，在內，所以禁中衣領，上橫壅頸，其狀曲也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '8442/5618', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (878, '袷襦', '', '袷襦，如襦而無絮也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '5320/5318', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (879, '要襦', '', '要襦，形如襦，其要上翹，下齊要也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '1784/5318', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (880, '半袖', '', '半袖，其袂半襦而施袖也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '725/5285', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (881, '留幕', '牢/絡', '留幕，冀州所名大褶[褻]，下至膝者也。留，牢也；幕，絡也，言牢絡在衣表也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '9195/4881', '757/0/8725', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (882, '袍', '苞/勹', '袍，丈夫著，下至跗者也。袍，苞[勹]也，苞內衣也。婦人以絳作衣裳，上下連，四起施緣，亦曰袍，義亦然也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5273', '388/0/5799', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (883, '侯頭/侯褕', '解瀆', '齊人謂如衫而小袖曰侯頭[侯褕]，侯頭猶解瀆，臂直通之言也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '3311/5599/0/3311/5256', '2850/7262', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (884, '被', '', '被，被也，被覆人也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '5323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (885, '衾', '广', '衾，广也，其下廣大，如广受人也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5324', '5921', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (886, '汗衣;澤;鄙袒/啚但;羞袒/羞但', '', '汗衣，近身受汗垢之衣也。詩謂之澤，受汗澤也。或曰鄙袒[啚但]，或曰羞袒[羞但]，作之，用六尺裁，足覆胸背，言羞鄙於袒而衣此耳。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '7434/5252;7220;4008/5340/0/3343/5167;9790/5340/0/9790/5167', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (887, '褌', '貫', '褌，貫也，貫兩腳，上繫要中也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '4872', '4339', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (888, '幅;行縢', '畐;騰/滕', '幅，所以自偪{畐}束也。今謂之行縢，言以裹腳，可以跳騰[滕]輕便也。', '', '釋衣服第十六', '釋名', '1;2', DEFAULT, DEFAULT, '4863;1266/8699', '3338;6237/0/7170', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (889, '韤', '末', '襪{韤}，末也，在腳末也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '3393', '3564', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (890, '履', '禮', '履，禮也，飾足所以爲禮也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5430', '12', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (891, '舄', '腊', '複其下曰舄。舄，腊也，行禮久立，地或泥濕，故複其下，使乾腊也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '2488', '4250', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (892, '屨;屝;不借;搏腊;麤/𧆓', '拘;皮;把乍/巴鮺;措', '屨，拘也，所以拘足也。齊人謂草屨曰屝，屝，皮也，以皮作之；或曰不借，言賤易有，宐各自蓄之，不假借人也；齊人云搏腊，搏腊猶把鮓{把乍}[巴鮺]，麤貌也。荆州人曰麤，絲麻韋草，皆同名也。麤[𧆓]，措也，言所以安措足也。', '', '釋衣服第十六', '釋名', '1;2;4;5', DEFAULT, DEFAULT, '5431;5416;7704/5075;7864/4250;6287/0/607', '1458;1987;7875/8412/0/9751/7660;7894', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (893, '屩', '蹻', '屩，草屨也。屩，蹻也，出行著之，蹻蹻輕便，因以爲名也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5434', '1343', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (894, '屐', '榰', '屐，榰也，爲雨，足榰以踐泥也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '5435', '3629', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (895, '鞾', '跨', '鞾，跨也，兩足各以一跨騎也。本胡服，趙武靈王服之。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '1851', '1353', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (896, '索睪', '速獨/束躅', '𩌈𩍜{索睪}，鞾之缺前壅者，胡中所名也。𩌈𩍜猶速獨[束躅]，足直前之言也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '3871/6645', '1121/6355/0/3897/1367', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (897, '鞵', '解', '鞵，解也，著時縮其上，如履然，解其上則舒解也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '1807', '2850', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (898, '帛屐', '', '帛屐，以帛作之，如屩者，不曰帛屩者，屩不可踐泥者也。屐可以踐泥者也。此亦可以步泥而浣之，故謂之屐也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '4927/5435', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (899, '夗下', '宛', '𩊁下{夗下}，如舄，其下宛宛而危，婦人短者著之，可以拜也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '4328/9', '4607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (900, '靸', '襲', '靸，韋履深頭者之名也。靸，襲也，以其深襲覆足也。', '', '釋衣服第十六', '釋名', '1', DEFAULT, DEFAULT, '1802', '5272', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (901, '仰角', '', '仰角，屐上施履之名也。行不得蹶，當仰履角，舉足乃行也。', '', '釋衣服第十六', '釋名', '', DEFAULT, DEFAULT, '5061/2826', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (902, '宮', '穹', '宮，穹也，屋見於垣上，穹隆然也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4628', '4670', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (903, '室', '實', '室，實也，人物實滿其中也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4557', '4585', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (904, '奥', '', '室中西南隅曰奥，不見戸明，所在秘奥也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '4562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (905, '屋漏', '', '西北隅曰屋漏。禮，每有親死者，輒撤屋之西北隅，薪以爨竈煮沐，供諸喪用。時若值雨，則漏，遂以名之也。必取是隅者，禮，旣祭，改設饌於西北隅，今徹毀之，示不復用也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '5420/7444', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (906, '窔/𡧮', '幽', '東南隅曰窔。窔[𡧮]，幽也，亦取幽冥也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4674/0/4561', '2501', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (907, '宧', '頤', '東北隅曰宧。宧，頤也。頤，養也，東北陽氣始出，布養物也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4560', '7825', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (908, '中霤', '', '中央曰中霤。古者𥨍穴，後室之霤，當今之棟下直室之中，古者霤下之處也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '231/7555', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (909, '宅', '擇', '宅，擇也，擇吉處而營之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4556', '7897', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (910, '舍', '', '舍，於中舍息也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '3273', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (911, '宇', '羽', '宇，羽也，如鳥羽翼，自覆蔽也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4564', '2241', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (912, '屋', '奥/燠', '屋亦奥也，其中温奥[燠]也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5420', '4562/0/6512', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (913, '宗廟', '尊/貌', '宗廟。宗，尊也。廟，貌也，先祖形貌所在也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4622/5964', '9879/0/5466', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (914, '寢/寑', '', '寢[寑]，寢也，所以寢息也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '4684/0/4601', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (915, '城', '盛', '城，盛也，盛受國都也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9081', '3140', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (916, '郭', '', '郭，廓也，廓落在城外也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '4150', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (917, '俾睨/俾倪;陴;女牆;𡑢', '裨;曡', '城上垣曰睥睨{俾睨}[俾倪]，言於其孔中睥睨非常也。亦曰陴。陴，裨也，言裨助城之高也。亦曰女牆，言其卑小，比之於城，若女子之於丈夫也。〔亦曰堞{𡑢}，取重曡之義也。〕', '', '釋宮室第十七', '釋名', '2;4', DEFAULT, DEFAULT, '5096/2128/0/5096/5097;9700;8109/3345;9083', '5331;4317', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (918, '寺', '嗣', '寺，嗣也，治事者相嗣續於其內也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '1981', '1433', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (919, '廷', '停', '廷，停也，人所停集之處也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '1261', '5205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (920, '獄;牢;圜土;囹圄', '确;領/御', '獄，确也，言實确，人之情僞也。又謂之牢，言所在堅牢也。又謂之圜土，言築土表牆，其形圜也。又謂之囹圄。囹，領也；圄，御也。領録囚徒，禁御之也。', '', '釋宮室第十七', '釋名', '1;4', DEFAULT, DEFAULT, '6390;757;3907/9019;3924/3925', '6035;5618/0/1258', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (921, '亭', '停', '亭，停也，亦人所停集也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3320', '5205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (922, '傳', '轉', '傳，轉也，人所止息而去，後人復來，轉轉相傳，無常主也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5103', '9575', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (923, '瓦', '踝/裸', '瓦，踝也。踝，确堅貌也；亦言腂{裸}也，在外腂見也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '8450', '1329/0/5344', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (924, '梁', '', '梁，言彊梁也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '3785', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (925, '柱', '主/侸', '柱，住{主}[侸]也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3625', '3183/0/5062', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (926, '檼;望;棟', '隱;中', '檼，隱也，所以隱桷也。或謂之望，言高可望也。或謂之棟，棟，中也，居屋之中也。', '', '釋宮室第十七', '釋名', '1;3', DEFAULT, DEFAULT, '3636;8413;3623', '9667;231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (927, '桷;椽;榱', '确;傳;衰/𤸬', '桷，确也，确堅而直也。或謂之椽，椽，傳也，相傳次而布列也。或謂之榱，在檼旁下列，衰[𤸬]衰然垂也。', '', '釋宮室第十七', '釋名', '1;2;3', DEFAULT, DEFAULT, '3638;3639;3640', '6035;5103;5358/0/4792', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (928, '梠;櫋;爵頭', '旅;緜', '梠，旅也，連旅旅也。或謂之櫋，櫋，緜也，緜連榱頭，使齊平也。上入曰爵頭，形似爵頭也。', '', '釋宮室第十七', '釋名', '1;2', DEFAULT, DEFAULT, '3642;3644;3201/5599', '4309;8513', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (929, '楣', '眉', '楣，眉也，近前各兩，若面之有眉也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3641', '2218', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (930, '棳需', '侏儒', '棳檽{棳需}，梁上短柱也。棳檽猶侏儒，侏儒短，故以名之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3446/7569', '0/4959', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (931, '梧', '牾', '梧，在梁上兩頭，相觸牾也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3535', '9798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (932, '欒', '攣', '欒，攣也，其體上曲，攣拳然也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3514', '7998', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (933, '櫨', '盧', '櫨，在柱端，如都盧，負屋之重也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3632', '3143', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (934, '斗', '', '斗，在欒兩頭如斗也。亦言斗上負檼也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '9489', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (935, '笮', '迮', '笮，迮也，編竹相連迫迮也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '2907', '1118', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (936, '甍', '蒙/冡', '屋脊曰甍。甍，蒙[冡]也，在上覆蒙屋也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '8453', '649/0/4803', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (937, '壁', '辟/僻', '壁，辟[僻]也，所以辟禦風寒也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9048', '5796/0/5128', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (938, '牆', '障', '牆，障也，所以自障蔽也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3345', '9666', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (939, '垣', '援', '垣，援也，人所依阻，以爲援衞也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9045', '7992', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (940, '墉', '容/頌', '墉，容[頌]也，所以蔽隱形容也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9082', '4587/0/5602', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (941, '離/杝;椐', '居', '籬{離}[杝]，離也，以柴竹作之，疏離離也。靑 徐曰椐，椐，居也，居於中也。', '', '釋宮室第十七', '釋名', '2', DEFAULT, DEFAULT, '2293/0/3669;3473', '5402', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (942, '柵;徹/屮', '蹟;詵', '柵，蹟也，以木作之，上平蹟然也。又謂之徹，徹[屮]，緊也，詵詵然緊也。', '', '釋宮室第十七', '釋名', '1;2', DEFAULT, DEFAULT, '3668;1996/0/233', '1093;1481', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (943, '殿', '', '殿，有殿鄂也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '1969', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (944, '陛', '卑', '陛，卑也，有高卑也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9693', '1931', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (945, '納陛/內陛', '', '天子殿謂之納陛[內陛]，言所以納人言之階陛也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '8544/9693/0/3280/9693', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (946, '階', '梯', '階，梯也。如梯之有等差也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9691', '3733', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (947, '陳', '敶', '陳，堂塗也，言賓主相迎陳[敶]列之處也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9686', '2027', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (948, '屏', '', '屏，自障屏也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '5421', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (949, '蕭牆', '肅', '蕭牆，在門內。蕭，肅也。臣將入於此，自肅敬之處也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '443/3345', '1938', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (950, '宁', '佇', '宁，佇也，將見君，所佇立定氣之處也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9719', '5209', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (951, '序', '敘', '序，有次序[敘]也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5936', '2064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (952, '夾室', '', '夾室，在堂兩頭，故曰夾也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '6598/4557', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (953, '堂', '', '堂犹堂堂，高顯貌也。名堂亦如之。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '9054', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (954, '房', '旁', '房，旁也，室之兩旁也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7723', '8', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (955, '楹;輕', '亭;勝', '楹，亭也，亭亭然孤立，旁無所依也。齊 魯楹曰輕，輕，勝也，孤立獨處，能勝任上重也。', '', '釋宮室第十七', '釋名', '1;2', DEFAULT, DEFAULT, '3626;9519', '3320;9227', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (956, '檐', '儋', '檐，儋也，接儋屋前後也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3645', '5024', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (957, '霤', '流', '霤，流也，水從屋上流下也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7555', '7474', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (958, '闕', '缺', '闕，在門兩旁，中央闕[缺]然爲道也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7744', '3301', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (959, '罘罳', '復/思', '罘罳，在門外。罘，復也；罳，思也。臣將入請事，於此復重思之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4836/4848', '1226/0/6701', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (960, '觀', '', '觀，觀也，於上觀望也。', '', '釋宮室第十七', '釋名', '', DEFAULT, DEFAULT, '5484', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (961, '樓', '婁', '樓，謂牗戸之閒有射孔，婁婁然也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3651', '8321', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (962, '臺', '持', '臺，持也，築土堅高，能自勝持也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7710', '7856', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (963, '櫓', '露', '櫓，露也，露上無覆屋也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3761', '7561', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (964, '門', '捫', '門，捫也，在外爲人所捫摸也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7730', '7870', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (965, '闈', '衞', '〔闈，衞也，在內以自〕障衞也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7732', '1277', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (966, '戸', '護', '戸，護也，所以謹護閉塞也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7720', '1562', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (967, '窻/囪', '聰', '窻[囪]，聦{聰}也，於內窺外，爲聦明也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4651/0/6577', '7802', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (968, '茨', '次', '屋以草葢曰茨。茨，次也，次比草爲之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '584', '5584', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (969, '廬', '慮', '寄止曰廬。廬，慮也，取自覆慮也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5925', '6702', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (970, '蒲;广', '敷;奄', '草圓屋曰蒲。蒲，敷也，總其上而敷下也。又謂之庵，庵{广}，奄也，所以自覆奄也。', '', '釋宮室第十七', '釋名', '1;2', DEFAULT, DEFAULT, '332;5921', '2007;6599', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (971, '廡;庌', '幠;雅', '大屋曰廡。廡，幠也；幠，覆也。幽 冀人謂之庌，庌，雅也；雅，正也，屋之正大者也。', '', '釋宮室第十七', '釋名', '1;2', DEFAULT, DEFAULT, '5930;5929', '4898;2279', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (972, '井', '淸', '井，淸也，泉之淸潔者也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3190', '7200', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (973, '瀱汋', '竭', '井一有水、一無水曰瀱汋。瀱，竭也；汋，有水聲汋汋也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7194/7193', '6688', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (974, '竈', '造', '竈，造也，造創食物也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '4636', '1114', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (975, '爨', '銓', '爨，銓也，銓度甘辛調和之處也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '1787', '9371', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (976, '倉', '藏/臧', '倉，藏[臧]也，藏穀物也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3277', '695/0/1954', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (977, '庫', '舍', '庫，舍也，物所在之舍也，故齊 魯謂庫曰舍也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5934', '3273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (978, '廏', '勼', '廏，勼也；勼，聚也，牛馬之所聚也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5935', '5805', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (979, '廩', '矜/憐', '廩，矜[憐]也，寶物可矜惜者，投之其中也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3340', '9510/0/6960', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (980, '囷', '綣', '囷，綣也，藏物繾綣束縛之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '3917', '8773', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (981, '庾', '裕', '庾，裕也，言盈裕也，露積之言也。盈裕不可稱受，所以露積之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5940', '5335', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (982, '庉', '屯', '庉，屯也，屯聚之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5928', '234', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (983, '篅', '團', '圌{篅}，以草作之，團團然也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '2943', '3908', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (984, '廁;溷;清;軒', '雜', '廁，雜也，言人雜廁在上，非一也。或曰溷，言溷濁也。或曰圊{清}，至穢之處，宜常修治，使潔淸也。或曰軒，前有伏，似殿軒也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '5942;7205;7200;9513', '5333', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (985, '泥', '邇', '泥，邇也；邇，近也。以水沃土，使相黏近也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '7106', '1184', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (986, '塗', '杜', '塗，杜也，杜塞孔穴也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9150', '3431', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (987, '堊', '亞', '堊，亞也；亞，次也。先泥之，次以白灰飾之也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9061', '9723', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (988, '墍', '緭/煒', '墍猶煟煟{緭}[煒]，細澤貌也。', '', '釋宮室第十七', '釋名', '1', DEFAULT, DEFAULT, '9060', '8591/0/6497', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (989, '牀;榻;獨坐;枰', '裝', '人所坐臥曰牀。牀，裝也，所以自裝載也。長狹而卑曰榻，言其體榻然近地也。小者曰獨坐{枰}，主人無二，獨所坐也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '3677;3837;6355/9067;3803', '5351', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (990, '枰', '平', '枰，平也，以板作之，其體平正也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '3803', '3073', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (991, '几', '庪', '几，庪也，所以庪物也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '9467', '5973', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (992, '筵', '衍', '筵，衍也，舒而平之，衍衍然也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '2910', '7136', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (993, '席', '釋', '席，釋也，可卷可釋也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '4902', '724', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (994, '簟', '覃', '簟，覃也，布之，覃覃然平正也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '2911', '3336', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (995, '薦/荐', '', '薦[荐]，所以自薦藉也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '6259/0/580', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (996, '蒲平', '', '蒲平，以蒲作之，其體平也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '332/3073', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (997, '氊', '旃', '氊，旃也，毛相著旃旃然也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '5390', '4297', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (998, '蓐', '辱', '褥{蓐}，辱也，人所坐䙝辱也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '698', '9794', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (999, '裘溲/氍毹', '寠數', '裘溲[氍毹]猶寠數，毛相離之言也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '5373/7371/0/5392/5393', '4611/2010', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1000, '榻登', '', '榻登，施之承大牀前、小榻上，登以上牀也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '3837/1079', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1001, '貂席', '', '貂席，連貂皮以爲席也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '6128/4902', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1002, '枕', '檢', '枕，檢也，所以檢項也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '3678', '3769', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1003, '帷', '圍', '帷，圍也，所以自障圍也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '4879', '3928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1004, '幕;帟', '奕', '幕，幕絡也，在表之稱也。小幕曰帟，張在人上，帟帟[奕]然也。', '', '釋牀帳第十八', '釋名', '2', DEFAULT, DEFAULT, '4881;4918', '6667', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1005, '幔', '曼/緜', '幔，漫{曼}[緜]也，漫漫相連綴之言也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '4876', '1910/0/8513', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1006, '帳;斗帳', '張', '帳，張也，張施於牀上也。小帳曰斗帳，形如覆斗也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '4880;9489/4880', '8486', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1007, '㡘', '廉', '㡘，廉也，自障蔽爲廉耻也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '4878', '5947', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1008, '幢容', '童', '幢容。幢，童也，施之車葢，童童然以隱蔽形容也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '4916/4587', '1746', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1009, '戸㡘', '', '戸㡘，施之於戸外也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '7720/4878', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1010, '襜', '聸', '牀前帷曰幨{襜}，言幨幨{聸}而垂也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '5290', '7797', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1011, '楃', '屋', '幄{楃}，屋也，以帛衣板施之，形如屋也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '3672', '5420', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1012, '承𪋻', '', '承塵{承𪋻}，施於上以承塵土也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '7912/6288', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1013, '搏壁', '', '搏壁，以席搏著壁也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '7864/9048', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1014, '扆', '倚', '扆，倚也，在後所依倚也。', '', '釋牀帳第十八', '釋名', '1', DEFAULT, DEFAULT, '7727', '5045', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1015, '屏風', '', '屏風，言可以屏障風也。', '', '釋牀帳第十八', '釋名', '', DEFAULT, DEFAULT, '5421/8977', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1016, '筆', '述', '筆，述也，述事而書之也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '1940', '1107', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1017, '硯', '研', '硯，研也，研墨使和濡也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '6053', '6046', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1018, '墨', '晦', '墨，晦也，言似物晦黑也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '9076', '4225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1019, '紙', '砥', '紙，砥也，謂平滑如砥石也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '8727', '5982', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1020, '版', '昄', '板{版}，昄也，昄昄平廣也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '4399', '4239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1021, '奏', '鄒/鯫', '奏，鄒[鯫]也，狹小之言也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '6659', '4130/0/7635', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1022, '札', '櫛', '札，櫛也，編之如櫛，齒相比也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '3768', '3681', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1023, '𥳑', '閒', '𥳑，閒也，編之篇篇有閒也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '2892', '7760', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1024, '笏;薄', '忽', '笏，忽也，君有敎命，及所啟白，則書其上，備忽忘也。或曰簿{薄}，言可以簿疏物也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '3014;561', '6843', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1025, '槧', '漸/𧽯', '槧，板之長三尺者也。槧，漸[𧽯]也，言其漸漸然長也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '3767', '7043/0/1060', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1026, '牘', '睦', '牘，睦也，手執之以進見，所以爲恭睦也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '4401', '2155', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1027, '籍', '', '籍，籍也，所以籍疏人名戸口也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '2886', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1028, '檄', '激', '檄，激也，下官所以激迎其上之書文也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '3770', '7186', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1029, '檢', '禁', '檢，禁也，禁閉諸物，使不得開露也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '3769', '71', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1030, '壐', '徙', '壐，徙也，封物使可轉徙而不可發也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '9075', '1135', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1031, '印', '信/因', '印，信也，所以封物爲信驗也。亦言因也，封物相因付也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '5789', '1523/0/3922', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1032, '謁', '詣', '謁，詣也；詣，告也，書其姓名於上，以告所至詣者也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '1483', '1580', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1033, '符', '付/赴', '符，付也，書所勑命於上，付使傳行之也。亦言赴也，執以赴君命也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '2898', '5056/0/981', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1034, '傳;過所', '轉', '傳，轉也，轉移所在，執以爲信也。亦曰過所，過所至關津以示之也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '5103;1110/9482', '9575', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1035, '卷/券', '綣', '卷[券]，綣也，相約束繾綣以爲限也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '5784/0/2804', '8773', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1036, '別', '', '{別}莂，別也，大書中央，中破別之也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '2566', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1037, '契', '栔', '契，栔也。栔，刻也，刻識其數也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '6612', '2816', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1038, '策', '', '策，書教令於上，所以驅策諸下也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '2972', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1039, '册', '嫧', '漢制：約勑封侯曰册。册，賾{嫧}也，勑使整賾，不犯之也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '1432', '8244', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1040, '示', '', '示，示也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '10', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1041, '棨', '詣', '棨，詣也，以告語官司，所至詣也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '3771', '1580', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1042, '書', '庶/箸', '書，庶也，紀庶物也。亦言著{箸}也，著之𥳑紙，永不滅也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '1942', '5956/0/2928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1043, '畫', '繪', '畫，繪也，以五色繪物象也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '1943', '8607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1044, '刺;寫', '', '書稱刺，以筆刺紙𥳑之上也。又曰寫，倒寫此文也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '2805;4598', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1045, '畫刺', '', '畫姓名於奏上曰畫刺，作「再拜」「起居」，字皆達其體，使書盡邊，徐引筆書之，如畫者也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '1943/2805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1046, '長刺;爵里刺', '', '下官刺曰長刺，長書中央一行而下之也。一曰爵里刺，書其官爵及郡縣鄉里也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '6067/2805;3201/9167/2805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1047, '題', '諦/第', '書牘稱題。題，諦也，審諦其名號也。亦言第也，因其第次也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '5609', '1516/0/3011', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1048, '署', '予', '書文書檢曰署。署，予也，題所予者官號也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '4840', '2509', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1049, '告', '覺', '上勑下曰告。告，覺也，使覺悟知己意也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '778', '5509', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1050, '表;上;言', '', '下言上曰表。思之於內，表施於外也。又曰上，示之於上也。又曰言，言其意也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '5259;6;1474', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1051, '約', '', '約，約束之也。', '', '釋書契第十九', '釋名', '', DEFAULT, DEFAULT, '8568', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1052, '敕', '飭', '敕，飭也，使自警飭，不敢廢慢也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '2021', '9247', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1053, '謂', '喟', '謂猶喟，得勑不自安，喟喟然也。', '', '釋書契第十九', '釋名', '1', DEFAULT, DEFAULT, '1479', '830', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1054, '三墳', '分', '三墳，墳，分也，論三才之分，天、地、人之治，其體有三也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '77/9141', '708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1055, '五典', '鎭', '五典，典，鎭也，制法所以鎭定上下，其等有五也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '9725/3022', '9358', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1056, '八索', '素', '八索，索，素也，著素王之法，若孔子者，聖而不王，制此法者有八也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '707/3871', '8774', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1057, '九丘', '區', '九丘，丘，區也，區別九州土氣，教化所宐施者也。此皆三王以前上古羲皇時書也。今皆亡，惟堯典存也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '9728/5231', '8417', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1058, '經', '徑', '經，徑也，常典也。如徑路無所不通，可常用也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '8530', '1225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1059, '緯', '圍', '緯，圍也，反覆圍繞，以成經也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '8536', '3928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1060, '圖', '度', '圖，度也，盡其品度也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '3913', '1928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1061, '讖', '纖', '讖，纖也，其義纖微而有效驗也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '1490', '8558', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1062, '易', '', '易，易也，言變易也。', '', '釋典藝第二十', '釋名', '', DEFAULT, DEFAULT, '6132', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1063, '禮', '體', '禮，體也，得其事體也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '12', '2586', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1064, '儀', '宜', '儀，宜也，得事宜也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '5082', '4597', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1065, '傳', '', '傳，傳也，以傳示後人也。', '', '釋典藝第二十', '釋名', '', DEFAULT, DEFAULT, '5103', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1066, '記', '紀', '記，紀也，紀識之也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '1567', '8540', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1067, '詩;興;賦;比;雅;頌', '之;敷布', '詩，之也，志之所之也。興物而作謂之興。{賦}敷布其義謂之賦。事類相似謂之比。言王政事謂之雅。稱頌成功謂之頌。隨作者之志而別名之也。', '', '釋典藝第二十', '釋名', '1;3', DEFAULT, DEFAULT, '1489;1782;3981;5227;2279;5602', '3859;2007/4910', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1068, '尙書', '上', '尙書，尙，上也，以堯爲上，始而書其時事也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '711/1942', '6', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1069, '春秋', '', '春秋，言春秋冬夏終而成歲，舉春秋，則冬夏可知也。春秋書人事，卒歲而究備。春秋温涼中，象政和也，故舉以爲名也。', '', '釋典藝第二十', '釋名', '', DEFAULT, DEFAULT, '683/4491', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1070, '國語;外傳', '', '國語，記諸國君臣相與言語、謀議之得失也。又曰外傳，春秋以魯爲內，以諸國爲外，外國所傳之事也。又曰外傳，春秋以魯爲內，以諸國爲外，外國所傳之事也。', '', '釋典藝第二十', '釋名', '', DEFAULT, DEFAULT, '3915/1477;4331/5103', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1071, '爾雅', '昵/義', '爾雅，爾，昵也；昵，近也。雅，義也；義，正也。五方之言不同，皆以近正爲主也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '2089/2279', '4251/0/8402', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1072, '論語', '', '論語，記孔子與諸弟子所論所語之言也。', '', '釋典藝第二十', '釋名', '', DEFAULT, DEFAULT, '1511/1477', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1073, '法', '逼', '法，逼也，人莫不欲從其志，逼正使有所限也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '6260', '1213', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1074, '律', '累/絫', '律，累[絫]也，累人心，使不得放肆也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '1257', '8684/0/9716', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1075, '令', '領', '令，領也，理領之，使不得相犯也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '5777', '5618', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1076, '科', '課', '科，課也，課其不如法者，罪責之也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '4494', '1539', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1077, '詔', '照/昭', '詔，照也，昭也，人暗不見事宐，則有所犯，以此照示之，使昭然知所由也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '1529', '6496/0/4197', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1078, '論', '倫/侖', '論，倫[侖]也，有倫理也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '1511', '5036/0/3271', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1079, '贊', '纂/䉵', '稱人之美曰讚。讚{贊}，纂[䉵]也，纂集其美而敘之也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '3947', '8650/0/3218', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1080, '敘', '抒', '敘，抒也，抒泄其實宣見之也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '2064', '7980', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1081, '名', '', '{名}銘，名也，述其功美，使可稱名也。', '', '釋典藝第二十', '釋名', '', DEFAULT, DEFAULT, '836', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1082, '誄', '絫', '誄，累{絫}也，累列其事而稱之也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '1716', '9716', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1083, '謚', '曳', '謚，曳也，物在後爲曳，言名之於人亦然也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '1680', '9803', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1084, '譜;緒', '布;敘', '譜，布也，布列見其事也。亦曰緒，主敘人世，類相繼如統緒也。', '', '釋典藝第二十', '釋名', '1;2', DEFAULT, DEFAULT, '1727;8519', '4910;2064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1085, '碑', '被', '碑，被也。此本王葬時所設也。施於轆轤，以繩被其上，引以下棺也。臣子追述君父之功美，以書其上。後人因焉。物故，建於道陌之頭、顯見之處，名其文就謂之碑也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '6021', '5323', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1086, '詞', '嗣', '詞，嗣也，令撰善言相續嗣也。', '', '釋典藝第二十', '釋名', '1', DEFAULT, DEFAULT, '5772', '1433', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1087, '斧', '甫', '斧，甫也；甫，始也。凡將制器，始用斧伐木，巳乃制之也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9475', '2082', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1088, '鎌', '廉', '鎌，廉也，體廉薄也。其所刈，稍稍取之，又似廉者也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9354', '5947', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1089, '斨', '戕', '斨，戕也，所伐皆戕毁也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9476', '8388', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1090, '仇矛', '讎', '仇矛。仇，讎也，所伐則平，如討仇讎也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '5171/9506', '1487', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1091, '錐', '利', '錐，利也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9365', '2751', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1092, '椎', '推', '椎，推也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '3743', '7846', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1093, '鑿', '', '鑿，有所穿鑿也。', '', '釋用器第二十一', '釋名', '', DEFAULT, DEFAULT, '9341', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1094, '鐫', '鐏', '鐫，鐏也，有所鐏入也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9340', '9407', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1095, '耒', '來/推', '耒，來也，亦推也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '2819', '3346/0/7846', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1096, '㭒', '齒', '耜{㭒}，齒也，似齒斷物也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '3687', '1278', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1097, '𤛿', '利', '犁{𤛿}，利也，利則發土絕草根也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '761', '2751', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1098, '檀/亶', '坦', '檀[亶]，坦也，摩之使坦然平也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '3527/0/3342', '9070', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1099, '鉏;橿;鶴', '助;畺/僵', '鋤{鉏}，助也，去穢助苗長也。齊人謂其柄曰橿，畺[僵]然正直也。頭曰鶴，似鶴頭也。', '', '釋用器第二十一', '釋名', '1;2', DEFAULT, DEFAULT, '9348;3504;2411', '9213;9200/0/5153', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1100, '枷;羅枷/連枷;𦫳', '加', '枷，加也，加杖於柄頭以撾穗而出其穀也。或曰羅枷[連枷]，羅三杖而用之也。或曰丫{𦫳}，丫以杖轉於頭，故以名之也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '3697;4831/3697/0/1168/3697;2325', '9241', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1101, '鍤/臿;銷/銚;華/釫;葉', '插;削;刳', '鍤[臿]，插也，插地起土也。或曰銷，銷[銚]，削也，能有所穿削也。或曰鏵，鏵{華}[釫]，刳也，刳地爲坎也。其板曰葉，象木葉也。', '', '釋用器第二十一', '釋名', '1;2;3', DEFAULT, DEFAULT, '9330/0/4524;9278/0/9307;3884/0/3686;495', '7895;2746;2770', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1102, '杷', '播/撥', '杷，播也，所以播除物也。亦言撥也，撥使聚也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '3693', '8036/0/7978', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1103, '槈', '薅', '耨{槈}，似鋤嫗薅禾也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '3684', '699', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1104, '鎛', '迫', '鎛亦鋤類也。鎛，迫也，〔迫地去草也。〕', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9388', '1182', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1105, '講', '溝', '𨪋{講}，溝也，旣割去壟上草，又辟其土以壅苗根，使壟下爲溝，受水潦也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '1581', '7261', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1106, '乂', '殺', '鉪{乂}，殺也，言殺草也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '8360', '1978', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1107, '銍', '挃', '銍，穫黍鐵也；銍銍[挃]，斷黍穗聲也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9357', '8037', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1108, '斤;鏟', '謹;斯/澌', '斤，謹也，板廣不可得削，又有節，則用此釿之，所以詳謹，令平滅斧跡也。鏟，鐁{斯}[澌]也，釿有高下之跡，以此鐁彌其上而平之也。', '', '釋用器第二十一', '釋名', '1;2', DEFAULT, DEFAULT, '9474;9321', '1520;9483/0/7338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1109, '鋸', '倨', '鋸，倨也，其體直，所𢧵應倨句之平也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '9363', '5001', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1110, '欘', '誅', '钃{欘}，誅也，主以誅除物根株也。', '', '釋用器第二十一', '釋名', '1', DEFAULT, DEFAULT, '3691', '1711', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1111, '鐘', '空', '鐘，空也，內空受氣多，故聲大也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '9386', '4646', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1112, '磬', '罄/硻', '磬，罄[硻]也，其聲罄罄然堅緻也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '6039', '3303/0/6030', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1113, '鼓', '郭', '鼓，郭也，張皮以冒之，其中空也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '3082', '4150', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1114, '鞉', '導', '鞉，導也，所以導樂作也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '1810', '1986', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1115, '鼙;朔;應', '裨', '鼙，裨也，裨助鼓節也。聲在前曰朔，朔，始也；在後曰應，應大鼓也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '3085;4274;6711', '5331', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1116, '鐉;虡;業', '峻;舉', '所以縣鐘鼓者，橫曰簨，簨{鐉}，峻也，在上高峻也。從曰虡，虡，舉也，在旁舉簨也。簨上之板曰業，刻爲牙，捷業如鋸齒也。', '', '釋樂器第二十二', '釋名', '1;2', DEFAULT, DEFAULT, '9435;3116;1749', '5876;7957', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1117, '瑟', '', '瑟，施弦張之，瑟瑟然也。', '', '釋樂器第二十二', '釋名', '', DEFAULT, DEFAULT, '8406', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1118, '箏', '錚', '箏，施弦高急，箏箏[錚]然也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '2998', '9392', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1119, '筑', '築', '筑，以竹鼓之，筑筑[築]然柲之也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '2997', '3617', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1120, '空矦', '', '{空矦}箜篌，此師延所作，靡靡之樂也。後岀於桑閒濮上之地，葢空國之侯所存也。師涓爲晉平公鼓焉，鄭 衞分其地而有之，遂號「鄭衞之音」，謂之淫樂也。', '', '釋樂器第二十二', '釋名', '', DEFAULT, DEFAULT, '4646/3311', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1121, '比把', '𢱧/𢃳', '批把{比把}，本出於胡中，馬上所鼓也。推手前曰批{𢱧}，引手卻曰把[𢃳]，象其鼓時，因以爲名也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '5227/7875', '8011/0/9752', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1122, '員/壎', '宣/吅', '塤{員}[壎]，喧{宣}[吅]也，聲濁喧喧然也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '3932/0/9073', '4558/0/971', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1123, '篪', '帝/嗁', '篪，啼{帝}[嗁]也，聲從孔出，如嬰兒啼聲也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '1429', '7/0/937', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1124, '簫', '肅', '簫，肅也，其聲肅肅而淸也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '2990', '1938', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1125, '笙;匏', '生', '笙，生也，竹之貫匏，象物貫地而生也。以匏爲之，故曰匏也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '2987;5816', '3874', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1126, '竽', '汙', '竽，汙也，其中汙空，以受簧也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '2986', '7348', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1127, '簧', '橫', '簧，橫也，於管頭橫施於中也，以竹鐵作，於口橫鼓之亦是也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '2988', '3794', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1128, '搏拊', '拊𢫦', '搏拊，以韋盛穅，形如鼓，以手附拍{拊𢫦}之也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '7864/7890', '7890/7889', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1129, '柷敔', '虎/衙', '柷敔。柷，狀如桼桶；敔，狀如伏虎。柷，始也，如物始見，柷柷然如作樂也。敔，衙也。衙，止也，所以止樂也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '3766/2058', '3117/0/1273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1130, '舂牘', '撞/築', '舂牘，舂，撞也；牘，築也，以撞築地爲節也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '4522/4401', '8014/0/3617', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1131, '籥/龠', '躍', '籥[龠]，躍也，氣躍出也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '2890/0/1427', '1348', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1132, '逐/笛', '滌', '篴{逐}[笛]，滌也，其聲滌滌然也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '1178/0/2996', '7400', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1133, '鐃', '譊', '鐃，聲鐃鐃[譊]也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '9382', '1588', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1134, '歌', '柯', '人聲曰歌。歌，柯也，所歌之言是其質也，以聲吟詠有上下，如草木之有柯葉也。故兖 冀言歌，聲如柯也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '5541', '3744', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1135, '吹/䶴', '推', '竹曰吹。吹[䶴]，推也，以氣推發其聲也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '829/0/1428', '7846', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1136, '吟', '嚴', '吟，嚴也，其聲本出於憂愁，故其聲嚴肅，使人聽之悽歎也。', '', '釋樂器第二十二', '釋名', '1', DEFAULT, DEFAULT, '921', '973', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1137, '擊壤', '', '〔擊壤，野老之戯也。〕', '', '釋樂器第二十二', '釋名', '', DEFAULT, DEFAULT, '8064/9029', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1138, '弓;簫;弭;付/㓡;淵', '穹;梢/稍;撫;宛', '弓，穹也，張之穹隆然也。其末曰簫，簫言梢[稍]也；又謂之弭，以骨爲之，滑弭弭也。中央曰弣，弣{付}[㓡]，撫也，人所撫持也。簫、弣之閒曰淵，淵，宛也，言曲宛也。', '', '釋兵第二十三', '釋名', '1;2;4;5', DEFAULT, DEFAULT, '8477;2990;8479;5056/0/2744;7209', '4670;3495/0/4490;7920;4607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1139, '弩;臂;牙;郭;縣刀;機', '怒', '弩，怒也，言其聲勢威嚮如怒，故以名其弩也。其柄曰臂，似人臂也。鉤弦者曰牙，似齒牙也。牙外曰郭，爲牙之規郭也。下曰縣刀，其形然也。含括之口曰機，言如機之巧也，亦言如門戸之樞機，開闔有節也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '8497;2626;1323;4150;5705/2743;3723', '6877', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1140, '矢;箭;足;鏑;鏃/族;交;榦;羽;衞;桰;叉', '指;進;敵;丵;會', '矢，指也，言其有所指向迅疾也；又謂之箭，進也，言前進也。其本曰足，矢形似木，木以下爲本，矢以本爲足也；又謂之鏑，鏑，敵也，言可以禦敵也。齊人謂之鏃，鏃[族]，族[丵]也，言其所中皆族滅也。關西曰鉸，鉸{交}，交也，言有交刃也。其體曰幹{榦}，言梃幹也。其旁曰羽，如鳥羽也。鳥須羽而飛，矢須羽而前也。齊人曰衞，所以導衞矢也。其末曰栝，栝{桰}，會也，與弦會也。栝旁曰叉，形似叉也。', '', '釋兵第二十三', '釋名', '1;2;4;5;10', DEFAULT, DEFAULT, '3307;2866;1326;9410;9441/0/4310;6625;3618;2241;1277;3752;1905', '7830;1113;2028;1748;3274', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1141, '箙;笮;步叉;鞬', '服;建', '其受矢之器，以皮曰箙，謂柔服之義也。織竹曰笮，相迫笮之名也。亦曰步叉，人步所帶，以箭叉於其中也。馬上曰鞬，鞬，建也，弓矢並建立於其中也。', '', '釋兵第二十三', '釋名', '1;4', DEFAULT, DEFAULT, '2977;2907;1081/1905;1840', '5447;1263', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1142, '刀;鏠;環;削;琫;珌', '到;蠭;陗;奉;卑', '刀，到也，以斬伐到其所，乃擊之也。其末曰鋒{鏠}，言若鋒{蠭}刺之毒利也。其本曰環，形似環也。其室曰削，削，峭{陗}也，其形峭殺，裹刀體也。室口之飾曰琫，琫，捧{奉}也，捧束口也。下末之飾曰琕，琕{珌}，卑也，在下之言也。', '', '釋兵第二十三', '釋名', '1;2;4;5;6', DEFAULT, DEFAULT, '2743;9405;108;2746;127;128', '7707;8959;9636;1756;1931', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1143, '𢫦髀;露𢫦', '', '短刀曰拍髀{𢫦髀}，帶時拍髀旁也。又曰露拍{露𢫦}，言露見也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '7889/2574;7561/7889', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1144, '佩刀;容刀', '𩔜', '佩刀，在佩旁之刀也。或曰容刀，有刀形而無刃，備儀容[𩔜]而巳。', '', '釋兵第二十三', '釋名', '2', DEFAULT, DEFAULT, '4958/2743;4587/2743', '5602', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1145, '剪刀', '進', '剪刀，剪，進也，所剪稍進前也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '2754/2743', '1113', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1146, '書刀', '', '書刀，給書𥳑札，有所刊削之刀也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '1942/2743', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1147, '封刀;鉸刀', '', '封刀、鉸刀，皆隨時名之也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '9074/2743;0/2743', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1148, '㦸', '格/𠲱', '㦸，格[𠲱]也，旁有枝格也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '8375', '3601/0/2818', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1149, '戈', '過', '戈，句孑㦸也。戈，過也，所刺擣則決過，所鉤引則制之弗得過也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '8373', '1110', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1150, '常', '', '車㦸曰常，長丈六尺，車上所持也。八尺曰尋，倍尋曰常，故稱常也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '4870', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1151, '手㦸', '', '手㦸，手所持擿之㦸也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '7827/8375', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1152, '矛;鐏;松櫝', '冒;獨/躅', '矛，冒也，刃下冒矜也。下頭曰鐏，鐏入地也。其矜曰松櫝，刃長三尺，其矜宜輕，以松作之也。櫝，速獨[束躅]，前刺之言也。', '', '釋兵第二十三', '釋名', '1;3', DEFAULT, DEFAULT, '9506;9407;3543/3680', '4807;6355/0/1367', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1153, '梢/槊;激矛', '稍', '矛長丈八尺曰矟{梢}[槊]，馬上所持，言其矟矟{稍}便殺也。又曰激矛，激，𢧵也，可以激𢧵敵陳之矛也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '3495/0/3835;7186/9506', '4490', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1154, '仇矛', '', '仇矛，頭有三叉，言可以討仇敵之矛也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '5171/9506', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1155, '夷矛', '𢓡', '夷矛，其矜長丈六尺。夷，常也，不言常而曰夷者，言其可夷[𢓡]滅敵，亦車上所持也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '6613/9506', '1239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1156, '𣒃矛/鈌矛', '豁', '𥍧矛{𣒃矛}[鈌矛]，長九尺者也。𥍧，霍{豁}也，所中霍然卽破裂也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '3694/9506/0/9442/9506', '7502', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1157, '殳', '殊', '殳，殊也，長丈二尺而無刃，有所撞挃於車上，使殊離也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '1958', '2535', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1158, '盾;吳魁;滇盾;羌盾;陷虜;露見;步盾;孑盾;木絡;犀盾;木盾', '遯;魁/顝', '盾，遯也，跪其後，避刃似隱遯也。大而平者曰吳魁，本出於吳，爲魁[顝]帥者所持也。隆者曰滇盾，本出於蜀，蜀 滇所持也。或曰羌盾，言出於羌也。約脇而鄒者曰陷虜，言可以陷破虜敵也，今謂之曰露見是也。狹而長者曰步盾，步兵所持，與刀相配者也。狹而短者曰孑盾，車上所持者也。孑，小稱也。以縫編板謂之木絡，以犀皮作之曰犀盾，以木作之曰木盾，皆因所用爲名也。', '', '釋兵第二十三', '釋名', '1;2', DEFAULT, DEFAULT, '2220;6619/9495;6998/2220;2356/2220;9642/4340;7561/5474;1081/2220;9780/2220;3411/8725;769/2220;3411/2220', '1172;9495/0/5632', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1159, '彭排', '旁', '彭排，彭，旁也，在旁排敵禦攻也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '3080/7848', '8', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1160, '鎧;介;函;甲', '塏/艮', '鎧猶塏，塏[艮]，堅重之言也。〔或謂之介、或謂之函，亦如之也。〕或謂之甲，似物有孚甲，以自禦也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '9411;714;4342;9739', '9115/0/5223', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1161, '劎;鐔;鏠', '檢/斂;尋', '劎，檢也，所以防檢非常也。又言斂也，其在身拱時斂在臂內也。其旁鼻曰鐔，鐔，尋也，帶所貫尋也。其末曰鋒{鏠}，鋒末之言也。', '', '釋兵第二十三', '釋名', '1;2', DEFAULT, DEFAULT, '2813;9395;9405', '3769/0/2023;1983', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1162, '鋋', '延', '鋋，延也；延，達也，去此至彼之言也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '9400', '1265', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1163, '鉤鑲', '攘', '鉤鑲，兩頭曰鉤，中央曰鑲，或推攘、或鉤引用之之宜也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '1460/9283', '7839', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1164, '常', '', '九旗之名，日月爲常，畫日月於其端，天子所建，言常明也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '4870', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1165, '旂', '倚', '交龍爲旂，旂，倚也，畫作兩龍相依倚也，諸侯所建也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4294', '5045', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1166, '旃', '戰', '通帛爲旃，旃，戰也，戰戰恭己而巳也。通以赤色爲之，無文采，三孤，所建象無事也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4297', '8382', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1167, '旗', '期', '熊虎爲旗。軍將所建，象其猛如熊虎，與衆期其下也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4289', '4280', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1168, '旟', '譽', '鳥隼爲旟。旟，譽也，軍吏所建，急疾趨事，則有稱譽也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4293', '1568', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1169, '物/勿', '', '雜帛爲物[勿]，以雜色綴其邊，爲燕尾也。將帥所建，象物雜色也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '771/0/6071', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1170, '旐', '兆', '龜蛇爲旐。旐，兆也。龜蛇知氣兆之吉凶，建之於後，察度事宜之形兆也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4290', '2080', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1171, '𣄧', '滑', '全羽爲𣄧。𣄧猶滑，順滑之貌也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4295', '7218', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1172, '旌', '精', '析羽爲旌。旌，精也，有精光也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4292', '4351', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1173, '緌;綏', '繠;衰/𤸬', '緌，有虞氏之旌也，注旄竿首，其形橤橤{繠}然也。或曰綏，夏后氏之旌也，其形衰衰[𤸬]也。', '', '釋兵第二十三', '釋名', '1;2', DEFAULT, DEFAULT, '8642;8762', '6981;5358/0/4792', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1174, '白旆', '', '白旆，殷旌也，以帛繼旐末也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '4929/4291', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1175, '翿', '陶', '翿，陶也，其貌陶陶下垂也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '2272', '9687', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1176, '幢', '童', '幢，童也，其貌童童然也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4916', '1746', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1177, '旛', '幡/蕃', '旛，幡[蕃]也，其貌幡幡然也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '4308', '4891/0/675', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1178, '校', '號', '校，號也，將帥號令之所在也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '3790', '3068', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1179, '節', '', '節，號令賞罰之節也。', '', '釋兵第二十三', '釋名', '', DEFAULT, DEFAULT, '2876', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1180, '鐸', '度', '鐸，度也，號令之限度也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '9383', '1928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1181, '金鼓', '禁', '金鼓。金，禁也，爲進退之禁也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '9259/3082', '71', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1182, '戚', '慼', '戚，慼也，斧以斬斷，見者皆慼懼也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '8400', '6934', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1183, '鉞/戉', '豁', '鉞[戉]，豁也，所向莫敢當前，豁然破散也。', '', '釋兵第二十三', '釋名', '1', DEFAULT, DEFAULT, '9420/0/8399', '7502', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1184, '車', '居/舍', '車，古者曰車、聲如居，言行所以居人也。今曰車，聲近舍，行者所處，若舍也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9512', '5402/0/3273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1185, '玉輅;象輅;金輅;革輅;木輅', '路', '天子所乘曰玉輅，以玉飾車也。輅亦車也，謂之輅者，言行於道路也。象輅、金輅、〔革輅〕、木輅，各隨所以爲飾名之也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '81/9530;6133/9530;9259/9530;1790/9530;3411/9530', '1410', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1186, '鉤車', '', '鉤車，以行爲陳，鉤股曲直有正，夏所制也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '1460/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1187, '胡奴車', '', '胡奴車，東胡以罪没入官，爲奴者引之，殷所制也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '2677/8161/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1188, '元戎車', '', '元戎車，元戎在軍前啓突敵陳，周所制也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '2/8377/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1189, '輦車', '', '輦車，人所輦也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '9604/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1190, '柏車', '伯', '柏車，柏，伯也；伯，大也，丁夫服任之大車也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3547/9512', '4965', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1191, '羊車;犢車', '祥', '羊車，羊，祥也；祥，善也。善飾之車，今犢車是也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '2332/9512;733/9512', '18', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1192, '墨車', '', '墨車，漆之正黑如墨，無文飾，大夫所乘也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '9076/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1193, '重較', '', '重較，其較重，卿所乘也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '5242/9531', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1194, '役車;棧車', '靖', '役車，給役之車也。棧車，棧，靖也，庥靖物之車也。皆庶人所乘也。', '', '釋車第二十四', '釋名', '2', DEFAULT, DEFAULT, '1976/9512;3730/9512', '6684', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1195, '軘車', '', '軘車，戎者所乘也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '9522/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1196, '容車', '', '容車，婦人所載小車也。其葢施帷，所以隱蔽其形容也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '4587/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1197, '衣車', '', '衣車，前戸，所以載衣服之車也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '5252/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1198, '獵車', '', '獵車，所乘以畋獵也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '6358/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1199, '小車', '', '小車，駕馬輕小之車也。駕馬宜輕，故使之局小也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '704/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1200, '高車', '', '高車，其葢高，立乘載之車也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '3318/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1201, '安車', '', '安車，葢卑坐乘，今吏所乘小車也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '4576/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1202, '驘車;羊車', '', '驘車、羊車，各以所駕名之也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '6244/9512;2332/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1203, '檻車', '', '檻車，上施檻欄，以格猛獸，亦囚禁罪人之車也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '3824/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1204, '軺車', '遙', '軺車，軺，遙也；遙，遠也，四向遠望之車也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9518/9512', '1222', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1205, '輜軿', '廁/屏', '輜軿。輜車，載輜重臥息其中之車也。輜，廁也，所載衣物雜廁其中也。軿車，軿，屏也，四面屏蔽，婦人所乘牛車也。輜軿之形同，有邸曰輜，無邸曰軿。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9514/9515', '5942/0/5421', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1206, '輈', '句', '輈，句也，轅上句也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9557', '1457', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1207, '衡', '橫', '衡，橫也，橫馬頸上也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '2843', '3794', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1208, '游環', '', '游環，環在服馬背上、驂馬之外，轡貫之，游移前卻，無定處也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '4304/108', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1209, '脅驅', '', '脅驅，在服馬外脅也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '2617/6207', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1210, '陰;靷;鋈;續', '蔭;引;𦰚', '陰，蔭也，橫側車前，所以蔭笭也。靷，所以引車也。鋈，沃{𦰚}也，冶白金以沃灌靷環也。續，續靷端也。', '', '釋車第二十四', '釋名', '1;2;3', DEFAULT, DEFAULT, '9621;1825;9262;8548', '524;8491;7276', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1211, '文鞇', '因', '文鞇，車中所坐者也。用虎皮爲之，有文采。鞇，因也，因與下轝相連著也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '5723/610', '3922', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1212, '絥', '伏', '𩊙{絥}，伏也，在前，人所伏也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8702', '5161', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1213, '軾', '式', '軾，式也，所伏以式敬者也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9529', '3030', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1214, '土薄;屟', '', '靯𩍿{土薄}，車中重薦也。輕靯𩍿，小貂[𧜣]也。{屟}', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '9019/561;5419', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1215, '轂', '确', '轂，埆{确}也，體堅埆也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9548', '6035', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1216, '轅', '援', '轅，援也，車之大援也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9556', '7992', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1217, '桄;縮;育', '橫', '桄，橫也。橫在前，如臥牀之有枕也。橫在下，薦板在上，如薦席也。齊人謂車桄以前曰縮，言局縮也。兖 冀曰育，御者坐中執御，育育然也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3796;8563;9786', '3794', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1218, '較', '榷', '較，在箱上，爲辜較[榷]也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9531', '3783', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1219, '立人;陽門', '', '立人，象人立也。或曰陽門，在前曰陽，兩旁似門也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '6677/4948;9622/7730', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1220, '槅;烏啄', '軶', '槅，枙{軶}也，所以枙牛頸也。枙馬頸曰烏啄，下向叉馬頸，似烏開口向下啄物時也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3777;2487/953', '9560', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1221, '隆強;車弓;筤', '朗', '隆強，言體隆而強也。或曰車弓，似弓曲也。{筤}其上竹曰朗，疏相遠，晶晶然也。', '', '釋車第二十四', '釋名', '3', DEFAULT, DEFAULT, '3877/8824;9512/8477;2930', '4277', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1222, '輹', '複', '輹，複也，重複非一之言也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9544', '5305', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1223, '网;輮;罠', '煣;緜', '{网}輞，罔也，罔羅周輪之外也。關西曰輮，言曲揉{煣}之也。或曰䡑，䡑{罠}，緜也，緜連其外也。', '', '釋車第二十四', '釋名', '2;3', DEFAULT, DEFAULT, '4813;9545;4830', '6480;8513', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1224, '輪;鏓', '綸', '輪，綸也，彌綸周匝之言也。或曰䡯{鏓}，言輻䡯入轂中也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9547;9391', '8652', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1225, '輿', '舉', '輿，舉也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9526', '7957', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1226, '軸', '抽', '軸，抽也，入轂中可抽出也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9543', '7993', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1227, '釭', '空', '釭，空也，其中空也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9416', '4646', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1228, '鐗', '閒', '鐗，閒也，閒釭軸之閒，使不相摩也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9415', '7760', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1229, '轄', '害', '轄，害也，車之禁害也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9574', '4614', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1230, '果/楇', '裹', '輠{果}[楇]，裹也，裹軹頭也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3566/0/3779', '5352', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1231, '軹', '指', '軹，指也，如指而見於轂頭也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9551', '7830', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1232, '笭', '', '笭，橫在車前，織竹作之，孔笭笭也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '2970', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1233, '葢', '盇', '葢，在上覆葢[盇]人也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '586', '3180', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1234, '畚/轒', '藩', '軬{畚}[轒]，藩也，藩蔽水雨也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8447/0/9600', '590', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1235, '轑', '橑', '轑，葢㕚也，如屋構橑也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9566', '3637', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1236, '杠', '公', '杠，公也，衆㕚所公共也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3674', '716', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1237, '俾棿', '祕齧/䩛齧', '䡟棿{俾棿}猶祕齧[䩛齧]也，在車軸上，正輪[轑]之祕齧前卻也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '5096/9597', '27/1313/0/1816/1313', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1238, '轐;伏兔;輹', '', '轐，似人屐也。又曰伏兔，伏於軸上似之也。又曰輹，亦如之。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '9541;5161/6293;9544', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1239, '鉤心', '', '鉤心，從輿心下鉤軸也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '1460/6703', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1240, '𩌏', '縛', '𩌏，縛也，在車下與輿相連縛也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '1829', '8578', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1241, '棠/樘', '𣥺', '棠[樘]，𨅨{𣥺}也，在車兩旁，𨅨幰使不得進卻也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3430/0/3627', '1066', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1242, '幰', '憲/煖', '幰，憲[煖]也，所以禦熱也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '4924', '6721/0/6513', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1243, '紲', '制', '紲，制也，牽制之也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8715', '2795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1244, '紛', '放', '紛，放也，防其放㢮，以拘之也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8707', '2512', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1245, '轡', '咈', '轡，咈也，牽引咈戾，以制馬也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8781', '886', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1246, '勒', '絡', '勒，絡也，絡其頭而引之也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '1836', '8725', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1247, '銜', '', '銜，在口中之言也。', '', '釋車第二十四', '釋名', '', DEFAULT, DEFAULT, '9422', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1248, '鑣', '苞/勹', '鑣，苞[勹]也，在旁苞斂其口也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '9423', '388/0/5799', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1249, '鞅;樊纓', '嬰/纓', '鞅，嬰也，喉下稱嬰，亦言纓絡之也。其下飾曰樊纓，其形樊樊而上屬纓也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '1845;1773/8640', '8259/0/8640', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1250, '𩎍', '經', '韅{𩎍}，經也，橫經其腹下也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '1822', '8530', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1251, '絆', '半', '靽{絆}，半也，拘使半行，不得自縱也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8710', '725', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1252, '奇/𦌭', '檢', '羇{奇}[𦌭]，檢也，所以檢持制之也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3059/0/4846', '3769', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1253, '繮', '疆', '韁{繮}，疆也，繫之使不得岀疆限也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8706', '9200', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1254, '緧', '遒', '鞧{緧}，遒也，在後遒迫，使不得卻縮也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '8709', '1179', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1255, '負', '背', '負，在背上之言也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '3963', '2616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1256, '尹/鞙', '縣', '𩉥{尹}[鞙]，縣也，所以縣縛軛也。', '', '釋車第二十四', '釋名', '1', DEFAULT, DEFAULT, '1913/0/1837', '5705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1257, '船;舟;垝/庪;舳;它;櫓', '循;周/𠣘;巍;拕;旅', '船，循也，循水而行也。又曰舟，言周[𠣘]流也。其前立柱曰桅。桅{垝}[庪]，巍也，巍巍高貌也。其尾曰柁。{舳}柁{它}，拕也，在後見拕曳也；且言弼正船使順流，不得他戾也。在旁曰櫓。櫓，旅也，用旅力然後舟行也。', '', '釋船第二十五', '釋名', '1;2;3;5;6', DEFAULT, DEFAULT, '5438;5436;9109/0/5973;5440;8993;3761', '1233;877/0/5809;5847;8071;4309', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1258, '筰', '作', '引舟者曰筰。筰，作也；作，起也，起舟使動行也。', '', '釋船第二十五', '釋名', '1', DEFAULT, DEFAULT, '2953', '5073', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1259, '櫂/擢;札;楫;交', '濯;捷/疌', '在旁撥水曰櫂。櫂[擢]，濯也，濯於水中也，且言使舟擢進也。又謂之札，形似札也。又謂之楫。楫，捷[疌]也，撥水使舟捷疾也。所用斥旁岸曰交，一人前、一人還，相交錯也。', '', '釋船第二十五', '釋名', '1;3', DEFAULT, DEFAULT, '3839/0/7994;3768;3788;6625', '7419;8087/0/1074', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1260, '凡/颿', '汎', '隨風張幔曰帆。帆{凡}[颿]，汎也，使舟疾汎汎然也。', '', '釋船第二十五', '釋名', '1', DEFAULT, DEFAULT, '9018/0/6206', '7162', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1261, '笭;笭突;覆;廬;飛廬;爵室;先登', '雀', '舟中牀以薦物者曰笭，言但有簀，如笭牀也。南方人謂之笭突，言濕漏之水突然從下過也。其上板曰覆，言所以覆衆桄也。其上屋曰廬，象廬舍也。其上重室曰飛廬，在上故曰飛也。又在其上曰爵室，於中候望之，如鳥雀之警示也。軍行在前曰先登，登之先向敵陳也。', '', '釋船第二十五', '釋名', '6', DEFAULT, DEFAULT, '2970;2970/4665;4852;5925;7692/5925;3201/4557;5470/1079', '2285', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1262, '冡衝', '', '外狹而長曰艨衝{冡衝}，以衝突敵船也。', '', '釋船第二十五', '釋名', '', DEFAULT, DEFAULT, '4803/0', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1263, '赤馬', '', '輕疾者曰赤馬，其體正赤，疾如馬也。', '', '釋船第二十五', '釋名', '', DEFAULT, DEFAULT, '6585/6135', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1264, '檻', '', '{檻}上下重板曰艦，四方施板，以禦矢石，其內如牢檻也。', '', '釋船第二十五', '釋名', '', DEFAULT, DEFAULT, '3824', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1265, '斥候/𣟄候', '', '五百斛以上，還有小屋曰斥候[𣟄候]，以視敵進退也。', '', '釋船第二十五', '釋名', '', DEFAULT, DEFAULT, '5967/5078/0/3670/5078', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1266, '𦩍', '貂/𧜣', '三百斛曰𦩍。𦩍，貂[𧜣]也；貂，短也，江南所名短而廣、安不傾危者也。', '', '釋船第二十五', '釋名', '1', DEFAULT, DEFAULT, '5448', '6128/0/5314', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1267, '艇/梃', '挺', '二百斛以下曰艇[梃]，其形徑挺，一人二人所乘行者也。', '', '釋船第二十五', '釋名', '1', DEFAULT, DEFAULT, '5450/0/3578', '7999', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1268, '疾', '', '疾，疾也，客氣中人急疾也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '4694', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1269, '病', '並', '病，並也，並與正氣在膚體中也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4695', '6696', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1270, '疹', '診', '疹，診也，有結氣可得診見也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2657', '1708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1271, '㝌', '久', '疚{㝌}，久也，久在體中也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4612', '3407', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1272, '痛', '通', '痛，通也，通在膚衇中也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4697', '1134', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1273, '蛘', '揚', '癢{蛘}，揚也，其氣在皮中，欲得發揚，使人搔發之而揚岀也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '8892', '7956', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1274, '眩', '縣', '眩，縣也，目視動亂，如縣物搖搖然不定也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2098', '5705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1275, '歷匘', '', '歷匘，匘從耳鼻中出，歷歷然也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '1070/5220', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1276, '秃楬/秃鬜', '瘕/痂', '秃毼{秃楬}[秃鬜]。秃，無髮沐秃也。毼，頭生創曰瘕[痂]，毼亦然也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '5472/3831/0/5472/5758', '4744/0/4743', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1277, '盲', '朚', '盲，茫{朚}也，茫茫無所見也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2199', '4322', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1278, '瞽', '鼓', '瞽，鼓也，瞑瞑然目平合如鼓皮也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2201', '3082', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1279, '矇', '蒙/冡', '矇，有眸子而失明，蒙蒙[冡]無所別也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2196', '649/0/4803', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1280, '瞍', '縮', '瞍，縮也，眸子縮壞也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2202', '8563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1281, '害', '迄', '瞎{害}，迄也，膚幕迄迫也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4614', '1216', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1282, '通視;麗視', '離', '眸子明而不正曰通視，言視通達目匡一方也。又謂之麗視，麗，離也，言一目視天，一目視地，目明分離，所視不同也。', '', '釋疾病第二十六', '釋名', '2', DEFAULT, DEFAULT, '1134/5475;6286/5475', '2293', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1283, '眇', '小', '目匡陷急曰眇。眇，小也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2197', '704', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1284, '𥋚', '末', '目眥傷赤曰䁾，䁾{𥋚}，末也，創在目兩末也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2185', '3564', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1285, '浸', '侵', '目生膚入眸子曰浸。浸，侵也，言侵明也。亦言浸淫轉大也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '7088', '5076', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1286, '聾', '籠', '聾，籠也，如在蒙籠之內，不可察也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '7811', '2956', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1287, '鼽', '久', '鼻塞曰鼽。鼽，久也，涕久不通，遂至窒塞也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2235', '3407', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1288, '齲', '朽', '齲，朽也，蟲齧之齒缺朽也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '1325', '2547', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1289, '瘖', '奄', '瘖，唵{奄}也，唵然無聲也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4722', '6599', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1290, '癭', '嬰', '癭，嬰也，在頸嬰喉下也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4723', '8259', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1291, '癰喉', '', '癰喉，氣著喉中不通，稸成癰也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '4739/786', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1292, '消㵣', '渴', '消㵣，㵣，渴也，腎氣不周於胸胃中，津潤消渴，故欲得水也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '7341/5562', '7343', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1293, '歐', '傴', '嘔{歐}，傴也，將有所吐，脊曲傴也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '5557', '5168', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1294, '欬', '刻', '欬，刻也，氣奔至，出入不平調，若刻物也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '5577', '2764', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1295, '喘', '湍', '喘，湍也；湍，疾也，氣岀入湍疾也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '825', '7184', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1296, '吐', '寫', '吐，瀉{寫}也，故揚 豫以東謂瀉爲吐也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '884', '4598', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1297, '妬', '褚/𢾅', '乳癰曰妬。妬，褚[𢾅]也，氣積褚不通，至腫潰也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '8272', '5360/0/2044', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1298, '疝', '詵/駪', '心痛曰疝。疝，詵[駪]也，氣詵詵然上而痛也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4727', '1481/0/6240', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1299, '痞', '否', '脴{痞}，否也，氣否結也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4773', '934', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1300, '哺露', '', '小兒氣結曰哺露，哺而寒露，乳食不消，生此疾也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '816/7561', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1301, '注病', '', '注病，一人死、一人復得，氣相灌注也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '7275/4695', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1302, '泄利', '渫', '泄利，言其出漏泄[渫]而利也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '7063/2751', '7417', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1303, '滯', '', '下重而赤白曰䐭{滯}，言厲䐭而難差也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '7335', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1304, '隤;疝', '詵', '陰腫曰隤，氣下隤也。又曰疝。疝，詵也，詵詵引小腹急痛也。', '', '釋疾病第二十六', '釋名', '2', DEFAULT, DEFAULT, '9645;4727', '1481', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1305, '疼痹', '', '疼痹，痹氣疼疼然煩也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '0/4752', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1306, '痔', '食', '痔，食也，蟲食之也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4750', '3204', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1307, '酸', '遜', '酸，遜也，遜遁在後也。言腳疼力少，行道在後，似遜遁者也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '9857', '1140', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1308, '消', '削', '消，削也，如見割削，筋力弱也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '7341', '2746', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1309, '懈', '解', '懈，解也，骨節解緩也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '6838', '2850', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1310, '厥逆', '', '厥逆，逆氣從下厥起，上行入心脅也。', '', '釋疾病第二十六', '釋名', '', DEFAULT, DEFAULT, '5984/1124', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1311, '瘧', '虐', '瘧，虐也。凡疾，或寒或熱耳，而此疾先寒後熱，兩疾似酷虐者也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4746', '3114', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1312, '疥', '齘', '疥，齘也，癢搔之，齒䫴齘也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4742', '1283', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1313, '癬', '徙', '癬，徙也，浸淫移徙處日廣也，故靑 徐謂癬爲徙也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4741', '1135', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1314, '胗', '展', '胗，展也，癢搔之，捷展起也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2657', '5405', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1315, '腫', '鍾', '腫，鍾也，寒熱氣所鍾聚也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2662', '9292', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1316, '癰', '邕', '癰，壅{邕}也，氣壅否結裹而潰也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4739', '7487', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1317, '痳', '𠘡', '痳，懍{𠘡}也，小便難，懍懍然也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4749', '7519', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1318, '創', '戕', '創，戕也，戕毀體使傷也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2812', '8388', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1319, '痍', '侈', '痍，侈也，侈開皮膚爲創也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4763', '5131', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1320, '瘢', '曼', '瘢，漫{曼}也，生漫故皮也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4764', '1910', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1321, '痕', '很', '痕，拫{很}也，急相拫引也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4765', '1252', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1322, '癅', '流', '癅，流也，血流聚所生癅腫也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '4735', '7474', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1323, '贅', '屬', '贅，屬也，橫生一肉，屬著體也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '3969', '5427', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1324, '肬', '丘', '肬，丘也，出皮上，聚高如地之有丘也。', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '2660', '5231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1325, '欶', '促', '〔欶，促也，用力急促也。〕', '', '釋疾病第二十六', '釋名', '1', DEFAULT, DEFAULT, '5570', '5162', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1326, '死', '澌', '人始氣絕曰死。死，澌也，就消澌也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '2561', '7338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1327, '不祿', '', '士曰不祿，不復食祿也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '7704/15', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1328, '卒', '', '大夫曰卒，言卒竟也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '5359', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1329, '薨', '', '諸侯曰薨。薨壞之聲也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '2562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1330, '崩', '', '天子曰崩，崩壞之形也。崩，硼聲也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '5894', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1331, '殪', '翳', '殪，翳也，就隱翳也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '2540', '2273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1332, '徂落/殂落', '祚', '徂落[殂落]。徂，祚也，福祚殞落也。徂，亦言往也，往去落也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '1106/549/0/2538/549', '76', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1333, '殺', '竄', '罪人曰殺。殺，竄也，埋竄之，使不復見也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '1978', '4666', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1334, '誅', '株/殊', '罪及餘人曰誅。誅，株[殊]也，如株木根，枝葉盡落也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '1711', '3563/0/2535', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1335, '溺/㲻', '弱', '死於水者曰溺。溺[㲻]，弱也，不能自勝之言也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '7002/0/7296', '5719', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1336, '燒', '燋/焦', '死於火者曰燒。燒，燋[焦]也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '6424', '6443/0/6486', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1337, '兵', '', '戰死曰兵，言死爲兵所傷也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '1768', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1338, '弑', '伺', '下殺上曰弑。弑，伺也，伺閒而後得施也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '1979', '5207', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1339, '縊', '阨', '縣繩曰縊。縊，阨也，阨其頸也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8761', '9664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1340, '雉經', '', '屈頸閉氣曰雉經，如雉之爲也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '2288/8530', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1341, '考竟', '攷', '獄死曰考竟，考[攷]得其情，竟其命於獄也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '5383/1743', '2049', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1342, '棄市', '', '市死曰棄市。市，衆所聚，言與衆人共棄之也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '2493/3323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1343, '斬', '暫', '斫頭曰斬，斫要曰要斬。斬，暫也，暫加兵卽斷也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '9608', '4235', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1344, '轘', '散', '車裂曰轘。轘，散也，肢體分散也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '9607', '2720', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1345, '亯', '', '煮之於鑊曰烹{亯}，若烹禽獸之肉也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '3331', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1346, '掠', '狼', '槌而死曰掠。掠，狼也，用威大暴，如犲狼也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8097', '6377', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1347, '壽終', '久', '老死曰壽終。壽，久也；終，盡也。生巳久遠，氣終盡也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '5382/8588', '3407', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1348, '夭', '', '少壯而死曰夭，如取物中夭折也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '6621', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1349, '殤', '傷', '未二十而死曰殤。殤，傷也，可哀傷也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '2537', '5156', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1350, '考', '槁', '父死曰考。考，成也。亦言槁也，槁於義爲成。凡五材：膠、漆、陶、冶、皮革，乾槁乃成也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '5383', '3604', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1351, '妣', '比', '母死曰妣。妣，比也，比之於父亦然也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8146', '5227', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1352, '物故', '', '漢以來謂死爲物故，言其諸物皆就朽故也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '771/2004', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1353, '尸/屍', '舒', '旣定死曰尸。尸[屍]，舒也，骨節解舒，不復能自勝斂也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '5400/0/5417', '2510', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1354, '襲', '帀', '衣尸曰襲。襲，帀也，以衣周帀，覆衣之也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '5272', '3861', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1355, '冒', '', '以囊韜其形曰冒，覆冒其形，使人勿惡也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '4807', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1356, '絞衿', '交/禁', '巳衣，所以束之曰絞衿。絞，交也，交結之也；衿，禁也，禁繫之也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '6627/0', '6625/0/71', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1357, '琀', '含', '含[琀]，以珠貝含其口中也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '203', '815', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1358, '握', '', '握，以物著尸手中，使握之也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '7873', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1359, '斂', '', '衣尸棺曰斂，斂藏不復見也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '2023', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1360, '棺', '關', '棺，關也，言關閉也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '3827', '7771', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1361, '椁', '郭', '槨{椁}，廓{郭}也，廓落在表之言也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '3830', '4150', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1362, '柩', '究', '尸巳在棺曰柩。柩，究也，送終隨身之制皆究備也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8440', '4671', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1363, '殯;欑', '賓;儹', '於西壁下塗之曰殯。殯，賓也，賓客遇之，言稍遠也。亦曰欑，攢{儹}木於上而塗之也。', '', '釋喪制第二十七', '釋名', '1;2', DEFAULT, DEFAULT, '2542;3747', '3966;5040', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1364, '縗', '摧', '三日不生，生者成服曰縗。縗，摧也，言傷摧也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8746', '7851', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1365, '絰', '實', '絰，實也，傷摧之實也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8747', '4585', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1366, '絞帶', '', '絞帶，絞麻繩爲帶也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '6627/4865', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1367, '斬', '', '三年之縗曰斬，不緝其末，直翦斬而巳。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '9608', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1368, '𪗋', '齊', '期[稘]曰𪗋。𪗋，齊也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '5354', '4393', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1369, '大功', '', '九月曰大功，其布加麤大之功，不善治練之也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '6596/9212', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1370, '小功', '', '五月曰小功，精細之功，小有飾也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '704/9212', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1371, '緦麻', '絲', '三月曰緦麻，緦，絲也，績麻細如絲也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8742/4534', '8780', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1372, '錫縗/緆縗', '易', '錫縗[緆縗]。錫，易也，治其麻，使滑易也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '9264/8746/0/8743/8746', '6132', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1373, '疑縗', '儗', '疑縗。疑，儗也，儗於吉也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '9778/8746', '5117', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1374, '繐縗', '', '繐縗，細疏如繐也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '8744/8746', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1375, '環絰', '', '環絰，末無餘散麻，圓如環也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '108/8747', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1376, '弁絰', '', '弁絰，如爵弁而素加絰也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '5467/8747', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1377, '重', '', '重，死者之資重也。含餘米以爲粥，投之甕而縣之，比葬，未作主權，以重主其神也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '5242', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1378, '葬', '藏/臧', '葬，藏[臧]也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '703', '695/0/1954', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1379, '壙', '曠', '壙，曠也，藏於空曠處也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '9114', '4201', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1380, '輀;柳;鼈甲;牆', '耏/而;聚/僂', '輿棺之車曰輀。輀，耏[而]也，縣於左右前後，銅魚搖絞之屬耏耏然也。其葢曰柳。柳，聚也，衆飾所聚也，亦言其形僂也。亦曰鼈甲，似鼈甲然也。其旁曰牆，似屋牆也。', '', '釋喪制第二十七', '釋名', '1;2', DEFAULT, DEFAULT, '9609;3512;9001/9739;3345', '6075/0/6074;5236/0/5169', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1381, '翣;黼;黻;畫', '箑', '翣，齊人謂扇爲翣[箑]，此似之也，象翣扇爲淸涼也。翣有黼、有黻、有畫，各以其飾名之也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '2274;4944;4945;1943', '2955', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1382, '披', '罷/㗗', '兩旁引之曰披。披，擺{罷}[㗗]也，各於一旁引擺之，備傾倚也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '7945', '4841/0/2567', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1383, '紼', '發', '從前引之曰紼。紼，發也，發車使前也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8757', '8502', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1384, '率', '將/𢪇', '縣下壙曰繂{率}。繂，將[𢪇]也，徐徐將下之也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '8783', '1982/0/7855', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1385, '緘;小要;衽', '函;任', '棺束曰緘。緘，函也，古者棺不釘也。旁際曰小要，其要約小也。又謂之袵。袵{衽}，任也，任制際會，使不解也。', '', '釋喪制第二十七', '釋名', '1;3', DEFAULT, DEFAULT, '8698;704/1784;5266', '4342;5087', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1386, '明器', '', '送死之器曰明器，神明之器，異於人也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '4321/1440', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1387, '塗車', '', '塗車，以泥塗爲車也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '9150/9512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1388, '芻靈', '束', '芻靈，束草爲人馬，以神靈名之也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '611/206', '3897', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1389, '奠;樸奠', '停', '喪祭曰奠。奠，停也，言停久也。亦言樸奠，合體用之也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '3026;3605/3026', '5205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1390, '殷奠', '', '朔望祭曰殷奠，所用殷衆也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '5251/3026', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1391, '虞', '娛', '旣葬，還祭於殯宮曰虞，謂虞[娛]樂安神，使還此也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '3109', '8233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1392, '卒哭', '', '又祭曰卒哭。卒，止也，止孝子無時之哭，朝夕而巳也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '5359/977', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1393, '祔祭', '', '又祭曰祔祭，祭於祖廟以後死，孫祔於祖也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '35/30', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1394, '小祥', '', '期而小祥，亦祭名也，孝子除首絰、服練冠也。祥，善也，加小善之飾也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '704/18', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1395, '大祥', '', '又期而大祥，亦祭名也，孝子除縗服，服朝服、縞冠，加大善之飾也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '6596/18', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1396, '禫', '澹', '閒月而禫，亦祭名也，孝子之意澹然，哀思益衰也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '72', '7211', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1397, '冢', '腫', '冢，腫也，象山頂之高腫起也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '5813', '2662', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1398, '墓', '慕', '墓，慕也，孝子思慕之處也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '9140', '6791', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1399, '丘;陵', '', '丘，象丘形也。陵亦然也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '5231;9618', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1400, '殔', '翳', '假葬於道側曰肂。肂{殔}，翳也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '2543', '2273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1401, '渴', '㵣', '日月未滿而葬曰渴，言渴[㵣]欲速葬，無恩也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '7343', '5562', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1402, '慢', '', '過時而不葬曰慢，謂慢傲不念早安神也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '6836', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1403, '薶', '晦', '葬不如禮曰埋。埋{薶}，痗{晦}也，趨使痗腐而巳也。', '', '釋喪制第二十七', '釋名', '1', DEFAULT, DEFAULT, '626', '4225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1404, '棄', '', '不得埋之曰棄，謂棄之於野也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '2493', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1405, '捐', '', '不得其尸曰捐，捐於他境也。', '', '釋喪制第二十七', '釋名', '', DEFAULT, DEFAULT, '8083', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1406, '律;呂;六同;六閒', '率/述;旅', '律呂。律，率也，所以率氣令生也。亦言述也，述氣者也。呂，旅也，旅陽宣氣也。六律爲陽，六呂爲陰，凡十有二。陽足以包陰，則單言六律。陽足以統陰，則呂亦稱律，總言十二律。六呂，亦曰六同，助陽宣氣，與同功也。亦曰六閒，在陽律之閒也。', '', '釋名佚文一：釋律呂', '釋名', '1;2', DEFAULT, DEFAULT, '1257;4630;9726/4801;9726/7760', '8783/0/1107;4309', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1407, '黃鐘', '種/踵/動', '黃鐘，十一月之律，子之氣也。鐘，種也，陽氣施種於黃泉，孳萌萬物，爲六氣元也。一曰鐘，踵也，陽氣踵黃泉而出也。一曰鐘，動也，言陽氣動於黃泉之下，養萬物也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '9201/9386', '4420/0/1358/0/9231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1408, '大呂', '旅/歫', '大呂，十二月之律，丑之氣也。呂，旅也，言陰大旅助黃鐘，宣氣而牙物也。一曰呂，拒{歫}也，言陽氣欲出，陰不許，旅抑拒難之也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '6596/4630', '4309/0/1068', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1409, '太蔟', '奏/湊', '太蔟，正月之律，寅之氣也，言萬物蔟生也，故曰泰蔟。一曰蔟，奏也，言陽氣大奏地而達物也。一曰太，大也。蔟，湊也，言萬物始大，湊地而出也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '6596/619', '6659/0/7293', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1410, '夾鐘', '甲/種', '夾鐘，二月之律，卯之氣也，言陰夾助太簇，宣四方之氣，而出種物也。一曰言陰陽相夾廁也：一曰夾，孚甲也，言萬物孚甲，種類分也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '6598/9386', '9739/0/4420', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1411, '姑洗', '故/鮮', '姑洗，三月之律，辰之氣也。姑，故也。洗，鮮也，言萬物皆去故就新，莫不鮮明也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '8144/7413', '2004/0/7646', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1412, '仲呂', '旅/中', '仲呂，四月之律，巳之氣也。言微陰始起，未成箸，于其中旅助姑洗，宣氣齊物也。仲之言中也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '4966/4630', '4309/0/231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1413, '蕤賓', '垂/儐', '蕤賓，五月之律，午之氣也。蕤，垂也。賓，儐也，言陰氣始起，陽氣自上下垂，儐導之也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '507/3966', '9147/0/5030', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1414, '林鐘', '衆/種', '林鐘，六月之律，未之氣也。林，衆也，言萬物成孰，種類衆多也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '3846/9386', '5235/0/4420', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1415, '夷則', '痍/賊', '夷則，七月之律，申之氣也。夷，痍也。痍，傷也。則，賊也，言萬物傷痍，爲陰氣賊害也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '6613/2755', '4763/0/8380', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1416, '南呂', '任/旅', '南呂，八月之律，酉之氣也。南，任也，言陰氣旅助夷則，任成萬物也。一曰言陽氣尚有任生薺麥也，故陰拒之也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '3873/4630', '5087/0/4309', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1417, '無射', '斁', '無射，九月之律，戌之氣也。射，斁也。斁，厭也。言陽氣究物，而使陰氣畢剥落之，終而復始，無厭巳也。一曰斁，終也，言萬物隨陽而終，當復隨陰而起，無有終巳也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '8414/3308', '2031', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1418, '應鐘', '種/動', '應鐘，十月之律，亥之氣也。言陰氣應無射，該藏萬物，而雜陽閡種也。一曰應鐘者，陽氣之應，不用事也。一曰應者，應也。鐘者，動也。言萬物應陽而動，下藏也。', '', '釋名佚文一：釋律呂', '釋名', '1', DEFAULT, DEFAULT, '6711/9386', '4420/0/9231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1419, '五聲;五音', '飲', '五聲。聲者，鳴也。亦曰五音。音者，飲也，言其剛柔清濁和而相飲也。', '', '釋名佚文二：釋五聲', '釋名', '2', DEFAULT, DEFAULT, '9725/7808;9725/1738', '5589', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1420, '宮', '中/𧀄/倡/綱/容/含', '宮，中也，居中央，暢{𧀄}1四方，倡始施生，爲四聲綱也。一曰宮，容也、含也，含容四時者也。', '[郭注-1]：亦作鬯', '釋名佚文二：釋五聲', '釋名', '1', DEFAULT, DEFAULT, '4628', '231/0/523/0/5138/0/8671/0/4587/0/815', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1421, '商', '章/張', '商，章也，物成孰可章度也。一曰商者，張也，陰氣開張，陽氣始降也。', '', '釋名佚文二：釋五聲', '釋名', '1', DEFAULT, DEFAULT, '1453', '1742/0/8486', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1422, '角', '觸/躍', '角，觸也，物觸地而出，戴芒角也。一曰角者，躍也，陽氣動躍。', '', '釋名佚文二：釋五聲', '釋名', '1', DEFAULT, DEFAULT, '2826', '2839/0/1348', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1423, '徵', '祉/止', '徵，祉也，物盛大而繁祉也。一曰徵者，止也，陽氣止。', '', '釋名佚文二：釋五聲', '釋名', '1', DEFAULT, DEFAULT, '5239', '19/0/1064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1424, '䨒', '宇/紆', '䨒，宇也，物聚藏，宇覆之也。一曰䨒者，紆也，陰氣在上、陽氣在下紆回也。', '', '釋名佚文二：釋五聲', '釋名', '1', DEFAULT, DEFAULT, '7570', '4564/0/8556', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1425, '三公', '貢', '三公。公，貢也，才德兼于人，人咸貢薦于王而用之也。韋辯之曰：公猶取正直無厶也，故公字从八厶。', '', '釋名佚文三：釋爵位', '釋名', '1', DEFAULT, DEFAULT, '77/716', '3946', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1426, '尚書;尚食;尚方', '上', '尚書。尚，上也，言最在上總領之也。韋辯之曰：尚猶奉也，百官言事，當省案平處奉之，故曰尚書。尚食、尚方亦然。', '', '釋名佚文三：釋爵位', '釋名', '1', DEFAULT, DEFAULT, '711/1942;711/3204;711/5453', '6', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1427, '御史中丞', '', '御史中丞，居中丞相者也。韋辯之曰：此中丞自御史大夫下，氶有二，其一別居殿中，舉不法，故曰中丞。', '', '釋名佚文三：釋爵位', '釋名', '', DEFAULT, DEFAULT, '1258/1932/231/1757', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1428, '卿', '慶/章', '卿，慶也，言萬國皆慶賴之也。又卿，章也，言貴盛章著也。', '', '釋名佚文三：釋爵位', '釋名', '1', DEFAULT, DEFAULT, '5795', '6755/0/1742', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1429, '太常;太僕;衛尉;光禄勳;宗正;執金吾;大司農;少府;大鴻臚;廷尉;大長秋;將作大匠', '', '漢置十二卿：一曰太常，二曰太僕，三曰衛尉，四日光禄勳，五曰宗正，六曰執金吾，七曰大司農，八曰少府，九曰大鴻臚，十曰廷尉，十一日大長秋，十二曰將作大匠。韋辯之曰：漢正卿九：一曰太常，二曰光禄勳，三曰衛尉，四曰太僕，五曰廷尉，六曰鴻臚，七曰宗正，八曰司農，九曰少府，是爲九卿。執金吾本中尉，掌徼循宮外，司執姦衺。至武帝，更名執金吾，爲外卿，不見九卿之列也。長秋本自皇后官，非天子卿。長秋主宮中，凡物次，春生秋成，欲使中宮之胙如之，故爲名辨皇后陰宮。秋者除之始，長者欲其久也。', '', '釋名佚文三：釋爵位', '釋名', '', DEFAULT, DEFAULT, '6596/4870;6596/1753;1277/6470;6509/15/9211;4622/1087;6646/9259/837;6596/5771/1786;705/5922;6596/2414/2598;1261/6470;6596/6067/4491;1982/5073/6596/8424', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1430, '大鴻臚', '敘', '大鴻臚，腹前肥者曰臚，此主王侯及蕃國言，以京師爲心體，王侯外國爲腹臚，以養之也。韋辯之曰：其職本故典客，掌賓禮，武帝時更爲鴻臚。鸿，大也。臚，敘也，欲大以禮敶敘賓客也。', '', '釋名佚文三：釋爵位', '釋名', '1', DEFAULT, DEFAULT, '6596/2414/2598', '2064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1431, '平準令', '', '平準令主染色，色有常平之法準的之也。韋辯之曰：其職主平物賈，使相依準。', '', '釋名佚文三：釋爵位', '釋名', '', DEFAULT, DEFAULT, '3073/7351/5777', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1432, '祭酒', '', '祭酒者，謂祭六神以酒餟之也。', '', '釋名佚文三：釋爵位', '釋名', '', DEFAULT, DEFAULT, '30/9805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1433, '驃騎;車騎', '', '驃騎將軍、車騎將軍，秩皆比三公。韋辯之曰：大將軍，位在三公之上，昭帝時，霍光爲大將軍，猶在丞相下。漢时，大將軍貴戚爲之，或録尚書事。驃騎、車騎，此二將軍秩本二千石。', '', '釋名佚文三：釋爵位', '釋名', '', DEFAULT, DEFAULT, '6154/6185;9512/6185', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1434, '奉車都尉', '', '奉車都尉，奉天子乘輿。', '', '釋名佚文三：釋爵位', '釋名', '', DEFAULT, DEFAULT, '1756/9512/4005/6470', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1435, '長水校尉', '中', '長水校尉，主水戰用船之事。韋辯之曰：其職典胡騎，不主水戰也，廐近長水，故以爲名。長水，盖中小水名。', '', '釋名佚文三：釋爵位', '釋名', '1', DEFAULT, DEFAULT, '6067/6982/3790/6470', '231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1436, '升;布', '', '祭雨曰升，祭星曰布，升取其氣之升也，布取其象之布也。', '', '釋名佚文四：其他', '釋名', '', DEFAULT, DEFAULT, '9505;4910', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1437, '蘆葦;狄/翟;蘆筍', '', '大曰蘆葦。荻{狄}[翟]，蘆筍。', '', '釋名佚文四：其他', '釋名', '', DEFAULT, DEFAULT, '277/645;6370/0/2244;277/2872', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1438, '謚', '', '{謚}古者諸侯薨時，天子論行以賜諡，唯王者無上，故於南郊稱天以諡之。當春秋時，周室卑微，臣諡其君，子諡其父，故諸侯之諡多不以實也。', '', '釋名佚文四：其他', '釋名', '', DEFAULT, DEFAULT, '1680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1439, '臣', '慎', '臣，慎也，慎于其事，以奉上也。', '', '附韋昭釋名：官職訓', '釋名', '1', DEFAULT, DEFAULT, '1952', '6712', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1440, '師;先生', '', '古者稱師曰先生。', '', '附韋昭釋名：官職訓', '釋名', '', DEFAULT, DEFAULT, '3862;5470/3874', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1441, '大司馬', '武', '大司馬，馬，武也，大總武事也。大司馬掌軍。古者兵車，一車四馬，故以馬名官。', '', '附韋昭辯釋名', '釋名', '1', DEFAULT, DEFAULT, '6596/5771/6135', '8395', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1442, '六卿', '', '六卿分掌，諸官卿孤不掌，傑然特立也。', '', '附韋昭辯釋名', '釋名', '', DEFAULT, DEFAULT, '9726/5795', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1443, '廷尉;縣尉', '罰', '廷尉、縣尉，皆古官也，以尉尉民心也。凡掌賊及司察之官皆曰尉。尉，罰也，言以辠罰姦非也。', '', '附韋昭辯釋名', '釋名', '2', DEFAULT, DEFAULT, '1261/6470;5705/6470', '2797', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1444, '祭酒', '', '祭酒，凡會同饗燕，必尊長先用，先用，必以酒祭先，故曰祭酒。漢時，吳王年長，以爲劉氏祭酒是也。', '', '附韋昭辯釋名', '釋名', '', DEFAULT, DEFAULT, '30/9805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1445, '奉車都尉', '', '奉車都尉，主乘舆。乘輿尊，不敢言主，故言奉。', '', '附韋昭辯釋名', '釋名', '', DEFAULT, DEFAULT, '1756/9512/4005/6470', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1446, '太中大夫', '', '太中大夫，在中最爲高大也。', '', '附韋昭辯釋名', '釋名', '', DEFAULT, DEFAULT, '6596/231/6596/6674', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1447, '郡尉', '', '郡尉，羅姦非。', '', '附韋昭辯釋名', '釋名', '', DEFAULT, DEFAULT, '4004/6470', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1448, '督郵', '', '督郵，主諸縣罰，以負督郵殷糾攝之也。', '', '附韋昭辯釋名', '釋名', '', DEFAULT, DEFAULT, '2171/4012', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1449, '功曹', '', '功曹。曹，羣也。功曹，吏所羣聚。戸曹，民所羣聚也。其他皆然。', '', '附韋昭辯釋名', '釋名', '', DEFAULT, DEFAULT, '9212/3050', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1450, '主薄', '普', '主薄，主諸薄書。薄，普關諸事。', '', '附韋昭辯釋名', '釋名', '1', DEFAULT, DEFAULT, '3183/561', '4256', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1451, '吏', '職', '門下之吏，當作三綱，幼未有用，從容在職也。', '', '附韋昭辯釋名', '釋名', '1', DEFAULT, DEFAULT, '5', '7805', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1452, '五百', '伍/佰', '五百，字本爲伍佰。伍，當也。佰，道也，使之導引當道佰中，以驅除也。今俗呼行杖人爲五百。', '', '附韋昭辯釋名', '釋名', '1', DEFAULT, DEFAULT, '9725/2231', '5066/0/5068', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1453, '黨;曉;哲;知', '昌/睹', '黨[昌/睹]、曉、哲，知也。楚謂之黨，1或曰曉。齊 宋之閒謂之哲。', '[郭注-1]：黨朗也，解寤貌。', '卷一', '方言', '1', DEFAULT, DEFAULT, '6562;4195;838;3315', '4237/0/2140', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1454, '虔;𠐛;慧;謾;貍;倢;隋;黠;鬼', '丯;疌;詑', '虔[丯]、𠐛1，慧也。2秦謂之謾3。45晉謂之㦟{貍}6。宋 楚之閒謂之倢[疌]。7楚或謂之䜏{隋}[詑]8。9自關而東趙 魏之閒謂之黠，或謂之鬼。10', '[音義-1]：音翾\n[郭注-2]：謂慧了。\n[音義-3]：莫錢反，又亡山反\n[郭注-4]：言詑謾也。\n[音義-5]：詑，大和反。\n[音義-6]：音悝，或莫佳反\n[郭注-7]：言便倢也。\n[音義-8]：他和反\n[郭注-9]：亦今通語。\n[郭注-10]：言鬼衇也。', '卷一', '方言', '1;6;7', DEFAULT, DEFAULT, '3111;4972;6733;1598;6124;5050;2670;6559;5819', '2817;1074;1597', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1455, '娥;嬴;好;媌;姣;姝;丰;妍', '娃', '娥、㜲{嬴}[娃]1，好也。秦曰娥。2宋 魏之閒謂之㜲。3秦 晉之閒凡好而輕者謂之娥。自關而東河 濟之閒謂之媌4，5或謂之姣6。7趙 魏 燕 代之閒曰姝8，9或曰妦{丰}10。11自關而西秦 晉之故都曰妍12。13好，其通語也。', '[音義-1]：音盈\n[郭注-2]：言娥娥也。\n[郭注-3]：言㜲㜲也。\n[音義-4]：莫交反\n[郭注-5]：今關西人呼好爲媌。\n[音義-6]：音狡\n[郭注-7]：言姣潔也。\n[音義-8]：昌朱反，又音株\n[郭注-9]：亦四方通語。\n[音義-10]：音蜂\n[郭注-11]：言妦容也。\n[音義-12]：五千反，一作忓\n[郭注-13]：秦舊都，今扶風 雍丘也。晉舊都，今太原 晉陽縣也。其俗通呼好爲妍。', '卷一', '方言', '2', DEFAULT, DEFAULT, '8166;8114;8189;8196;8193;8188;3875;8297', '8298', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1456, '烈;𣡌;餘;肄', '', '烈、枿{𣡌}1，餘也。2陳 鄭之閒曰枿。晉 衞之閒曰烈。秦 晉之閒曰肄3，4或曰烈。', '[音義-1]：五割反\n[郭注-2]：謂㱚餘也。\n[音義-3]：音謚\n[郭注-4]：傳曰：「夏肄是屏。」', '卷一', '方言', '', DEFAULT, DEFAULT, '6425;3802;3245;1937', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1457, '台;胎;陶;鞠;養;艾', '宧;匋;𡫬', '台[宧]1、胎、陶[匋]、鞠[𡫬]，養也。晉 衞 燕 魏曰台。2陳 楚 韓 鄭之閒曰鞠，秦或曰陶。汝 潁 梁 宋之閒曰胎，或曰艾。3', '[音義-1]：音怡\n[郭注-2]：台猶頤也。\n[郭注-3]：爾雅云：「艾，養也。」', '卷一', '方言', '1;3;4', DEFAULT, DEFAULT, '868;2596;9687;1809;3219;389', '4560;3287;4616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1458, '憮;㛪;憐;牟;愛', '𢜮;㤅', '憮1、㤿{㛪}2、憐、牟[𢜮]，愛[㤅]也。韓 鄭曰憮。晉 衞曰㤿。3汝 潁之閒曰憐。宋 魯之閒曰牟，或曰憐。憐，通語也。', '[音義-1]：亡輔反\n[音義-2]：音淹\n[郭注-3]：㤿憸，多意氣也。', '卷一', '方言', '4;5', DEFAULT, DEFAULT, '6778;8333;6960;751;3367', '6785;6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1459, '夌;憮;矜;悼;憐;哀', '憐;憐', '㥄{夌}[憐]1、憮、矜[憐]、悼、憐，哀也。齊 魯之閒曰矜。陳 楚之閒曰悼。趙 魏 燕 代之閒曰㥄。2自楚之北郊曰憮。秦 晉之閒或曰矜，或曰悼。', '[音義-1]：音陵\n[郭注-2]：㥄亦憐耳。', '卷一', '方言', '1;3', DEFAULT, DEFAULT, '3364;6778;9510;6941;6960;936', '6960;6960', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1460, '咺;唏;勺;怛;痛;唴;唴良;噭咷;喑;惄', '咷', '咺1、唏2、㣿{勺}[咷]3、怛，痛也。凡哀泣而不止曰咺，哀而不泣曰唏。於方則楚言哀曰唏。燕之外鄙、4朝鮮 洌水之閒5，6少兒泣而不止曰咺。7自關而西秦 晉之閒凡大人少兒泣而不止謂之唴8，哭極音絶亦謂之唴。平原謂啼極無聲謂之唴哴{唴良}9。10楚謂之噭咷11。齊 宋之閒謂之喑12，或謂之惄13。', '[音義-1]：香遠反\n[音義-2]：虛几反\n[音義-3]：音的，一音灼\n[郭注-4]：鄙，邊邑名。\n[音義-5]：音烈\n[郭注-6]：朝鮮，今樂浪郡是也。洌水，在遼東。\n[郭注-7]：少兒，猶言小兒。\n[音義-8]：丘尙反\n[音義-9]：音亮\n[郭注-10]：今關西語亦然。\n[音義-11]：叫、逃兩音，字或作叴，音求\n[音義-12]：音蔭\n[音義-13]：奴歷反', '卷一', '方言', '3', DEFAULT, DEFAULT, '796;850;9465;6898;4697;797;797/3339;781/798;799;6803', '798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1461, '悼;惄;悴;憖;傷', '', '悼、惄、悴、憖1，傷也。自關而東汝 潁 陳 楚之閒通語也。汝謂之惄。秦謂之悼。宋謂之悴。楚 潁之閒謂之憖。2', '[音義-1]：魚吝反\n[郭注-2]：詩曰：「不憖遺一老。」亦恨傷之言也。', '卷一', '方言', '', DEFAULT, DEFAULT, '6941;6803;6928;6751;5156', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1462, '愼;濟;憯;惄;濕;桓;憂', '疾;咺', '愼、濟[疾]、䁮{憯}1、惄、濕、桓[咺]，憂也。宋 衞或謂之愼，或曰䁮。2陳 楚或曰濕，或曰濟。自關而西秦 晉之閒或曰惄，或曰濕。自關而西秦 晉之閒凡志而不得、欲而不獲、高而有墜、得而中亡謂之濕，34或謂之惄。', '[音義-1]：作念反\n[郭注-2]：䁮者，憂而不動也。\n[郭注-3]：濕者，失意潛沮之名。\n[音義-4]：沮，一作阻。', '卷一', '方言', '2;6', DEFAULT, DEFAULT, '6712;7093;6899;6803;7071;3671;3366', '4694;796', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1463, '鬱悠;懷;惄;惟;慮;願;念;靖;愼;思', '', '鬱悠、懷、惄、惟、慮、願、念、靖、愼，思也。晉 宋 衞 魯之閒謂之鬱悠。1惟，凡思也。慮，謀思也。願，欲思也。念，常思也。東齊 海 岱之閒曰靖。2秦 晉或曰愼。凡思之貌亦曰愼，3或曰惄。', '[郭注-1]：鬱悠，猶鬱陶也。\n[郭注-2]：岱，太山。\n[郭注-3]：謂感思者之容。', '卷一', '方言', '', DEFAULT, DEFAULT, '3848/6927;6762;6803;6761;6702;5633;6719;6684;6712;6701', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1464, '敦;豐;厖;𡗦;幠;般;嘏;奕;戎;京;奘;將;大;夏', '惇;𣞤;鞶;濃;壯;嘏', '敦[惇]、豐、厖1、𡗦2、幠[𣞤]3、般[鞶]4、嘏5、奕、戎[濃]、京、奘6、將[壯]，大也。凡物之大貌曰豐。厖，深之大也。東齊 海 岱之閒曰𡗦，或曰幠。宋 魯 陳 衞之閒謂之嘏，或曰戎。秦 晉之閒凡物壯大謂之嘏，或曰夏[嘏]。秦 晉之閒凡人之大謂之奘，或謂之壯。燕之北鄙、齊 楚之郊或曰京，或曰將。皆古今語也。7初別國不相往來之言也，今或同，而舊書雅記故俗語不失其方，8而後人不知，故爲之作釋也。9', '[音義-1]：音「鶜鴟，𪁪」\n[音義-2]：音介\n[音義-3]：海狐反\n[音義-4]：音般桓\n[音義-5]：音賈\n[音義-6]：在朗反\n[郭注-7]：語聲轉耳。\n[郭注-8]：皆本其言之所出也。雅，爾雅也。\n[郭注-9]：釋詁、釋言之屬。', '卷一', '方言', '1;5;6;9;12;14', DEFAULT, DEFAULT, '2038;3103;5995;6608;4898;5446;1462;6667;8377;3329;6668;1982;6596;3371', '6727;3847;1799;7331;228;1462', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1465, '假;格;懷;摧;詹;戾;艐;至', '徦;瞻', '假[徦]1、𢓜{格}2、懷、摧、詹[瞻]、戾、艐3，至也。邠 唐 冀 兗之閒曰假，4或曰𢓜。齊 楚之會郊，5或曰懷。摧、詹、戾，楚語也。6艐，宋語也。皆古雅之別語也，7今則或同。', '[音義-1]：音駕\n[音義-2]：古格字\n[郭注-3]：古屆字\n[郭注-4]：邠，今在始平 漆縣。唐，今在太原 晉陽縣。\n[郭注-5]：兩境之閒。\n[郭注-6]：詩曰：「先祖于摧」「六日不詹」「魯侯戾止」之謂也。此亦方國之語，不專在楚也。\n[郭注-7]：雅謂風雅。', '卷一', '方言', '1;5', DEFAULT, DEFAULT, '5074;3601;6762;7851;713;6354;5443;7706', '1248;2156', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1466, '嫁;逝;徂;適;往', '', '嫁、逝、徂、適，往也。自家而出謂之嫁，由女而出爲嫁也。逝，秦 晉語也。徂，齊語也。適，宋 魯語也。往，凡語也。', '', '卷一', '方言', '', DEFAULT, DEFAULT, '8125;1105;1106;1109;1229', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1467, '謾台;脅鬩;懼;嘽咺', '憚吅', '謾台1、脅鬩2，懼也。燕 代之閒曰謾台，齊 楚之閒曰脅鬩。宋 衞之閒凡怒而噎噫3，4謂之脅鬩。5南楚 江 湘之閒謂之嘽咺[憚吅]6。7', '[音義-1]：蠻、怡兩音\n[音義-2]：呼隔反\n[音義-3]：央媚反\n[郭注-4]：謂噎憂也。\n[郭注-5]：脅鬩猶𤄎沭也。\n[音義-6]：香遠反\n[郭注-7]：湘，水名，今在零陵。', '卷一', '方言', '4', DEFAULT, DEFAULT, '1598/868;2617/1899;6772;821/796', '6940/971', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1468, '虔;劉;慘;惏;殺;殘;貪;歁', '丯', '虔[丯]、劉、慘、㨆{惏}1，2殺也。秦 晉 宋 衞之閒謂殺曰劉，晉之北鄙亦曰劉。秦 晉之北鄙、燕之北郊、翟縣之郊謂賊爲虔。3晉 魏 河內之北謂㨆曰殘，楚謂之貪，南楚 江 湘之閒謂之歁。4', '[音義-1]：音廩，或洛感反\n[郭注-2]：今關西人呼打爲㨆。\n[郭注-3]：今上黨 潞縣卽古翟國。\n[郭注-4]：言歁㨆難猒也。', '卷一', '方言', '1', DEFAULT, DEFAULT, '3111;9444;6900;6859;1978;2550;3982;5571', '2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1469, '亟;憐;憮;㛪;愛', '㥛;㤅', '亟[㥛]1、憐、憮、㤿{㛪}，愛[㤅]也。東齊 海 岱之閒曰亟。自關而西秦 晉之閒凡相敬愛謂之亟。陳 楚 江 淮之閒曰憐。宋 衞 邠 陶之閒曰憮，或曰㤿。2', '[音義-1]：音詐欺\n[郭注-2]：陶唐，晉都處。', '卷一', '方言', '1;5', DEFAULT, DEFAULT, '9014;6960;6778;8333;3367', '6812;6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1470, '眉;棃;耊;老;耇鮐', '魅', '眉[魅]、棃、耊1、鮐，老也。東齊曰眉。2燕 代之北鄙曰棃。3宋 衞 兗 豫之內曰耊。4秦 晉之郊、陳 兗之會曰耇鮐5。6', '[音義-1]：音絰\n[郭注-2]：言秀眉也。\n[郭注-3]：言面色似凍棃。\n[郭注-4]：八十爲耊。\n[音義-5]：音垢\n[郭注-6]：言背皮如鮐魚。', '卷一', '方言', '1', DEFAULT, DEFAULT, '2218;3416;5376;5375;5379/7650', '5826', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1471, '脩;駿;融;繹;尋;延;長;充;永', '攸;峻', '脩[攸]、駿[峻]、融、繹、尋、延，長也。陳 楚之閒曰脩。海 岱 大野之閒曰尋。1宋 衞 荆 吳之閒曰融。自關而西秦 晉 梁 益之閒凡物長謂之尋。周官之法，度廣爲尋，2幅廣爲充。3延、永，長也。凡施於年者謂之延，施於衆長謂之永。4', '[郭注-1]：大野，今高平 鉅野。\n[郭注-2]：度謂絹帛橫廣。\n[郭注-3]：爾雅曰：「緇廣充幅。」\n[郭注-4]：各隨事爲義。', '卷一', '方言', '1;2', DEFAULT, DEFAULT, '2685;6168;1862;8518;1983;1265;6067;5460;7495', '2033;5876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1472, '允;訦;恂;展;諒;穆;信', '眞;睦', '允、訦1、恂2、展[眞]、諒3、穆[睦]，信也。齊 魯之閒曰允。燕 代 東齊曰訦。宋 衞 汝 潁之閒曰恂。荆 吳 淮 汭4之閒曰展。5西甌6毒屋 黃石野之閒曰穆。7衆信曰諒，周南 召南 衞之語也。', '[音義-1]：音諶\n[音義-2]：音荀\n[音義-3]：音亮\n[音義-4]：音芮\n[郭注-5]：汭，水口也。\n[音義-6]：音嘔\n[郭注-7]：西甌，駱越別種也。其餘皆未詳所在。', '卷一', '方言', '4;6', DEFAULT, DEFAULT, '5458;1524;6759;5405;1480;4428;1523', '5213;2155', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1473, '碩;沈;巨;濯;訏;敦;夏;于;大;寇;夥;過;僉;劒;弩;嘏;郴', '甚;鉅;卓;惇;嘏;僉;怒;林', '碩、沈[甚]、巨[鉅]、濯[卓]、訏1、敦[惇]、夏[嘏]、于，大也。齊 宋之閒曰巨、曰碩。凡物盛多謂之寇。2齊 宋之郊、楚 魏之際曰夥3。自關而西秦 晉之閒凡人語而過謂之𨘌{過}4，或曰僉。東齊謂之劒[僉]，或謂之弩[怒]，弩猶怒也。陳 鄭之閒曰敦。荆 吳 揚 甌之郊曰濯。中齊 西楚之閒曰訏。5自關而西秦 晉之閒凡物之壯大者而愛偉之謂之夏。周 鄭之閒謂之嘏6。郴[林]7，齊語也。于，通語也。', '[音義-1]：亦作芋，音義同耳。香于反\n[郭注-2]：今江東有小鳬，其多無數，俗謂之寇鳧。\n[音義-3]：音禍\n[音義-4]：于果反\n[郭注-5]：西楚，謂今汝南 彭城。\n[音義-6]：音賈\n[音義-7]：洛含反', '卷一', '方言', '2;3;4;6;7;14;15;17', DEFAULT, DEFAULT, '5628;7318;3032;7419;1670;2038;3371;3069;6596;2042;4335;1110;3270;2813;8497;1462;4111', '3041;9446;5222;6727;1462;3270;6877;3846', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1474, '抵;致;會', '', '抵1、𠊷{致}2，會也。雍 梁之閒曰抵，秦 晉亦曰抵。凡會物謂之𠊷。', '[音義-1]：音觸牴\n[音義-2]：音致', '卷一', '方言', '', DEFAULT, DEFAULT, '7850;3365;3274', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1475, '華;𠌶;晠', '', '華、荂{𠌶}1，晠也。齊 楚之閒或謂之華，或謂之荂。2', '[音義-1]：音誇\n[郭注-2]：荂亦華別名。', '卷一', '方言', '', DEFAULT, DEFAULT, '3884;3882;4262', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1476, '墳;大', '', '墳，地大也。靑 幽之閒凡土而高且大者謂之墳。1', '[郭注-1]：卽大陵也。', '卷一', '方言', '', DEFAULT, DEFAULT, '9141;6596', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1477, '𩫖;摸', '', '張小使大謂之廓{𩫖}，陳 楚之閒謂之摸1。', '[音義-1]：音莫', '卷一', '方言', '', DEFAULT, DEFAULT, '3327;8024', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1478, '𡣱;蟬;閻;撚;未;續', '繯;嬗;𢪈', '𡣱[繯]1、蟬[嬗]、䌪{閻}[𢪈]2、撚3、未，續也。楚曰𡣱。蟬，出也。4楚曰蟬，或曰未及也。', '[音義-1]：火全反\n[音義-2]：音剡\n[音義-3]：諾典反\n[郭注-4]：別異義。', '卷一', '方言', '1;2;3', DEFAULT, DEFAULT, '8211;8867;7740;8069;9799;8548', '8573;8251;7867', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1479, '𨃚;䠛;𧿳;跳;折;蹠', '𧼪', '𨃚1、䠛2、𧿳3，跳也。楚曰䟷{折}[𧼪]4。5陳 鄭之閒曰䠛。楚曰蹠。自關而西秦 晉之閒曰跳，或曰𨃚。', '[音義-1]：古蹋字，他匣切\n[音義-2]：音逍遙\n[音義-3]：音拂\n[音義-4]：勑厲反\n[郭注-5]：亦中州語。', '卷一', '方言', '5', DEFAULT, DEFAULT, '1375;1376;1373;1370;628;1374', '1036', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1480, '躡;郅;跂;格;躋;䟑;登', '𤴘;陟;屐', '躡[𤴘]、郅[陟]1、跂[屐]2、𢓜{格}、躋3、䠯{䟑}4，登也。自關而西秦 晉之閒曰躡。東 齊 海 岱之閒謂之躋。魯 衞曰郅。梁 益之閒曰𢓜，5或曰跂。', '[音義-1]：音質\n[音義-2]：音企\n[音義-3]：音濟渡\n[音義-4]：音踊躍\n[郭注-5]：𢓜亦訓來。', '卷一', '方言', '1;2;3', DEFAULT, DEFAULT, '1352;4071;1394;3601;1347;1034;1079', '1075;9641;5435', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1481, '逢;逆;迎', '', '逢、逆，迎也。自關而東曰逆，自關而西或曰迎，或曰逢。', '', '卷一', '方言', '', DEFAULT, DEFAULT, '1130;1124;1125', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1482, '尋;搴;摭;挻;取;籑', '篡', '撏{尋}1、攓{搴}2、摭3、挻4，取也。南楚曰攓。陳 宋之閒曰摭。衞 魯 揚 徐 荆 衡之郊曰撏。5自關而西秦 晉之閒凡取物而逆謂之籑[篡]6。楚部或謂之挻。', '[音義-1]：常含反\n[音義-2]：音蹇，一音騫\n[音義-3]：音盜蹠\n[音義-4]：音羊羶\n[郭注-5]：衡，衡山，南岳名，今在長沙。\n[音義-6]：音饌', '卷一', '方言', '6', DEFAULT, DEFAULT, '1983;8000;7984;7900;1925;3218', '5844', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1483, '餥;飵;食;餐;䬯;䭡䭓', '', '餥1、飵2，食也。陳 楚之內相謁而食麥饘3謂之餥，4楚曰飵。凡陳 楚之郊、南楚之外相謁而飱{餐}，5或曰飵，或曰䬯6。秦 晉之際、河陰之閒曰䭡䭓7，8此秦語也。9', '[音義-1]：音非\n[音義-2]：音昨\n[音義-3]：音旃\n[郭注-4]：謁，請也。饘，糜也。\n[郭注-5]：晝飯爲飱。\n[音義-6]：音黏\n[音義-7]：惡恨反。五恨反\n[郭注-8]：今馮翊、郃陽、河東、龍門是其處也。\n[郭注-9]：今關西人呼食欲飽爲䭡䭓。', '卷一', '方言', '', DEFAULT, DEFAULT, '3216;3235;3204;3227;3236;3237/3238', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1484, '勉;薄努;勉努;愐釗;勖茲', '迫努;愐劭;勖孜', '釗1、薄，勉也。2秦 晉曰釗，或曰薄，故其鄙語曰薄努[迫努]，猶勉努也。3南楚之外曰薄努。自關而東周 鄭之閒曰勔釗{愐釗}[愐劭]4。齊 魯曰勖茲[勖孜]。5', '[音義-1]：居遼反\n[郭注-2]：相勸勉也。\n[郭注-3]：如今人言努力也。\n[音義-4]：音沈湎\n[郭注-5]：勖勔，亦訓勉也。', '卷一', '方言', '2;4;5', DEFAULT, DEFAULT, '9220;561/9254;9220/9254;6788/2794;9222/526', '1182/9254;6788/9221;9222/2012', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1485, '陗;嫽;好', '', '釥{陗}1、嫽2，好也。靑 徐 海 岱之閒曰釥，或謂之嫽。3好，凡通語也。', '[音義-1]：錯眇反\n[音義-2]：洛夭反\n[郭注-3]：今通呼小姣潔喜好者爲嫽釥。', '卷二', '方言', '', DEFAULT, DEFAULT, '9636;8174;8189', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1486, '朦;厖;豐;伃;杼', '厖;眝;眝', '朦[厖]1、厖2，豐也。自關而西秦 晉之閒凡大貌謂之朦，或謂之厖。豐，其通語也。趙 魏之郊、燕之北鄙凡大人謂之豐人。燕記曰：「豐人杼首。」杼首，長首也。楚謂之伃[眝]3。燕謂之杼[眝]。燕 趙之閒言圍大謂之豐。4', '[音義-1]：忙紅反\n[音義-2]：音「鶜鴟，𪁪」\n[音義-3]：音序\n[郭注-4]：謂度圍物也。', '卷二', '方言', '1;4;5', DEFAULT, DEFAULT, '4281;5995;3103;4970;3725', '5995;2175;2175', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1487, '娃;嫷;窕;豔;美;好;娥;窈', '', '娃1、嫷2、窕3、豔，美也。吳 楚 衡 淮之閒曰娃。南楚之外曰嫷。4宋 衞 晉 鄭之閒曰豔。陳 楚 周南之閒曰窕。自關而西秦 晉之閒凡美色或謂之好，或謂之窕。故吳有館娃之宮，秦有㯃娥之臺5。6秦 晉之閒美貌謂之娥。7美狀爲窕，8美色爲豔，9美心爲窈。10', '[音義-1]：烏佳反\n[音義-2]：諾過反\n[音義-3]：途了反\n[郭注-4]：言婑嫷也。\n[音義-5]：音七\n[郭注-6]：皆戰國時諸侯所立也。\n[郭注-7]：言娥娥也。\n[郭注-8]：言閑都也。\n[郭注-9]：言光豔也。\n[郭注-10]：言幽靜也。', '卷二', '方言', '', DEFAULT, DEFAULT, '8298;8187;4669;3104;2355;8189;8166;4676', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1488, '奕;僷;容', '僷', '奕[僷]、僷1，容也。自關而西凡美容謂之奕，或謂之僷。2宋 衞曰僷。陳 楚 汝 潁之閒謂之奕。', '[音義-1]：音葉\n[郭注-2]：奕、僷皆輕麗之貌。', '卷二', '方言', '1', DEFAULT, DEFAULT, '6667;4976;4587', '4976', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1489, '𥌗;鑠;盱;揚;𠈪;矆;順;𥌭', '訓', '䫵{𥌗}1、鑠2、盱3、揚、膡{𠈪}4，矆也。南楚 江 淮之閒曰䫵，或曰膡。好目謂之順[訓]。5黸瞳之子謂之𥌣{𥌭}。6宋 衞 韓 鄭之閒曰鑠。7燕 代 朝鮮 洌水之閒曰盱，8或謂之揚。9', '[音義-1]：音緜，下作𥌣，音字同耳\n[音義-2]：舒灼反\n[音義-3]：香于反\n[音義-4]：音滕\n[郭注-5]：言流澤也。\n[郭注-6]：黸，黑也。言𥌣邈也。\n[郭注-7]：言光明也。\n[郭注-8]：謂舉眼也。\n[郭注-9]：詩曰：「美目揚兮」是也。此本論雙耦，因廣其訓，復言目耳。', '卷二', '方言', '7', DEFAULT, DEFAULT, '2103;9279;2133;7956;5107;2154;5652;2101', '1495', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1490, '嫢;笙;揫;摻;細;徥', '靖;媞', '嫢1、笙[靖]、揫2、摻3，細也。自關而西秦 晉之閒凡細而有容謂之嫢。4或曰徥[媞]5。6凡細貌謂之笙，斂物而細謂之揫，或曰摻。', '[音義-1]：羌箠反\n[音義-2]：音遒\n[音義-3]：素檻反\n[郭注-4]：嫢嫢，小成貌。\n[音義-5]：度皆反\n[郭注-6]：言徥𢔡也。', '卷二', '方言', '2;6', DEFAULT, DEFAULT, '8227;2987;3396;8093;8559;1237', '6684;8228', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1491, '𤪿;渾;濞;䑋;廫;泡;盛', '葆/飽', '𠐤{𤪿}、渾1、2䑄{濞}3、4䑋5、𠐋{廫}6、泡[葆/飽]7，盛也。𠐤，8自關而西秦 晉之閒語也。陳 宋之閒曰𠐋。9江 淮之閒曰泡。10秦 晉或曰䑋。梁 益之閒凡人言盛及其所愛、偉其肥晠謂之䑋。11', '[音義-1]：狐本反\n[郭注-2]：們渾，肥滿也。\n[音義-3]：匹四反\n[郭注-4]：䑄呬，充壯也。\n[音義-5]：音壤\n[音義-6]：恪膠反\n[音義-7]：音庖\n[郭注-8]：言瓌瑋也。\n[郭注-9]：𠐋𠈭，麤大貌。\n[郭注-10]：泡肥，洪張貌。\n[郭注-11]：肥䑋多肉。', '卷二', '方言', '6', DEFAULT, DEFAULT, '4979;7195;7167;2648;5969;7072;3140', '674/0/3242', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1492, '私;策;纖;兌;稺;杪;小;綾;靡;蔑;葼', '厶;朿;𦺶;糜', '私[厶]、策[朿]、纖、䓲{兌}[𦺶]1、稺2、杪3，小也。自關而西秦 晉之郊梁 益之閒凡物小者謂之私，或曰纖，繒帛之細者謂之纖。東齊言布帛之細者曰綾4，秦 晉曰靡[糜]。5凡草生而初達謂之䓲。6稺，年小也。木細枝謂之杪，7江 淮 陳 楚之內謂之蔑，8靑 齊 兗 冀之閒謂之葼9，燕之北鄙、朝鮮 洌水之閒謂之策，故傳曰：「慈母之怒子也，雖折葼笞之，其惠存焉。」10', '[音義-1]：音銳\n[音義-2]：古稚字\n[音義-3]：莫召反\n[音義-4]：音凌\n[郭注-5]：靡，細好也。\n[郭注-6]：鋒萌始出。\n[郭注-7]：言杪梢也。\n[郭注-8]：蔑，小貌也。\n[音義-9]：音馬𩭤\n[郭注-10]：言敎在其中也。', '卷二', '方言', '1;2;4;9', DEFAULT, DEFAULT, '4429;2972;8558;5459;4422;3581;704;8603;7696;2331;508', '5843;4395;496;4360', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1493, '奄;枼;微', '', '殗{奄}1、殜{枼}2，微也。宋 衞之閒曰殗。自關而西秦 晉之閒凡病而不甚曰殗殜。3', '[音義-1]：於怯反\n[音義-2]：音葉\n[郭注-3]：病半臥半起也。', '卷二', '方言', '', DEFAULT, DEFAULT, '6599;3814;1236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1494, '臺;敵;匹', '持', '臺[持]、敵，匹也。東齊 海 岱之閒曰臺。自關而西秦 晉之閒凡物力同者謂之臺敵。', '', '卷二', '方言', '1', DEFAULT, DEFAULT, '7710;2028;8422', '7856', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1495, '抱嬎;耦', '勽嬎', '抱㛯{抱嬎}[勽嬎]1，耦也。2荆 吳 江 湖之閒曰抱㛯。宋 潁之閒或曰㛯。', '[音義-1]：匹萬反，又音赴。一作嬔\n[郭注-2]：耦亦匹，互見其義耳。', '卷二', '方言', '1', DEFAULT, DEFAULT, '7909/8136;2821', '5807/8136', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1496, '倚;踦;奇', '奇', '倚[奇]1、踦2，奇也3。自關而西秦 晉之閒凡全物而體不具謂之倚，梁 楚之閒謂之踦，雍 梁之西郊凡嘼支體不具者謂之踦。', '[音義-1]：丘寄反\n[音義-2]：卻奇反\n[音義-3]：音奇耦', '卷二', '方言', '1', DEFAULT, DEFAULT, '5045;1331;3059', '3059', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1497, '逴;𤡯;透;驚', '悼;倏', '逴[悼]1、𤡯2、透[倏]3，驚也。4自關而西秦 晉之閒凡蹇者或謂之逴，5體而偏長短亦謂之逴。宋 衞 南楚凡相驚曰𤡯，或曰透。', '[音義-1]：勑略反\n[音義-2]：音鑠\n[音義-3]：式六反\n[郭注-4]：皆驚貌也。\n[郭注-5]：行略逴也。', '卷二', '方言', '1;3', DEFAULT, DEFAULT, '1201;6331;1218;6216', '6941;6348', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1498, '儀;格;來;懷', '', '儀、𢓜{格}，來也。陳 潁之閒曰儀。自關而東周 鄭之郊、齊 魯之閒曰𢓜，或曰懷。', '', '卷二', '方言', '', DEFAULT, DEFAULT, '5082;3601;3346;6762', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1499, '䵑;𡫽;黏', '', '䵑1、𪏮{𡫽}2，黏也。齊 魯 靑 徐自關而東或曰䵑，3或曰𪏮。', '[音義-1]：音日\n[音義-2]：音汝\n[郭注-3]：言黏䵑也。', '卷二', '方言', '', DEFAULT, DEFAULT, '4512;4687;4510', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1500, '餬;託;庇;寓;𠈪;寄', '', '餬1、託、庇2、寓、媵{𠈪}3，寄也。齊 衞 宋 魯 陳 晉 汝 潁 荆州 江 淮之閒曰庇，或曰寓。寄食爲餬。4凡寄爲託，寄物爲媵。', '[音義-1]：音胡\n[音義-2]：音庇蔭\n[音義-3]：音孕\n[郭注-4]：傳曰：「餬予口於四方」是也。', '卷二', '方言', '', DEFAULT, DEFAULT, '3239;1566;5955;4610;5107;4609', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1501, '逞;苦;了;快', '憭', '逞、苦、了[憭]，快也。自山而東或曰逞，楚曰苦，1秦曰了。23', '[郭注-1]：苦而爲快者，猶以臭爲香、亂爲治、徂爲存，此訓義之反覆用之是也。\n[郭注-2]：今江東人呼快爲愃。\n[音義-3]：愃，相緣反。', '卷二', '方言', '3', DEFAULT, DEFAULT, '1196;323;9779;6716', '6734', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1502, '每;匿;赧;愧', '瞢;𢘋', '挴{每}[瞢]、㥾{匿}[𢘋]1、赧，愧也。晉曰挴，或曰㥾。秦 晉之閒凡愧而見上謂之赧。2梁 宋曰㥾。3', '[音義-1]：音匿\n[郭注-2]：小雅曰：「面赤愧曰赧。」\n[郭注-3]：勑㥾，亦慙貌也。', '卷二', '方言', '1;2', DEFAULT, DEFAULT, '235;8418;6588;8344', '2329;6819', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1503, '叨;惏;殘', '', '叨1、惏2，殘也。陳 楚曰惏。', '[音義-1]：託高反\n[音義-2]：洛含反', '卷二', '方言', '', DEFAULT, DEFAULT, '3250;6859;2550', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1504, '馮;齘;苛;怒', '訶', '憑{馮}、齘、苛[訶]，怒也。楚曰憑。1小怒曰齘。2陳謂之苛。3', '[郭注-1]：憑，恚盛貌，楚辭曰：「康回憑怒。」\n[郭注-2]：言噤齘也。\n[郭注-3]：相苛責也。', '卷二', '方言', '3', DEFAULT, DEFAULT, '6201;1283;543;6877', '1683', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1505, '朿;剌;痛', '', '憡{朿}1、剌，痛也。自關而西秦 晉之閒或曰憡。2', '[音義-1]：音策\n[郭注-2]：㦧憡，小痛也。', '卷二', '方言', '', DEFAULT, DEFAULT, '4395;3900;4697', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1506, '撟捎;選', '', '撟捎1，選也。2自關而西秦 晉之閒凡取物之上謂之撟捎。', '[音義-1]：矯、騷兩音\n[郭注-2]：此妙擇積聚者也。', '卷二', '方言', '', DEFAULT, DEFAULT, '7964/7965;1143', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1507, '僩;梗;爽;猛', '更/庚', '撊{僩}1、梗[更/庚]2、爽，猛也。晉 魏之閒曰撊。3韓 趙之閒曰梗。齊 晉曰爽。', '[音義-1]：呼旱反\n[音義-2]：音魚鯁\n[郭注-3]：傳曰：「撊然登埤。」', '卷二', '方言', '2', DEFAULT, DEFAULT, '5007;3541;2090;6343', '2020/0/9753', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1508, '瞯;睇;睎;䀩;眄', '', '瞯1、睇2、睎、䀩3，眄也。陳 楚之閒、南楚之外曰睇。東齊 靑 徐之閒曰睎。吳 揚 江 淮之閒或曰瞯，或曰䀩。自關而西秦 晉之閒曰眄。', '[音義-1]：音閑\n[音義-2]：音悌\n[音義-3]：音略', '卷二', '方言', '', DEFAULT, DEFAULT, '2189;2177;2173;2178;2198', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1509, '息;喙;呬;息', '喟', '䭒{息}1、喙[喟]2、呬3，息也。周 鄭 宋 沛之閒曰䭒。自關而西秦 晉之閒或曰喙，或曰䭒，東齊曰呬。', '[音義-1]：音消息\n[音義-2]：音口喙\n[音義-3]：許四反', '卷二', '方言', '2', DEFAULT, DEFAULT, '6704;783;824;6704', '830', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1510, '𠂢;規;裁;斲', '撝', '䤨{𠂢}1、摫{規}[撝]2，裁也。3梁 益之閒裁木爲器曰䤨，裂帛爲衣曰摫。䤨又斲也，晉 趙之閒謂之䤨。', '[音義-1]：音劈歷\n[音義-2]：音規\n[郭注-3]：皆析破之名也。', '卷二', '方言', '2', DEFAULT, DEFAULT, '7497;6675;5253;9480', '8020', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1511, '鐫;琢', '', '鐫1，琢也。晉 趙謂之鐫。2', '[音義-1]：子旋反\n[郭注-2]：謂鏨鐫也。', '卷二', '方言', '', DEFAULT, DEFAULT, '9340;145', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1512, '鍇;稽;堅', '', '鍇1、𨯀{稽}2，堅也。自關而西秦 晉之閒曰鍇。吳 揚 江 淮之閒曰𨯀。', '[音義-1]：音楷\n[音義-2]：音啟', '卷二', '方言', '', DEFAULT, DEFAULT, '9269;3889;1950', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1513, '揄鋪;𢅡巫;帗縷;葉褕;毳', '𢅡幠', '揄鋪1、𢅡𢃀{𢅡巫}[𢅡幠]2、帗縷3、葉褕4，毳也5。6荆 揚 江 湖之閒曰揄鋪。楚曰𢅡𢃀，陳 宋 鄭 衞之閒謂之帗縷。燕之北郊、朝鮮 洌水之閒曰葉褕。7', '[音義-1]：音敷\n[音義-2]：音藍\n[音義-3]：音拂\n[音義-4]：音臾\n[音義-5]：音脆\n[郭注-6]：皆謂物之行敝者也。\n[郭注-7]：今名短度絹爲葉褕也。', '卷二', '方言', '2', DEFAULT, DEFAULT, '7968/9434;4874/3035;4858/8674;495/5256;5398', '4874/4898', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1514, '孑;藎;餘;俊;遵', '㶳;尊', '孑、藎[㶳]1，餘也。2周 鄭之閒曰藎，或曰孑。靑 徐 楚之閒曰孑。自關而西秦 晉之閒炊薪不盡曰藎。孑，俊也。遵[尊]，俊也。3', '[音義-1]：昨吝反\n[郭注-2]：謂遺餘。\n[郭注-3]：廣異語耳。', '卷二', '方言', '2;5', DEFAULT, DEFAULT, '9780;304;3245;4960;1108', '6478;9879', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1515, '翿;幢;翳', '橦', '翿1、幢[橦]2，翳也。3楚曰翿。關西 關東皆曰幢。', '[音義-1]：音濤\n[音義-2]：徒江反\n[郭注-3]：儛者所以自蔽翳也。', '卷二', '方言', '2', DEFAULT, DEFAULT, '2272;4916;2273', '3673', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1516, '𢯱;略;求;攈;摭', '', '𢯱、略，求也。秦 晉之閒曰𢯱。就室曰𢯱，於道曰略。略，強取也。攈1、摭2，取也。此通語也。', '[音義-1]：古捃字\n[音義-2]：音盜蹠', '卷二', '方言', '', DEFAULT, DEFAULT, '8090;9190;5373;7985;7984', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1517, '朚;矜;奄;遽', '拮;弇', '茫{朚}1、矜[拮]、奄[弇]，遽也。2吳 揚曰茫。3陳 潁之閒曰奄。秦 晉或曰矜，或曰遽。', '[音義-1]：莫光反\n[郭注-2]：謂遽矜也。\n[郭注-3]：今北方通然也。', '卷二', '方言', '2;3', DEFAULT, DEFAULT, '4322;9510;6599;1206', '8030;1759', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1518, '速;逞;搖扇;疾', '', '速、逞、搖扇，疾也。東齊 海 岱之閒曰速。燕之外鄙、朝鮮 洌水之閒曰搖扇。楚曰逞。', '', '卷二', '方言', '', DEFAULT, DEFAULT, '1121;1196;7949/7722;4694', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1519, '予;賴;讎', '讕', '予、賴[讕]，讎也。南楚之外曰賴。1秦 晉曰讎。', '[郭注-1]：賴亦惡名。', '卷二', '方言', '2', DEFAULT, DEFAULT, '2509;3962;1487', '1707', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1520, '恒慨;參綏;羞繹;紛毋;廣大', '蓡綏', '恒慨、蔘綏{參綏}[蓡綏]1、羞繹2、紛毋，言旣廣又大也。荆 揚之閒凡言廣大者謂之恒慨。東甌之閒謂之蔘綏，3或謂之羞繹、紛毋。', '[音義-1]：索含反\n[音義-2]：音奕\n[郭注-3]：東甌亦越地，今臨海 永寧是也。', '卷二', '方言', '2', DEFAULT, DEFAULT, '9015/6729;4315/8762;9790/8518;8707/8355;5938/6596', '315/8762', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1521, '劋;蹶;獪;蔿;姡', '訬;𣬎;僞;獪', '劋[訬]1、蹶[𣬎]2，獪也。3秦 晉之閒曰獪。楚謂之劋，或曰蹶。4楚 鄭曰蔿[僞]5，6或曰姡[獪]7。8', '[音義-1]：雀潦反，又子了反\n[音義-2]：音厥\n[郭注-3]：古狡㹟字。\n[郭注-4]：言踣蹶也。\n[音義-5]：音指撝\n[郭注-6]：亦獪聲之轉也。\n[音義-7]：胡刮切\n[郭注-8]：言黠姡也，今建平郡人呼狡爲姡。', '卷二', '方言', '1;2;4;5', DEFAULT, DEFAULT, '2788;1369;6340;447;8225', '1666;6292;5134;6340', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1522, '釐孶;連子;孿生;嫁子', '𠩺孶', '陳 楚之閒凡人嘼乳而雙產謂之釐孶[𠩺孶]1。秦 晉之閒謂之僆子{連子}2。自關而東趙 魏之閒謂之孿生3。女謂之嫁子。4', '[音義-1]：音茲\n[音義-2]：音輦\n[音義-3]：蘇官反\n[郭注-4]：言往適人。', '卷三', '方言', '1', DEFAULT, DEFAULT, '9168/9774;1168/9764;9769/3874;8125/9764', '2055/9774', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1523, '知;倩', '壻', '東齊之閒聟{知}[壻]謂之倩。12', '[郭注-1]：言可借倩也，今俗呼女聟爲卒便是也。\n[音義-2]：卒便，一作平使。', '卷三', '方言', '1', DEFAULT, DEFAULT, '3315;4969', '227', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1524, '娠', '臣', '燕 齊之閒養馬者謂之娠[臣]1。2官婢女廝謂之娠。3', '[音義-1]：音振\n[郭注-2]：今之温厚也。\n[郭注-3]：女廝，婦人給使者，亦名娠。', '卷三', '方言', '1', DEFAULT, DEFAULT, '8134', '1952', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1525, '亭父;亭公;卒;弩父;褚', '', '楚 東海之閒亭父謂之亭公。1卒謂之弩父，2或謂之褚3。4', '[郭注-1]：亭民。\n[郭注-2]：主擔弩導引，因名云。\n[音義-3]：音赭\n[郭注-4]：言衣赤也。', '卷三', '方言', '', DEFAULT, DEFAULT, '3320/1907;3320/716;5359;8497/1907;5360', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1526, '臧;甬;侮;獲', '庸', '臧、甬[庸]1、侮、獲，奴婢賤稱也。荆 淮 海 岱 雜齊之閒，2罵奴曰臧，罵婢曰獲。齊之北鄙、燕之北郊，凡民男而聟{知}婢謂之臧，女而婦奴謂之獲；亡奴謂之臧，亡婢謂之獲。皆異方罵奴婢之醜稱也。自關而東陳 魏 宋 楚之閒保庸謂之甬。3秦 晉之閒罵奴婢曰侮。4', '[音義-1]：音勇\n[郭注-2]：俗不純爲雜。\n[郭注-3]：保，言可保信也。\n[郭注-4]：言爲人所輕弄。', '卷三', '方言', '2', DEFAULT, DEFAULT, '1954;4344;5148;6362', '2083', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1527, '蔿;譌;譁;涅;化', '化;化', '蔿[化]1、譌[化]2、譁3、4涅，化也。燕 朝鮮 洌水之閒曰涅，或曰譁。雞伏卵而未孚5、始化之時謂之涅。', '[音義-1]：音花\n[音義-2]：音訛言\n[音義-3]：五瓜反\n[郭注-4]：皆化聲之轉也。\n[音義-5]：音赴', '卷三', '方言', '1;2', DEFAULT, DEFAULT, '447;1660;1658;7232;5214', '5214;5214', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1528, '斟;協;汁', '汁', '斟、協[汁]，汁也。1北燕 朝鮮 洌水之閒曰斟，自關而東曰協，關西曰汁。', '[郭注-1]：謂和協也，或曰潘汁，所未能詳。', '卷三', '方言', '2', DEFAULT, DEFAULT, '9497;9258;7394', '7394', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1529, '蘇;芥;草;莽;荏;公蕡;害;䖆柔', '丯;茻;丯', '蘇、芥[丯]，草也。江 淮 南楚之閒曰蘇，1自關而西或曰草，或曰芥。2南楚 江 湘之閒謂之莽[茻]3。蘇亦荏也。4關之東西或謂之蘇，或謂之荏。56周 鄭之閒謂之公蕡7。沅 湘之南或謂之𦵯{害}[丯]8，9其小者謂之䖆葇{䖆柔}。10', '[郭注-1]：漢書曰：「樵蘇而爨。」蘇猶蘆，語轉也。\n[郭注-2]：或言菜也。\n[音義-3]：音嫫母\n[郭注-4]：荏屬也。爾雅曰：「蘇，桂荏也。」\n[郭注-5]：今江東人呼荏爲䓊。\n[音義-6]：䓊，音魚。\n[音義-7]：音翡翠\n[音義-8]：音車轄\n[郭注-9]：沅，水名，在武陵。今長沙人呼野蘇爲𦵯。\n[郭注-10]：荲葇也，亦蘇之種類，因名云。', '卷三', '方言', '2;4;7', DEFAULT, DEFAULT, '256;632;680;702;257;716/575;4614;268/3607', '2817;700;2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1530, '豐;蕘;蕪菁;大芥;辛芥;幽芥;蘆菔;拉遝', '葑', '蘴{豐}[葑]1、蕘2，蕪菁也。陳 楚之郊謂之蘴。魯 齊之郊謂之蕘。關之東西謂之蕪菁。趙 魏之郊謂之大芥，其小者謂之辛芥，或謂之幽芥，其紫華者謂之蘆菔3。4東魯謂之菈𦿚{拉遝}5。', '[音義-1]：舊音蜂，今江東音嵩，字作菘也\n[音義-2]：音鈴鐃\n[音義-3]：羅、匐二音\n[郭注-4]：今江東名爲温菘，實如小豆。\n[音義-5]：洛荅、徒合兩反', '卷三', '方言', '1', DEFAULT, DEFAULT, '3103;621;544/276;6596/632;9754/632;2501/632;277/278;7852/1116', '399', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1531, '役;芡;雞頭;鴈頭;烏頭', '', '䓈{役}1、芡2，雞頭也。北燕謂之䓈。3靑 徐 淮 泗之閒謂之芡。南楚 江 湘之閒謂之雞頭，或謂之鴈頭，或謂之烏頭。4', '[音義-1]：音役\n[音義-2]：音儉\n[郭注-3]：今江東亦呼䓈耳。\n[郭注-4]：狀似鳥頭，故博以名之。', '卷三', '方言', '', DEFAULT, DEFAULT, '1976;415;2290/5599;2422/5599;2487/5599', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1532, '茦;壯;梗;劌;刺;棘', '戕', '凡草木刺人，北燕 朝鮮之閒謂之茦，1或謂之壯[戕]。2自關而東或謂之梗，3或謂之劌4。5自關而西謂之刺。江 湘之閒謂之棘6。7', '[郭注-1]：爾雅曰：「茦，刺也。」\n[郭注-2]：今淮南人亦呼壯。壯，傷也。山海經謂刺爲傷也。\n[郭注-3]：今之梗榆。\n[音義-4]：音鱥魚\n[郭注-5]：劌者，傷割人名。\n[音義-6]：己力反\n[郭注-7]：楚辭曰：「曾枝剡棘。」亦通語耳。', '卷三', '方言', '2', DEFAULT, DEFAULT, '397;228;3541;2763;2805;4397', '8388', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1533, '瘌;癆;瞑;眩', '', '凡飲藥傅藥而毒，南楚之外謂之瘌1。北燕 朝鮮之閒謂之癆2。3東齊 海 岱之閒謂之瞑，或謂之眩。4自關而西謂之毒。瘌，痛也。', '[音義-1]：音乖瘌\n[音義-2]：音聊\n[郭注-3]：癆、瘌皆辛螫也。\n[郭注-4]：瞑、眩亦今通語耳。', '卷三', '方言', '', DEFAULT, DEFAULT, '4789;4790;2181;2098', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1534, '逞;曉;恔;苦;快', '', '逞、曉、恔、苦，快也。自關而東或曰曉，或曰逞。江 淮 陳 楚之閒曰逞。宋 鄭 周 洛 韓 魏之閒曰苦。東齊 海 岱之閒曰恔。1自關而西曰快。', '[郭注-1]：恔卽狡，狡戲亦快事也。', '卷三', '方言', '', DEFAULT, DEFAULT, '1196;4195;6735;323;6716', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1535, '謬;譎;詐', '', '謬、譎，詐也。涼州西南之閒曰謬。自關而東西或曰譎，或曰謬。詐，通語也。12', '[郭注-1]：汝南人呼欺爲讉[囮]，亦曰詒。\n[音義-2]：讉，託回反。詒，音殆。', '卷三', '方言', '', DEFAULT, DEFAULT, '1663;1668;1669', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1536, '揠;擢;拂;戎;拔', '拔', '揠1、擢、拂[拔]、戎，拔也。自關而西或曰拔，或曰擢。自關而東江 淮 南楚之閒或曰戎。東齊 海 岱之閒曰揠。2', '[音義-1]：烏拔反\n[郭注-2]：今呼拔草心爲揠。', '卷三', '方言', '3', DEFAULT, DEFAULT, '7996;7994;8060;8377;7995', '7995', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1537, '慰;廛;度;凥;踐', '宅', '慰、廛1、度[宅]，凥也。江 淮 靑 徐之閒曰慰。東齊 海 岱之閒或曰度，或曰廛，2或曰踐。', '[音義-1]：音纏約\n[郭注-2]：周官云：「夫一廛。」宅也。', '卷三', '方言', '3', DEFAULT, DEFAULT, '6781;5943;1928;9469;1357', '4556', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1538, '萃;雜;集;聚', '', '萃、雜，集也。東齊曰聚。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '540;5333;2367;5236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1539, '𨽿;遝;及', '', '迨{𨽿}1、遝，及也。東齊曰迨。關之東西曰遝，或曰及。', '[音義-1]：音殆', '卷三', '方言', '', DEFAULT, DEFAULT, '1946;1116;1917', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1540, '荄;杜;根;茇', '土', '荄1、2杜[土]，根也。東齊曰杜，3或曰茇4。', '[音義-1]：音陔\n[郭注-2]：今俗名韭根爲荄。\n[郭注-3]：詩曰：「徹彼桑杜」是也。\n[音義-4]：音撥', '卷三', '方言', '2', DEFAULT, DEFAULT, '515;3431;3562;517', '9019', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1541, '班;徹;列', '', '班、徹，列也。北燕曰班。東齊曰徹。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '222;1996;2771', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1542, '瘼;復;病;湛', '', '瘼1、癁{復}，病也。東齊 海 岱之閒曰瘼，或曰癁。2秦曰瘎{湛}3。', '[音義-1]：音莫\n[郭注-2]：謂勞復也。\n[音義-3]：音諶', '卷三', '方言', '', DEFAULT, DEFAULT, '4703;1226;4695;7294', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1543, '掩;醜;掍;𪓌;同', '弇;雔', '掩[弇]、醜[雔]、掍1、綷{𪓌}2，同也。江 淮 南楚之閒曰掩。宋 衞之閒曰綷，或曰掍。東齊曰醜。', '[音義-1]：音衮衣\n[音義-2]：作憒反', '卷三', '方言', '1;2', DEFAULT, DEFAULT, '8033;5834;8089;4946;4801', '1759;2362', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1544, '裕;猷;道', '邎;邎', '裕[邎]、猷[邎]，道也。東齊曰裕，或曰猷。', '', '卷三', '方言', '1;2', DEFAULT, DEFAULT, '5335;6373;1205', '1100;1100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1545, '虔;散;殺', '丯', '虔[丯]、散，殺也。東齊曰散。靑 徐 淮 楚之閒曰虔。', '', '卷三', '方言', '1', DEFAULT, DEFAULT, '3111;2720;1978', '2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1546, '氾;浼;㶄;洼;洿', '㶄', '氾[㶄]1、浼2、㶄3、洼4，洿也，5自關而東或曰洼，或曰氾。東齊 海 岱之閒或曰浼，或曰㶄。6', '[音義-1]：音汎\n[音義-2]：音漫\n[音義-3]：音湯爓\n[音義-4]：烏蛙反\n[郭注-5]：皆洿池也。\n[郭注-6]：荆州呼潢也。', '卷三', '方言', '1', DEFAULT, DEFAULT, '7180;7428;7427;7253;7347', '7427', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1547, '庸;恣;比;侹;更;佚;遞;代', '佽;迭', '庸、恣[佽]、比1、侹2、更、佚[迭]3、遞，代也。齊曰佚。江 淮 陳 楚之閒曰侹。餘四方之通語也。4', '[音義-1]：音比次\n[音義-2]：音挺直\n[音義-3]：音蹉跌\n[郭注-4]：今俗亦名更代作爲恣作也。', '卷三', '方言', '2;6', DEFAULT, DEFAULT, '2083;6846;5227;5011;2020;5142;1133;5081', '5048;1166', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1548, '氓;民', '', '氓1，民也。2', '[音義-1]：音萌\n[郭注-2]：民之總名。', '卷三', '方言', '', DEFAULT, DEFAULT, '8358;8357', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1549, '朹;仇', '', '朹1，仇也。2', '[音義-1]：音舊\n[郭注-2]：謂怨仇也。', '卷三', '方言', '', DEFAULT, DEFAULT, '2939;5171', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1550, '寓;寄', '', '寓，寄也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '4610;4609', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1551, '露;敗', '', '露，敗也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '7561;2040', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1552, '別;治', '', '別，治也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '2566;7087', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1553, '棖;法', '', '棖，法也。1', '[郭注-1]：救傾之法。', '卷三', '方言', '', DEFAULT, DEFAULT, '3734;6260', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1554, '謫;怒', '', '謫1，怒也。2', '[音義-1]：音賾\n[郭注-2]：相責怒也。', '卷三', '方言', '', DEFAULT, DEFAULT, '1690;6877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1555, '閒;非', '', '閒，非也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '7760;7694', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1556, '格;正', '', '格，正也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '3601;1087', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1557, '𣀷;數', '', '𣀷，數也。1', '[郭注-1]：偶物爲麗，故云數也。', '卷三', '方言', '', DEFAULT, DEFAULT, '2009;2010', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1558, '軫;戾', '紾', '軫[紾]1，戾也。2', '[音義-1]：江東音善\n[郭注-2]：相了戾也。', '卷三', '方言', '1', DEFAULT, DEFAULT, '9540;6354', '8572', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1559, '㞕;潔', '絜', '㞕1，潔[絜]也。2', '[音義-1]：音薛\n[郭注-2]：謂潔淸也。', '卷三', '方言', '2', DEFAULT, DEFAULT, '5404;7467', '8752', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1560, '諄;罪', '敦', '諄[敦]1，罪也。2', '[音義-1]：章順反\n[郭注-2]：謂罪惡也。', '卷三', '方言', '1', DEFAULT, DEFAULT, '1503;4822', '2038', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1561, '俚;聊', '僇', '俚1，聊[僇]也。2', '[音義-1]：音吏\n[郭注-2]：謂苟且也。', '卷三', '方言', '2', DEFAULT, DEFAULT, '5004;7800', '5170', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1562, '梱;就', '稛', '梱[稛]1，就也。2', '[音義-1]：恪本反\n[郭注-2]：梱梱，成就貌。', '卷三', '方言', '1', DEFAULT, DEFAULT, '3661;3330', '4467', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1563, '笠;圂', '', '苙{笠}1，圂也。2', '[音義-1]：音立\n[郭注-2]：謂蘭圂也。', '卷三', '方言', '', DEFAULT, DEFAULT, '2967;3930', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1564, '叜;隱', '縮', '廋{叜}[縮]1，隱也。2', '[音義-1]：音捜索也\n[郭注-2]：謂隱匿也。', '卷三', '方言', '1', DEFAULT, DEFAULT, '1908;9667', '8563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1565, '銛;取', '', '銛1，取也。2', '[音義-1]：音忝\n[郭注-2]：謂挑取物。', '卷三', '方言', '', DEFAULT, DEFAULT, '9342;1925', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1566, '棖;隨', '', '棖，隨也。1', '[郭注-1]：棖柱令相隨也。', '卷三', '方言', '', DEFAULT, DEFAULT, '3734;1102', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1567, '嬯;服;辟', '僰', '儓{嬯}1、𧟵{服}[僰]2，農夫之醜稱也。南楚凡罵庸賤謂之田儓，3或謂之𧟵，45或謂之辟6。辟，商人醜稱也。7', '[音義-1]：音臺\n[音義-2]：音僰\n[郭注-3]：㑣儓，駑鈍貌。或曰「僕臣儓」，亦至賤之號也。\n[郭注-4]：𧟵，丁健貌也。\n[音義-5]：廣雅以爲奴，字作僰，音同。\n[音義-6]：音擘\n[郭注-7]：辟辟，便黠貌也。', '卷三', '方言', '2', DEFAULT, DEFAULT, '8316;5447;5796', '5188', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1568, '庸;松;嬾', '慵', '庸[慵]謂之倯{松}1，轉語也。2{嬾}', '[音義-1]：相容反\n[郭注-2]：倯猶保倯也。今隴右人名孏爲倯。', '卷三', '方言', '1', DEFAULT, DEFAULT, '2083;3543;8320', '6966', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1569, '褸裂;須捷;挾斯;敗;襤褸', '須𧚨;挾澌', '褸裂1、須捷[須𧚨]、挾斯[挾澌]，敗也。南楚凡人貧衣被醜敝謂之須捷，2或謂之褸裂，3或謂之襤褸，故左傳曰：「篳路襤褸，以启山林。」4殆謂此也，或謂之挾斯。5器物弊亦謂之挾斯。', '[音義-1]：音縷\n[郭注-2]：須捷，狎翣也。\n[郭注-3]：褸裂，衣壞貌。\n[郭注-4]：篳路，柴車。\n[郭注-5]：挾斯猶挾變也。', '卷三', '方言', '2;3', DEFAULT, DEFAULT, '5267/5338;5706/8087;7869/9483;2040;5281/5267', '5706/5268;7869/7338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1570, '㯷;鋌;澌;盡;賜', '澌', '𢷏{㯷}1、鋌2、澌，盡也。南楚凡物盡生者曰𢷏生。3物空盡者曰鋌。4鋌，賜[澌]也。連此𢷏、澌，皆盡也。鋌，空也，語之轉也。', '[音義-1]：音打𢷏\n[音義-2]：音挺\n[郭注-3]：今種物皆生云𢷏地生也。\n[郭注-4]：亦中國之通語也。', '卷三', '方言', '5', DEFAULT, DEFAULT, '3492;9287;7338;3156;3959', '7338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1571, '㯷;翕;葉;聚', '吸;疊', '𢷏{㯷}、翕[吸]、葉[疊]，聚也。楚謂之𢷏，1或謂之翕。葉，楚通語也。', '[郭注-1]：𢷏屬，藂相著貌。', '卷三', '方言', '2;3', DEFAULT, DEFAULT, '3492;2257;495;5236', '827;4317', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1572, '斟;益', '', '斟，益也。1南楚凡相益而又少謂之不斟。凡病少愈而加劇亦謂之不斟，或謂之何斟。2', '[郭注-1]：言斟酌益之。\n[郭注-2]：言雖少損無所益也。', '卷三', '方言', '', DEFAULT, DEFAULT, '9497;3154', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1573, '瘥;閒;知;瘉;慧;憭;瘳;蠲;除', '', '差{瘥}、閒、知，愈{瘉}也。南楚病愈者謂之差，或謂之閒，1或謂之知。知，通語也。或謂之慧，或謂之憭，2或謂之瘳，或謂之蠲3，4或謂之除。', '[郭注-1]：言有閒隙。\n[郭注-2]：慧、憭皆意精明。\n[音義-3]：音涓，一音圭\n[郭注-4]：蠲亦除也。', '卷三', '方言', '', DEFAULT, DEFAULT, '4791;7760;3315;4793;6733;6734;4794;8827;9690', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1574, '襌衣;褋;左衣;裎衣;深衣', '', '襌衣，江 淮 南楚之閒謂之褋1，2關之東西謂之襌衣。有袌者3，4趙 魏之閒謂之袏衣{左衣}；無袌者謂之裎衣5。古謂之深衣。6', '[音義-1]：音簡牒\n[郭注-2]：楚辭曰：「遺余褋兮澧浦。」\n[音義-3]：房報反\n[郭注-4]：前施袌囊也。\n[音義-5]：音逞\n[郭注-6]：制見禮記。', '卷四', '方言', '', DEFAULT, DEFAULT, '5321/5252;5275;3027/5252;5345/5252;7034/5252', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1575, '襜褕;童容;裋褕;襤褸;充屈;緻', '𧝉', '襜褕，江 淮 南楚謂之𧝎褣{童容}1，自關而西謂之襜褕，其短者謂之裋褕2，以布而無緣、敝而紩之謂之襤褸。自關而西謂之䘪䘿{充屈}3，4其敝者謂之緻[𧝉]5。6', '[音義-1]：裳凶反\n[音義-2]：音豎\n[音義-3]：音倔\n[郭注-4]：俗名䘿掖。\n[音義-5]：丁履反\n[郭注-6]：緻縫納敝，故名之也。', '卷四', '方言', '6', DEFAULT, DEFAULT, '5290/5256;1746/4587;5355/5256;5281/5267;5460/5428;8764', '5342', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1576, '汗襦;曾;袛裯;甲襦;襜襦;襌襦', '袷襦', '汗襦，1江 淮 南楚之閒謂之䙢{曾}2。自關而西或謂之袛裯3。4自關而東謂之甲襦[袷襦]。陳 魏 宋 楚之閒謂之襜襦，或謂之襌襦。5', '[郭注-1]：廣雅作褕。\n[音義-2]：音甑\n[音義-3]：音氐。丁牢反\n[郭注-4]：亦呼爲掩汗也。\n[郭注-5]：今或呼衫爲襌襦。', '卷四', '方言', '4', DEFAULT, DEFAULT, '7434/5318;710;5279/5280;9739/5318;5290/5318;5321/5318', '5320/5318', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1577, '帬;帔;罷', '帔', '帬，陳 魏之閒謂之帔1，自關而東或謂之襬{罷}[帔]2。3', '[音義-1]：音披\n[音義-2]：音碑\n[郭注-3]：今關西語然也。', '卷四', '方言', '3', DEFAULT, DEFAULT, '4869;4868;4841', '4868', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1578, '蔽厀;褘;袚;大巾;冉', '襜', '蔽厀，江 淮之閒謂之褘1，或謂之袚2。魏 宋 南楚之閒謂之大巾。自關東西謂之蔽厀。齊 魯之郊謂之袡{冉}[襜]3。', '[音義-1]：音韋，或暉\n[音義-2]：音弗\n[音義-3]：昌詹反', '卷四', '方言', '5', DEFAULT, DEFAULT, '550/5783;5270;5362;6596/4854;6073', '5290', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1579, '襦;曲領', '', '襦，1西南蜀 漢謂之曲領，或謂之襦。', '[郭注-1]：字亦作褕，又襦無右也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5318;8442/5618', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1580, '㡓;𢃓', '', '褌{㡓}，陳 楚 江 淮之閒謂之䘴{𢃓}1。', '[音義-1]：息勇反', '卷四', '方言', '', DEFAULT, DEFAULT, '4872;4873', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1581, '絝;褰;襱', '', '袴{絝}，齊 魯之閒謂之䙭{褰}1，2或謂之襱3。4關西謂之袴。', '[音義-1]：音騫\n[郭注-2]：傳曰：「徵䙭與襦。」\n[音義-3]：音鮦魚\n[郭注-4]：今俗呼袴踦爲襱。', '卷四', '方言', '', DEFAULT, DEFAULT, '8659;5297;5298', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1582, '褕;袖', '', '褕謂之袖。1', '[郭注-1]：襦䙭有袖者，因名云。', '卷四', '方言', '', DEFAULT, DEFAULT, '5256;5285', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1583, '及;褗', '襋', '衱{及}[襋]謂之褗1。2', '[音義-1]：劫、偃兩音\n[郭注-2]：卽衣領也。', '卷四', '方言', '1', DEFAULT, DEFAULT, '1917;5264', '5262', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1584, '圭;裾', '', '袿{圭}謂之裾。1', '[郭注-1]：衣後裾也。或作袪，廣雅云「衣袖」。', '卷四', '方言', '', DEFAULT, DEFAULT, '9149;5294', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1585, '褸;衽', '', '褸謂之衽。1', '[郭注-1]：衣襟也。或曰裳際也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5267;5266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1586, '褸;緻', '𧝉', '褸謂之緻[𧝉]。1', '[郭注-1]：襤褸，緻紩者也。', '卷四', '方言', '2', DEFAULT, DEFAULT, '5267;8764', '5342', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1587, '裯;襤', '', '裯謂之襤。1', '[郭注-1]：袛裯，敝衣，亦謂襤褸。', '卷四', '方言', '', DEFAULT, DEFAULT, '5280;5281', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1588, '襤;䙃;鼻', '', '無緣之衣謂之襤。無袂1之衣謂之䙃2。3無𧙥之袴謂之襣{鼻}。4', '[音義-1]：音藝\n[音義-2]：音慢惰\n[郭注-3]：袂，衣袖也。\n[郭注-4]：袴無踦者，卽今犢鼻褌也。𧙥亦襱，字異耳。', '卷四', '方言', '', DEFAULT, DEFAULT, '5281;5282;2232', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1589, '綃;占', '㡇', '䘯{綃}謂之袩{占}[㡇]1。2', '[音義-1]：千苕、丁俠兩反\n[郭注-2]：未詳其義。', '卷四', '方言', '2', DEFAULT, DEFAULT, '8522;2078', '4915', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1590, '䘳;交', '', '衿{䘳}謂之交。1', '[郭注-1]：衣交領也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5269;6625', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1591, '裺;襦', '', '裺謂之襦1。', '[音義-1]：央劍反', '卷四', '方言', '', DEFAULT, DEFAULT, '5265;5318', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1592, '襜;掖', '', '襜謂之䘸{掖}。1', '[郭注-1]：衣掖下也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5290;8092', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1593, '佩紟;裎', '綎', '佩紟謂之裎[綎]1。2', '[音義-1]：音禁\n[郭注-2]：所以係玉佩帶也。', '卷四', '方言', '2', DEFAULT, DEFAULT, '4958/8656;5345', '8653', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1594, '褸;占', '㡇', '褸謂之袩{占}[㡇]。1', '[郭注-1]：卽衣衽也。', '卷四', '方言', '2', DEFAULT, DEFAULT, '5267;2078', '4915', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1595, '覆卒;襌衣;偏襌;襌襦', '', '覆䘹{覆卒}謂之襌衣1。偏襌謂之襌襦。2', '[音義-1]：作憒反\n[郭注-2]：卽衫也。', '卷四', '方言', '', DEFAULT, DEFAULT, '4852/5359;5321/5252;5118/5321;5321/5318', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1596, '約纏;襌', '', '𧘑繵{約纏}謂之襌1。2', '[音義-1]：灼、纏兩音\n[郭注-2]：今又呼爲涼衣也。', '卷四', '方言', '', DEFAULT, DEFAULT, '8568/8570;5321', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1597, '袒飾;直領', '', '袒飾謂之直袊{直領}1。2', '[音義-1]：音但\n[郭注-2]：婦人初嫁，所著上衣直袊也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5340/4894;8410/5618', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1598, '襃明;袍', '', '襃明謂之袍。1', '[郭注-1]：廣雅云：「襃明，長襦也。」', '卷四', '方言', '', DEFAULT, DEFAULT, '5301/4321;5273', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1599, '繞領;帬', '', '繞袊{繞領}謂之帬。1', '[郭注-1]：俗人呼接下，江東通言下裳。', '卷四', '方言', '', DEFAULT, DEFAULT, '8571/5618;4869', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1600, '縣裺;緣', '', '懸裺{縣裺}謂之緣1。2', '[音義-1]：音掩\n[郭注-2]：衣縫緣也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5705/5265;8657', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1601, '絜襦;蔽厀', '', '絜襦謂之蔽厀。1', '[郭注-1]：廣異名也。', '卷四', '方言', '', DEFAULT, DEFAULT, '8752/5318;550/5783', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1602, '陶矞;袖', '韜繘', '裪𧝃{陶矞}[韜繘]謂之袖1。23', '[音義-1]：音橘\n[郭注-2]：衣褾，江東呼䘼。\n[音義-3]：䘼，音婉。', '卷四', '方言', '1', DEFAULT, DEFAULT, '9687/1452;5285', '3386/8718', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1603, '扈表;被巾', '扈襮', '帍裱{扈表}[扈襮]謂之被巾1。2', '[音義-1]：方廟反\n[郭注-2]：婦人領巾也。', '卷四', '方言', '1', DEFAULT, DEFAULT, '4023/5259;5323/4854', '4023/5263', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1604, '繞盾;篤裺', '𧛔裺', '繞䋸{繞盾}謂之𧞶裺{篤裺}[𧛔裺]1。2', '[音義-1]：音循\n[郭注-2]：衣督脊也。', '卷四', '方言', '2', DEFAULT, DEFAULT, '8571/2220;6196/5265', '5283/5265', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1605, '厲;帶', '烈', '厲[烈]謂之帶。1', '[郭注-1]：小爾雅曰：「帶之垂者爲厲。」', '卷四', '方言', '1', DEFAULT, DEFAULT, '5983;4865', '6425', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1606, '幡卷;幭', '幡㠾', '襎裷{幡卷}[幡㠾]謂之幭1。2', '[音義-1]：煩、冤兩音。亡別反\n[郭注-2]：卽帊幞也。', '卷四', '方言', '1', DEFAULT, DEFAULT, '4891/5784;4897', '4891/4890', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1607, '繄絡;䙔', '', '繄袼{繄絡}謂之䙔1。2', '[音義-1]：翳、洛、嘔三音\n[郭注-2]：卽小兒㳄裹衣也。', '卷四', '方言', '', DEFAULT, DEFAULT, '8687/8725;5356', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1608, '襤;褸;緻;充屈', '𧝉', '楚謂無緣之衣曰襤，紩衣謂之褸，秦謂之緻[𧝉]。自關而西秦 晉之閒無緣之衣謂之䘪䘿{充屈}。1', '[郭注-1]：嫌上說有未了，故復分明之。', '卷四', '方言', '3', DEFAULT, DEFAULT, '5281;5267;8764;5460/5428', '5342', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1609, '複襦;裋;筩褺', '', '複襦，江 湘之閒謂之𧞀{裋}1，或謂之筩褹{筩褺}。2', '[音義-1]：音豎\n[郭注-2]：今筩袖之襦也。褹卽袂字耳。', '卷四', '方言', '', DEFAULT, DEFAULT, '5305/5318;5355;2946/5315', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1610, '絝;到頓;交了', '交尞', '{絝}大袴謂之倒頓{到頓}，1小袴謂之䘨𧘈{交了}[交尞]2。3楚通語也。', '[郭注-1]：今雹袴也。\n[音義-2]：皎、了兩音\n[郭注-3]：今𧝜袴也。', '卷四', '方言', '3', DEFAULT, DEFAULT, '8659;7707/5658;6625/9779', '6625/6420', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1611, '𢄐;巾;𢁥;帤', '', '𢄐，巾也。1大巾謂之𢁥2。3嵩嶽之南，4陳 潁之閒謂之帤5，亦謂之𢄐。', '[郭注-1]：巾，主覆者，故名𢄐也。\n[音義-2]：音芬\n[郭注-3]：江東通呼巾𢁥耳。\n[郭注-4]：嵩高，中岳山也，今在河南 陽城縣。\n[音義-5]：奴豬反', '卷四', '方言', '', DEFAULT, DEFAULT, '4896;4854;4855;4861', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1612, '絡頭;佰頭;鈔繢;䰎帶;采帶;𣦼;奄;幧頭', '帴;裛', '絡頭，帞頭{佰頭}也1。紗繢{鈔繢}、䰎帶2、䰂帶{采帶}3、𢂺{𣦼}[帴]4、㡋{奄}[裛]5，幧頭也。自關以西秦 晉之郊曰絡頭。南楚 江 湘之閒曰帞頭。自河以北趙 魏之閒曰幧頭，或謂之𢂺，或謂之㡋。其偏者謂之䰎帶。6或謂之䰂帶。7', '[音義-1]：音貊\n[音義-2]：羌位反\n[音義-3]：音菜\n[音義-4]：音綪，亦千\n[音義-5]：於怯反\n[郭注-6]：今之偏疊幧頭也。\n[郭注-7]：䰂亦結也。', '卷四', '方言', '6;7', DEFAULT, DEFAULT, '8725/5599;5068/5599;9436/8538;5750/4865;3792/4865;2524;6599;4920/5599', '4871;5353', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1613, '覆結;幘巾;承露;覆采', '覆䯰', '覆結[覆䯰]謂之幘巾，或謂之承露，或謂之覆䰂{覆采}，1皆趙 魏之閒通語也。', '[郭注-1]：今結籠是也。', '卷四', '方言', '1', DEFAULT, DEFAULT, '4852/8575;4866/4854;7912/7561;4852/3792', '4852/5751', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1614, '屝;屨;麤;履;複舄;夗下;鞮;不借;䩕角;妥;䋀', '𧆓', '屝1、屨、麤[𧆓]，履也。徐 兗之郊謂之屝，自關而西謂之屨。中有木者謂之複舄，自關而東謂之複履。其庳者謂之𩊁下{夗下}2，襌者謂之鞮，3絲作之者謂之履，麻作之者謂之不借，粗者謂之屨，東北朝鮮 洌水之閒謂之䩕角4。南楚 江 沔之閒總謂之麤。5西南梁 益之閒或謂之㞜{妥}6，或謂之䋀7。履，其通語也。徐土 邳 圻之閒8，9大麤謂之䩕角。10', '[音義-1]：音翡\n[音義-2]：音婉\n[郭注-3]：今韋鞮也。\n[音義-4]：音卬\n[郭注-5]：沔水，今在襄陽。\n[音義-6]：他回反，字或作「𡲾」，音同\n[音義-7]：乎瓦反，一音畫\n[音義-8]：音祈\n[郭注-9]：今下邳也。\n[郭注-10]：今漆履有齒者。', '卷四', '方言', '3', DEFAULT, DEFAULT, '5416;5431;6287;5430;5305/2488;4328/9;1804;7704/5075;1803/2826;8347;8749', '607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1615, '緉;爽;絞', '雙', '緉1、𦄍{爽}[雙]2，絞也3。4關之東西或謂之緉，或謂之𦄍。絞，通語也。', '[音義-1]：音兩\n[音義-2]：音爽\n[音義-3]：音校\n[郭注-4]：謂履中絞也。', '卷四', '方言', '2', DEFAULT, DEFAULT, '8751;2090;6627', '2364', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1616, '纑;鬒', '', '纑謂之縝{鬒}1。2', '[音義-1]：音振\n[郭注-2]：謂纑縷也。', '卷四', '方言', '', DEFAULT, DEFAULT, '8734;5713', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1617, '鍑;錪;瓶;錡;鏤;鬲;釜', '', '鍑1，2北燕 朝鮮 洌水之閒或謂之錪3，或謂之鉼{瓶}4。江 淮 陳 楚之閒謂之錡5，6或謂之鏤。吳 揚之閒謂之鬲7。釜，自關而西或謂之釜，或謂之鍑。8', '[音義-1]：音富\n[郭注-2]：釜屬也。\n[音義-3]：音腆\n[音義-4]：音餅\n[音義-5]：音技\n[郭注-6]：或曰三腳釜也。\n[音義-7]：音歷\n[郭注-8]：鍑亦釜之總名。', '卷五', '方言', '', DEFAULT, DEFAULT, '9299;9301;3291;9329;9271;1853;1860', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1618, '甑;甗;鬵;酢餾', '笮餾', '甑，自關而東謂之甗1，或謂之鬵2，3或謂之酢餾[笮餾]4。', '[音義-1]：音言\n[音義-2]：音岑\n[郭注-3]：涼州呼鉹。\n[音義-4]：音屋霤', '卷五', '方言', '4', DEFAULT, DEFAULT, '8454;8455;1858;9860/3206', '2907/3206', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1619, '盂;盌;銚銳;櫂;柯;卷', '銚;閜', '盂1，宋 楚 魏之閒或謂之盌2。盌謂之盂，或謂之銚銳3。盌謂之櫂[銚]，盂謂之柯[閜]。4海 岱 東齊 北燕之閒或謂之𥁠{卷}5。', '[音義-1]：音于\n[音義-2]：烏管反\n[音義-3]：音謠\n[郭注-4]：轉相釋者，廣異語也。\n[音義-5]：音書卷', '卷五', '方言', '4;5', DEFAULT, DEFAULT, '3138;3139;9307/9367;3839;3744;5784', '9307;7756', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1620, '疋;椷;盞;氾;閜;楊;麻;桮', '雅;㔶;醆', '㿿{疋}[雅]1、椷[㔶]2、盞[醆]3、䀀{氾}4、閜5、𥂸{楊}6、𥂓{麻}7，桮也。秦 晉之郊謂之㿿。8自關而東趙 魏之閒曰椷，或曰盞，9或曰䀀。其大者謂之閜，吳 越之閒曰𥂸。齊右 平原以東或謂之𥂓。桮，其通語也。', '[音義-1]：音雅\n[音義-2]：音封緘\n[音義-3]：音酒醆\n[音義-4]：薄淹反\n[音義-5]：呼雅反\n[音義-6]：又章反\n[音義-7]：音摩\n[郭注-8]：所謂伯㿿者也。\n[郭注-9]：最小桮也。', '卷五', '方言', '1;2;3', DEFAULT, DEFAULT, '1421;3708;209;7180;7756;3510;4534;3703', '2279;8429;9833', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1621, '蠡;簞;戲;瓢', '', '㼖{蠡}1，2陳 楚 宋 魏之閒或謂之簞，或謂之𣟵{戲}3，4或謂之瓢。', '[音義-1]：音麗\n[郭注-2]：瓠勺也。\n[音義-3]：音羲\n[郭注-4]：今江東通呼勺爲𣟵。', '卷五', '方言', '', DEFAULT, DEFAULT, '8965;2924;8383;4553', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1622, '案;寫', '', '案，陳 楚 宋 魏之閒謂之𣞐{寫}，自關東西謂之案。', '', '卷五', '方言', '', DEFAULT, DEFAULT, '3706;4598', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1623, '桮笿;豆筥', '', '桮𥯛{桮笿}，1陳 楚 宋 衞之閒謂之桮𥯛，又謂之豆筥。自關東西謂之桮𥯛。', '[郭注-1]：盛桮器籠也。', '卷五', '方言', '', DEFAULT, DEFAULT, '3703/2933;3095/2921', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1624, '箸筩;䈰;籯;桶蔥', '箸籠', '箸筩[箸籠]，1陳 楚 宋 魏之閒謂之筲{䈰}2，或謂之籯3。4自關而西謂之桶𣞷{桶蔥}5。6', '[郭注-1]：盛朼箸籫也。\n[音義-2]：音鞭鞘\n[音義-3]：音盈\n[郭注-4]：漢書曰：「遺子黃金滿籯。」\n[音義-5]：音籠冠，或作筩。蘇勇反\n[郭注-6]：今俗亦通呼小籠爲桶𣞷。', '卷五', '方言', '1', DEFAULT, DEFAULT, '2928/2946;2920;2937;3760/633', '2928/2956', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1625, '亢;冘;廡;䍃;奠;從;𦉈;瓮;瓿婁;瓨;疑;罌', '䍌婁', '㼚{亢}1、瓭{冘}2、𤮢{廡}3、䍃4、㽀{奠}5、㼻{從}6、甀{𦉈}7、瓮、瓿甊{瓿婁}[䍌婁]8、〔瓨〕、㽈{疑}9，甖{罌}也10。靈 桂之郊謂之㼚，11其小者謂之瓭。周 魏之閒謂之𤮢。12秦之舊都謂之㽀。淮 汝之閒謂之䍃。江 湘之閒謂之㼻。自關而西晉之舊都河 汾之閒，13其大者謂之甀，其中者謂之瓿甊。自關而東趙 魏之郊謂之瓮，或謂之甖。東齊 海 岱之閒謂之㽈。甖，其通語也。', '[音義-1]：音岡\n[音義-2]：都感反，亦音沈\n[音義-3]：音舞\n[音義-4]：音由\n[音義-5]：音鄭\n[音義-6]：胙江反\n[音義-7]：度睡反\n[音義-8]：音部。洛口反\n[音義-9]：牛志反\n[音義-10]：於庚反\n[郭注-11]：今江東通名大瓮爲㼚。\n[郭注-12]：今江東亦呼甖爲𤮢子。\n[郭注-13]：汾水出太原，經絳北，西南入河。', '卷五', '方言', '9', DEFAULT, DEFAULT, '6653;3324;5930;3298;3026;5225;3289;8459;8465/8321;8460;9778;3288', '3290/8321', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1626, '罃;俞;朱;長;儋;𦉈', '臾;臾', '罃，陳 魏 宋 楚之閒曰㼶{俞}[臾]1，或曰㼡{朱}[臾]2。燕之東北朝鮮 洌水之閒謂之瓺{長}3。齊之東北海 岱之閒謂之儋4。5周 洛 韓 鄭之閒謂之甀{𦉈}，或謂之罃。', '[音義-1]：音臾\n[音義-2]：音殊\n[音義-3]：音暢，亦腸\n[音義-4]：音擔，字或作甔\n[郭注-5]：所謂「家無儋石之儲」者也。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '3294;5437;3561;6067;5024;3289', '9802;9802', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1627, '罃;㼰', '', '罃謂之㼰。1', '[音義-1]：音鼓鼙。', '卷五', '方言', '', DEFAULT, DEFAULT, '3294;8463', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1628, '𦉥;斯', '', '𦉨{𦉥}謂之㽄{斯}。', '', '卷五', '方言', '', DEFAULT, DEFAULT, '3292;9483', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1629, '缶;瓿禺;缾', '䍌甌', '缶謂之瓿㼴{瓿禺}[䍌甌]1，2其小者謂之瓶{缾}。', '[音義-1]：音偶\n[郭注-2]：卽盆也。', '卷五', '方言', '2', DEFAULT, DEFAULT, '3285;8465/5842;3291', '3290/8458', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1630, '罃甈;盎;盆;升甌;甂;是', '榹', '罃甈謂之盎1。2自關而西或謂之盆，或謂之盎。其小者謂之升甌3。甂4，陳 魏 宋 楚之閒謂之㼵{是}[榹]5。6自關而西謂之甂，其大者謂之甌。', '[音義-1]：卻罽反。烏浪反\n[郭注-2]：案爾雅：「甈，康瓠」，而方言以爲盆，未詳也。\n[音義-3]：惡牟反，亦音憂\n[音義-4]：音邊\n[音義-5]：杜啟反\n[郭注-6]：今河北人呼小盆爲㼵子。', '卷五', '方言', '6', DEFAULT, DEFAULT, '3294/8469;3146;3147;9505/8458;8464;1089', '3705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1631, '注斛;鬲;注箕;羅', '', '所以注斛，1陳 魏 宋 楚之閒謂之䈪{鬲}2。3自關而西謂之注箕。陳 魏 宋 楚之閒謂之籮{羅}。4', '[郭注-1]：盛米穀寫斛中者也。\n[音義-2]：音巫覡\n[郭注-3]：今江東亦呼爲䈪。\n[郭注-4]：䈪亦籮屬也，形小而高，無耳。', '卷五', '方言', '', DEFAULT, DEFAULT, '7275/9490;1853;7275/3018;4831', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1632, '炊䉛;縮;叜;㕣', '籔;籔;匴', '炊䉛謂之縮[籔]，1或謂之𥰞{叜}[籔]2，或謂之㔯{㕣}[匴]3。4', '[郭注-1]：漉米䉛也。\n[音義-2]：音藪\n[音義-3]：音旋\n[郭注-4]：江東呼淅籖。', '卷五', '方言', '2;3;4', DEFAULT, DEFAULT, '6455/2916;8563;1908;959', '2917;2917;8428', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1633, '篝;牆居', '', '篝，1陳 楚 宋 魏之閒謂之牆居。', '[郭注-1]：今薰籠也。', '卷五', '方言', '', DEFAULT, DEFAULT, '2932;3345/5402', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1634, '扇;箑', '', '扇，自關而東謂之箑1，2自關而西謂之扇。', '[音義-1]：音䈉\n[郭注-2]：今江東亦通名扇爲箑。', '卷五', '方言', '', DEFAULT, DEFAULT, '7722;2955', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1635, '碓機;梴;磑;妻', '碓磑', '碓機[碓磑]，1陳 魏 宋 楚自關而東謂之梴2。磑或謂之䃀{妻}3。4', '[郭注-1]：碓梢也。\n[音義-2]：音延\n[音義-3]：錯碓反\n[郭注-4]：卽磨也。', '卷五', '方言', '1', DEFAULT, DEFAULT, '6049/3723;3597;6048;8129', '6049/6048', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1636, '繘;綆;絡', '落', '繘1，自關而東周 洛 韓 魏之閒謂之綆，或謂之絡[落]2。關西謂之繘。3', '[音義-1]：音橘\n[音義-2]：音洛\n[郭注-3]：繘綆，汲水索也。', '卷五', '方言', '3', DEFAULT, DEFAULT, '8718;8719;8725', '549', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1637, '櫪;宿;草', '翮;槽;槽', '櫪[翮]，1梁 宋 齊 楚 北燕之閒或謂之樎{宿}[槽]2，或謂之皁{草}[槽]。3', '[郭注-1]：養馬器也。\n[音義-2]：音縮\n[郭注-3]：皁隸之名，於此乎出？', '卷五', '方言', '1;2;3', DEFAULT, DEFAULT, '3822;4600;680', '2253;3758;3758', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1638, '飤馬槖;裺囊;裺篼;婁篼;振', '裛囊;裛篼', '飤馬槖，自關而西謂之裺囊[裛囊]1，或謂之裺篼[裛篼]，或謂之㡞篼{婁篼}2。燕 齊之閒謂之帪{振}。3', '[音義-1]：音䳺\n[音義-2]：音樓\n[郭注-3]：廣雅作「振」，字音同耳。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '3222/6135/3902;5265/3903;5265/2962;8321/2962;7961', '5353/3903;5353/2962', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1639, '鉤;鹿觡;鉤格;微', '', '鉤，1宋 楚 陳 魏之閒謂之鹿觡，2或謂之鉤格。自關而西謂之鉤，或謂之䥩{微}3。', '[郭注-1]：懸物者。\n[郭注-2]：或呼鹿角。\n[音義-3]：音微', '卷五', '方言', '', DEFAULT, DEFAULT, '1460;6261/2848;1460/3601;1236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1640, '臿;𣂀;華;韋;畚;喿;梩', '釫;䤥;銚', '臿，燕之東北朝鮮 洌水之閒謂之𣂀1。2宋 魏之閒謂之鏵{華}[釫]，或謂之鍏{韋}[䤥]3。江 淮 南楚之閒謂之臿。沅 湘之閒謂之畚。趙 魏之閒謂之喿[銚]4。56東齊謂之梩7。', '[音義-1]：湯料反\n[郭注-2]：此亦鍫聲轉也。\n[音義-3]：音韋\n[音義-4]：字亦作鍫也\n[郭注-5]：江東又呼鍫刃爲鐅。\n[音義-6]：鐅，普蔑反。\n[音義-7]：音駭', '卷五', '方言', '3;4;6', DEFAULT, DEFAULT, '4524;9504;3884;3382;8447;1426;3687', '3686;9344;9307', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1641, '杷;渠挐;渠疏', '氍毹;氍毹', '杷，12宋 魏之閒謂之渠挐[氍毹]3，4或謂之渠疏[氍毹]。5', '[郭注-1]：有齒曰杷，無齒爲朳。\n[音義-2]：朳，音八。\n[音義-3]：諾豬反\n[郭注-4]：今江東名亦然。\n[郭注-5]：語轉也。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '3693;7263/7877;7263/9787', '5392/5393;5392/5393', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1642, '僉;欇殳;度;棓;柫;柍;孛', '柫', '僉，1宋 魏之閒謂之欇殳2，3或謂之度4，5自關而西謂之棓6，或謂之柫7。齊 楚 江 淮之閒謂之柍8，9或謂之桲{孛}[柫]10。', '[郭注-1]：今連枷，所以打穀者。\n[音義-2]：音殊\n[郭注-3]：亦杖名也。\n[音義-4]：音量度\n[郭注-5]：今江東呼打爲度。\n[音義-6]：蒲項反\n[音義-7]：音拂\n[音義-8]：音悵怏，亦音爲車鞅\n[郭注-9]：此皆打之別名也。\n[音義-10]：音勃', '卷五', '方言', '7', DEFAULT, DEFAULT, '3270;3574/1958;1928;3741;3696;3439;3870', '3696', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1643, '刈鉤;鉊;過;鎌;鍥', '戈', '刈鉤，江 淮 陳 楚之閒謂之鉊1，或謂之鐹{過}[戈]2。自關而西或謂之鉤，或謂之鎌，或謂之鍥3。', '[音義-1]：音昭\n[音義-2]：音果\n[音義-3]：音結', '卷五', '方言', '3', DEFAULT, DEFAULT, '8360/1460;9356;1110;9354;9355', '8373', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1644, '薄;䒼;𩍔;蓬薄', '䒼', '薄，宋 魏 陳 楚 江 淮之閒謂之䒼，或謂之麴{𩍔}[䒼]。1自關而西謂之薄，南楚謂之蓬薄。', '[郭注-1]：此直語楚聲轉耳。', '卷五', '方言', '3', DEFAULT, DEFAULT, '561;618;4363;671/561', '618', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1645, '橛;段', '', '橛，燕之東北朝鮮 洌水之閒謂之椴{段}1。2', '[音義-1]：音段\n[郭注-2]：楬杙也。江東呼都。', '卷五', '方言', '', DEFAULT, DEFAULT, '3737;1971', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1646, '槌;植;羊;栚;帶;㭙;僉;鐉;𦇏;𤪹', '㭙;鬵;𦇏', '槌1，2宋 魏 陳 楚 江 淮之閒謂之植[㭙]3。自關而西謂之槌。齊謂之样{羊}4。其橫，關西曰㮳{栚}5。67宋 魏 陳 楚 江 淮之閒謂之㯂{帶}8。齊部謂之㭙9。所以縣㯂，關西謂之䌞{僉}[鬵]10。東齊 海 岱之閒謂之𦇗{鐉}11。宋 魏 陳 楚 江 淮之閒謂之𦇏12，或謂之𤪹[𦇏]13。', '[音義-1]：度畏反\n[郭注-2]：縣蠶薄柱也。\n[音義-3]：音值\n[音義-4]：音陽\n[音義-5]：音朕\n[郭注-6]：亦名校。\n[音義-7]：校，音交。\n[音義-8]：音帶\n[音義-9]：丁革反\n[音義-10]：力冉反\n[音義-11]：相主反\n[音義-12]：音𢸃甲\n[音義-13]：音𣞲', '卷五', '方言', '2;7;10', DEFAULT, DEFAULT, '3715;3648;2332;3717;4865;3716;3270;9435;8573;108', '3716;1858;8573', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1647, '簟;笙;籧䒼;折;籧篨;蓋棪', '蓋簟', '簟，宋 魏之閒謂之笙，1或謂之籧䒼。自關而西謂之簟，或謂之䇽{折}，2其粗者謂之籧篨。34自關而東或謂之篕棪{蓋棪}[蓋簟]5。', '[郭注-1]：今江東通言笙。\n[郭注-2]：今云䇽，篾篷也。\n[郭注-3]：江東呼籧篨爲䉬。\n[音義-4]：䉬，音廢。\n[音義-5]：音剡', '卷五', '方言', '6', DEFAULT, DEFAULT, '2911;2987;2912/618;628;2912/2913;586/3448', '586/2911', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1648, '行唐;倚羊', '', '筕篖{行唐}，12自關而東周 洛 楚 魏之閒謂之倚佯{倚羊}3。自關而西謂之筕篖。南楚之外謂之篖。', '[郭注-1]：似籧篨，直文而粗。江東呼笪。\n[音義-2]：笪，音靼。\n[音義-3]：音羊', '卷五', '方言', '', DEFAULT, DEFAULT, '1266/878;5045/2332', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1649, '牀;簀;笫;杠;樹;趙;𣓀;版;牒;牑', '桃;枼', '牀，1齊 魯之閒謂之簀2。陳 楚之閒或謂之笫3。其杠，北燕 朝鮮之閒謂之樹。自關而西秦 晉之閒謂之杠。南楚之閒謂之趙[桃]。4東齊 海 岱之閒謂之𣘘{𣓀}5。其上板{版}，衞之北郊、趙 魏之閒謂之牒[枼]6，或曰牑7。', '[郭注-1]：牀版也。\n[音義-2]：音迮\n[音義-3]：音滓，又音姊\n[郭注-4]：趙當作桃，聲之轉也。中國亦呼杠爲桃牀，皆通語也。\n[音義-5]：音詵\n[音義-6]：音簡牒\n[音義-7]：履屬', '卷五', '方言', '6;9', DEFAULT, DEFAULT, '3677;2908;2909;3674;3558;1030;3426;4399;4402;4403', '3424;3814', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1650, '俎;几;㮐;桯;椸;虡', '㮐', '俎，几也。西南蜀 漢之郊曰杫{㮐}1。榻前几，江 沔之閒曰桯2。3趙 魏之閒謂之椸[㮐]4。几，其高者謂之虡5。6', '[音義-1]：音賜\n[音義-2]：音刑\n[郭注-3]：今江東呼爲承。\n[音義-4]：音易\n[音義-5]：音巨\n[郭注-6]：卽筍虡也。', '卷五', '方言', '5', DEFAULT, DEFAULT, '9472;9467;3701;3675;3836;3116', '3701', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1651, '籆;袁;格', '爰;檷', '籆，榬{袁}[爰]也1。2兗 豫 河 濟之閒謂之榬。絡謂之格[檷]。3', '[音義-1]：音爰\n[郭注-2]：所以絡絲者也。\n[郭注-3]：所以轉籆絡車也。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '2902;5313;3601', '2516;3722', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1652, '繀車;歷鹿車;道軌', '歷録車', '繀車1，趙 魏之閒謂之轣轆車{歷鹿車}[歷録車]，東齊 海 岱之閒謂之道軌。', '[音義-1]：蘇對反', '卷五', '方言', '2', DEFAULT, DEFAULT, '8529/9512;1070/6261/9512;1205/9582', '1070/9276/9512', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1653, '鍵;𨷲', '', '戸鑰，自關而東陳 楚之閒謂之鍵1。自關而西謂之鑰{𨷲}。', '[音義-1]：巨蹇反', '卷五', '方言', '', DEFAULT, DEFAULT, '9312;7772', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1654, '簙;蔽;箘;箭裏;簙毒;夗專;尊旋;棊;枰;廣平;局;曲道;弈', '簙筑', '簙謂之蔽，或謂之箘1。秦 晉之閒謂之簙。吳 楚之閒或謂之蔽，或謂之箭裏，或謂之簙毒[簙筑]，2或謂之夗專3，或謂之𠥙璇{尊旋}4，5或謂之棊。所以投簙謂之枰6。或謂之廣平。所以行棊謂之局，或謂之曲道。圍棊謂之弈，自關而東齊魯之閒皆謂之弈。', '[音義-1]：音囷\n[郭注-2]：簙箸，一名箭，廣雅云。\n[音義-3]：於辯反。音轉\n[音義-4]：銓、旋兩音\n[郭注-5]：或曰竹器，所以整頓簙者。\n[音義-6]：音評論', '卷五', '方言', '5', DEFAULT, DEFAULT, '3003;550;2867;2866/5260;3003/236;4328/1984;9879/4306;3754;3803;5938/3073;958;8442/1205;1770', '3003/2997', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1655, '𢕈;𤟌;欲', '', '聳{𢕈}1、𤟌，欲也。2荊 吳之閒曰聳，晉 趙曰𤟌。自關而西秦 晉之閒相勸曰聳，或曰𤟌。中心不欲而由旁人之勸，語亦曰聳。凡相被飾亦曰𤟌。', '[音義-1]：山項反\n[郭注-2]：皆強欲也。', '卷六', '方言', '', DEFAULT, DEFAULT, '7812;6324;5540', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1656, '𢕈;䏁;聾;𦘍;䎳', '', '聳{𢕈}、䏁1，聾也。半聾，梁 益之閒謂之䏁。2秦 晉之閒聽而不聰、聞而不達謂之䏁。生而聾，陳 楚 江 淮之閒謂之聳。3荆 揚之閒及山之東西雙聾者謂之聳。聾之甚者，秦 晉之閒謂之𦘍4。56吳 楚之外郊凡無有耳者亦謂之𦘍。其言𦘍者，若秦 晉中土謂墮耳者䎳也7。', '[音義-1]：音宰\n[郭注-2]：言胎䏁煩憒也。\n[郭注-3]：言無所聞，常聳耳也。\n[音義-4]：五刮反\n[郭注-5]：言聉無所聞知也，外傳：「聾聵伺火。」\n[音義-6]：聵，音蒯聵。\n[音義-7]：五刮反', '卷六', '方言', '', DEFAULT, DEFAULT, '7812;7813;7811;7817;7816', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1657, '陂;㑾;衺', '', '陂1、傜{㑾}2，衺也。陳 楚 荆 揚曰陂。自山而西凡物細大不純者謂之傜。3', '[音義-1]：音偏頗\n[音義-2]：音逍遙\n[郭注-3]：言俄傜也。', '卷六', '方言', '', DEFAULT, DEFAULT, '9625;5144;5347', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1658, '由迪;正', '邎迪', '由迪[邎迪]，正也。東齊 靑 徐之閒相正謂之由迪。', '', '卷六', '方言', '1', DEFAULT, DEFAULT, '8514/1132;1087', '1100/1132', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1659, '㥏;恧;慙;䀣', '', '㥏1、恧2，慙也。荆 揚 靑 徐之閒曰㥏，若梁 益 秦 晉之閒言心內慙矣。山之東西自愧曰恧。3趙 巍之閒謂之䀣4。', '[音義-1]：音腆\n[音義-2]：人力反，又女六反\n[郭注-3]：小雅曰：「心愧爲恧。」\n[音義-4]：音密，亦祕', '卷六', '方言', '', DEFAULT, DEFAULT, '6955;6958;6957;2123', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1660, '蹇;展;難;憚;燀', '憚;憚', '寋{蹇}1、展[憚]，難也。齊 晉曰寋。山之東西凡難貌曰展。荆 吳之人相難謂之展，若秦 晉之言相憚矣。齊 魯曰燀[憚]2。3', '[音義-1]：音蹇\n[音義-2]：昌羨反\n[郭注-3]：難而雄也。', '卷六', '方言', '2;5', DEFAULT, DEFAULT, '1395;5405;2401;6940;6454', '6940;6940', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1661, '胥;由;輔', '邎', '胥、由[邎]，輔也。1吳 越曰胥。燕之北鄙曰由。', '[郭注-1]：胥，相也。由，正也。皆謂輔持也。', '卷六', '方言', '2', DEFAULT, DEFAULT, '2698;8514;9565', '1100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1662, '蛩㤨;戰栗;恐', '', '蛩㤨1，戰慄{戰栗}也。荆 吳曰蛩㤨。蛩㤨，又恐也。', '[音義-1]：鞏、恭兩音', '卷六', '方言', '', DEFAULT, DEFAULT, '8927/6946;8382/4391;6942', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1663, '錪;錘;重', '', '錪1、錘2，重也。東齊之閒曰錪。宋 魯曰錘。', '[音義-1]：吐本反\n[音義-2]：直睡反', '卷六', '方言', '', DEFAULT, DEFAULT, '9301;9376;5242', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1664, '含;龕;受;盛', '堪', '鋡{含}1、龕[堪]，受也。2齊 楚曰鋡。揚 越曰龕。受，盛也，猶秦 晉言容盛也。', '[音義-1]：音含\n[郭注-2]：今云龕囊，依此名也。', '卷六', '方言', '2', DEFAULT, DEFAULT, '815;7689;2518;3140', '9052', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1665, '矔;眮', '', '矔1、眮2，轉目也。梁 益之閒瞋目曰矔，轉目顧視亦曰矔。吳 楚曰眮。', '[音義-1]：音慣習\n[音義-2]：音侹侗', '卷六', '方言', '', DEFAULT, DEFAULT, '2116;2122', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1666, '逴;騷;逴;蹇', '簫', '逴1、騷[簫]2、𡯴{逴}，蹇也。3吳 楚偏蹇曰騷，齊 楚 晉曰逴。4', '[音義-1]：勑略反\n[音義-2]：先牢反\n[郭注-3]：跛者行𧿒踔也。\n[郭注-4]：行略逴也。', '卷六', '方言', '2', DEFAULT, DEFAULT, '1201;6229;1201;1395', '2990', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1667, '𤺊;嗌;噎', '', '𤺊1、嗌2，噎也3。4楚曰𤺊。秦 晉或曰嗌，又曰噎。', '[音義-1]：音斯\n[音義-2]：惡介反\n[音義-3]：音翳\n[郭注-4]：皆謂咽痛也。', '卷六', '方言', '', DEFAULT, DEFAULT, '4719;790;881', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1668, '怠;阤;壞', '殆', '怠[殆]、阤1，壞也。2', '[音義-1]：音蟲豸\n[郭注-2]：謂壞落也。', '卷六', '方言', '1', DEFAULT, DEFAULT, '6837;9650;9118', '2548', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1669, '涅;墊;下', '', '㘿{涅}1、墊2，下也。凡柱而下曰㘿，屋而下曰墊。', '[音義-1]：音涅\n[音義-2]：丁念反', '卷六', '方言', '', DEFAULT, DEFAULT, '7232;9085;9', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1670, '勿;邈;離;越;遠', '', '伆{勿}1、邈，離也。2楚謂之越，或謂之遠。吳 越曰伆。', '[音義-1]：音刎\n[郭注-2]：謂乖離也。', '卷六', '方言', '', DEFAULT, DEFAULT, '6071;1214;2293;990;1198', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1671, '顚;頂;上', '', '顚、頂，上也。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '5606;5607;6', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1672, '誣;㛪与;阿与', '', '誣、䛳1，与也。吳 越曰誣。荆 齊曰䛳与{㛪与}，猶秦 晉言阿与。2', '[音義-1]：乙劒反\n[郭注-2]：相阿与者，所以致誣䛳也。', '卷六', '方言', '', DEFAULT, DEFAULT, '1609;8333/9466;9624/9466', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1673, '掩;𡩡;取;狙', '揜;抯', '掩[揜]、𡩡，取也。自關而東曰掩。自關而西曰𡩡，或曰狙[抯]。1', '[郭注-1]：狙伺也。', '卷六', '方言', '1;4', DEFAULT, DEFAULT, '8033;4615;1925;6374', '7910;7981', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1674, '䁙;䀩;視', '', '䁙1、䀩2，視也。東齊曰䁙。吳 揚曰䀩。3凡以目相戲曰䁙。', '[音義-1]：烏拔反\n[音義-2]：音略\n[郭注-3]：今中國亦云目䀩也。', '卷六', '方言', '', DEFAULT, DEFAULT, '2168;2178;5475', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1675, '遙;廣;遠', '繇', '遙[繇]、廣，遠也。梁 楚曰遙。', '', '卷六', '方言', '1', DEFAULT, DEFAULT, '1222;5938;1198', '8514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1676, '汩;遙;疾行', '𡿯;搖', '汩[𡿯]1、遙[搖]，疾行也。南楚之外曰汩，2或曰遙。', '[音義-1]：于筆反\n[郭注-2]：汩汩，急貌也。', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '7448;1222;4694/1266', '7485;7949', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1677, '蹇;妯;擾', '怞', '蹇、妯[怞]1，擾也。2人不靜曰妯。秦 晉曰蹇。齊 宋曰妯。', '[音義-1]：音迪\n[郭注-2]：謂躁擾也。', '卷六', '方言', '2', DEFAULT, DEFAULT, '1395;8287;7933', '6784', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1678, '絓;挈;煢;介;特;雙;乘', '閨;絜/孑;赹;孑', '絓[閨]1、挈[絜/孑]2、𠎽{煢}[赹]3、介[孑]，特也。楚曰𠎽。晉曰絓。秦曰挈。物無耦曰特，獸無耦曰介。4飛鳥曰雙，鴈曰乘。', '[音義-1]：音乖\n[音義-2]：口八反\n[音義-3]：古煢字\n[郭注-4]：傳曰：「逢澤有介麋」。', '卷六', '方言', '1;2;3;4', DEFAULT, DEFAULT, '8527;7857;7700;714;731;2364;3410', '7735;8752/0/9780;1015;9780', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1679, '台;旣;失', '駘', '台[駘]、旣，失也。宋 魯之閒曰台。', '', '卷六', '方言', '1', DEFAULT, DEFAULT, '868;3197;7976', '6232', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1680, '旣;隱;據;定', '𤔌', '旣、隱[𤔌]、據，定也。', '', '卷六', '方言', '2', DEFAULT, DEFAULT, '3197;9667;7865;4574', '2521', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1681, '稟;浚;敬', '𠘡;恮', '稟[𠘡]、浚[恮]，敬也。秦 晉之閒曰稟。齊曰浚。吳 楚之閒自敬曰稟。', '', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '3341;7372;5818', '7519;6748', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1682, '悛;懌;改', '譯', '悛1、懌[譯]2，改也。自山而東或曰悛，或曰懌。3', '[音義-1]：音銓\n[音義-2]：音弈\n[郭注-3]：論語曰：「悅而不懌」。', '卷六', '方言', '2', DEFAULT, DEFAULT, '6792;6978;2018', '1722', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1683, '坻;坥;場', '', '坻1、坥2，場也3。梁 宋之閒蚍蜉䵓鼠之場謂之坻。4螾場謂之坥5。6', '[音義-1]：音水泜\n[音義-2]：音癰疽\n[音義-3]：音傷\n[郭注-4]：䵓鼠，蚡鼠也。\n[音義-5]：音引\n[郭注-6]：螾，蛐蟮也，其糞名坥。', '卷六', '方言', '', DEFAULT, DEFAULT, '9086;9134;9145', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1684, '徥;用;行', '', '徥1、用，行也。朝鮮 洌水之閒或曰徥。2', '[音義-1]：度揩反\n[郭注-2]：徥𢔡，行貌。', '卷六', '方言', '', DEFAULT, DEFAULT, '1237;2081;1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1685, '鋪頒;索;斗藪', '布班;𡩡', '鋪頒[布班]1，索[𡩡]也。東齊曰鋪頒，猶秦 晉言抖藪{斗藪}也。2', '[音義-1]：音敷\n[郭注-2]：謂斗藪舉索物也。', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '9434/5629;3871;9489/563', '4910/222;4615', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1686, '參;蠡;分;離', '', '參、蠡1，分也。2齊曰參。楚曰蠡。秦 晉曰離。', '[音義-1]：音麗\n[郭注-2]：謂分割也。', '卷六', '方言', '', DEFAULT, DEFAULT, '4315;8965;708;2293', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1687, '𤺊;披;散;釁;㠲', '柀', '𤺊、披[柀]，散也。東齊聲散曰𤺊，器破曰披。秦 晉聲變曰𤺊，器破而不殊，其音亦謂之𤺊。器破而未離謂之璺{釁}1，南楚之閒謂之𢻹{㠲}2。', '[音義-1]：音問\n[音義-2]：妨美反，一音圮塞', '卷六', '方言', '2', DEFAULT, DEFAULT, '4719;7945;2720;1789;4882', '3464', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1688, '緍;緜;施', '', '緍1、緜，施也。秦曰緍。趙曰緜。吳 越之閒脫衣相被謂之緍緜。2', '[音義-1]：音旻\n[郭注-2]：相覆及之名也。', '卷六', '方言', '', DEFAULT, DEFAULT, '8723;8513;4301', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1689, '悀;畐;滿', '涌', '悀[涌]1、偪{畐}2，滿也。凡以器盛而滿謂之悀，3腹滿曰偪。4', '[音義-1]：音踊\n[音義-2]：妨逼反\n[郭注-3]：言涌出也。\n[郭注-4]：言勑偪也。', '卷六', '方言', '1', DEFAULT, DEFAULT, '9243;3338;7217', '7190', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1690, '徯醯;冉鎌;危', '', '徯醯1、冉鐮{冉鎌}2，危也。東齊㨳3物而危謂之徯醯，僞物謂之冉鐮。', '[音義-1]：音醯酢\n[音義-2]：音髯\n[音義-3]：居枝反', '卷六', '方言', '', DEFAULT, DEFAULT, '1244/3152;6073/9354;6007', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1691, '紕;繹;督;雉;理', '比;夷', '紕[比]1、繹2、督、雉[夷]，理也。秦 晉之閒曰紕。凡物曰督之，3絲曰繹之。4', '[音義-1]：音𣬈\n[音義-2]：音亦\n[郭注-3]：言正理也。\n[郭注-4]：言解繹也。', '卷六', '方言', '1;4', DEFAULT, DEFAULT, '8759;8518;2171;2288;147', '5227;6613', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1692, '矤;呂;長', '', '矤1、呂，長也。東齊曰矤。宋 魯曰呂。', '[音義-1]：古矧字', '卷六', '方言', '', DEFAULT, DEFAULT, '3314;4630;6067', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1693, '屈;膐;力', '𠢤', '䠇{屈}[𠢤]、膐，力也。東齊曰䠇。1宋 魯曰膐。膐，田力也。2', '[郭注-1]：律䠇，多力貌。\n[郭注-2]：謂耕墾也。', '卷六', '方言', '1', DEFAULT, DEFAULT, '5428;4630;9210', '9224', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1694, '瘱;帶;審', '諟', '瘱1、𧫚{帶}[諟]2，審也。齊 楚曰瘱。秦 晉曰𧫚。', '[音義-1]：音瘞埋，又翳\n[音義-2]：音瓜蔕', '卷六', '方言', '2', DEFAULT, DEFAULT, '6736;4865;722', '1515', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1695, '壹帶;諟', '瘱諟', '𧬇𧫚{壹帶}[瘱諟]1，諟也2。3吳 越曰𧬇𧫚。', '[音義-1]：翳、蔕兩音\n[音義-2]：音帝\n[郭注-3]：諟亦審，互見其義耳。', '卷六', '方言', '1', DEFAULT, DEFAULT, '6642/4865;1515', '6736/1515', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1696, '音;揜;錯;摩;藏', '揜;臧', '揞{音}[揜]1、揜、錯2、摩，藏[臧]也。荆 楚曰揞。吳 揚曰揜。周 秦曰錯。陳之東鄙曰摩。', '[音義-1]：烏感反\n[音義-2]：音酢', '卷六', '方言', '1;5', DEFAULT, DEFAULT, '1738;7910;9327;8009;695', '7910;1954', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1697, '去摸;去', '', '抾摸{去摸}，去也。齊 趙之總語也。袪摸猶言持去也。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '3164/8024;3164', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1698, '舒勃;展', '', '舒勃，展也。東齊之閒凡展物謂之舒勃。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '2510/9244;5405', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1699, '摳揄;旋', '', '摳揄，旋也。秦 晉凡物樹稼早成熟謂之旋。燕 齊之閒謂之摳揄。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '7835/7968;4306', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1700, '緪;筳;竟', '㮓', '緪[㮓]1、筳2，竟也。秦 晉或曰緪，或曰竟。楚曰筳。', '[音義-1]：岡鄧反\n[音義-2]：湯丁反', '卷六', '方言', '1', DEFAULT, DEFAULT, '8717;2903;1743', '3817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1701, '閻;業;續;擘;紉', '𢪈;緁;襞', '䌪{閻}[𢪈]1、𠟪{業}[緁]2，續也。秦 晉續折謂之䌪，繩索謂之𠟪。擘[襞]3，楚謂之紉4。5', '[音義-1]：音剡\n[音義-2]：音妾\n[音義-3]：音檗\n[音義-4]：音刃\n[郭注-5]：今亦以綫貫針爲紉。', '卷六', '方言', '1;2;4', DEFAULT, DEFAULT, '7740;1749;8548;8019;8691', '7867;8678;5336', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1702, '閻苫;開;闓', '𤴿苫', '閻苫[𤴿苫]，開也。東齊開戸謂之閻苫。楚謂之闓。1', '[郭注-1]：亦開字也。', '卷六', '方言', '1', DEFAULT, DEFAULT, '7740/587;7754;7755', '4761/587', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1703, '杼;柚;作', '軸', '杼、柚[軸]，作也。東齊土作謂之杼，木作謂之柚。', '', '卷六', '方言', '2', DEFAULT, DEFAULT, '3725;3414;5073', '9543', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1704, '厲;卬;爲', '', '厲、卬，爲也。1甌 越曰卬。吳曰厲。', '[郭注-1]：爾雅曰：「俶、厲，作。」爲亦作也。', '卷六', '方言', '', DEFAULT, DEFAULT, '5983;5221;1881', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1705, '戲;憚;怒', '', '戲、憚，怒也。齊曰戲。楚曰憚。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '8383;6940;6877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1706, '爰;爰;恚', '咺;咺', '爰[咺]、喛{爰}[咺]，恚也。1楚曰爰。秦 晉曰喛。皆不欲譍而強畣之意也。', '[郭注-1]：謂悲恚也。', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '2516;2516;6875', '796;796', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1707, '叜;艾;長;老;公;翁;父;父老;皇;母姼;父姼', '餀;母媞;父媞', '𠋢{叜}、艾[餀]、長，老也。東齊 魯 衞之閒凡尊老謂之𠋢，或謂之艾。1周 晉 秦 隴謂之公，或謂之翁。南楚謂之父，或謂之父老。南楚 𤄗 洭之閒2，3母謂之媓{皇}，謂婦妣曰母姼[母媞]4，稱婦考曰父姼[父媞]。5', '[郭注-1]：禮記曰：「五十爲艾。」\n[音義-2]：㬥、匡兩音\n[郭注-3]：洭水在桂陽。\n[音義-4]：音多\n[郭注-5]：古者通以考妣爲生存之稱。', '卷六', '方言', '2;10;11', DEFAULT, DEFAULT, '1908;389;6067;5375;716;2248;1907;1907/5375;80;8139/8157;1907/8157', '3246;8139/8228;1907/8228', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1708, '巍;嶢;崝;險;高', '', '巍、嶢、崝、嶮{險}，高也。1', '[郭注-1]：嶕嶢、崝嵤，皆高峻之貌也。', '卷六', '方言', '', DEFAULT, DEFAULT, '5847;5897;5891;9629;3318', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1709, '猒;塞;安', '懕;㥶', '猒[懕]、塞[㥶]，安也。1', '[郭注-1]：物足則定。', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '3040;9093;4576', '6796;6758', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1710, '夌;𢜮;憐', '憐', '㥄{夌}[憐]1、𢜮2，憐也。', '[音義-1]：音凌\n[音義-2]：亡主反', '卷六', '方言', '1', DEFAULT, DEFAULT, '3364;6785;6960', '6960', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1711, '掩;翳;𥴨', '', '掩、翳，薆{𥴨}也1。2', '[音義-1]：音愛\n[郭注-2]：謂蔽薆也，詩曰：「薆而不見」。', '卷六', '方言', '', DEFAULT, DEFAULT, '8033;2273;3005', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1712, '佚愓;緩', '', '佚愓1，緩也。', '[音義-1]：跌、唐兩音', '卷六', '方言', '', DEFAULT, DEFAULT, '5142/6847;8779', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1713, '諄憎;疾;可惡', '敦憎', '諄憎[敦憎]1，所疾也。宋 魯凡相惡謂之諄憎，若秦 晉言可惡矣。', '[音義-1]：之潤反', '卷七', '方言', '1', DEFAULT, DEFAULT, '1503/6881;4694;3058/6880', '2038/6881', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1714, '杜;蹻;歰', '', '杜、蹻1，歰也。趙曰杜。2山之東西或曰蹻。3', '[音義-1]：音笑噱\n[郭注-2]：今俗語通言歰如杜，杜棃子歰，因名之。\n[郭注-3]：卻蹻，燥歰貌。', '卷七', '方言', '', DEFAULT, DEFAULT, '3431;1343;1077', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1715, '䄪/到;抗;縣', '', '𠄏{䄪/到}1、抗，縣也。趙 魏之閒曰𠄏。自山之東西曰抗。燕 趙之郊縣物於臺之上謂之𠄏。2', '[音義-1]：音佻，又丁小反\n[郭注-2]：了𠄏，縣物貌。', '卷七', '方言', '', DEFAULT, DEFAULT, '4448/0/7707;8066;5705', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1716, '發;稅;舍車', '挩', '發、稅[挩]，舍車也1。東齊 海 岱之閒謂之發。2宋 趙 陳 魏之閒謂之稅。3', '[音義-1]：宜音寫\n[郭注-2]：今通言發寫也。\n[郭注-3]：稅猶脫也。', '卷七', '方言', '2', DEFAULT, DEFAULT, '8502;4486;3273/9512', '7977', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1717, '肖;類;法;哨', '嗾', '肖、類，法也。齊曰類。西楚 梁 益之閒曰肖。秦 晉之西鄙、自冀 隴而西，1使犬曰哨[嗾]2。西南梁 益之閒凡言相類者亦謂之肖。3', '[郭注-1]：冀縣，今在天水。\n[音義-2]：音騷\n[郭注-3]：肖者，似也。', '卷七', '方言', '4', DEFAULT, DEFAULT, '2643;6369;6260;929', '943', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1718, '憎;讓;憚', '', '憎、懹{讓}，憚也。1陳曰懹。', '[郭注-1]：相畏憚也。', '卷七', '方言', '', DEFAULT, DEFAULT, '6881;1692;6940', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1719, '譙;讙;讓', '', '譙1、讙2，讓也。齊 楚 宋 衞 荆 陳之閒曰譙。自關而西秦 晉之閒凡言相責讓曰譙讓。北燕曰讙。', '[音義-1]：字或作誚\n[音義-2]：火袁反', '卷七', '方言', '', DEFAULT, DEFAULT, '1693;1657;1692', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1720, '僉;胥;皆', '', '僉、胥，皆也。自山而東五國之郊曰僉。1東齊曰胥。', '[郭注-1]：六國惟秦在山西。', '卷七', '方言', '', DEFAULT, DEFAULT, '3270;2698;2226', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1721, '侔莫;強', '勖慔;彊', '侔莫[勖慔]，強[彊]也。北燕之外郊凡勞而相勉若言努力者謂之侔莫。', '', '卷七', '方言', '1;2', DEFAULT, DEFAULT, '5037/701;8824', '9222/6787;8489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1722, '巩松;罵', '巩庸', '𠌖倯{巩松}[巩庸]1，罵也。2燕之北郊曰𠌖倯。', '[音義-1]：音邛竹\n[郭注-2]：羸小可憎之名也。', '卷七', '方言', '1', DEFAULT, DEFAULT, '1887/3543;4845', '1887/2083', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1723, '展;惇;信', '眞', '展[眞]、惇，信也。東齊 海 岱之閒曰展。燕曰惇。1', '[郭注-1]：惇亦誠信貌。', '卷七', '方言', '1', DEFAULT, DEFAULT, '5405;6727;1523', '5213', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1724, '斯;𨤏;離', '播', '斯、𨤏[播]，離也。齊 陳曰斯。燕之外郊朝鮮 洌水之閒曰𨤏。', '', '卷七', '方言', '2', DEFAULT, DEFAULT, '9483;721;2293', '8036', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1725, '蝎;噬;逮', '遏;逝', '蝎[遏]1、噬[逝]2，逮也。東齊曰蝎。北燕曰噬。逮，通語也。', '[音義-1]：音曷\n[音義-2]：音卜筮', '卷七', '方言', '1;2', DEFAULT, DEFAULT, '8823;811;1147', '1185;1105', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1726, '皮傅;彈憸;強', '彊', '皮傅、彈憸1，強[彊]也。2秦 晉言非其事謂之皮傅。東齊 陳 宋 江 淮之閒曰彈憸。', '[音義-1]：音僉\n[郭注-2]：謂強語也。', '卷七', '方言', '3', DEFAULT, DEFAULT, '1987/5042;8501/6805;8824', '8489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1727, '膊;曬;晞;㬥', '', '膊1、曬2、晞，㬥也。東齊及秦之西鄙言相㬥僇爲膊。3燕之外郊朝鮮 洌水之閒凡㬥肉發人之私、披牛羊之五藏謂之膊。㬥五穀之類，秦 晉之閒謂之曬。東齊 北燕 海 岱之郊謂之晞。', '[音義-1]：音膊脯，又普博反\n[音義-2]：霜智反\n[郭注-3]：㬥僇，謂相㬥𣩊惡事。', '卷七', '方言', '', DEFAULT, DEFAULT, '2688;4247;4249;4246', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1728, '熬;取;煎;備;鞏', '𩱦;𤐸;烘', '熬、㷅{取}[𩱦]1、煎、㷶{備}[𤐸]2、鞏[烘]3，火乾也。凡以火而乾五穀之類，自山而東齊 楚以往謂之熬。關西 隴 冀以往謂之㷶。秦 晉之閒或謂之㷅。凡有汁而乾謂之煎，東齊謂之鞏。', '[音義-1]：卽𩱈字也。創眇反\n[音義-2]：皮力反\n[音義-3]：音拱手', '卷七', '方言', '2;4;5', DEFAULT, DEFAULT, '6460;1925;6459;5028;1800', '1875;6464;6456', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1729, '胹;飪;亨;爛;糦;酋;酷;熟', '秋;焅', '胹1、飪2、亨、爛、糦3、酋[秋]4、酷[焅]，熟也。自關而西秦 晉之郊曰胹。徐 揚之閒曰飪。嵩嶽以南陳 潁之閒曰亨。自河以北趙 魏之閒火熟曰爛，氣熟曰糦，久熟曰酋，穀熟曰酷。熟，其通語也。', '[音義-1]：音而\n[音義-2]：音荏\n[音義-3]：音熾\n[音義-4]：音囚', '卷七', '方言', '6;7', DEFAULT, DEFAULT, '2699;3207;3331;6468;3217;9878;9827;1885', '4491;6519', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1730, '嫢盈;怒', '', '嫢盈，怒也。燕之外郊朝鮮 洌水之閒凡言呵叱者謂之嫢盈。', '', '卷七', '方言', '', DEFAULT, DEFAULT, '8227/3155;6877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1731, '長敄;豈企;立', '', '䠆𨂣{長敄}1、隑企{豈企}2，立也。東齊 海 岱 北燕之郊跪謂之䠆𨂣，3委痿謂之隑企。4', '[音義-1]：音務\n[音義-2]：欺豉反\n[郭注-3]：今東郡人亦呼長跽爲䠆𨂣。\n[郭注-4]：腳躄不能行也。', '卷七', '方言', '', DEFAULT, DEFAULT, '6067/2000;3092/4952;6677', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1732, '瀧涿;霑漬', '', '瀧涿謂之霑漬1。2', '[音義-1]：音籠\n[郭注-2]：瀧涿猶瀨滯也。', '卷七', '方言', '', DEFAULT, DEFAULT, '7312/7311;7553/7325', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1733, '晞;鑠;摩;呂', '鑢', '希{晞}、鑠，摩也。燕 齊摩鋁{呂}[鑢]謂之希1。', '[音義-1]：音慮', '卷七', '方言', '4', DEFAULT, DEFAULT, '4249;9279;8009;4630', '9370', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1734, '平均;賦', '', '平均，賦也。燕之北鄙、東齊北郊凡相賦斂謂之平均。', '', '卷七', '方言', '', DEFAULT, DEFAULT, '3073/9028;3981', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1735, '羅;離', '', '羅謂之離，離謂之羅。1', '[郭注-1]：皆行列物也。', '卷七', '方言', '', DEFAULT, DEFAULT, '4831;2293', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1736, '釗;超;遠', '迢;迢', '釗[迢]、超[迢]，遠也。燕之北郊曰釗。東齊曰超。', '', '卷七', '方言', '1;2', DEFAULT, DEFAULT, '2794;983;1198', '1220;1220', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1737, '漢漫;辰眩;懣', '熯懣', '漢漫[熯懣]、䀼眩{辰眩}1，懣也。朝鮮 洌水之閒煩懣謂之漢漫，顚眴謂之䀼眩。', '[音義-1]：音瞋恚。音懸', '卷七', '方言', '1', DEFAULT, DEFAULT, '7007/1910;9793/2098;6890', '6432/6890', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1738, '憐職;愛', '㤅', '憐職，愛[㤅]也。言相愛憐者，吳 越之閒謂之憐職。', '', '卷七', '方言', '2', DEFAULT, DEFAULT, '6960/7805;3367', '6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1739, '茹;食', '', '茹1，食也。吳 越之閒凡貪飲食者謂之茹。2', '[音義-1]：音勝如\n[郭注-2]：今俗呼能粗食者爲茹。', '卷七', '方言', '', DEFAULT, DEFAULT, '614;3204', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1740, '竘;貌;治;巧', '', '竘1、貌，治也。2吳 越飾貌爲竘，或謂之巧。3', '[音義-1]：恪垢反\n[郭注-2]：謂治作也。\n[郭注-3]：語楚聲轉耳。', '卷七', '方言', '', DEFAULT, DEFAULT, '6686;5466;7087;3031', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1741, '煦;叚;熱;乾', '煦', '煦1、煆{叚}[煦]2，熱也，乾也。3吳 越曰煦煆。', '[音義-1]：音州吁\n[音義-2]：呼夏反\n[郭注-3]：熱則乾燥。', '卷七', '方言', '2', DEFAULT, DEFAULT, '6431;1927;6510;9741', '6431', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1742, '嬴;膂;賀;幐;儋;負佗', '何', '攍{嬴}1、膂、賀[何]、幐2，儋也。3齊 楚 陳 宋之閒曰攍。4燕之外郊、越之垂、甌 吳之外鄙謂之膂。5南楚或謂之攍。自關而西隴 冀以往謂之賀。6凡以驢馬馲駝載物者謂之負佗7，亦謂之賀。', '[音義-1]：音盈\n[音義-2]：音鄧\n[郭注-3]：今江東呼擔兩頭有物爲幐。\n[郭注-4]：莊子曰：「攍粮而赴之。」\n[郭注-5]：擔者用膂力，因名云。\n[郭注-6]：今江東語亦然。\n[音義-7]：音大', '卷七', '方言', '3', DEFAULT, DEFAULT, '8114;4630;3945;4903;5024;3963/5022', '5023', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1743, '樹植;立', '', '樹植，立也。燕之外郊朝鮮 洌水之閒凡言置立者謂之樹植。', '', '卷七', '方言', '', DEFAULT, DEFAULT, '3558/3648;6677', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1744, '過度;涉濟', '', '過度謂之涉濟。1', '[郭注-1]：猶今云濟度。', '卷七', '方言', '', DEFAULT, DEFAULT, '1110/1928;7475/7093', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1745, '福祿;祓戩', '', '福祿謂之祓戩1。', '[音義-1]：廢、箭兩音', '卷七', '方言', '', DEFAULT, DEFAULT, '20/15;49/8393', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1746, '察;眙;逗', '刹', '傺{察}[刹]1、眙2，逗也3。南楚謂之傺。西秦謂之眙。4逗，其通語也。', '[音義-1]：音際\n[音義-2]：敕吏反\n[音義-3]：卽今住字也\n[郭注-4]：西秦，酒泉、燉煌、張掖是也。眙謂住視也。', '卷七', '方言', '1', DEFAULT, DEFAULT, '4581;2176;1153', '2810', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1747, '虎;李父;李耳;於䖘;伯都', '', '虎，陳 魏 宋 楚之閒或謂之李父。江 淮 南楚之閒謂之李耳，1或謂之於䖘2。3自關東西或謂之伯都。4', '[郭注-1]：虎食物值耳卽止，以觸其諱故。\n[音義-2]：音烏。音狗竇\n[郭注-3]：今江南山夷呼虎爲䖘。\n[郭注-4]：俗曰伯都事神虎說。', '卷八', '方言', '', DEFAULT, DEFAULT, '3117;3423/1907;3423/7792;2487/3133;4965/4005', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1748, '貔;來;否;貍', '', '𧴀{貔}1，2陳 楚 江 淮之閒謂之𧳟{來}3。北燕 朝鮮之閒謂之𧳏{否}4。5關西謂之狸{貍}。6', '[音義-1]：音𣬈\n[郭注-2]：狸別名也。未聞語所出。\n[音義-3]：音來\n[音義-4]：音丕\n[郭注-5]：今江南呼爲𧳏狸。\n[郭注-6]：此通名耳。', '卷八', '方言', '', DEFAULT, DEFAULT, '6114;3346;934;6124', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1749, '貛;貒', '', '貛1，2關西謂之貒3。', '[音義-1]：音歡\n[郭注-2]：豚也。\n[音義-3]：音波湍', '卷八', '方言', '', DEFAULT, DEFAULT, '6126;6125', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1750, '雞;鸊氏;割雞;從;抱;鷇;涅', '鸊鷉', '雞，陳 楚 宋 魏之閒謂之鸊䲬{鸊氏}[鸊鷉]1。桂林之中謂之割雞，或曰𩀨{從}2。北燕 朝鮮 洌水之閒謂伏雞曰抱3。45爵子及雞雛皆謂之鷇6。7其卵伏而未孚始化謂之涅。', '[音義-1]：避、祇兩音\n[音義-2]：音從\n[音義-3]：房奧反\n[郭注-4]：江東呼蓲。\n[音義-5]：蓲，央富反。\n[音義-6]：音顧，又恪遘反\n[郭注-7]：關西曰鷇。', '卷八', '方言', '2', DEFAULT, DEFAULT, '2290;2429/8367;2777/2290;5225;7909;2479;7232', '2429/2430', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1751, '豬;豭;彘;豕;豨;豚;豯;豬子;竲', '', '豬，北燕 朝鮮之閒謂之豭。1關東西或謂之彘，或謂之豕。南楚謂之豨，其子或謂之豚，或謂之豯2。吳 揚之閒謂之豬子，其檻及蓐曰橧{竲}3。4', '[郭注-1]：猶云豭斗也。\n[音義-2]：音奚\n[音義-3]：音繒\n[郭注-4]：爾雅曰：「所寢，橧。」', '卷八', '方言', '', DEFAULT, DEFAULT, '6077;6084;6104;6076;6093;6108;6079;6077/9764;6695', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1752, '布穀;結誥;擊穀', '秸𪈓', '布穀，自關東西梁 楚之閒謂之結誥[秸𪈓]。周 魏之閒謂之擊穀。自關而西或謂之布穀。1', '[郭注-1]：今江東呼爲穫穀。', '卷八', '方言', '2', DEFAULT, DEFAULT, '4910/4483;8575/1528;8064/4483', '4472/2380', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1753, '旱鴠;定甲;獨舂;城旦;倒縣;鶡鴠', '渴鴠', '䳚鴠{旱鴠}1，2周 魏 齊 宋 楚之閒謂之定甲，或謂之獨舂。3自關而東謂之城旦。4或謂之倒縣，5或謂之䳚鴠。自關而西秦 隴之內謂之鶡鴠[渴鴠]。', '[音義-1]：侃、旦兩音\n[郭注-2]：鳥似雞，五色，冬無毛，赤倮，晝夜鳴。\n[郭注-3]：好自低仰。\n[郭注-4]：言其辛苦有似於罪禍者。\n[郭注-5]：好自縣於樹也。', '卷八', '方言', '6', DEFAULT, DEFAULT, '4228/2382;4574/9739;6355/4522;9081/4283;5200/5705;2469/2382', '7343/2382', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1754, '鳩;良皋;鸊皋;匊鳩;鳻鳩;役鳩;癸鳩;孚鳩;鶻鳩;鵻', '頒鳩', '鳩，自關而東周 鄭之郊、韓 魏之都謂之𪁜鷎{良皋}1，其𪁛鳩謂之鸊鷎{鸊皋}。自關而西秦 漢之閒謂之鵴鳩{匊鳩}2，其大者謂之鳻鳩[頒鳩]3，其小者謂之𪁛鳩{役鳩}，4或謂之䳫鳩{癸鳩}5，或謂之䳕鳩{孚鳩}6，或謂之鶻鳩。梁 宋之閒謂之鵻。', '[音義-1]：郎、皋兩音\n[音義-2]：音菊花\n[音義-3]：音班\n[郭注-4]：今荆鳩也。\n[音義-5]：音葵\n[音義-6]：音浮', '卷八', '方言', '5', DEFAULT, DEFAULT, '2375;3339/6660;2429/6660;5803/2375;2482/2375;1976/2375;9763/2375;1880/2375;2378/2375;2377', '5629/2375', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1755, '尸鳩;畐不;戴任;戴南;䲱澤;戴鳻;戴勝;任;服畐;复畐;或', '戴任;𪀐鵖', '䲩鳩{尸鳩}1，2燕之東北朝鮮 洌水之閒謂之鶝鴀{畐不}3。自關而東謂之戴鵀{戴任}。東齊 海 岱之閒謂之戴南[戴任]，南猶鵀也，4或謂之鶭鸅{䲱澤}，5或謂之戴鳻，或謂之戴勝。6東 齊 吳 揚之閒謂之鵀{任}。自關而西謂之服鶝{服畐}，或謂之𪃃鶝{复畐}[𪀐鵖]。燕之東北朝鮮 洌水之閒謂之𪂉{或}7。', '[音義-1]：音尸\n[郭注-2]：按爾雅卽布穀，非戴勝也，或云鸇，皆失之也。\n[音義-3]：福、不兩音\n[郭注-4]：此亦語楚聲轉也。\n[郭注-5]：按爾雅說戴鵀，下鶭鸅自別一鳥名，方言似依此義，又失也。\n[郭注-6]：勝，所以纏絍。\n[音義-7]：音域', '卷八', '方言', '4;10', DEFAULT, DEFAULT, '5400/2375;3338/7704;1778/5087;1778/3873;2391/7220;1778/2482;1778/9227;5087;5447/3338;3363/3338;8385', '1778/5087;2434/2435', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1756, '蝙蝠;服翼;飛鼠;老鼠;㒨鼠;戠墨', '', '蝙蝠1，自關而東謂之服翼，或謂之飛鼠，或謂之老鼠，或謂之㒨鼠。自關而西秦 隴之閒謂之蝙蝠。北燕謂之蟙䘃{戠墨}2。', '[音義-1]：邊、福兩音\n[音義-2]：職、墨兩音', '卷八', '方言', '', DEFAULT, DEFAULT, '8929/8930;5447/7693;7692/6391;5375/6391;5186/6391;8397/9076', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1757, '鴈;鴚䳘;䳘;鶬鴚', '', '鴈，自關而東謂之鴚䳘1。南楚之外謂之䳘，或謂之鶬鴚。2', '[音義-1]：音加\n[郭注-2]：今江東通呼爲鴚。', '卷八', '方言', '', DEFAULT, DEFAULT, '2422;2420/2421;2421;2444/2420', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1758, '桑飛;工爵;過𦟀;女匠;寧鴂;懱爵', '末尐', '桑飛，1自關而東謂之工爵，或謂之過鸁{過𦟀}2，或謂之女𪀘{女匠}。3自關而東謂之鸋鴂{寧鴂}4。5自關而西謂之桑飛，或謂之懱爵[末尐]6。', '[郭注-1]：卽鷦鷯也，又名鷦鸎。\n[音義-2]：音螺\n[郭注-3]：今亦名爲巧婦，江東呼布母。\n[音義-4]：甯、玦兩音\n[郭注-5]：按爾雅云：「鸋鴂，鴟鴞。」鴟屬。非此小雀明矣。\n[音義-6]：音懱𢧵', '卷八', '方言', '6', DEFAULT, DEFAULT, '3858/7692;3029/3201;1110/2729;8109/8424;3056/2389;6826/3201', '3564/706', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1759, '麗黃;倉庚;黃鳥;楚雀', '離黃', '𪈳黃{麗黃}[離黃]，自關而東謂之鶬鶊{倉庚}。1自關而西謂之𪈳黃，2或謂之黃鳥，或謂之楚雀。', '[郭注-1]：又名商庚。\n[郭注-2]：其色黧黑而黃，因名之。', '卷八', '方言', '1', DEFAULT, DEFAULT, '6286/9201;3277/9753;9201/2368;3849/2285', '2293/9201', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1760, '野鳧;鷿鷉;鶻蹏', '', '野鳧，其小而好沒水中者，南楚之外謂之鷿鷉1，大者謂之鶻蹏2。', '[音義-1]：音指擘。他奚反\n[音義-2]：滑、蹄兩音', '卷八', '方言', '', DEFAULT, DEFAULT, '9169/1957;2429/2430;2378/1327', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1761, '守宮;盧廛;蜥易;易易;蛇醫;榮蚖;虒侯;祝延;蛤解', '', '守宮，秦 晉 西夏謂之守宮，或謂之蠦𧔊{盧廛}1，或謂之蜥易，2其在澤中者謂之易蜴{易易}3。南楚謂之蛇醫，或謂之蠑螈{榮蚖}4。東齊 海 岱謂之螔䗔{虒侯}5。6北燕謂之祝蜒{祝延}7。桂林之中守宮大者而能鳴謂之蛤解。8', '[音義-1]：盧、纏兩音\n[郭注-2]：南陽人又呼蝘蜓。\n[音義-3]：音析\n[音義-4]：榮、元兩音\n[音義-5]：斯、侯兩音\n[郭注-6]：似蜥易大而有鱗，今所在通言蛇醫耳。\n[音義-7]：音延\n[郭注-8]：似蛇醫而短，身有鱗采。江東人呼爲蛤蚖，音頭頷。汝 潁人直名爲蛤，音䳺鶉。言「解」，聲誤也。', '卷八', '方言', '', DEFAULT, DEFAULT, '4594/4628;3143/5943;8801/6132;6132/6132;8993/9853;3536/8804;3130/3311;47/1265;8900/2850', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1762, '鼠;隹', '', '宛 野謂鼠爲𪕪{隹}1。2', '[音義-1]：音錐\n[郭注-2]：宛、新野，今皆在南陽。', '卷八', '方言', '', DEFAULT, DEFAULT, '6391;2278', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1763, '雞雛;秋子', '', '雞雛，徐 魯之閒謂之䨂子{秋子}1。2', '[音義-1]：子幽反\n[郭注-2]：徐，今下邳 僮縣東南大徐城是也。', '卷八', '方言', '', DEFAULT, DEFAULT, '2290/2291;4491/9764', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1764, '戟;孑;戭;戈;鏝胡;鉤孑鏝胡', '', '戟，楚謂之孑。1凡戟而無刃秦 晉之閒謂之孑，或謂之鏔{戭}2。吳 揚之閒謂之戈。東齊 秦 晉之閒謂其大者曰鏝胡3，其曲者謂之鉤孑鏝胡。4', '[郭注-1]：取名於鉤孑也。\n[音義-2]：音寅\n[音義-3]：音泥鏝\n[郭注-4]：卽今雞鳴，鉤孑戟也。', '卷九', '方言', '', DEFAULT, DEFAULT, '8375;9780;8391;8373;9368/2677;1460/9780/9368/2677', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1765, '三刃枝;匽戟;柲;殳', '', '三刃枝，1南楚 宛 郢2，3謂之匽戟4。其柄自關而西謂之柲5，或謂之殳6。', '[郭注-1]：今戟中有小孑刺者，所謂雄戟也。\n[音義-2]：余整反\n[郭注-3]：郢，今江陵也。\n[音義-4]：音偃\n[音義-5]：音祕\n[音義-6]：音殊', '卷九', '方言', '', DEFAULT, DEFAULT, '77/2811/3569;8420/8375;3746;1958', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1766, '矛;鉈;鋋;鏦;矜', '', '矛，吳 揚 江 淮 南楚 五湖之閒謂之鍦{鉈}1，2或謂之鋋3，或謂之鏦4。5其柄謂之矜6。', '[音義-1]：嘗蛇反\n[郭注-2]：五湖，今吳興 大湖也。先儒處之多亦不了，所未能詳者。\n[音義-3]：音蟬\n[音義-4]：錯江反\n[郭注-5]：漢書曰：「鏦殺吳王。」\n[音義-6]：今字作𥎊，巨巾反', '卷九', '方言', '', DEFAULT, DEFAULT, '9506;9402;9400;9403;9510', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1767, '箭;矢;鍭', '', '箭，自關而東謂之矢。江 淮之閒謂之鍭1。關西曰箭。2', '[音義-1]：音侯\n[郭注-2]：箭者竹名，因以爲號。', '卷九', '方言', '', DEFAULT, DEFAULT, '2866;3307;9409', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1768, '鑽;耑', '䤸;䤸', '鑽[䤸]謂之鍴{耑}[䤸]1。', '[音義-1]：音端', '卷九', '方言', '1;2', DEFAULT, DEFAULT, '9369;4538', '9403;9403', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1769, '矜;杖', '', '矜謂之杖。1', '[郭注-1]：矛戟𥎊，卽杖也。', '卷九', '方言', '', DEFAULT, DEFAULT, '9510;3739', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1770, '室;郭;削;鞞', '', '劍削，自河而北燕 趙之閒謂之室。自關而東或謂之廓{郭}，或謂之削。自關而西謂之𩏂{鞞}1。', '[音義-1]：方婢反', '卷九', '方言', '', DEFAULT, DEFAULT, '4557;4150;2746;1812', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1771, '盾;瞂;干', '𢧀', '盾，自關而東或謂之瞂1，或謂之干[𢧀]。2關西謂之盾。', '[音義-1]：音伐\n[郭注-2]：干者，扞也。', '卷九', '方言', '3', DEFAULT, DEFAULT, '2220;2221;1441', '8379', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1772, '紩;畢;綦', '䩛', '車下鉄{紩}，陳 宋 淮 楚之閒謂之畢[䩛]，1大者謂之綦2。3', '[郭注-1]：未詳。\n[音義-2]：音忌\n[郭注-3]：鹿車也。', '卷九', '方言', '2', DEFAULT, DEFAULT, '8679;2491;8627', '1816', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1773, '轊;龍', '', '車轊1，2齊謂之𨏠{龍}。3', '[音義-1]：于厲反\n[郭注-2]：車軸頭也。\n[郭注-3]：又名𨎬。', '卷九', '方言', '', DEFAULT, DEFAULT, '9552;7687', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1774, '枸簍;恢;蛩籠;仢;冥;畚;逢;隆屈', '槶;穹隆;糸;轒', '車枸簍1，2宋 魏 陳 楚之閒謂之䈐{恢}[槶]3，4或謂之𥳎籠{蛩籠}[穹隆]5。其上約謂之𥭓{仢}6，7或謂之䈿{冥}[糸]8。秦 晉之閒自關而西謂之枸簍。西隴謂之㮥{畚}[轒]9；南楚之外謂之篷{逢}，10或謂之隆屈11。', '[音義-1]：音縷\n[郭注-2]：卽車弓也。\n[音義-3]：音巾幗\n[郭注-4]：今呼車子弓爲䈐。\n[音義-5]：穹、隆兩音\n[音義-6]：音瓜瓝\n[郭注-7]：卽軬帶也。\n[音義-8]：音覛\n[音義-9]：卽軬字，薄晚反\n[郭注-10]：今亦通呼篷。\n[音義-11]：音尾屈', '卷九', '方言', '2;3;5;6', DEFAULT, DEFAULT, '3501/2929;6740;8927/2956;5034;4311;8447;1130;3877/5428', '3732;4670/3877;8515;9600', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1775, '輪;軑;軝;㚇', '鏓', '輪，1韓 楚之閒謂之軑2，或謂之軝3。4關西謂之𨍈{㚇}[鏓]5。', '[郭注-1]：車輅也。\n[音義-2]：音大\n[音義-3]：音祇\n[郭注-4]：詩曰：「約軝錯衡。」\n[音義-5]：音總', '卷九', '方言', '4', DEFAULT, DEFAULT, '9547;9554;9550;3373', '9391', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1776, '輑;軸', '', '輑謂之軸1。', '[音義-1]：牛忿反', '卷九', '方言', '', DEFAULT, DEFAULT, '9539;9543', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1777, '轅;輈', '', '轅，楚 衞之閒謂之輈1。', '[音義-1]：張由反', '卷九', '方言', '', DEFAULT, DEFAULT, '9556;9557', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1778, '箱;篚', '', '箱謂之輫{篚}1。', '[音義-1]：音俳', '卷九', '方言', '', DEFAULT, DEFAULT, '2968;2969', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1779, '軫;枕', '', '軫謂之枕。1', '[郭注-1]：車後橫木。', '卷九', '方言', '', DEFAULT, DEFAULT, '9540;3678', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1780, '紂;秋;曲匋;曲綸', '緧;曲絛', '車紂，自關而東周 洛 韓 鄭 汝 潁而東謂之䋺{秋}[緧]1，或謂之曲綯{曲匋}[曲絛]，2或謂之曲綸3。4自關而西謂之紂。', '[音義-1]：音秋\n[郭注-2]：綯亦繩名，詩曰：「宵爾索綯。」\n[音義-3]：音倫\n[郭注-4]：今江東通呼索綸。', '卷九', '方言', '2;3', DEFAULT, DEFAULT, '8708;4491;8442/3287;8442/8652', '8709;8442/8664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1781, '輨;軑;鍊𨬍', '輨軑', '輨1、軑2，鍊𨬍[輨軑]也3。關之東西曰輨。南楚曰軑。趙 魏之閒曰鍊𨬍。', '[音義-1]：音管\n[音義-2]：音大\n[音義-3]：音柬。音度果反', '卷九', '方言', '3', DEFAULT, DEFAULT, '9555;9554;9280/9350', '9555/9554', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1782, '釭;楇;昆', '', '車釭，齊 燕 海 岱之閒謂之鍋{楇}1，或謂之錕{昆}2。自關而西謂之釭，盛膏者乃謂之鍋。', '[音義-1]：音戈\n[音義-2]：音衮衣', '卷九', '方言', '', DEFAULT, DEFAULT, '9416;3779;4254', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1783, '拘腸;羊頭;錍;鈀;甲鑪;飛䖟;平題;箙;鞬;韇丸', '', '凡箭鏃胡合嬴者，1四鐮，2或曰拘腸，三鐮者謂之羊頭，其廣長而薄鐮謂之錍3，或謂之鈀4。5其小而長中穿二孔者謂之鉀鑪{甲鑪}6。7其三鐮長尺六者謂之飛䖟，8禸者謂之平題。9所以藏箭弩謂之箙。10弓謂之鞬11，或謂之𥀲丸{韇丸}12。', '[郭注-1]：胡鏑在於喉下。嬴，邊也。\n[郭注-2]：稜也。\n[音義-3]：普蹄反\n[音義-4]：音葩\n[郭注-5]：江東呼𲇚箭。\n[音義-6]：嗑、嚧兩音\n[郭注-7]：今箭錍鑿空兩邊者也。\n[郭注-8]：此謂今射箭也。\n[郭注-9]：今戲射箭頭題猶羊頭也。\n[郭注-10]：盛弩箭器也。外傳曰：「檿弧箕箙。」\n[音義-11]：音犍牛\n[音義-12]：音牛犢', '卷九', '方言', '', DEFAULT, DEFAULT, '1458/2611;2332/5599;9338;9378;9739/9322;7692/8963;3073/5609;2977;1840;1841/6003', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1784, '鶴厀;鉤孑;矛;孑;錟;鈹;骹;銎;鐏;釬', '', '矛骹細如鴈脛者謂之鶴厀。1有小枝刃者謂之鉤𨥂{鉤孑}。矛或謂之𨥂{孑}。錟謂之鈹2。3骹謂之銎4。5鐏謂之釬6。78', '[郭注-1]：今江東呼爲鈐釘。\n[音義-2]：音聃。音彼\n[郭注-3]：今江東呼大矛爲鈹。\n[音義-4]：音凶\n[郭注-5]：卽矛刃下口。\n[音義-6]：音扞\n[郭注-7]：或名爲鐓。\n[音義-8]：鐓，音頓。', '卷九', '方言', '', DEFAULT, DEFAULT, '2411/5783;1460/9780;9506;9780;9404;9333;2581;9336;9407;9412', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1785, '舟;船;𣃚;舸;差;冒宿;艇;帶;符;巩;欚', '', '舟，自關而西謂之船。自關而東或謂之舟，或謂之航{𣃚}1。南楚 江 湘凡船大者謂之舸2，小舸謂之艖{差}3。4艖謂之艒䑿{冒宿}5，小艒䑿謂之艇。6艇長而薄者謂之艜{帶}7，短而深者謂之䒀{符}8，9小而深者謂之㮪{巩}10。11東南丹陽 會稽之閒謂艖爲欚12。', '[音義-1]：音行伍\n[音義-2]：姑可反\n[音義-3]：音叉\n[郭注-4]：今江東呼艖小底者也。\n[音義-5]：目、宿兩音\n[郭注-6]：𦨴也。\n[音義-7]：音衣帶\n[音義-8]：音步\n[郭注-9]：今江東呼艖䒀者。\n[音義-10]：音邛竹\n[郭注-11]：卽長舼也。\n[音義-12]：音禮', '卷九', '方言', '', DEFAULT, DEFAULT, '5436;5438;5454;5449;3028;4807/4600;5450;4865;2898;1887;3789', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1786, '泭;椑;伐;薦', '橃', '泭1謂之𥴖{椑}，𥴖謂之筏{伐}[橃]2。筏，秦 晉之通語也。江 淮家居𥴖中謂之薦3。', '[音義-1]：音敷\n[音義-2]：音伐\n[音義-3]：音荐', '卷九', '方言', '3', DEFAULT, DEFAULT, '7282;3712;5165;6259', '3787', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1787, '方舟;㶇;艁舟;浮梁', '', '方舟謂之㶇1，2艁舟謂之浮梁。3', '[音義-1]：音橫\n[郭注-2]：揚州人呼渡津舫爲杭，荆州人呼㶇。\n[郭注-3]：卽今浮橋。', '卷九', '方言', '', DEFAULT, DEFAULT, '5453/5436;7281;1114/5436;7178/3785', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1788, '楫;橈;櫂;䉃;緝;篙;維;鼎;首;閤閭;益首;舳;僞;仡', '擢;閤艫;鶃首;吪;扤/𦨉', '楫謂之橈1，或謂之櫂[擢]。2所以隱櫂謂之𥷃{䉃}3，4所以縣櫂謂之緝，5所以刺船謂之㰏{篙}6。維之謂之鼎，7首謂之閤閭[閤艫]，8或謂之艗首{益首}[鶃首]9。10後曰舳11，12舳，制水也。僞[吪]謂之仡[扤/𦨉]13，14仡，不安也。', '[音義-1]：如寮反\n[郭注-2]：今云櫂歌，依此名也。\n[音義-3]：音𤟌\n[郭注-4]：摇樐小橛也。江東又名爲胡人。\n[郭注-5]：繫櫂頭索也。\n[音義-6]：音高\n[郭注-7]：繫船爲維。\n[郭注-8]：今江東呼船頭屋謂之飛閭是也。\n[音義-9]：音亦\n[郭注-10]：鷁，鳥名也。今江東貴人船前作靑雀，是其像也。\n[音義-11]：音軸\n[郭注-12]：今江東呼柁爲舳。\n[音義-13]：音訛。吾勃反\n[郭注-14]：船動摇之貌也。', '卷九', '方言', '3;10;11;13;14', DEFAULT, DEFAULT, '3788;3591;3839;2888;8731;3016;8701;4407;5701;7736/7739;3154/5701;5440;5134;5000', '7994;7736/5441;2441/5701;930;8039/0/5442', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1789, '媱;愓;遊;僖', '', '媱、愓1，遊也。江 沅之閒謂戲爲媱，或謂之愓，或謂之嬉{僖}2。', '[音義-1]：音羊\n[音義-2]：香其反', '卷十', '方言', '', DEFAULT, DEFAULT, '8210;6847;4304;5090', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1790, '曾;訾;何', '', '曾、訾，何也。湘 潭之原1、2荆之南鄙謂何爲曾，或謂之訾，3若中夏言何爲也。', '[音義-1]：音覃，一音淫\n[郭注-2]：潭，水名，出武陵。\n[郭注-3]：今江東人語亦云訾，爲聲如斯。', '卷十', '方言', '', DEFAULT, DEFAULT, '710;1626;5023', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1791, '央亡;墨杘;𡜶;獪;無賴;㺒;娗;滑', '獪;謬', '央亡、嚜杘{墨杘}1、𡜶[獪]2，獪也。江 湘之閒或謂之無賴，或謂之㺒[謬]3。4凡小兒多詐而獪謂之央亡，或謂之嚜杘，5或謂之𡜶。6𡜶，娗也。7或謂之猾{滑}8。皆通語也。', '[音義-1]：音目。丑夷反\n[音義-2]：胡𠜜反\n[音義-3]：恪交反\n[郭注-4]：㤍㤉，多智也。\n[郭注-5]：潛潛狡也。\n[郭注-6]：言黠𡜶也。\n[郭注-7]：言恫娗也。\n[音義-8]：音滑', '卷十', '方言', '3;6', DEFAULT, DEFAULT, '3325/8411;9076/3748;8225;6340;8414/3962;6322;8340;7218', '6340;1663', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1792, '𢀇;子', '', '崽{𢀇}者1，子也。湘 沅之會2，3凡言是子者謂之崽，若東齊言子矣4。', '[音義-1]：音枲，聲之轉也\n[音義-2]：音獪\n[郭注-3]：兩水合處也。\n[音義-4]：聲如宰', '卷十', '方言', '', DEFAULT, DEFAULT, '9764;9764', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1793, '來;亡;秕', '癡', '誺{來}[癡]1，不知也。沅 澧之閒2，3凡相問而不知，荅曰誺；使之而不肯，荅曰吂{亡}4。5粃{秕}，不知也。6', '[音義-1]：音癡眩，江東曰咨，此亦癡聲之轉也\n[音義-2]：音禮\n[郭注-3]：澧水，今在長沙。\n[音義-4]：音茫\n[郭注-5]：今中國語亦然。\n[郭注-6]：今淮 楚閒語呼聲如非也。', '卷十', '方言', '1', DEFAULT, DEFAULT, '3346;8411;4475', '4795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1794, '果;火;𤈦', '火', '㷄{果}[火]1，火也。楚轉語也，猶齊言𤈦2，火也。', '[音義-1]：呼隗反\n[音義-2]：音毁', '卷十', '方言', '1', DEFAULT, DEFAULT, '3566;6414;6416', '6414', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1795, '嘳;無寫;憐;思;人兮', '', '嘳1、無寫，憐也。2沅 澧之原凡言相憐哀謂之嘳、或謂之無寫。江濱謂之思。3皆相見驩喜有得亡之意也。九疑 湘 潭之閒謂之人兮。4', '[音義-1]：音蒯\n[郭注-2]：皆南鄙之代語也。\n[郭注-3]：濱，水邊也。\n[郭注-4]：九疑，山名，今在零陵 營道縣。', '卷十', '方言', '', DEFAULT, DEFAULT, '830;8414/4598;6960;6701;4948/3063', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1796, '岸;嫧;鮮;好', '妍;鱻', '婩{岸}[妍]1、嫧2、鮮[鱻]，好也。南楚之外通語也。', '[音義-1]：魚踐反\n[音義-2]：音策', '卷十', '方言', '1;3', DEFAULT, DEFAULT, '5916;8244;7646;8189', '8297;7680', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1797, '闌牢;謰謱;拏;支主;占謕;惹;㛪', '支主', '㘓哰{闌牢}1、謰謱2，拏也3。4東齊 周 晉之鄙曰㘓哰，㘓哰亦通語也；5南楚曰謰謱，或謂之支註{支主}6，或謂之詀謕{占謕}[支主]7，轉語也。拏，揚州 會稽之語也，或謂之惹8，9或謂之䛳{㛪}。10', '[音義-1]：闌、牢兩音\n[音義-2]：音連。力口反\n[音義-3]：奴加反\n[郭注-4]：言譇拏也。\n[郭注-5]：平原人好㘓哰也。\n[音義-6]：之䜴反。音注\n[音義-7]：託兼反。音啼\n[音義-8]：汝邪反，一音若\n[郭注-9]：言情惹也。\n[郭注-10]：言誣䛳也。', '卷十', '方言', '5', DEFAULT, DEFAULT, '7766/757;1601/1602;8077;1934/3183;2078/1655;6975;8333', '1934/3183', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1798, '丩;𠻮;貪;吝', '', '亄{丩}1、𠻮，貪也。2荆 汝 江 湘之郊凡貪而不施謂之亄，3或謂之𠻮，或謂之悋{吝}。悋，恨也。4', '[音義-1]：音懿\n[郭注-2]：謂慳貪也。\n[郭注-3]：亦中國之通語。\n[郭注-4]：慳者多惜恨也。', '卷十', '方言', '', DEFAULT, DEFAULT, '1454;3344;3982;932', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1799, '遙;窕;淫', '媱;姚;婬', '遙[媱]、窕[姚]，淫[婬]也。九疑 荆郊之鄙謂淫曰遙，1沅 湘之閒謂之窕。2', '[郭注-1]：言心遙蕩也。\n[郭注-2]：窈窕冶容。', '卷十', '方言', '1;2;3', DEFAULT, DEFAULT, '1222;4669;7221', '8210;8115;8336', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1800, '潛;涵;沈;游', '湛', '潛、涵1，沈[湛]也。楚 郢以南曰涵，或曰潛，潛又游也。2', '[音義-1]：音含，或古南反\n[郭注-2]：潛行水中，亦爲游也。', '卷十', '方言', '3', DEFAULT, DEFAULT, '7288;7321;7318;4304', '7294', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1801, '豸;安;靜', '𡧯', '𡧘{豸}[𡧯]1、安，靜也。江 湘 九疑之郊謂之𡧘。', '[音義-1]：音寂', '卷十', '方言', '1', DEFAULT, DEFAULT, '6110;4576;3189', '4580', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1802, '半;棄;敲;投', '播', '拌{半}[播]1，棄也。楚凡揮棄物謂之拌，或謂之敲2。3淮 汝之閒謂之投。45', '[音義-1]：音伴，又普槃反\n[音義-2]：恪校反\n[郭注-3]：今汝 潁閒語亦然。或云撅也。\n[郭注-4]：江東又呼擫。\n[音義-5]：擫，音黶，又音揞。', '卷十', '方言', '1', DEFAULT, DEFAULT, '725;2493;2052;7925', '8036', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1803, '椓;愬', '', '諑{椓}，愬也。楚以南謂之諑。1', '[郭注-1]：諑、譖亦通語也。', '卷十', '方言', '', DEFAULT, DEFAULT, '3798;1686', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1804, '戲;泄;歇;奄', '羲', '戲[羲]1、泄，歇也。楚謂之戲泄。奄，息也。楚 揚謂之泄。', '[音義-1]：音羲', '卷十', '方言', '1', DEFAULT, DEFAULT, '8383;7063;5534;6599', '3065', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1805, '弗;曬', '', '昲{弗}1、曬，乾物也。揚 楚通語也。2', '[音義-1]：音霏\n[郭注-2]：亦皆北方常語耳。或云㬓。', '卷十', '方言', '', DEFAULT, DEFAULT, '8361;4247', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1806, '菲/棐;猝;突', '匪', '䕁{菲/棐}[匪]1，猝也。2江 湘之閒凡卒相見謂之䕁相見，或曰突3。', '[音義-1]：音斐\n[郭注-2]：謂倉卒也。\n[音義-3]：他骨反', '卷十', '方言', '1', DEFAULT, DEFAULT, '641/0/3753;6316;4665', '8430', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1807, '迹迹;㞕㞕;塞塞;省省', '', '迹迹、㞕㞕，不安也。1江 沅之閒謂之迹迹。秦 晉謂之㞕㞕，或謂之塞塞，或謂之省省，不安之語也。', '[郭注-1]：皆往來之貌也。', '卷十', '方言', '', DEFAULT, DEFAULT, '1093/1093;5404/5404;9093/9093;2219/2219', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1808, '鬩沭;正伀;遑遽', '鬩怵;䋫㤨', '𤄎沭{鬩沭}[鬩怵]1、佂伀{正伀}[䋫㤨]，遑遽也。江 湘之閒凡窘猝怖遽謂之𤄎沭，2或謂之佂伀。', '[音義-1]：音鬩\n[郭注-2]：喘𠴲貌也。', '卷十', '方言', '1;2', DEFAULT, DEFAULT, '1899/7079;1087/4971;1212/1206', '1899/6944;8693/6946', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1809, '翥;舉', '', '翥，舉也。1楚謂之翥。', '[郭注-1]：謂軒翥也。', '卷十', '方言', '', DEFAULT, DEFAULT, '2256;7957', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1810, '衂怩;慙歰;蹙咨', '恧怩', '忸怩{衂怩}[恧怩]，慙歰也。1楚 郢 江 湘之閒謂之忸怩，或謂之㗤咨{蹙咨}2。', '[郭注-1]：歰猶苦者。\n[音義-2]：子六、莊伊二反', '卷十', '方言', '1', DEFAULT, DEFAULT, '3172/6968;6957/1077;1417/841', '6958/6968', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1811, '垤;封;場', '', '垤、封，場也。楚 郢以南蟻土謂之垤。垤，中齊語也。', '', '卷十', '方言', '', DEFAULT, DEFAULT, '9133;9074;9145', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1812, '謫;過;衇;慧', '', '讁{謫}1，過也。2南楚以南凡相非議人謂之讁，或謂之衇3。衇，又慧也。4', '[音義-1]：音賾，亦音適，罪罰也\n[郭注-2]：謂罪過也。\n[音義-3]：音血脈\n[郭注-4]：今名黠爲鬼衇。', '卷十', '方言', '', DEFAULT, DEFAULT, '1690;1110;7498;6733', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1813, '膊;兄;蠡', '䨣;剺', '膊[䨣]，兄也。荆 揚之鄙謂之膊。桂林之中謂之⿰彖𥁄{劙}{蠡}[剺]。1', '[郭注-1]：皆音義所未詳。', '卷十', '方言', '1;3', DEFAULT, DEFAULT, '2688;5462;8965', '7557;2778', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1814, '蹇;極;吃;軋;歰', '㥛;乙', '𧬯{蹇}、極[㥛]，吃也。楚語也。1或謂之軋[乙]2，3或謂之歰。45', '[郭注-1]：亦北方通語也。\n[音義-2]：烏八反\n[郭注-3]：鞅軋，氣不利也。\n[郭注-4]：語歰難也。今江南又名吃爲𠿚。\n[音義-5]：𠿚，若葉反。', '卷十', '方言', '2;4', DEFAULT, DEFAULT, '1395;3624;888;9579;1077', '6812;9740', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1815, '啙;罷;短;鮆;齊;㾈', '䇑', '啙1、矲{罷}[䇑]2，短也。江 湘之會謂之啙。凡物生而不長大亦謂之鮆，又曰癠{齊}3。4桂林之中謂短矲。5矲，通語也。東陽之閒謂之㾈。6', '[音義-1]：昨啟反\n[音義-2]：蒲楷反\n[音義-3]：音薺菜\n[郭注-4]：今俗呼小爲癠。\n[郭注-5]：言矲𥏪也。\n[郭注-6]：言俯視之，因名云。', '卷十', '方言', '2', DEFAULT, DEFAULT, '1084;4841;3313;7625;4393;4730', '6694', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1816, '鉗;反;敝;惡', '', '鉗、㽹{反}1、憋{敝}2，3惡也。南楚凡人殘罵謂之鉗，4又謂之㽹。5', '[音義-1]：妨反反\n[音義-2]：妨滅反\n[郭注-3]：憋怤，急性也。\n[郭注-4]：殘猶惡也。鉗害，又惡也。\n[郭注-5]：㽹恎，惡腹也。', '卷十', '方言', '', DEFAULT, DEFAULT, '9361;1919;4941;6880', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1817, '癡;騃;耳;斫', '譺/懝;佁;聉', '癡[譺/懝]，騃[佁]也1。揚 越之郊凡人相侮以爲無知謂之眲{耳}[聉]2。眲，耳目不相信也。3或謂之斫4。5', '[音義-1]：吾駭反\n[音義-2]：諾革反\n[郭注-3]：因字名也。\n[音義-4]：音斫卻\n[郭注-5]：斫，頑直之貌，今關西語亦皆然。', '卷十', '方言', '1;2;3', DEFAULT, DEFAULT, '4795;6203;7792;9477', '1604/0/6830;5132;7815', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1818, '混;誖;頓愍;惛;氐惆;眠眩', '溷', '惃{混}[溷]1、𢠜{誖}2、頓愍，惛也。3楚 揚謂之惃，或謂之𢠜。江 湘之閒謂之頓愍，4或謂之氐惆5。6南楚飲毒藥懣謂之氐惆，亦謂之頓愍，猶中齊言眠眩也。愁恚憒憒、毒而不發謂之氐惆。', '[音義-1]：音衮衣\n[音義-2]：音㪍\n[郭注-3]：謂迷昏也。\n[郭注-4]：頓愍猶頓悶也。\n[音義-5]：丁弟、丁牢二反\n[郭注-6]：氐惆猶懊憹也。', '卷十', '方言', '1', DEFAULT, DEFAULT, '7141;1617;5658/6906;6867;8369/6893;2181/2098', '7205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1819, '挩;舒;蘇', '', '悅{挩}、舒，蘇也。1楚通語也。', '[郭注-1]：謂蘇息也。', '卷十', '方言', '', DEFAULT, DEFAULT, '7977;2510;256', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1820, '眠娗;脈痬;賜施;茭媞;亶謾;詈詑;欺謾', '謧詍;謧詍', '眠娗1、脈蜴{脈痬}2、賜施[謧詍]3、茭媞4、譠謾{亶謾}5、㦒忚{詈詑}[謧詍]6，皆欺謾之語也。楚 郢以南東揚之郊通語也。7', '[音義-1]：莫典、塗殄二反\n[音義-2]：音析\n[音義-3]：音輕易\n[音義-4]：恪校、得懈二反\n[音義-5]：託蘭、莫蘭二反\n[音義-6]：麗、醯兩音\n[郭注-7]：六者亦中國相輕易蚩弄之言也。', '卷十', '方言', '3;6', DEFAULT, DEFAULT, '2181/8340;7498/4774;3959/4301;612/8228;3342/1598;4844/1597;5586/1598', '1624/1625;1624/1625', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1821, '旃;頟;顔;顙', '', '𩔣{旃}1、頟、顔，顙也。湘 江之閒謂之𩔣。2中夏謂之頟。東齊謂之顙。汝 潁 淮 泗之閒謂之顔。', '[音義-1]：音旃裘\n[郭注-2]：今建平人呼頟爲𩔣。', '卷十', '方言', '', DEFAULT, DEFAULT, '4297;5610;5600;5608', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1822, '頷;頤;頜', '顄', '頷[顄]、頤，頜也。1南楚謂之頷。2秦 晉謂之頜。頤，其通語也。', '[郭注-1]：謂頷車也。\n[郭注-2]：亦今通語耳。', '卷十', '方言', '1', DEFAULT, DEFAULT, '5647;7825;5615', '5616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1823, '紛怡;喜;巸巳', '媐巳/饎巳', '紛怡，喜也。湘 潭之閒曰紛怡，或曰巸巳[媐巳/饎巳]1。', '[音義-1]：嬉、怡兩音', '卷十', '方言', '3', DEFAULT, DEFAULT, '8707/6744;3074;7826/9795', '8231/9795/0/3217/9795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1824, '邯;或', '', '𣵷{邯}1，或也。沅 澧之閒凡言或如此者曰𣵷如是。2', '[音義-1]：音酒酣\n[郭注-2]：此亦憨聲之轉耳。', '卷十', '方言', '', DEFAULT, DEFAULT, '4064;8385', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1825, '䍃;療;治;憂', '', '愮{䍃}1、療，治也。江 湘郊會謂醫治之曰愮。2愮又憂也。3或曰療。', '[音義-1]：音曜\n[郭注-2]：俗云厭愮病。\n[郭注-3]：博異義也。', '卷十', '方言', '', DEFAULT, DEFAULT, '3298;4788;7087;3366', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1826, '芔;莽;草', '茻', '芔1、莽[茻]2，草也。東越 揚州之閒曰芔，南楚曰莽。', '[音義-1]：凶位反\n[音義-2]：音嫫母', '卷十', '方言', '2', DEFAULT, DEFAULT, '629;702;680', '700', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1827, '悈;思;乾;都;耇;革;老', '', '悈1、鰓{思}2、乾3、都、耇4、革，老也。5皆南楚 江 湘之閒代語也。6', '[音義-1]：音良愅\n[音義-2]：音魚鰓\n[音義-3]：音干\n[音義-4]：音姤\n[郭注-5]：皆老者皮色枯瘁之形也。\n[郭注-6]：凡以異語相易謂之代也。', '卷十', '方言', '', DEFAULT, DEFAULT, '6753;6701;9741;4005;5379;1790;5375', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1828, '柲;抌;推;忽;攩', '椎;㨡;予', '㧙{柲}1、抌2，推[椎]也。南楚凡相推搏曰㧙，或曰㧾{忽}[㨡]3。沅 涌 𣿆4幽之語，5或曰攩[予]6。7', '[音義-1]：音撞㧙\n[音義-2]：都感反，亦音甚\n[音義-3]：苦骨反\n[音義-4]：音扶\n[郭注-5]：涌水，今在南郡 華容縣也。𣿆水，今在桂陽。\n[音義-6]：音晃\n[郭注-7]：今江東人亦名推爲攩。', '卷十', '方言', '3;4;5', DEFAULT, DEFAULT, '3746;8062;7846;6843;7916', '3743;8028;2509', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1829, '食閻;慫恿;勸', '', '食閻1、慫慂{慫恿}2，勸也。南楚凡己不欲喜而旁人說之、不欲怒而旁人怒之謂之食閻，或謂之慫慂。', '[音義-1]：音鹽\n[音義-2]：子竦反。音涌', '卷十', '方言', '', DEFAULT, DEFAULT, '3204/7740;6840/9243;9223', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1830, '欸;殹;然', '唉', '欸[唉]1、𧫦{殹}2，然也。南楚凡言然者曰欸，或曰𧫦。', '[音義-1]：音醫，或音塵埃\n[音義-2]：音鳧鷖', '卷十', '方言', '1', DEFAULT, DEFAULT, '5555;1970;6421', '855', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1831, '緤;末;紀;緒;端', '', '緤1、末、紀，緒也。南楚皆曰緤，或曰端、或曰紀、或曰末，皆楚轉語也。', '[音義-1]：音薛', '卷十', '方言', '', DEFAULT, DEFAULT, '8715;3564;8540;8519;6680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1832, '葼;𠭥;闚;占;占;伺;視;瞻', '䚕;窺;覘;覘', '䁓{葼}1、𣛒{𠭥}[䚕]2、闚[窺]、䀡{占}[覘]3、占[覘]、覗{伺}，視也。凡相竊視，南楚謂之闚、或謂之䁓、或謂之䀡、或謂之占、或謂之𣛒。𣛒，中夏語也。4闚，其通語也。自江而北謂之䀡、或謂之覗。凡相候謂之占，占猶瞻也。', '[音義-1]：音總\n[音義-2]：音麗\n[音義-3]：勑纖反\n[郭注-4]：亦言𥉹也。', '卷十', '方言', '2;3;4;5', DEFAULT, DEFAULT, '508;1922;7777;2078;2078;5207;5475;2156', '5476;4659;5496;5496', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1833, '邕;濃;晠;多', '', '郺{邕}1、𨑊{濃}2、晠，多也。南楚凡大而多謂之郺，或謂之𨑊。凡人語言過度及妄施行亦謂之𨑊。', '[音義-1]：惡孔反\n[音義-2]：奴動反', '卷十', '方言', '', DEFAULT, DEFAULT, '7487;7331;4262;4334', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1834, '抯;𠭯;取', '', '抯1、摣{𠭯}2，取也。南楚之閒凡取物溝泥中謂之抯，或謂之摣。', '[音義-1]：音柤棃\n[音義-2]：仄加反', '卷十', '方言', '', DEFAULT, DEFAULT, '7981;1914;1925', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1835, '汎;僄;輕', '', '𠆩{汎}1、僄2，輕也。楚凡相輕薄謂之相𠆩，或謂之僄也。', '[音義-1]：音汎\n[音義-2]：音飄零', '卷十', '方言', '', DEFAULT, DEFAULT, '7162;5137;9519', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1836, '多蚗;螇鹿;蟪蛄;蛉蛄;蛁勞;是勞;蜓木', '𧉅蚗;蛁尞', '蛥蚗{多蚗}[𧉅蚗]1，齊謂之螇螰{螇鹿}2。楚謂之蟪蛄，3或謂之蛉蛄4。秦謂之蛥蚗。自關而東謂之虭蟧{蛁勞}[蛁尞]5，或謂之蝭蟧{是勞}6，或謂之蜓蚞{蜓木}7，西楚與秦通名也。8', '[音義-1]：音折。于列反，一音玦\n[音義-2]：奚、鹿二音\n[郭注-3]：莊子曰：「蟪蛄不知春秋也。」\n[音義-4]：音零\n[音義-5]：貂、料兩音\n[音義-6]：音帝\n[音義-7]：廷、木兩音\n[郭注-8]：江東人呼嘄蟧。', '卷十一', '方言', '1;5', DEFAULT, DEFAULT, '4334/8870;8869/6261;8939/8832;8874/8832;8793/9234;1089/9234;8803/3411', '8861/8870;8793/6420', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1837, '蟬;蜩;唐蜩;蜋蜩;奇;勞;蝒馬;麥札;蜻蜻;尐;戔;蜺;蟊蜩;應;寒蜩;瘖蜩', '𧉄馬;麥蠽', '蟬，楚謂之蜩1。宋 衞之閒謂之螗蜩{唐蜩}。2陳 鄭之閒謂之蜋蜩3。秦 晉之閒謂之蟬。海 岱之閒謂之䗁{奇}4。5其大者謂之蟧{勞}，或謂之蝒馬[𧉄馬]；6其小者謂之麥蚻{麥札}[麥蠽]，78有文者謂之蜻蜻，9其雌蜻謂之尐10，大而黑者謂之䗃{戔}11，黑而赤者謂之蜺12。蜩蟧謂之𧓿蜩{蟊蜩}。13𧕄{應}謂之寒蜩14，寒蜩，瘖蜩也。15', '[音義-1]：音調\n[郭注-2]：今胡蟬也，似蟬而小，鳴聲淸亮，江南呼螗蛦。\n[音義-3]：音良\n[音義-4]：音技\n[郭注-5]：齊人呼爲巨䗁。\n[郭注-6]：案爾雅云：「蝒者，馬蜩。」非別名蝒馬也，此方言誤耳。\n[郭注-7]：如蟬而小，靑色。今關西呼麥蠽。\n[音義-8]：蠽，音癰𤻵。\n[郭注-9]：卽蚻也，爾雅云耳。\n[音義-10]：祖一反\n[音義-11]：音棧\n[音義-12]：音雲霓\n[郭注-13]：江東呼爲𧓿蠽也。\n[音義-14]：音應\n[郭注-15]：案爾雅以蜺爲寒蜩，月令亦曰「寒蜩鳴」，知寒蜩非瘖者也。此諸蜩名，通出爾雅而多駮雜，未可詳據也。寒蜩，螿也，似蟬而小，色靑。', '卷十一', '方言', '7;8', DEFAULT, DEFAULT, '8867;8866;878/8866;8842/8866;3059;9234;8839/6135;3348/3768;8873/8873;706;8398;8868;8954/8866;6711;4613/8866;4722/8866', '8871/6135;3348/8952', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1838, '蛄諸;杜各;螻窒;螻蛄;象蛉;杜狗;𠯑螻', '䒷蔞', '蛄諸謂之杜蛒{杜各}1。螻螲{螻窒}謂之螻蛄2，或謂之蟓蛉{象蛉}3。南楚謂之杜狗，或謂之𧋣螻{𠯑螻}[䒷蔞]。', '[音義-1]：音格\n[音義-2]：音窒塞\n[音義-3]：象、鈴兩音', '卷十一', '方言', '7', DEFAULT, DEFAULT, '8832/1488;3431/933;8831/4664;8831/8832;6133/8874;3431/6302;942/8831', '398/377', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1839, '蜻蛚;蟋率;蛩;王孫', '悉𧍓', '蜻蛚1，2楚謂之蟋蟀{蟋率}[悉𧍓]，或謂之蛬{蛩}3。4南楚之閒謂之蚟孫{王孫}5。', '[音義-1]：精、列兩音\n[郭注-2]：卽趨織也。\n[音義-3]：音鞏\n[郭注-4]：梁國呼蛬。\n[音義-5]：一作絲', '卷十一', '方言', '2', DEFAULT, DEFAULT, '8873/8872;8943/8783;8927;78/8512', '723/8838', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1840, '堂蜋;髦;丁;芈芈', '', '螳螂{堂蜋}謂之髦，1或謂之虰{丁}，2或謂之蝆蝆{芈芈}。', '[郭注-1]：有斧蟲也。江東呼爲石蜋，又名齕肬。\n[郭注-2]：案爾雅云：「螳蜋，蛑。」「虰」義自應下屬。方言依此說，失其指也。', '卷十一', '方言', '', DEFAULT, DEFAULT, '9054/8842;5734;9745;2333/2333', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1841, '蛄䗐;強芈', '', '姑䗐{蛄䗐}謂之強蝆{強芈}。12', '[郭注-1]：米中小黑甲蟲也。江東謂之𧉪。建平人呼芈子。\n[音義-2]：芈，音芈姓。𧉪，音加。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8832/8847;8824/2333', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1842, '莽;蟘;蟅莽;螣', '蜢;虴蜢;蟘', '蟒{莽}[蜢]1，2宋 魏之閒謂之𧊇{蟘}3。南楚之外謂之蟅蟒{蟅莽}[虴蜢]4，或謂之蟒，或謂之螣[蟘]5。', '[音義-1]：莫鯁反\n[郭注-2]：卽蝗也。\n[音義-3]：音貸\n[音義-4]：音近詐，亦呼虴蛨\n[音義-5]：音滕', '卷十一', '方言', '1;3;4', DEFAULT, DEFAULT, '702;8807;8864/702;8786', '8942;8941/8942;8807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1843, '蜻蛉;卽蛉', '', '蜻蛉謂之蝍蛉{卽蛉}1。23', '[音義-1]：音靈\n[郭注-2]：六足四翼蟲也。江東名爲狐黎，淮南人呼䗧𧉅。\n[音義-3]：䗧𧉅，音康伊。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8873/8874;3196/8874', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1844, '舂黍;𧐱蝑', '蜙蝑', '舂黍謂之𧐱蝑[蜙蝑]1。2', '[音義-1]：音藂。音壞沮\n[郭注-2]：又名蚣𧐱，江東呼虴蛨。', '卷十一', '方言', '2', DEFAULT, DEFAULT, '4522/4507;8791/8863', '8862/8863', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1845, '資就;尺蠖', '', '蠀𧑙{資就}謂之蚇蠖{尺蠖}1。2', '[音義-1]：卽、踧兩音。烏郭反\n[郭注-2]：又呼步屈。', '卷十一', '方言', '', DEFAULT, DEFAULT, '3940/3330;5424/8829', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1846, '蠭;蠓螉;翳螉;幼蛻;壺蠭', '', '蠭，燕 趙之閒謂之蠓螉1。其小者謂之蠮螉{翳螉}2，3或謂之蚴蛻{幼蛻}4；其大而蜜者謂之壺蠭。5', '[音義-1]：蒙、翁兩音\n[音義-2]：音鯁噎\n[郭注-3]：小細腰蠭也。\n[音義-4]：幽、悅兩音\n[郭注-5]：今黑蠭穿竹木作孔亦有蜜者，或呼笛師。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8959;8875/8790;2273/8790;2498/8888;6640/8959', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1847, '蠅;羊', '', '蠅，東齊謂之羊，1陳 楚之閒、自關而西秦 晉之閒謂之蠅。', '[郭注-1]：此亦語轉耳。今江東人呼羊聲如蠅，凡此之類，皆不宜別立名也。', '卷十一', '方言', '', DEFAULT, DEFAULT, '9006;2332', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1848, '蚍蜉;蚼象;玄蚼;蛾蛘;坻;垤', '駒象;玄駒', '蚍蜉1，2齊 魯之閒謂之蚼蟓{蚼象}[駒象]3。西南梁 益之閒謂之玄蚼[玄駒]。4燕謂之蛾蛘5。67其場謂之坻8，或謂之垤。9', '[音義-1]：𣬈、浮兩音\n[郭注-2]：亦呼蟞蜉。\n[音義-3]：駒、養兩音\n[郭注-4]：法言曰：「玄駒之步」是。\n[音義-5]：蟻、養兩音\n[郭注-6]：建平人呼蚳。\n[音義-7]：蚳，音侈。\n[音義-8]：直尸反\n[郭注-9]：亦言冢也。', '卷十一', '方言', '2;3', DEFAULT, DEFAULT, '8973/8967;8926/6133;2506/8926;8834/8892;9086;9133', '6138/6133;2506/6138', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1849, '資𧖤;賁;蝤資;卷屬;宣㱿;各;蝎;蛭各;蠹;天螻', '𧓉𧕐;蝤𧓉', '蠀螬{資𧖤}[𧓉𧕐]謂之蟦{賁}1。自關而東謂之蝤蠀{蝤資}[蝤𧓉]2，或謂之䖭蠾{卷屬}3，或謂之蝖𧏚{宣㱿}4。5梁 益之閒謂之蛒{各}6，或謂之蝎，或謂之蛭蛒{蛭各}7。秦 晉之閒謂之蠹，或謂之天螻。8四方異語而通者也。', '[音義-1]：音翡翠\n[音義-2]：酋、餈兩音\n[音義-3]：音書卷\n[音義-4]：喧、斛兩音\n[郭注-5]：亦呼當齊，或呼地蠶，或呼蟦蝖。\n[音義-6]：音格\n[音義-7]：音質\n[郭注-8]：按爾雅云：「𧏚，天螻。」謂螻蛄耳，而方言以爲蝎，未詳其義也。', '卷十一', '方言', '1;3', DEFAULT, DEFAULT, '3940/8956;3944;8821/3940;5784/5427;4558/1962;933;8823;8809/933;8964;3/8831', '8822/8956;8821/8822', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1850, '由延;螾衍;入耳;長麗;蚨于;衄怩', '恧怩', '蚰蜒{由延}1，自關而東謂之螾𧍢{螾衍}2，或謂之入耳，或謂之䗅𧕯{長麗}3。趙 魏之閒或謂之蚨虶{蚨于}4。北燕謂之䖡蚭{衄怩}[恧怩]5。67', '[音義-1]：由、延兩音\n[音義-2]：音引\n[音義-3]：音麗\n[音義-4]：扶、于兩音\n[音義-5]：奴六反。音尼\n[郭注-6]：江東又呼蛩。\n[音義-7]：蛩，音鞏。', '卷十一', '方言', '6', DEFAULT, DEFAULT, '8514/1265;8789/7136;3279/7792;6067/6286;8911/3069;3172/6968', '6958/6968', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1851, '䵹鼄;鼄蝥;屬蝓;毒余', '', '䵹鼄1，鼄蝥也2。自關而西秦 晉之閒謂之鼄蝥。34自關而東趙 魏之郊謂之鼅鼄，或謂之蠾蝓{屬蝓}5。蠾蝓者，侏儒語之轉也。北燕 朝鮮 洌水之閒謂之蝳蜍{毒余}6。7', '[音義-1]：知、株兩音\n[音義-2]：音無\n[郭注-3]：今江東呼蝃蝥。\n[音義-4]：蝃，音掇。\n[音義-5]：燭、臾兩音\n[音義-6]：音毒餘\n[郭注-7]：齊人又呼社公，亦言罔工。', '卷十一', '方言', '', DEFAULT, DEFAULT, '9007/9008;9008/8859;5427/8905;236/718', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1852, '蜉流;渠略', '浮游;螶𧎾', '蜉䖻{蜉流}[浮游]1，秦 晉之閒謂之蟝𧐯{渠略}[螶𧎾]。2', '[音義-1]：浮、由二音\n[郭注-2]：似天牛而小，有甲角，出糞土中，朝生夕死。', '卷十一', '方言', '1;2', DEFAULT, DEFAULT, '8967/7474;7263/9190', '7178/4304;8961/8876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1853, '馬玄;且渠;馬由', '馬蠲', '馬蚿{馬玄}[馬蠲]1，北燕謂之蛆蟝{且渠}2，其大者謂之馬蚰{馬由}3。4', '[音義-1]：音弦\n[音義-2]：音蝍蛆\n[音義-3]：音逐\n[郭注-4]：今關西云。', '卷十一', '方言', '1', DEFAULT, DEFAULT, '6135/2506;9471/7263;6135/8514', '6135/8827', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1854, '爰;爰;哀', '咺;咺', '爰[咺]、喛{爰}[咺]1，哀也。2', '[音義-1]：音𫨻\n[郭注-2]：喛，哀而恚也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '2516;2516;936', '796;796', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1855, '儒輸;愚', '孺輸', '儒輸[孺輸]，愚也。1', '[郭注-1]：儒輸猶愞𨕖也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '4959/9576;6827', '9770/9576', 'null', 'null');
