INSERT INTO `t_han_nga` VALUES (1879, '㞕;往;勞', '', '㞕、往，勞也。1', '[郭注-1]：㞕㞕、往來，皆劬勞也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '5404;1229;9234', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1880, '㞕;往;獪', '泆;誑', '㞕[泆]、𢛛{往}[誑]1，獪也2。', '[音義-1]：音王相\n[音義-2]：音巿儈', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '5404;1229;6340', '7223;1605', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1881, '皦;烓;明', '炯', '皦1、烓[炯]2，明也。', '[音義-1]：音皎\n[音義-2]：口類反', '卷十二', '方言', '2', DEFAULT, DEFAULT, '4937;6452;4321', '6503', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1882, '湊;將;戚', '', '湊、將，威{戚}也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '7293;1982;8400', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1883, '嬀;娗;謾', '僞', '嬀[僞]1、娗2，（⿰亻𭦟）{謾}也3。4', '[音義-1]：居僞反\n[音義-2]：音挻\n[音義-3]：博丹反\n[郭注-4]：爛（⿰亻𭦟），健狡也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '8116;8340;1598', '5134', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1884, '𠐛;虔;謾', '丯', '𠐛、虔[丯]，謾也1。2', '[音義-1]：莫錢反\n[郭注-2]：謂惠黠也。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '4972;3111;1598', '2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1885, '𠐛;佻;疾', '𧾎;跳', '〔𠐛[𧾎]〕、佻[跳]1，疾也。2', '[音義-1]：音糶\n[郭注-2]：謂輕疾也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '4972;5127;4694', '1023;1370', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1886, '鞅;孛;強;懟', '怏;艴', '鞅[怏]、侼{孛}[艴]1，強也。2鞅、3侼，懟也。4', '[音義-1]：音勃\n[郭注-2]：謂強戾也。\n[郭注-3]：鞅猶怏也。\n[郭注-4]：亦爲怨懟。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '1845;3870;8824;6886', '6889;5792', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1887, '追;末;隨', '', '追、末，隨也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '1177;3564;1102', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1888, '僉;怚;劇;夥', '勮', '僉、怚1，劇[勮]也。2僉，夥也3。4', '[音義-1]：音驕怚\n[郭注-2]：謂勤劇。\n[音義-3]：音禍\n[郭注-4]：僉者同，故爲多。', '卷十二', '方言', '3', DEFAULT, DEFAULT, '3270;6820;2809;4335', '9235', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1889, '夸;烝;婬', '', '夸、烝，婬也。1', '[郭注-1]：上婬爲烝。', '卷十二', '方言', '', DEFAULT, DEFAULT, '6600;6429;8336', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1890, '𣬈;賓;懣', '𤼤/濞;𤼤', '𣬈[𤼤/濞]、顮{賓}[𤼤]1，懣也。2', '[音義-1]：音頻\n[郭注-2]：謂憤懣也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '6700;3966;6890', '4729/0/7167;4729', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1891, '煢;激;淸', '瀅', '煢[瀅]、激，淸也。', '', '卷十二', '方言', '1', DEFAULT, DEFAULT, '7700;7186;7200', '7252', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1892, '紓;退;緩', '', '紓1、退，緩也。2', '[音義-1]：音舒\n[郭注-2]：謂寛緩也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '8554;1249;8779', '', 'null', 'null');
