INSERT INTO `t_han_nga` VALUES (1894, '紓;瘛;豸;解;蕆;逞;解', '𤸪', '紓1、瘛[𤸪]2、〔豸〕，解也。蕆3、逞，解也。4', '[音義-1]：音抒井\n[音義-2]：胡計反\n[音義-3]：音展\n[郭注-4]：蕆訓勑，復言解，錯用其義。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '8554;4784;6110;2850;696;1196;2850', '7946', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1895, '柢;柲;刺', '', '柢1、柲，刺也。2', '[音義-1]：音觸牴\n[郭注-2]：皆矛戟之𥎊，所以刺物者也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '3560;3746;2805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1896, '倩;荼;借', '賖', '倩、荼[賖]，1借也。', '[郭注-1]：荼猶徒也。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '4969;668;5075', '3967', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1897, '辟朴;猝', '', '憵朴{辟朴}1，猝也。2', '[音義-1]：音劈歷。音打撲\n[郭注-2]：謂急速也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '5796/3571;6316', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1898, '麋;棃;老', '眉', '麋[眉]、1棃，老也。', '[郭注-1]：麋猶眉也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '6270;3416;5375', '2218', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1899, '萃;離;時', '卒;待', '萃[卒]、離，時[待]也。', '', '卷十二', '方言', '1;3', DEFAULT, DEFAULT, '540;2293;4189', '5359;1245', 'null', 'null');
