INSERT INTO `t_han_nga` VALUES (2089, '箄;簍;與;𤔱;𥴧;筐', '', '箄1、簍2、籅{與}3、䈱{𤔱}4，𥴧也5。江 沔之閒謂之籅。趙 代之閒謂之䈱。淇 衞之閒謂之牛筐。6𥴧，其通語也。𥴧小者，南楚謂之簍。自關而西秦 晉之閒謂之箄。7', '[音義-1]：方氏反\n[音義-2]：音縷\n[音義-3]：音餘\n[音義-4]：音弓弢\n[音義-5]：古筥字\n[郭注-6]：淇，水名也。\n[郭注-7]：今江南亦名籠爲箄。', '卷十三', '方言', '', DEFAULT, DEFAULT, '2926;2929;1781;8444;2961;2960', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2090, '籠;旁;笯', '匚', '籠，南楚 江 沔之閒謂之篣{旁}[匚]1。2或謂之笯3。4', '[音義-1]：音彭\n[郭注-2]：今零陵人呼籠爲篣。\n[音義-3]：那墓反\n[郭注-4]：亦呼籃。', '卷十三', '方言', '2', DEFAULT, DEFAULT, '2956;8;2948', '8423', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2091, '䈰;𥬔旅', '𥬔盧', '𥰠，1南楚謂之筲{䈰}2。3趙 魏之郊謂之𥬔𥰠{𥬔旅}[𥬔盧]。4', '[郭注-1]：盛餅筥也。\n[音義-2]：音鞭鞘\n[郭注-3]：今建平人呼筲。\n[郭注-4]：今通語也。', '卷十三', '方言', '2', DEFAULT, DEFAULT, '2920;3163/4309', '3163/3143', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2092, '錐;苕', '', '錐謂之鍣{苕}。1', '[郭注-1]：廣雅作鉊字。', '卷十三', '方言', '', DEFAULT, DEFAULT, '9365;664', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2093, '刀斗', '鐎斗', '無[緣之]升(斗)謂之刁斗{刀斗}[鐎斗]1。2', '[音義-1]：音貂，見漢書\n[郭注-2]：謂小鈴也。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '2743/9489', '9309/9489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2094, '匕;匙', '', '匕謂之匙1。', '[音義-1]：音祇', '卷十三', '方言', '', DEFAULT, DEFAULT, '5215;5216', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2095, '盂;盡;安盞;盌;喬;銚銳;肙夬', '', '盂謂之㯸{盡}1。河 濟之閒謂之䀂𥂫{安盞}。椀{盌}謂之䀉{喬}，盂謂之銚銳2，椀謂之梋㭈{肙夬}3。4', '[音義-1]：子殄反\n[音義-2]：音謠\n[音義-3]：蠲、玦兩音\n[郭注-4]：椀亦盂屬，江東名盂爲凱，亦曰甌也。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3138;3156;4576/209;3139;6622;9307/9367;2731/1912', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2096, '餌;餻;餈;令;奄;元', '', '餌謂之餻，或謂之餈，或謂之𩚹{令}1，或謂之餣{奄}2，或謂之䬧{元}3。', '[音義-1]：音鈴\n[音義-2]：央怯反\n[音義-3]：音元', '卷十三', '方言', '', DEFAULT, DEFAULT, '1874;3267;3213;5777;6599;2', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2097, '餅;屯;粻昆', '', '餅謂之飩{屯}1，或謂之餦餛{粻昆}2。', '[音義-1]：音屯\n[音義-2]：長、渾兩音', '卷十三', '方言', '', DEFAULT, DEFAULT, '3212;234;4382/4254', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2098, '餳;張皇;飴;亥;𧯡;隓;糖', '', '𩛿{餳}謂之餦餭{張皇}。1飴謂之䬵{亥}2。𩜌{𧯡}謂之䭉{隓}3。4𩛿謂之餹{糖}5。6凡飴謂之𩛿，自關而東陳 楚 宋 衞之閒通語也。', '[郭注-1]：卽乾飴也。\n[音義-2]：音該\n[音義-3]：音髓\n[郭注-4]：以豆屑雜𩛿也。\n[音義-5]：音唐\n[郭注-6]：江東皆言餹。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3210;8486/80;3209;9881;3099;9651;4387', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2099, '𪍠;䴭;䴳;麰;稗;𨢊;稞;𩍔', '', '𪍠1、䴭2、䴳3、麰4、䴽{稗}5、䵆{𨢊}6、7䴹{稞}8，9麴{𩍔}也。自關而西秦 豳10之閒曰𪍠。11晉之舊都曰䴭。12齊右 河 濟曰䴳，或曰麰。13北鄙曰䴽。14麴，其通語也。', '[音義-1]：音哭\n[音義-2]：音才\n[音義-3]：于八反\n[音義-4]：音牟\n[音義-5]：音脾\n[音義-6]：音蒙\n[郭注-7]：有衣麴。\n[音義-8]：音𩸄\n[郭注-9]：小麥麴爲䴹，卽䴷也。\n[音義-10]：音斌\n[郭注-11]：豳卽邠。\n[郭注-12]：今江東人呼麴爲䴭。\n[郭注-13]：大麥麴。\n[郭注-14]：細餅麴。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3358;3360;3359;3349;4444;9806;4468;4363', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2100, '梠;櫺', '', '屋梠謂之欞{櫺}1。2', '[音義-1]：音鈴\n[郭注-2]：雀梠卽屋檐也，亦呼爲連綿。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3642;3654', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2101, '脢;霤', '甍', '𤭐{脢}[甍]謂之㽌{霤}1。2', '[音義-1]：今字作甍，音萌。音霤\n[郭注-2]：卽屋檼也。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '2622;7555', '8453', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2102, '冢;墳;培;俞;采;㝗;壠;㐀;塿;墓;無', '宰;摹', '冢，秦 晉之閒謂之墳，1或謂之培2，或謂之堬{俞}3，或謂之采[宰]，4或謂之埌{㝗}5，或謂之壠。6自關而東謂之㐀，小者謂之塿7，8大者謂之㐀，9凡葬而無墳謂之墓，10所以墓謂之墲{無}[摹]。11', '[郭注-1]：取名於大防也。\n[音義-2]：音部\n[音義-3]：音臾\n[郭注-4]：古者卿大夫有采地，死葬之，因名也。\n[音義-5]：音波浪\n[郭注-6]：有界埓似耕壠，以名之。\n[音義-7]：洛口反\n[郭注-8]：培塿，亦堆高之貌。\n[郭注-9]：又呼冢爲墳也。\n[郭注-10]：言不封也，墓猶慕也。\n[郭注-11]：墲謂規度墓地也，漢書曰：「初陵之墲」是也。', '卷十三', '方言', '5;11', DEFAULT, DEFAULT, '5813;9141;9101;5437;3792;4571;9142;5231;9124;9140;8414', '4593;8024', 'null', 'null');
