INSERT INTO `t_han_nga` VALUES (3872, '休流;扶持;下謀;雲門;六莖;五英;大章;簫韶;大夏;大護;大武;勺;大予', '酌', '休流、扶持、下謀、雲門、六䪫{六莖}1、五韺{五英}2、大章3、簫韶4、〔大夏〕5、大頀{大護}6、大武7、勺[酌]8、大予9。', '[音義-1]：莖。顓頊樂\n[音義-2]：英。帝俈樂\n[音義-3]：堯樂\n[音義-4]：舜樂\n[音義-5]：禹樂\n[音義-6]：湯樂\n[音義-7]：武王樂\n[音義-8]：只藥。周公樂也，斟酌文 武之道\n[音義-9]：漢明帝 永平三年秋八月戊辰，改大樂爲大予樂', '釋樂', '廣雅', '12', DEFAULT, DEFAULT, '3816/7474;7854/7856;9/1507;7576/7730;9726/493;9725/502;6596/1742;2990/1741;6596/3371;6596/1562;6596/8395;9465;6596/2509', '9834', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3873, '足鼓;植鼓;縣鼓;雷鼓;靈鼓;路鼓;鼖鼓;鼛鼓;晉鼓;𧒕鼓;鼙鼓;鞀鼓;應;朄;搏拊', '皋鼓', '足鼓1、植鼓2、縣鼓3、雷鼓4、靈鼓5、路鼓6、鼖鼓7、鼛鼓[皋鼓]8、晉鼓9、鼜鼓{𧒕鼓}10、鼙鼓11、鞀鼓12、應、朄13、搏拊14。', '[音義-1]：夏后氏鼓，四足也\n[音義-2]：見禮明堂記。詩「植我鞉鼓」\n[音義-3]：禮記曰「周縣鼓」，鄭注云「縣於栒虡也」\n[音義-4]：周禮「雷鼓，鼓神祀」，鄭注曰「雷鼓八面」\n[音義-5]：周禮「靈鼓，鼓社祭」，鄭注曰「靈鼓六面也」\n[音義-6]：周禮「路鼓，鼓鬼享」，鄭注「路鼓四面」\n[音義-7]：周禮「鼖鼓，鼓軍事」，鄭注「大鼓也，長八尺」\n[音義-8]：周禮「鼛鼓，鼓役事」，考工記「長尋有四尺也」\n[音義-9]：周禮「晉鼓，鼓金奏」，鄭注「長六尺六寸也」\n[音義-10]：周禮「凡軍旅，夜鼓鼜」，鄭云「夜戒守鼓」。音「造次」之次\n[音義-11]：周禮「旅帥執鼙」\n[音義-12]：周禮「小師之職掌鼓鼗」。釋名云「鞀，導也」\n[音義-13]：詩云「應朄縣鼓」\n[音義-14]：禮記「拊搏」，鄭注「以韋爲之，充之以糠，形如小鼓，以節樂」', '釋樂', '廣雅', '8', DEFAULT, DEFAULT, '1326/3082;3648/3082;5705/3082;7526/3082;206/3082;1410/3082;3084/3082;3083/3082;4203/3082;3079/3082;3085/3082;1810/3082;6711;9801;7864/7890', '6660/3082', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3874, '琴;鳴廉;脩營;藍脅;號鍾;空中;自鳴;焦尾', '攸況;濫協;號鐘', '神農氏琴長三尺六寸六分，上有五弦，曰宮商角徵羽。文王增二弦，曰少宮、〔少〕商。鳴廉、脩營[攸況]。藍脅[濫協]、號鍾[號鐘]、空中、自鳴、焦尾。', '', '釋樂', '廣雅', '3;4;5', DEFAULT, DEFAULT, '8405;2480/5947;2685/4629;282/2617;3068/9292;4646/231;2223/2480;6486/5426', '2033/7160;7179/9258;3068/9386', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3875, '瑟', '', '伏羲氏瑟長七尺二寸，上有〔二十七〕弦1。', '[音義-1]：見世本', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '8406', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3876, '柷', '', '柷，象桶1，方三尺五寸，深尺八寸，四角有陞鼠2。', '[音義-1]：動\n[音義-2]：升', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '3766', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3877, '敔', '', '敔1，象伏虎，背上有二十七刻。', '[音義-1]：魚呂', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2058', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3878, '鍾', '鐘', '倕[垂]氏鍾[鐘]十六枚1。', '[音義-1]：世本「倕造鍾」。倕，舜臣', '釋樂', '廣雅', '1', DEFAULT, DEFAULT, '9292', '9386', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3879, '磬', '', '毋句氏磬十六枚1。', '[音義-1]：世本「毋句作磬」。毋句，堯臣也', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '6039', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3880, '員', '壎', '塤{員}[壎]1，象稱錘，以土爲之，有六孔2。', '[音義-1]：許圓\n[音義-2]：古史考曰：「有塤尙矣。周幽王時，暴辛公善塤」', '釋樂', '廣雅', '1', DEFAULT, DEFAULT, '3932', '9073', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3881, '䶵', '', '䶵1，以竹爲之，長尺四寸，有八孔，〔一孔上出寸三分〕2。', '[音義-1]：池\n[音義-2]：前有一孔，上有三孔，後有四孔，頭有一孔', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '1429', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3882, '籟;簫', '', '籟1謂之簫，大者二十四管，小者十六管，有底。', '[音義-1]：賴', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2992;2990', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3883, '笙', '', '笙，以瓠爲之，十三管，宮管在左方。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2987', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3884, '竽', '', '竽，象笙，三十六管，宮管在中央。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2986', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3885, '龠;笛', '', '龠謂之笛，有七孔。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '1427;2996', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3886, '管', '', '管，象䶵1，長尺圍寸，〔有〕六孔，無底。', '[音義-1]：池', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2994', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3887, '佾', '', '{佾}天子樂八佾，諸公六佾，諸侯四佾。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '5199', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3888, '慟;歈;謳;詠;吟;歌', '', '㗢{慟}1、歈2、謳、詠、吟，歌也。', '[音義-1]：洞\n[音義-2]：頭', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '6974;5588;1572;1573;921;5541', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3889, '太初;太始;太素;九頭;五龍;攝提;合雒;連通;序命;循蜚;因提;禪通;疏訖', '合洛;循飛', '太初，氣之始也，生於酉仲1，清濁未分也。太始，形之始也，生於戌仲，清者爲精、濁者爲形也。太素，質之始也，生於亥仲，已有素朴而未散也。三氣相接，至於子仲，剖判分離，輕清者上爲天，重濁者下爲地，中和爲萬物2。天地辟設人皇以來，至魯哀公十有四年，積二百七十六萬歲，分爲十紀，曰九頭、五龍、攝提、合雒[合洛]，連通、序命、循蜚[循飛]、因提、禪通、疏訖3。', '[音義-1]：八月酉仲，號爲太初，屬雄；九月戌仲，號爲太始，屬雌\n[音義-2]：詩緯曰：「陽本爲雄，陰本爲雌，物本爲魂。雄雌俱行三節，而雄合物魂，號曰太素也。三氣未分別，號曰渾淪」\n[音義-3]：帝王世紀：「自天地闢設人皇以來，迄魏 咸熙二年，凡二百七十二代，積二百七十六萬七百四十五年，分爲十紀，一曰九頭，至十曰疏訖」', '釋天', '廣雅', '7;10', DEFAULT, DEFAULT, '6596/2753;6596/8182;6596/8774;9728/5599;9725/7687;7866/7879;3269/2281;1168/1134;5936/840;1233/8975;3922/7879;55/1134;9787/1577', '3269/7015;1233/7692', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3890, '昦天;陽天;赤天;朱天;成天;幽天;玄天;變天;鈞天', '', '東方昦天、東南陽天、南方赤天、西南朱天、西方成天、西北幽天、北方玄天、東北變天、中央鈞天。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6664/3;9622/3;6585/3;3561/3;9747/3;2501/3;2506/3;2019/3;9377/3', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3891, '天度', '', '{天度}天圜廣南北二億三萬三千五百里七十五步，東西短減四步，周六億十萬七百里二十五步。從地至天，一億一萬六千七百八十七里〔半〕，下度地之厚，與天高等。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/1928', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3892, '七宿度', '', '{七宿度}東方七宿，七十五度；南方七宿，百一十二度；西方七宿，八十度；北方七宿，九十八度四分度之一。四方凡三百六十五度四分度之一。一度二千九百三十二里，二十八宿閒相距積一百七萬九百一十三里，徑三十五萬六千九百七十〔一〕里。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9727/4600/1928', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3893, '條風;明庶風;清明風;景風;涼風;閶闔風;不周風;廣莫風', '調風/融風', '東北條風[調風/融風]、東方明庶風、東南清明風、南方景風、西南涼風、西方閶闔風、西北不周風、北方廣莫風。', '', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '3570/8977;4321/5956/8977;7200/4321/8977;4211/8977;7389/8977;7731/7747/8977;7704/877/8977;5938/701/8977', '1549/8977/0/1862/8977', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3894, '昌光;握譽;可錯;持勝;履予', '', '昌光、握譽、可錯、持勝、履予。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4237/6509;7873/1568;3058/9327;7856/9227;5430/2509', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3895, '格擇;旬始;天狗;枉矢;氛;祲;倍;譎;冠;珥', '格澤;背;鐍/璚/穴', '格擇[格澤]1、旬始、天狗、枉矢、氛、祲2、倍[背]、譎[鐍/璚/穴]、冠、珥。', '[音義-1]：乎格。宅\n[音義-2]：子枕', '釋天', '廣雅', '1;7;8', DEFAULT, DEFAULT, '3601/7897;5806/8182;3/6302;3590/3307;225;66;5114;1668;4797;125', '3601/7220;2616;2859/0/94/0/4632', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3896, '赤霄;濛澒;朝霞;正陽;淪陰;沆瀣;列缺;倒景', '厖鴻;陯陰', '赤霄、濛澒[厖鴻]1、朝霞、正陽、淪陰[陯陰]2、沆瀣3、列缺、倒景。', '[音義-1]：莫孔。乎孔\n[音義-2]：倫\n[音義-3]：乎朗。乎戒', '釋天', '廣雅', '2;5', DEFAULT, DEFAULT, '6585/7533;7317/7445;4287/7571;1087/9622;7176/9621;7165/7453;2771/3301;5200/4211', '5995/2414;9706/9621', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3897, '歉;饑;饉;㱂;大侵', '荒', '一穀不升曰歉1。二穀不升曰饑。三穀不升曰饉。四穀不升曰㱂[荒]2。五穀不升曰大侵。', '[音義-1]：苦簟反\n[音義-2]：康', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '5574;3255;3256;5585;6596/5076', '546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3898, '靈威仰;赤熛怒;含樞紐;白招矩;叶光紀', '白招歫', '蒼曰靈威仰。赤曰赤熛怒。黃曰含樞紐。白曰白招矩[白招歫]。黑曰叶光紀。', '', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '206/8145/5061;6585/6439/6877;815/3649/8651;4929/7919/3032;9258/6509/8540', '4929/7919/1068', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3899, '靑道;黃道;赤道;白道;黑道', '', '立春、春分，東從靑道二出黃道東，交於房二度中；立夏、夏至，南從赤道二出黃道南，交於七星四度中；立秋、秋分，西從白道二出黃道西，交於胃十二度中；立冬、冬至，北從黑道二出黃道北，交於虛二度中。四季之月，還從黃道。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3188/1205;9201/1205;6585/1205;4929/1205;6540/1205', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3900, '月𧘂', '', '{月𧘂}正月不溫，七月不涼。二月不風，八月雷不藏。三月風不衰，九月無降霜。四月雷不見，十月蟄蟲行。五月陽暑不蒸，十一月不合凍。六月浮雲不布，十二月草不喪。七月白露不降，正月有微霜。八月浮雲不歸，二月雷不行。九月物不凋1，三月草木傷。十月流火不定，四月䖵蟲不育。十一月寒不降，五月雨雹。十二月萌類不見，六月五穀不實。', '[音義-1]：彫', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4273/1270', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3901, '營室;東壁;奎;婁;胃;昴;畢;觜觿;參;東井;輿鬼;柳;七星;張;翼;軫;角;亢;貫氐;房;心;尾;箕;斗;牽牛;須女;虛;危', '觜蠵;嬃女', '日月五星行黃道，始營室、東壁、奎、婁、胃之陽，入昴、畢閒，行觜觿[觜蠵]、參之陰，度東井、輿鬼，行柳、七星、張、翼、軫之陰，入角、〔亢〕閒，貫氐、房，出心、尾、箕之陰，入斗、牽牛閒，行須女[嬃女]、虛、危之陽，復至營室。', '', '釋天', '廣雅', '8;26', DEFAULT, DEFAULT, '4629/4557;3844/9048;6597;8321;2609;4230;2491;2849/2851;4315;3844/3190;9526/5819;3512;9727/4314;8486;7693;9540;2826;6653;4339/8369;7723;6703;5426;3018;9489;755/728;5706/8109;5232;6007', '2849/8915;8170/8109', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3902, '离;馮夷;奇相;鬽;賁羊;罔象;畢方;游光;清明', '馮遲;羒羊;必方', '山神謂之离1。河伯謂之馮夷[馮遲]。江神謂之奇相。物神謂之鬽。土神謂之羵羊{賁羊}[羒羊]2。水神謂之冈象{罔象}。木神謂之畢方[必方]。火神謂之游光。金神謂之清明。', '[音義-1]：勑支\n[音義-2]：墳', '釋天', '廣雅', '2;5;7', DEFAULT, DEFAULT, '9732;6201/6613;3059/2161;5826;3944/2332;4813/6133;2491/5453;4304/6509;7200/4321', '6201/1148;2340/2332;717/5453', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3903, '朱明;燿靈;東君;日', '', '朱明、曜靈{燿靈}、東君，日也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3561/4321;6501/206;3844/839;4187', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3904, '夜光;月', '', '夜光謂之月。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4326/6509;4273', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3905, '天河;天漢', '', '天河謂之天漢。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/6984;3/7007', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3906, '震;霣;追;并;雷', '啍;雱', '震、霣1、䨨{追}[啍]2、𩂦{并}[雱]，雷也。', '[音義-1]：于慜\n[音義-2]：追', '釋天', '廣雅', '3;4', DEFAULT, DEFAULT, '7531;7527;1177;5226;7526', '831;8', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3907, '雲;運;雨;榘', '秬', '〔雲〕，運也；雨，榘[秬]也1。', '[音義-1]：俱雨反', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '7576;1138;7525;3032', '3202', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3908, '晷;柱;景', '', '晷、柱，景也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4218;3625;4211', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3909, '飛廉;荓翳;豐隆', '蜚螊', '風師謂之飛廉[蜚螊]。雨師謂之荓翳1。雲師謂之豐隆。', '[音義-1]：蒲形', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '7692/5947;358/2273;3103/3877', '8975/8898', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3910, '羲和;望舒', '朢朔', '日御謂之羲和。月御謂之望舒[朢朔]。', '', '釋天', '廣雅', '2', DEFAULT, DEFAULT, '3065/846;8413/2510', '5240/4274', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3911, '靑龍;天一;太陰;太歲', '', '靑龍、天一、太陰，太歲也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3188/7687;3/1;6596/9621;6596/1082', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3912, '榦枝', '', '{榦枝}甲乙爲榦。榦者，日之神也。寅卯爲枝。枝〔者〕，月之靈也。甲剛乙柔、丙剛丁柔、戊剛己柔、庚剛辛柔、壬剛癸柔。甲齊、乙東夷、丙楚、丁南夷、戊魏、己韓、庚秦、辛西夷、壬衛、癸北夷、子周、丑狄、寅楚、卯鄭、辰晉、巳衞、午秦、未宋、申齊、酉魯、戌趙、亥燕。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3618/3569', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3913, '星宿', '', '{星宿}角、亢，鄭；氐、房、心，宋；尾、箕，燕；斗、牽牛、嬃女，吳〔越〕；虛、危，齊；營室、東壁，衞；奎、婁，魯；胃、昴、畢，趙；觜〔觿〕、參，魏；東井、輿鬼，秦；柳、七星、張，周；翼、軫，楚。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4314/4600', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3914, '樞;旋;機;權;衡;開陽;揺光', '', '北斗七星：一爲樞、二爲旋、三爲機、四爲權、五爲衡、六爲開陽、七爲揺光。樞爲雍州，旋爲冀州，機爲靑、兖州，權爲徐、揚州，衡爲荆州，開陽爲梁州，揺光爲豫州。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3649;4306;3723;3519;2843;7754/9622;7949/6509', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3915, '歲星;重華;應星', '', '歲星謂之重華，或謂之應星1。', '[音義-1]：木宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '1082/4314;5242/3884;6711/4314', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3916, '營惑;罰星;執法', '䁝惑', '營惑[䁝惑]謂之罰星，或謂之執法1。', '[音義-1]：火宿也', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '4629/6863;2797/4314;6646/6260', '2203/6863', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3917, '鎭星;地侯', '', '鎭星謂之地侯1。', '[音義-1]：土宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9358/4314;9020/3311', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3918, '太白;長庚;大囂', '', '太白謂之長庚，或謂之大囂1。', '[音義-1]：金宿也。晨見東方爲啟明，昏見西方爲長庚', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6596/4929;6067/9753;6596/1437', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3919, '辰星;爨星;兔星;鉤星', '', '辰星謂之爨星，〔或謂之〕兔星，或謂之鉤星1。', '[音義-1]：水宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9793/4314;1787/4314;6293/4314;1460/4314', '', 'null', 'null');
