INSERT INTO `t_han_nga` VALUES (3934, '天淵;三淵;紐茲', '', '天淵謂之三淵。天淵謂之紐兹{紐茲}。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/7209;77/7209;8651/526', '', 'null', 'null');
