INSERT INTO `t_han_nga` VALUES (3937, '圓丘;大壇', '', '圓丘、大壇，祭天也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3911/5231;6596/9144', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3938, '方澤;大折', '', '方澤、大折，祭地也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '5453/7220;6596/628', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3939, '大昭', '', '大昭，祭四時也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6596/4197', '', 'null', 'null');
