INSERT INTO `t_han_nga` VALUES (3946, '廟;祧;壇;場;鬼', '', '廟、祧、壇、場、鬼，祭先祖也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '5964;74;9144;9145;5819', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3947, '昔;曹;卒;䬽;膢;臘;祓;絜;餟;祼;軷;䄟;𥛻;夌;禖;禪;祧;醮;禬;卷;望;𩴪;祥;禫;禱;禜;禳;祭', '', '䄍{昔}1、䄚{曹}2、祽{卒}3、祱{䬽}4、䄛{膢}5、臘、祓、禊{絜}6、餟7、祼8、軷9、䄟10、𥛻11、祾{夌}12、禖13、禪、祧14、醮15、禬16、飬{卷}、望、禨{𩴪}17、祥、禫、禱18、禜、禳，祭也。', '[音義-1]：士駕反\n[音義-2]：曹\n[音義-3]：七外\n[音義-4]：税\n[音義-5]：力侯切\n[音義-6]：乎計\n[音義-7]：加税\n[音義-8]：古奐反\n[音義-9]：步末\n[音義-10]：毳\n[音義-11]：布庚\n[音義-12]：陵，又力登\n[音義-13]：梅\n[音義-14]：他聊\n[音義-15]：子咲反\n[音義-16]：古外\n[音義-17]：巨衣\n[音義-18]：倒', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4250;3050;5359;3263;2667;2666;49;8752;3262;45;9571;46;37;3364;58;55;74;9835;54;5784;8413;5829;18;72;51;52;53;30', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3948, '臘;獵/接;蜡;索;清祀;嘉平;大蜡;臘', '', '臘，〔□{獵/接}也〕。〔蜡〕，索也。夏曰清祀1，殷曰嘉平2，周曰大蜡，秦曰臘。', '[音義-1]：以清潔而祭祀\n[音義-2]：嘉，善也；平，成也。以歲終萬物善成，就而報其功', '釋天', '廣雅', '', DEFAULT, DEFAULT, '2666;6358/0/7917;8881;3871;7200/31;3081/3073;6596/8881;2666', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3949, '鬯;薰;茝蘭;蕭;艾', '', '天子祭以鬯，諸侯以薰，卿、大夫以茝蘭，士以蕭，庶人〔以〕艾。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3199;294;292/286;443;389', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3950, '肆兵', '肄兵', '{肆兵}[肄兵]王者以四時畋，以奉宗廟，因簡戎事。刈草爲防，敺1而射之，不題禽，不垝[恑]遇2，不捷草，越防不追。天子取三十焉，一爲乾梪，二爲賓客，三曰充君之庖，其餘以與士。', '[音義-1]：欺于\n[音義-2]：車美', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '6068/1768', '1937/1768', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3951, '旞;旌;旗', '', '全羽曰旞。析羽曰旌。熊虎曰旗。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4295;4292;4289', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3952, '杠', '', '天子杠高九仞。諸侯七仞。〔卿〕大夫五仞。〔士三仞〕。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3674', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3953, '游', '', '{游}天子十二斿，至地。諸侯九斿，至軫。卿大夫七斿，至軹1。士三斿，至肩。', '[音義-1]：紙', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4304', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3954, '年;稔;秋;穀熟', '', '〔年、稔、秋，穀熟也。〕', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4482;4484;4491;4483/1885', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3955, '四海九州', '', '{四海九州}神農度四海內，東西九十萬里，南北八十一萬里。帝堯所治九州，地二千四百三十萬八千二十四頃，其墾者九百一十萬八千二十四頃。夏禹所治四海內，地東西二萬八千里、南北二萬六千里，出水者八千里，受水者八千里。', '', '釋地', '廣雅', '', DEFAULT, DEFAULT, '9718/7129/9728/7490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3956, '湖;藪;陂;塘;都;㽘;斥澤;埏;衍;皋;沼;池', '唐;沆;㚖', '湖、藪、陂、塘[唐]、都、㽘[沆]1、斥澤、埏2、衍、皋[㚖]3、沼，池也。', '[音義-1]：古朗\n[音義-2]：延\n[音義-3]：古豪', '釋地', '廣雅', '4;6;10', DEFAULT, DEFAULT, '7258;563;9625;9157;4005;9186;5967/7220;9152;7136;6660;7256;7257', '878;7165;6669', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3957, '都野;孟豬;彭蠡;少厡;振澤;諸毗;沛澤;雷澤;幽都', '孟都/望諸', '都野、孟豬[孟都/望諸]、彭蠡、少厡1、振澤、諸毗2、沛澤3、雷澤、幽都。', '[音義-1]：原\r\n[音義-2]：符夷\n[音義-3]：盃妹', '釋地', '廣雅', '2', DEFAULT, DEFAULT, '4005/9169;9772/6077;3080/8965;705/7494;7961/7220;1488/6700;7098/7220;7526/7220;2501/4005', '9772/4005/0/8413/1488', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3958, '瓊支;瑾瑜;昭華;白珩;琁;璜;弁和;璵璠;垂棘;碧黸;藍田;琜𧶠;琬;琰;璐;唐;璑;囷;赤瑕', '苕華', '瓊支、瑾瑜、昭華[苕華]、白珩1、璇{琁}2、璜、弁和、璵璠、垂棘、碧瓐{碧黸}、藍田、琜瓄{琜𧶠}3、琬、琰、璐4、瑭{唐}5、璑、㻒{囷}6、赤瑕。', '[音義-1]：衡\n[音義-2]：旋\n[音義-3]：來瀆\n[音義-4]：音路\n[音義-5]：唐\n[音義-6]：渠慜', '釋地', '廣雅', '3', DEFAULT, DEFAULT, '94/1934;90/91;4197/3884;4929/123;94;109;5467/846;89/88;9147/4397;185/6541;282/9170;93/3991;113;115;98;878;101;3917;6585/144', '664/3884', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3959, '水精;石英;𤥗離;珊瑚;玟瑰;夜光;隨侯;虎魄;金精;璣', '石瑛', '水精謂之石英[石瑛]。瑠璃{𤥗離}、珊瑚、玟瑰1、夜光、隋侯{隨侯}2、虎魄、金精、璣。', '[音義-1]：梅。古回\n[音義-2]：隋侯見虵傷治之，後虵銜珠以報', '釋地', '廣雅', '2', DEFAULT, DEFAULT, '6982/4351;6009/502;202/2293;200/201;195/196;4326/6509;1102/3311;3117/5822;9259/4351;197', '6009/100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3960, '蜀石;碝;玟;車渠;馬匘;武夫;琨吾;珉石;咸力;珂', '玪𤨙', '蜀石、碝1、玟2、硨磲{車渠}3、碼碯{馬匘}4、武夫、琨珸{琨吾}5、瑉石{珉石}、瑊玏{咸力}[玪𤨙]6、珂。', '[音義-1]：而兗\n[音義-2]：忙巾\n[音義-3]：車渠\n[音義-4]：馬。奴道\n[音義-5]：昆吾\n[音義-6]：古咸，又咸。勒', '釋地', '廣雅', '9', DEFAULT, DEFAULT, '8826/6009;6012;195;9512/7263;6135/5220;8395/6674;186/837;187/6009;872/9210;213', '158/159', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3961, '合;鷩付;玃如;相繇;化虵', '', '東方有魚焉，如鯉、六足、鳥尾，其名曰鮯{合}1。南方有鳥焉，三首、六目、六足、三翼，其名曰鷩𩿧{鷩付}2。西方有獸焉，如鹿、白尾、馬足、人手、四角，其名曰玃如3。北方有民焉，九首、虵身，其名曰相繇4。中央有虵焉，人面、豺身、鳥翼、虵行，其名曰化虵。此五方之異物也。', '[音義-1]：古合\n[音義-2]：必舌。付于反\n[音義-3]：九縳\n[音義-4]：由', '釋地', '廣雅', '', DEFAULT, DEFAULT, '3269;2465/5056;6372/8243;2161/8514;5214/8993', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3962, '鄰;朋;里;邑;都;師', '', '八家爲鄰，三鄰爲朋，三朋爲里，五里爲邑，十邑爲都，十都爲師，州十有二師焉1。', '[音義-1]：見尙書', '釋地', '廣雅', '', DEFAULT, DEFAULT, '4006;2369;9167;4002;4005;3862', '', 'null', 'null');
