INSERT INTO `t_han_nga` VALUES (4394, '殷虞;晉獒;楚黃;韓黸;宋足;狼狐;㹥;賁', '楚獷;宋獡', '殷虞。晉獒。楚黃[楚獷]1。韓獹{韓黸}。宋㹱{宋足}[宋獡]2。狼狐。㹥3。獖{賁}4。', '[音義-1]：楚有犬名女黃\n[音義-2]：七勺反\n[音義-3]：霔\n[音義-4]：扶粉', '釋嘼', '廣雅', '3;5', DEFAULT, DEFAULT, '5251/3109;4203/6335;3849/9201;3397/6541;4620/1326;6377/6380;6310;3944', '3849/6332;4620/6331', 'null', 'null');
