INSERT INTO `t_han_nga` VALUES (5884, '蓋;戴;燾;蒙;冒;覆', '盍;冡', '蓋[盍]、戴、燾、蒙[冡]、冒，覆也。', '', '廣詁第一', '小爾雅', '1;4', DEFAULT, DEFAULT, '586;1778;6520;649;4807;4852', '3180;4803', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5885, '鐘;崇;府;衆;積;灌;叢;樸;聚', '中;𦼉', '鐘[中]、崇、府、衆、積、灌[𦼉]、叢、樸1，聚也。', '[音義-1]：博木切', '廣詁第一', '小爾雅', '1;6', DEFAULT, DEFAULT, '9386;5880;5922;5235;4465;7042;1750;3605;5236', '231;644', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5886, '閱;搜;履;庇;具', '數;體;比', '閱、搜[數]、履[體]、庀{庇}[比]，具也。', '', '廣詁第一', '小爾雅', '2;3;4', DEFAULT, DEFAULT, '7781;8090;5430;5955;1771', '2010;2586;5227', 'null', 'null');
