INSERT INTO `t_han_nga` VALUES (5892, '邵;媚;旨;伐;美', '妭', '邵、媚、旨、伐[妭]，美也。', '', '廣詁第一', '小爾雅', '4', DEFAULT, DEFAULT, '4049;8183;3042;5165;2355', '8158', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5893, '賢;襃;䋣;優;饒;夥;多', '', '賢、裒{襃}、繁{䋣}、優、饒、夥，多也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '3943;5301;8705;5089;3244;4335;4334', '', 'null', 'null');
