INSERT INTO `t_han_nga` VALUES (6101, '寡;煢;嫠', '赹', '凡無妻、無夫通謂之寡。寡夫曰㷀{煢}[赹]1，寡婦曰嫠2。', '[音義-1]：渠營切，亦作惸\n[音義-2]：陵之切', '廣義第四', '小爾雅', '2', DEFAULT, DEFAULT, '4606;7700;8353', '1015', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6102, '屬婦;逮', '', '妾婦之賤者謂之屬婦。屬1，逮也。逮婦之名，言其微也。', '[音義-1]：時欲切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '5427/8130;1147', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6103, '幸', '', '非分而得謂之幸1。', '[音義-1]：扶問切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '6623', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6104, '讓', '', '詰責以辭謂之讓。', '', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '1692', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6105, '淫;烝;報;通', '勽', '男女不以禮交謂之淫。上淫曰烝，下淫曰報[勽]，旁淫曰通。', '', '廣義第四', '小爾雅', '3', DEFAULT, DEFAULT, '7221;6429;6649;1134', '5807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6106, '慙;愧;戁;恧;逡', '夋', '不直失節謂之慙，慙，愧也。面慙曰戁1，心慙曰恧2，體慙曰逡[夋]。', '[音義-1]：乃版切\n[音義-2]：女六切', '廣義第四', '小爾雅', '5', DEFAULT, DEFAULT, '6957;8344;6723;6958;1161', '3362', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6107, '大行', '', '諱死謂之大行。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '6596/1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6108, '蘇', '', '死而復生謂之蘇。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '256', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6109, '阽', '', '疾甚謂之阽1。', '[音義-1]：余廉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '9689', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6110, '戚;近;從', '', '請天子命曰「未可以戚先王」，請諸侯命曰「未可以近先君」，請大夫命曰「未可以從先子1」。', '[音義-1]：牆容切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '8400;1180;5225', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6111, '櫬;柩', '', '空棺謂之櫬，有屍謂之柩。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3828;8440', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6112, '賵;襚', '', '饋死者謂之賵1，衣服謂之襚2。', '[音義-1]：撫鳯切\n[音義-2]：徐醉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3994;5363', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6113, '殔;池;壙;竁;窆;封', '', '埋柩謂之殔1。殔坎謂之池。壙謂之竁2。下棺謂之窆。塡竁謂之封。', '[音義-1]：羊至切\n[音義-2]：昌縜切，又充芮切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2543;7257;9114;4678;4679;9074', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6114, '宰;冢;壟;塋', '', '宰，塚{冢}也。壟，塋也。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '4593;5813;9142;9139', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6115, '殤', '', '無主之鬼謂之殤。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2537', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6116, '織;繒', '', '治絲曰織。織，繒也1。', '[音義-1]：慈陵切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8531;8590', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6117, '布', '', '麻、紵、葛曰布。布，通名也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4910', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6118, '纊;緜;絮;纊', '', '纊，綿{緜}也。絮之細者曰纊。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8726;8513;8724;8726', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6119, '繒;縞;素', '', '繒之精者曰縞1，縞之麄者曰素。', '[音義-1]：古老切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8590;8599;8774', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6120, '葛;絺;綌', '', '葛之精者曰絺，麄者曰綌。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '458;8737;8738', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6121, '元服', '', '在首謂之元服。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '2/5447', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6122, '弁髦', '', '弁髦，大古冠，布冠而敝之者也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5467/5734', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6123, '題;定;顚;顔;顙;頟', '頂', '題、定[頂]、顚、顔、䫙{顙}，額{頟}也。', '', '廣服第六', '小爾雅', '2', DEFAULT, DEFAULT, '5609;4574;5606;5600;5608;5610', '5607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6124, '璽;印', '', '璽謂之印。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '9075;5789', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6125, '紱;綬', '', '紱謂之綬1。', '[音義-1]：分物切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4925;8646', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6126, '襜褕;童容', '', '襜褕謂之童容1。', '[音義-1]：蚩占切。容朱切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5290/5256;1746/4587', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6127, '藍縷', '襤褸', '布褐而紩之謂之藍縷[襤褸]1。', '[音義-1]：直質切', '廣服第六', '小爾雅', '1', DEFAULT, DEFAULT, '282/8674', '5281/5267', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6128, '絝;裳', '', '袴{絝}謂之裳。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8659;4870', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6129, '冉', '襜', '蔽膝謂之袡{冉}[襜]1。', '[音義-1]：蚩廉切', '廣服第六', '小爾雅', '1', DEFAULT, DEFAULT, '6073', '5290', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6130, '厲', '', '帶之垂者謂之厲。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5983', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6131, '幎', '', '大巾謂之幂{幎}1。', '[音義-1]：莫狄切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4875', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6132, '楃;幕', '', '覆帳謂之幄。幄{楃}，幕也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '3672;4881', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6133, '簀;笫', '', '簀，牀笫也1。', '[音義-1]：士莊切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '2908;2909', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6134, '扇;䈉', '', '大扇謂之䈉。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '7722;2955', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6135, '杖;梃', '', '杖謂之梃。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '3739;3578', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6136, '鍵;籥', '𨷲', '鍵謂之籥[𨷲]1。', '[音義-1]：巨偃切', '廣服第六', '小爾雅', '2', DEFAULT, DEFAULT, '9312;2890', '7772', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6137, '弈', '', '棊局謂之弈。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '1770', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6138, '屨;逹屨', '', '在足謂之屨。屨，尊者曰逹屨。逹，謂之金舄，舄而金絇也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5431;1163/5431', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6139, '侯;鵠;正;執', '臬', '射有張布謂之侯。侯中者謂之鵠1。鵠中者謂之正，正方二尺。正中者謂之𣙗{執}[臬]2，𣙗方六寸。', '[音義-1]：古毒切\n[音義-2]：諸城切。倪結切，亦作臬', '廣器第七', '小爾雅', '4', DEFAULT, DEFAULT, '3311;2413;1087;6646', '3759', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6140, '棘;戟', '', '棘，戟也。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '4397;8375', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6141, '戚;鉞;斧', '戉', '鏚{戚}1、鉞[戉]，斧也。', '[音義-1]：鎗歷切', '廣器第七', '小爾雅', '2', DEFAULT, DEFAULT, '8400;9420;9475', '8399', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6142, '干;瞂;盾', '𢧀', '干[𢧀]、瞂1，盾也。', '[音義-1]：房越切，又徒本切', '廣器第七', '小爾雅', '1', DEFAULT, DEFAULT, '1441;2221;2220', '8379', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6143, '戈;戟', '', '戈，句孑戟也1。', '[音義-1]：古侯切。吉列切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8373;8375', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6144, '鞘;室;鞞;棓;珌;飾', '琫', '刀之韒{鞘}謂之室，室謂之鞞1。鞛{棓}[琫]2、珌3，鞞之飾也。', '[音義-1]：布頂切\n[音義-2]：布孔切\n[音義-3]：壁吉切', '廣器第七', '小爾雅', '4', DEFAULT, DEFAULT, '1849;4557;1812;3741;128;4894', '127', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6145, '服;弢', '', '矢服謂之弢1。', '[音義-1]：他刀切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '5447;8496', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6146, '船;艇;符;頭;舳;尾;艫;楫;橈', '', '小船謂之艇1，艇之小者曰䒀{符}2。船頭謂之舳，尾謂之艫。楫謂之橈。', '[音義-1]：待鼎切\n[音義-2]：薄故切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '5438;5450;2898;5599;5440;5426;5441;3788;3591', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6147, '龍;轅;輈;軫;枕;較;榦;衡;軶;烏啄', '', '車轅上者謂之𨏠{龍}1，轅謂之輈，軫謂之枕，較謂之幹{榦}2。衡，軶也，軶上謂之烏啄。', '[音義-1]：力公切\n[音義-2]：訖岳切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7687;9556;9557;9540;3678;9531;3618;2843;9560;2487/953', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6148, '纍;綆;繘', '', '纍1、綆，繘也2。', '[音義-1]：力追切\n[音義-2]：以律切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8684;8719;8718', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6149, '韜;索;繩', '絛', '縚{韜}[絛]1，索也。大者謂之索，小者謂之繩。', '[音義-1]：他刀切，亦作絛', '廣器第七', '小爾雅', '1', DEFAULT, DEFAULT, '3386;3871;8692', '8664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6150, '䋫;索', '', '詘而戾之爲䋫1，繆而紾之爲索2。', '[音義-1]：側莖切\n[音義-2]：章忍切，或作縝', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8693;3871', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6151, '墉;坰;陴', '', '墉地謂之坰。墉牆謂之陴1。', '[音義-1]：頻彌切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '9082;3322;9700', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6152, '邍;大原', '', '{邍}高平謂之大原。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '1204;6596/7494', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6153, '汪;池', '', '汪，池也。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7157;7257', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6154, '汭', '', '水之北謂之汭。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7144', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6155, '廣;衍', '', '澤之廣者謂之衍。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '5938;7136', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6156, '稾;稈;芻', '犓', '藁{稾}謂之稈1，稈謂之芻[犓]，生曰芻。', '[音義-1]：古旱切', '廣物第八', '小爾雅', '3', DEFAULT, DEFAULT, '4474;4473;611', '758', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6157, '穀;粒;菜;蔬;穗;穎;𢧵;銍', '', '穀謂之粒1，菜謂之蔬。禾穗謂之穎，截{𢧵}穎謂之銍2。', '[音義-1]：（⿰禾立）力入切，亦作粒\n[音義-2]：陟栗切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '4483;4356;558;691;4447;4446;8386;9357', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6158, '拔;揠;擢', '', '拔心曰揠1，拔根曰擢。', '[音義-1]：烏轄切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '7995;7996;7994', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6159, '把;秉;筥;稯;秅', '', '把謂之秉，秉四曰筥1，筥十曰稯2。〔稯十曰秅。〕', '[音義-1]：居許切\n[音義-2]：於求切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '7875;1918;2921;4496;4498', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6160, '棘;棗', '', '棘之實謂之棗。', '', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '4397;4396', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6161, '桑;葚', '', '桑之實謂之葚1。', '[音義-1]：食荏切，又作（⿱艸⿰禾甚）、椹', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '3858;480', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6162, '柞;象', '樣', '柞之實謂之橡{象}[樣]1。', '[音義-1]：徐兩切', '廣物第八', '小爾雅', '2', DEFAULT, DEFAULT, '3481;6133', '3477', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6163, '土;根', '', '〔桑土謂之桑根。〕', '', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '9019;3562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6164, '陽鳥;鴻;鴈', '', '去陰就陽謂之陽鳥，鴻、鴈是也。', '', '廣鳥第九', '小爾雅', '', DEFAULT, DEFAULT, '9622/2368;2414;2422', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6165, '慈烏;雅烏;鸒;燕烏;蒼烏', '', '純黑而反哺者謂之慈烏。小而腹下白、不反哺者謂之雅烏，鸒也1。白項而群飛者謂之燕烏，白脰烏也2。〔大而白脰者謂之蒼烏。〕', '[音義-1]：羊茹切\n[音義-2]：田候切', '廣鳥第九', '小爾雅', '', DEFAULT, DEFAULT, '6745/2487;2279/2487;2385;7686/2487;538/2487', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6166, '豕;彘;豬;豚;豜;豵', '', '豕，彘也。彘，猪{豬}也，其子曰豚。豕之大者謂之豜1，小者謂之豵2。', '[音義-1]：古賢切\n[音義-2]：子紅切', '廣獸第十', '小爾雅', '', DEFAULT, DEFAULT, '6076;6104;6077;6108;6082;6080', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6167, '巢;窠', '', '鳥之所乳謂之巢，鷄、雉所乳謂之窠。', '', '廣獸第十', '小爾雅', '', DEFAULT, DEFAULT, '3892;4650', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6168, '場;堀;潛;槮', '罧;滲', '鹿之所息謂之場，兔之所息謂之窟{堀}，魚之所息謂之潜{潛}[罧]，潜，槮[滲]也1，積柴水中而魚舍焉2。', '[音義-1]：桑感切\n[音義-2]：如字', '廣獸第十', '小爾雅', '3;4', DEFAULT, DEFAULT, '9145;9053;7288;3596', '4829;7203', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6169, '䞨;步;仞;尋;常', '', '跬{䞨}1，一舉足也。倍跬謂之步。四尺謂之仞，倍仞謂之尋，尋，舒兩肱也。倍尋謂之常。', '[音義-1]：丘弭切', '度', '小爾雅', '', DEFAULT, DEFAULT, '1050;1081;4953;1983;4870', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6170, '墨;丈;端;兩;匹;束', '', '五尺謂之墨，倍墨謂之丈，倍丈謂之端，倍端謂之兩1，倍兩謂之匹，匹有五謂之束。', '[音義-1]：力讓切', '度', '小爾雅', '', DEFAULT, DEFAULT, '9076;1464;6680;4811;8422;3897', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6171, '盛;溢;匊', '', '一手之盛謂之溢1，兩手謂之掬{匊}。', '[音義-1]：時征切。弋質切', '量', '小爾雅', '', DEFAULT, DEFAULT, '3140;7398;5803', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6172, '匊;豆;區;釜;藪;缶;鍾;秉;斛', '', '掬，一升也。掬{匊}四謂之豆，豆四謂之區，區四謂之釜，釜二有半謂之藪，藪二有半謂之缶，二缶謂之鍾，二鍾謂之秉。秉，十六斛也。', '', '量', '小爾雅', '', DEFAULT, DEFAULT, '5803;3095;8417;1860;563;3285;9292;1918;9490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6173, '銖;兩;捷;舉;鋝;鍰;斤;衡;稱;鈞;石;鼓', '䄷', '二十四銖曰兩1，兩有半曰捷，倍捷曰舉，倍舉曰鋝2，鋝謂之鍰3，二鍰四兩謂之斤，斤十謂之衡，衡有半謂之秤{稱}，秤二謂之鈞，鈞四謂之石[䄷]，石四謂之鼓。然則鼓四百八十斤也。', '[音義-1]：慵朱切\n[音義-2]：龍輟切\n[音義-3]：胡關切', '衡', '小爾雅', '11', DEFAULT, DEFAULT, '9372;4811;8087;7957;9373;9374;9474;2843;4493;9377;6009;3082', '4499', 'null', 'null');
