INSERT INTO `t_han_nga` VALUES (1850, '悲;惄;悵', '', '蕜{悲}1、惄，悵也。2', '[音義-1]：音翡\n[郭注-2]：謂惋惆也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '6905;6805;6896', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1851, '鬱;熙;長', '巸', '鬱、熙[巸]1，長也。2', '[音義-1]：音怡\n[郭注-2]：謂壯大也。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '3850;6527;6069', '7828', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1852, '娋;孟;姊', '嬃', '娋[嬃]、1孟，2姊也。3', '[郭注-1]：音義未詳。\n[郭注-2]：外傳曰：「孟啖我」是也。\n[郭注-3]：今江東山越閒呼姊聲如市，此因字誤遂俗也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '8287;9774;8149', '8172', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1853, '築里;匹;耦', '妯里/妯𠩺', '築娌{築里}[妯里/妯𠩺]1，2匹也。娌，耦也。', '[音義-1]：度六反，廣雅作妯\n[郭注-2]：今關西兄弟婦相呼爲築娌。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '3619/9169;8424;2822', '8289/9169/0/8289/2056', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1854, '嬴;裔;習', '贏;習', '䃷{嬴}[贏]1、裔[習]，習也。2', '[音義-1]：音盈\n[郭注-2]：謂玩習也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '8116;5312;2240', '3963;2240', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1855, '躔;逡;循;歷;行', '', '躔1、逡2，循也。躔、歷，行也。日運爲躔，3月運爲逡。4', '[音義-1]：度展反\n[音義-2]：音逡巡\n[郭注-3]：躔猶踐也。\n[郭注-4]：運猶行也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '1356;1161;1233;1070;1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1856, '逭;育;轉;步', '', '逭1、逳{育}2，轉也。逭、逳，步也。3', '[音義-1]：音換，亦管\n[音義-2]：陽六反\n[郭注-3]：轉相訓耳。', '卷十二', '方言', '', DEFAULT, DEFAULT, '1171;9788;9577;1081', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1857, '㷭;虞;望', '', '㷭，1虞，望也。', '[郭注-1]：今云烽火是也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '6524;3110;8415', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1858, '揄;橢/墮;脫', '輸;挩', '揄[輸]，㨊{橢/墮}，脫[挩]也。', '', '卷十二', '方言', '1;3', DEFAULT, DEFAULT, '7970;3716/0/9653;2653', '9578;7979', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1859, '解;輸;梲', '挩', '解、輸，梲[挩]也。1', '[郭注-1]：梲猶脫耳。', '卷十二', '方言', '3', DEFAULT, DEFAULT, '2851;9578;3744', '7979', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1860, '賦;與;操', '', '賦、與，操也。1', '[郭注-1]：謂操持也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '3983;1782;7863', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1861, '淥;歇;涸', '', '𣿍{淥}1、歇2，涸也3。4', '[音義-1]：音鹿\n[音義-2]：音泄气\n[音義-3]：音鶴\n[郭注-4]：謂渴也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '7376;5536;7342', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1862, '潎;澂;淸', '', '潎1、澂2，淸也。', '[音義-1]：妨計反\n[音義-2]：音澄', '卷十二', '方言', '', DEFAULT, DEFAULT, '7423;7201;7202', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1863, '逯;遡;行', '', '逯1、遡2，行也。', '[音義-1]：音鹿，亦録\n[音義-2]：音素', '卷十二', '方言', '', DEFAULT, DEFAULT, '1164;7287;1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1864, '墾;牧;司;力;飤;監;督;察', '', '墾、牧，司也。墾，力也。1牧，飤也。2監、牧、〔督〕，察也。', '[郭注-1]：耕墾用力。\n[郭注-2]：謂放飤牛馬也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '9158;2068;5773;9212;3224;5247;2172;4583', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1865, '雚;始;化', '幻', '𡚊{雚}[幻]1，始也。𡚊，化也。2', '[音義-1]：音歡\n[郭注-2]：別異訓也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '2324;8184;5216', '2512', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1866, '鋪;脾;止', '罷', '鋪1、脾[罷]，止也。2', '[音義-1]：妨孤反\n[郭注-2]：義有不同，故異訓之。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '9436;2607;1064', '4843', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1867, '攘;掩;止', '', '攘、掩，止也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '7841;8035;1064', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1868, '幕;覆', '', '幕，覆也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '4883;4854', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1869, '侗;同;狀', '', '侗1、胴{同}2，狀也。3', '[音義-1]：他動反\n[音義-2]：音挺挏\n[郭注-3]：謂形狀也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '4994;4803;6335', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1870, '尐;杪;小', '', '尐、杪，小也。1', '[郭注-1]：樹細枝爲杪也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '706;3583;704', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1871, '㞕;往;勞', '', '㞕、往，勞也。1', '[郭注-1]：㞕㞕、往來，皆劬勞也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '5406;1229;9236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1872, '㞕;往;獪', '泆;誑', '㞕[泆]、𢛛{往}[誑]1，獪也2。', '[音義-1]：音王相\n[音義-2]：音市儈', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '5406;1229;6342', '7225;1605', 'null', 'null');
