INSERT INTO `t_han_nga` VALUES (1874, '湊;將;戚', '', '湊、將，威{戚}也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '7295;1983;8402', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1875, '嬀;娗;謾', '僞', '嬀[僞]1、娗2，（⿰亻𭦟）{謾}也3。4', '[音義-1]：居僞反\n[音義-2]：音挻\n[音義-3]：博丹反\n[郭注-4]：爛（⿰亻𭦟），健狡也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '8118;8342;1598', '5136', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1876, '𠐛;虔;謾', '丯', '𠐛、虔[丯]，謾也1。2', '[音義-1]：莫錢反\n[郭注-2]：謂惠黠也。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '4974;3112;1598', '2818', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1877, '𠐛;佻;疾', '𧾎;跳', '〔𠐛[𧾎]〕、佻[跳]1，疾也。2', '[音義-1]：音糶\n[郭注-2]：謂輕疾也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '4974;5129;4696', '1023;1370', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1878, '鞅;孛;強;懟', '怏;艴', '鞅[怏]、侼{孛}[艴]1，強也。2鞅、3侼，懟也。4', '[音義-1]：音勃\n[郭注-2]：謂強戾也。\n[郭注-3]：鞅猶怏也。\n[郭注-4]：亦爲怨懟。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '1846;3872;8826;6888', '6891;5794', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1879, '追;末;隨', '', '追、末，隨也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '1177;3566;1102', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1880, '僉;怚;劇;夥', '勮', '僉、怚1，劇[勮]也。2僉，夥也3。4', '[音義-1]：音驕怚\n[郭注-2]：謂勤劇。\n[音義-3]：音禍\n[郭注-4]：僉者同，故爲多。', '卷十二', '方言', '3', DEFAULT, DEFAULT, '3272;6822;2810;4337', '9237', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1881, '夸;烝;婬', '', '夸、烝，婬也。1', '[郭注-1]：上婬爲烝。', '卷十二', '方言', '', DEFAULT, DEFAULT, '6602;6431;8338', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1882, '𣬈;賓;懣', '𤼤/濞;𤼤', '𣬈[𤼤/濞]、顮{賓}[𤼤]1，懣也。2', '[音義-1]：音頻\n[郭注-2]：謂憤懣也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '6702;3968;6892', '4731/0/7169;4731', 'null', 'null');
