INSERT INTO `t_han_nga` VALUES (1884, '紓;退;緩', '', '紓1、退，緩也。2', '[音義-1]：音舒\n[郭注-2]：謂寛緩也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '8556;1249;8781', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1885, '淸;躡;急', '䋫;㛼', '淸[䋫]、躡[㛼]，急也。', '', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '7202;1352;6811', '8695;8315', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1886, '紓;瘛;豸;解;蕆;逞;解', '𤸪', '紓1、瘛[𤸪]2、〔豸〕，解也。蕆3、逞，解也。4', '[音義-1]：音抒丼\n[音義-2]：胡計反\n[音義-3]：音展\n[郭注-4]：蕆訓勑，復言解，錯用其義。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '8556;4786;6112;2851;696;1196;2851', '7948', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1887, '柢;柲;刺', '', '柢1、柲，刺也。2', '[音義-1]：音觸牴\n[郭注-2]：皆矛戟之𥎊，所以刺物者也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '3562;3748;2806', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1888, '倩;荼;借', '賖', '倩、荼[賖]，1借也。', '[郭注-1]：荼猶徒也。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '4971;668;5077', '3969', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1889, '辟朴;猝', '', '憵朴{辟朴}1，猝也。2', '[音義-1]：音劈歷。音打撲\n[郭注-2]：謂急速也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '5798/3573;6318', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (1890, '麋;棃;老', '眉', '麋[眉]、1棃，老也。', '[郭注-1]：麋猶眉也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '6272;3418;5377', '2219', 'null', 'null');
