INSERT INTO `t_han_nga` VALUES (2081, '箄;簍;與;𤔱;𥴧;筐', '', '箄1、簍2、籅{與}3、䈱{𤔱}4，𥴧也5。江 沔之閒謂之籅。趙 代之閒謂之䈱。淇 衞之閒謂之牛筐。6𥴧，其通語也。𥴧小者，南楚謂之簍。自關而西秦 晉之閒謂之箄。7', '[音義-1]：方氏反\n[音義-2]：音縷\n[音義-3]：音餘\n[音義-4]：音弓弢\n[音義-5]：古筥字\n[郭注-6]：淇，水名也。\n[郭注-7]：今江南亦名籠爲箄。', '卷十三', '方言', '', DEFAULT, DEFAULT, '2927;2930;1782;8446;2962;2961', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2082, '籠;旁;笯', '匚', '籠，南楚 江 沔之閒謂之篣{旁}[匚]1。2或謂之笯3。4', '[音義-1]：音彭\n[郭注-2]：今零陵人呼籠爲篣。\n[音義-3]：那墓反\n[郭注-4]：亦呼籃。', '卷十三', '方言', '2', DEFAULT, DEFAULT, '2957;8;2949', '8425', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2083, '䈰;𥬔旅', '𥬔盧', '𥰠，1南楚謂之筲{䈰}2。3趙 魏之郊謂之𥬔𥰠{𥬔旅}[𥬔盧]。4', '[郭注-1]：盛餅筥也。\n[音義-2]：音鞭鞘\n[郭注-3]：今建平人呼筲。\n[郭注-4]：今通語也。', '卷十三', '方言', '2', DEFAULT, DEFAULT, '2921;3164/4311', '3164/3144', 'null', 'null');
