INSERT INTO `t_han_nga` VALUES (2085, '刀斗', '鐎斗', '無[緣之]升(斗)謂之刁斗{刀斗}[鐎斗]1。2', '[音義-1]：音貂，見漢書\n[郭注-2]：謂小鈴也。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '2744/9491', '9311/9491', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2086, '匕;匙', '', '匕謂之匙1。', '[音義-1]：音祇', '卷十三', '方言', '', DEFAULT, DEFAULT, '5217;5218', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2087, '盂;盡;安盞;盌;喬;銚銳;肙夬', '', '盂謂之㯸{盡}1。河 濟之閒謂之䀂𥂫{安盞}。椀{盌}謂之䀉{喬}，盂謂之銚銳2，椀謂之梋㭈{肙夬}3。4', '[音義-1]：子殄反\n[音義-2]：音謠\n[音義-3]：蠲、玦兩音\n[郭注-4]：椀亦盂屬，江東名盂爲凱，亦曰甌也。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3139;3157;4578/209;3140;6624;9309/9369;2732/1913', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2088, '餌;餻;餈;令;奄;元', '', '餌謂之餻，或謂之餈，或謂之𩚹{令}1，或謂之餣{奄}2，或謂之䬧{元}3。', '[音義-1]：音鈴\n[音義-2]：央怯反\n[音義-3]：音元', '卷十三', '方言', '', DEFAULT, DEFAULT, '1875;3269;3215;5779;6601;2', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2089, '餅;屯;粻昆', '', '餅謂之飩{屯}1，或謂之餦餛{粻昆}2。', '[音義-1]：音屯\n[音義-2]：長、渾兩音', '卷十三', '方言', '', DEFAULT, DEFAULT, '3214;234;4384/4256', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2090, '餳;張皇;飴;亥;𧯡;隓;糖', '', '𩛿{餳}謂之餦餭{張皇}。1飴謂之䬵{亥}2。𩜌{𧯡}謂之䭉{隓}3。4𩛿謂之餹{糖}5。6凡飴謂之𩛿，自關而東陳 楚 宋 衞之閒通語也。', '[郭注-1]：卽乾飴也。\n[音義-2]：音該\n[音義-3]：音髓\n[郭注-4]：以豆屑雜𩛿也。\n[音義-5]：音唐\n[郭注-6]：江東皆言餹。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3212;8488/80;3211;9883;3100;9653;4389', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2091, '𪍠;䴭;䴳;麰;稗;𨢊;稞;𩍔', '', '𪍠1、䴭2、䴳3、麰4、䴽{稗}5、䵆{𨢊}6、7䴹{稞}8，9麴{𩍔}也。自關而西秦 豳10之閒曰𪍠。11晉之舊都曰䴭。12齊右 河 濟曰䴳，或曰麰。13北鄙曰䴽。14麴，其通語也。', '[音義-1]：音哭\n[音義-2]：音才\n[音義-3]：于八反\n[音義-4]：音牟\n[音義-5]：音脾\n[音義-6]：音蒙\n[郭注-7]：有衣麴。\n[音義-8]：音𩸄\n[郭注-9]：小麥麴爲䴹，卽䴷也。\n[音義-10]：音斌\n[郭注-11]：豳卽邠。\n[郭注-12]：今江東人呼麴爲䴭。\n[郭注-13]：大麥麴。\n[郭注-14]：細餅麴。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3360;3362;3361;3351;4446;9808;4470;4365', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2092, '梠;櫺', '', '屋梠謂之欞{櫺}1。2', '[音義-1]：音鈴\n[郭注-2]：雀梠卽屋檐也，亦呼爲連綿。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3644;3656', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2093, '脢;霤', '甍', '𤭐{脢}[甍]謂之㽌{霤}1。2', '[音義-1]：今字作甍，音萌。音霤\n[郭注-2]：卽屋檼也。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '2623;7557', '8455', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2094, '冢;墳;培;俞;采;㝗;壠;㐀;塿;墓;無', '宰;摹', '冢，秦 晉之閒謂之墳，1或謂之培2，或謂之堬{俞}3，或謂之采[宰]，4或謂之埌{㝗}5，或謂之壠。6自關而東謂之㐀，小者謂之塿7，8大者謂之㐀，9凡葬而無墳謂之墓，10所以墓謂之墲{無}[摹]。11', '[郭注-1]：取名於大防也。\n[音義-2]：音部\n[音義-3]：音臾\n[郭注-4]：古者卿大夫有采地，死葬之，因名也。\n[音義-5]：音波浪\n[郭注-6]：有界埒似耕壠，以名之。\n[音義-7]：洛口反\n[郭注-8]：培塿，亦堆高之貌。\n[郭注-9]：又呼冢爲墳也。\n[郭注-10]：言不封也，墓猶慕也。\n[郭注-11]：墲謂規度墓地也，漢書曰：「初陵之墲」是也。', '卷十三', '方言', '5;11', DEFAULT, DEFAULT, '5815;9143;9103;5439;3794;4573;9144;5233;9126;9142;8416', '4595;8026', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2095, '古;昔;先;創;方;作;造;朔;萌;芽;本;根;櫱;鼃;䢦;昌;孟;鼻;業;始', '刱;糱;𠤎;律;唱', '古、昔、先、創[刱]、方、作、造、朔、萌、芽、本、根、櫱[糱]、鼃[𠤎]1、䔞{䢦}[律]2、昌[唱]、孟、鼻、業，始也。', '[音義-1]：戸瓜反\n[音義-2]：律', '釋詁', '廣雅', '4;13;14;15;16', DEFAULT, DEFAULT, '1461;4252;5472;2813;5455;5075;1114;4276;491;490;3561;3564;3804;9000;1095;4239;9774;2233;1749;8184', '3195;4357;5213;1257;845', 'null', 'null');
