INSERT INTO `t_han_nga` VALUES (3864, '休流;扶持;下謀;雲門;六莖;五英;大章;簫韶;大夏;大護;大武;勺;大予', '酌', '休流、扶持、下謀、雲門、六䪫{六莖}1、五韺{五英}2、大章3、簫韶4、〔大夏〕5、大頀{大護}6、大武7、勺[酌]8、大予9。', '[音義-1]：莖。顓頊樂\n[音義-2]：英。帝俈樂\n[音義-3]：堯樂\n[音義-4]：舜樂\n[音義-5]：禹樂\n[音義-6]：湯樂\n[音義-7]：武王樂\n[音義-8]：只藥。周公樂也，斟酌文 武之道\n[音義-9]：漢明帝 永平三年秋八月戊辰，改大樂爲大予樂', '釋樂', '廣雅', '12', DEFAULT, DEFAULT, '3818/7476;7856/7858;9/1507;7578/7732;9728/493;9727/502;6598/1742;2991/1741;6598/3373;6598/1562;6598/8397;9467;6598/2510', '9836', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3865, '足鼓;植鼓;縣鼓;雷鼓;靈鼓;路鼓;鼖鼓;鼛鼓;晉鼓;𧒕鼓;鼙鼓;鞀鼓;應;朄;搏拊', '皋鼓', '足鼓1、植鼓2、縣鼓3、雷鼓4、靈鼓5、路鼓6、鼖鼓7、鼛鼓[皋鼓]8、晉鼓9、鼜鼓{𧒕鼓}10、鼙鼓11、鞀鼓12、應、朄13、搏拊14。', '[音義-1]：夏后氏鼓，四足也\n[音義-2]：見禮明堂記。詩「植我鞉鼓」\n[音義-3]：禮記曰「周縣鼓」，鄭注云「縣於栒虡也」\n[音義-4]：周禮「雷鼓，鼓神祀」，鄭注曰「雷鼓八面」\n[音義-5]：周禮「靈鼓，鼓社祭」，鄭注曰「靈鼓六面也」\n[音義-6]：周禮「路鼓，鼓鬼享」，鄭注「路鼓四面」\n[音義-7]：周禮「鼖鼓，鼓軍事」，鄭注「大鼓也，長八尺」\n[音義-8]：周禮「鼛鼓，鼓役事」，考工記「長尋有四尺也」\n[音義-9]：周禮「晉鼓，鼓金奏」，鄭注「長六尺六寸也」\n[音義-10]：周禮「凡軍旅，夜鼓鼜」，鄭云「夜戒守鼓」。音「造次」之次\n[音義-11]：周禮「旅帥執鼙」\n[音義-12]：周禮「小師之職掌鼓鼗」。釋名云「鞀，導也」\n[音義-13]：詩云「應朄縣鼓」\n[音義-14]：禮記「拊搏」，鄭注「以韋爲之，充之以糠，形如小鼓，以節樂」', '釋樂', '廣雅', '8', DEFAULT, DEFAULT, '1326/3083;3650/3083;5707/3083;7528/3083;206/3083;1410/3083;3085/3083;3084/3083;4205/3083;3080/3083;3086/3083;1811/3083;6713;9803;7866/7892', '6662/3083', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3866, '琴;鳴廉;脩營;藍脅;號鍾;空中;自鳴;焦尾', '攸況;濫協;號鐘', '神農氏琴長三尺六寸六分，上有五弦，曰宮商角徵羽。文王增二弦，曰少宮、〔少〕商。鳴廉、脩營[攸況]。藍脅[濫協]、號鍾[號鐘]、空中、自鳴、焦尾。', '', '釋樂', '廣雅', '3;4;5', DEFAULT, DEFAULT, '8407;2481/5949;2686/4631;282/2618;3069/9294;4648/231;2224/2481;6488/5428', '2034/7162;7181/9260;3069/9388', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3867, '瑟', '', '伏羲氏瑟長七尺二寸，上有〔二十七〕弦1。', '[音義-1]：見世本', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '8408', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3868, '柷', '', '柷，象桶1，方三尺五寸，深尺八寸，四角有陞鼠2。', '[音義-1]：動\n[音義-2]：升', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '3768', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3869, '敔', '', '敔1，象伏虎，背上有二十七刻。', '[音義-1]：魚呂', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2059', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3870, '鍾', '鐘', '倕[垂]氏鍾[鐘]十六枚1。', '[音義-1]：世本「倕造鍾」。倕，舜臣', '釋樂', '廣雅', '1', DEFAULT, DEFAULT, '9294', '9388', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3871, '磬', '', '毋句氏磬十六枚1。', '[音義-1]：世本「毋句作磬」。毋句，堯臣也', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '6041', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3872, '員', '壎', '塤{員}[壎]1，象稱錘，以土爲之，有六孔2。', '[音義-1]：許圓\n[音義-2]：古史考曰：「有塤尙矣。周幽王時，暴辛公善塤」', '釋樂', '廣雅', '1', DEFAULT, DEFAULT, '3934', '9075', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3873, '䶵', '', '䶵1，以竹爲之，長尺四寸，有八孔，〔一孔上出寸三分〕2。', '[音義-1]：池\n[音義-2]：前有一孔，上有三孔，後有四孔，頭有一孔', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '1429', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3874, '籟;簫', '', '籟1謂之簫。大者二十四管，〔無底。〕小者十六管，有底。', '[音義-1]：賴', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2993;2991', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3875, '笙', '', '笙，以瓠爲之，十三管，宮管在左方。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2988', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3876, '竽', '', '竽，象笙，三十六管，宮管在中央。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2987', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3877, '龠;笛', '', '龠謂之笛，有七孔。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '1427;2997', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3878, '管', '', '管，象䶵1，長尺圍寸，〔有〕六孔，無底。', '[音義-1]：池', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2995', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3879, '佾', '', '{佾}天子樂八佾，諸公六佾，諸侯四佾。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '5201', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3880, '慟;歈;謳;詠;吟;歌', '', '㗢{慟}1、歈2、謳、詠、吟，歌也。', '[音義-1]：洞\n[音義-2]：頭', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '6976;5590;1572;1573;921;5543', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3881, '太初;太始;太素;九頭;五龍;攝提;合雒;連通;序命;循蜚;因提;禪通;疏訖', '合洛;循飛', '太初，氣之始也，生於酉仲1，淸濁未分也。太始，形之始也，生於戌仲，淸者爲精、濁者爲形也。太素，質之始也，生於亥仲，已有素朴而未散也。三氣相接，至於子仲，剖判分離，輕淸者上爲天，重濁者下爲地，中和爲萬物2。天地辟設人皇以來，至魯哀公十有四年，積二百七十六萬歲，分爲十紀，曰九頭、五龍、攝提、合雒[合洛]，連通、序命、循蜚[循飛]、因提、禪通、疏訖3。', '[音義-1]：八月酉仲，號爲太初，屬雄；九月戌仲，號爲太始，屬雌\n[音義-2]：詩緯曰：「陽本爲雄，陰本爲雌，物本爲魂。雄雌俱行三節，而雄合物魂，號曰太素也。三氣未分別，號曰渾淪」\n[音義-3]：帝王世紀：「自天地闢設人皇以來，迄魏 咸熙二年，凡二百七十二代，積二百七十六萬七百四十五年，分爲十紀，一曰九頭，至十曰疏訖」', '釋天', '廣雅', '7;10', DEFAULT, DEFAULT, '6598/2754;6598/8184;6598/8776;9730/5601;9727/7689;7868/7881;3271/2282;1168/1134;5938/840;1233/8977;3924/7881;55/1134;9789/1577', '3271/7017;1233/7694', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3882, '昦天;陽天;赤天;朱天;成天;幽天;玄天;變天;鈞天', '', '東方昦天、東南陽天、南方赤天、西南朱天、西方成天、西北幽天、北方玄天、東北變天、中央鈞天。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6666/3;9624/3;6587/3;3563/3;9749/3;2502/3;2507/3;2020/3;9379/3', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3883, '天度', '', '{天度}天圜廣南北二億三萬三千五百里七十五步，東西短減四步，周六億十萬七百里二十五步。從地至天，一億一萬六千七百八十七里〔半〕，下度地之厚，與天高等。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/1929', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3884, '七宿度', '', '{七宿度}東方七宿，七十五度；南方七宿，百一十二度；西方七宿，八十度；北方七宿，九十八度四分度之一。四方凡三百六十五度四分度之一。一度二千九百三十二里，二十八宿閒相距積一百七萬九百一十三里，徑三十五萬六千九百七十〔一〕里。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9729/4602/1929', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3885, '條風;明庶風;淸明風;景風;涼風;閶闔風;不周風;廣莫風', '調風/融風', '東北條風[調風/融風]、東方明庶風、東南淸明風、南方景風、西南涼風、西方閶闔風、西北不周風、北方廣莫風。', '', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '3572/8979;4323/5958/8979;7202/4323/8979;4213/8979;7391/8979;7733/7749/8979;7706/877/8979;5940/701/8979', '1549/8979/0/1863/8979', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3886, '昌光;握譽;可錯;持勝;履予', '', '昌光、握譽、可錯、持勝、履予。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4239/6511;7875/1568;3059/9329;7858/9229;5432/2510', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3887, '格擇;旬始;天狗;枉矢;氛;祲;倍;譎;冠;珥', '格澤;背;鐍/璚/穴', '格擇[格澤]1、旬始、天狗、枉矢、氛、祲2、倍[背]、譎[鐍/璚/穴]、冠、珥。', '[音義-1]：乎格。宅\n[音義-2]：子枕', '釋天', '廣雅', '1;7;8', DEFAULT, DEFAULT, '3603/7899;5808/8184;3/6304;3592/3309;225;66;5116;1668;4799;125', '3603/7222;2617;2860/0/94/0/4634', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3888, '赤霄;濛澒;朝霞;正陽;淪陰;沆瀣;列缺;倒景', '厖鴻;陯陰', '赤霄、濛澒[厖鴻]1、朝霞、正陽、淪陰[陯陰]2、沆瀣3、列缺、倒景。', '[音義-1]：莫孔。乎孔\n[音義-2]：倫\n[音義-3]：乎朗。乎戒', '釋天', '廣雅', '2;5', DEFAULT, DEFAULT, '6587/7535;7319/7447;4289/7573;1087/9624;7178/9623;7167/7455;2772/3303;5202/4213', '5997/2415;9708/9623', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3889, '歉;饑;饉;㱂;大侵', '荒', '一穀不升曰歉1。二穀不升曰饑。三穀不升曰饉。四穀不升曰㱂[荒]2。五穀不升曰大侵。', '[音義-1]：苦簟反\n[音義-2]：康', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '5576;3257;3258;5587;6598/5078', '546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3890, '靈威仰;赤熛怒;含樞紐;白招矩;叶光紀', '白招歫', '蒼曰靈威仰。赤曰赤熛怒。黃曰含樞紐。白曰白招矩[白招歫]。黑曰叶光紀。', '', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '206/8147/5063;6587/6441/6879;815/3651/8653;4931/7921/3033;9260/6511/8542', '4931/7921/1068', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3891, '靑道;黃道;赤道;白道;黑道', '', '立春、春分，東從靑道二出黃道東，交於房二度中；立夏、夏至，南從赤道二出黃道南，交於七星四度中；立秋、秋分，西從白道二出黃道西，交於胃十二度中；立冬、冬至，北從黑道二出黃道北，交於虛二度中。四季之月，還從黃道。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3189/1205;9203/1205;6587/1205;4931/1205;6542/1205', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3892, '月𧘂', '', '{月𧘂}正月不溫，七月不涼。二月不風，八月雷不藏。三月風不衰，九月無降霜。四月雷不見，十月蟄蟲行。五月陽暑不蒸，十一月不合凍。六月浮雲不布，十二月草不喪。七月白露不降，正月有微霜。八月浮雲不歸，二月雷不行。九月物不凋1，三月草木傷。十月流火不定，四月䖵蟲不育。十一月寒不降，五月雨雹。十二月萌類不見，六月五穀不實。', '[音義-1]：彫', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4275/1270', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3893, '營室;東壁;奎;婁;胃;昴;畢;觜觿;參;東丼;輿鬼;柳;七星;張;翼;軫;角;亢;貫氐;房;心;尾;箕;斗;牽牛;須女;虛;危', '觜蠵;嬃女', '日月五星行黃道，始營室、東壁、奎、婁、胃之陽，入昴、畢閒，行觜觿[觜蠵]、參之陰，度東丼、輿鬼，行柳、七星、張、翼、軫之陰，入角、〔亢〕閒，貫氐、房，出心、尾、箕之陰，入斗、牽牛閒，行須女[嬃女]、虛、危之陽，復至營室。', '', '釋天', '廣雅', '8;26', DEFAULT, DEFAULT, '4631/4559;3846/9050;6599;8323;2610;4232;2492;2850/2852;4317;3846/3191;9528/5821;3514;9729/4316;8488;7695;9542;2827;6655;4341/8371;7725;6705;5428;3019;9491;755/728;5708/8111;5234;6009', '2850/8917;8172/8111', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3894, '离;馮夷;奇相;鬽;賁羊;罔象;畢方;游光;淸明', '馮遲;羒羊;必方', '山神謂之离1。河伯謂之馮夷[馮遲]。江神謂之奇相。物神謂之鬽。土神謂之羵羊{賁羊}[羒羊]2。水神謂之冈象{罔象}。木神謂之畢方[必方]。火神謂之游光。金神謂之淸明。', '[音義-1]：勑支\n[音義-2]：墳', '釋天', '廣雅', '2;5;7', DEFAULT, DEFAULT, '9734;6203/6615;3060/2162;5828;3946/2333;4815/6135;2492/5455;4306/6511;7202/4323', '6203/1148;2341/2333;717/5455', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3895, '朱明;燿靈;東君;日', '', '朱明、曜靈{燿靈}、東君，日也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3563/4323;6503/206;3846/839;4189', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3896, '夜光;月', '', '夜光謂之月。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4328/6511;4275', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3897, '天河;天漢', '', '天河謂之天漢。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/6986;3/7009', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3898, '震;霣;追;并;雷', '啍;雱', '震、霣1、䨨{追}[啍]2、𩂦{并}[雱]，雷也。', '[音義-1]：于慜\n[音義-2]：追', '釋天', '廣雅', '3;4', DEFAULT, DEFAULT, '7533;7529;1177;5228;7528', '831;8', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3899, '雲;運;雨;榘', '秬', '〔雲〕，運也；雨，榘[秬]也1。', '[音義-1]：俱雨反', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '7578;1138;7527;3033', '3204', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3900, '晷;柱;景', '', '晷、柱，景也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4220;3627;4213', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3901, '飛廉;荓翳;豐隆', '蜚螊', '風師謂之飛廉[蜚螊]。雨師謂之荓翳1。雲師謂之豐隆。', '[音義-1]：蒲形', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '7694/5949;358/2274;3104/3879', '8977/8900', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3902, '羲和;望舒', '朢朔', '日御謂之羲和。月御謂之望舒[朢朔]。', '', '釋天', '廣雅', '2', DEFAULT, DEFAULT, '3066/846;8415/2511', '5242/4276', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3903, '靑龍;天一;太陰;太歲', '', '靑龍、天一、太陰，太歲也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3189/7689;3/1;6598/9623;6598/1082', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3904, '榦枝', '', '{榦枝}甲乙爲榦。榦者，日之神也。寅卯爲枝。枝〔者〕，月之靈也。甲剛乙柔、丙剛丁柔、戊剛己柔、庚剛辛柔、壬剛癸柔。甲齊、乙東夷、丙楚、丁南夷、戊魏、己韓、庚秦、辛西夷、壬衛、癸北夷、子周、丑狄、寅楚、卯鄭、辰晉、巳衞、午秦、未宋、申齊、酉魯、戌趙、亥燕。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3620/3571', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3905, '星宿', '', '{星宿}角、亢，鄭；氐、房、心，宋；尾、箕，燕；斗、牽牛、嬃女，吳〔越〕；虛、危，齊；營室、東壁，衞；奎、婁，魯；胃、昴、畢，趙；觜〔觿〕、參，魏；東丼、輿鬼，秦；柳、七星、張，周；翼、軫，楚。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4316/4602', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3906, '樞;旋;機;權;衡;開陽;揺光', '', '北斗七星：一爲樞、二爲旋、三爲機、四爲權、五爲衡、六爲開陽、七爲揺光。樞爲雍州，旋爲冀州，機爲靑、兖州，權爲徐、揚州，衡爲荆州，開陽爲梁州，揺光爲豫州。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3651;4308;3725;3521;2844;7756/9624;7951/6511', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3907, '歲星;重華;應星', '', '歲星謂之重華，或謂之應星1。', '[音義-1]：木宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '1082/4316;5244/3886;6713/4316', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3908, '營惑;罰星;執法', '䁝惑', '營惑[䁝惑]謂之罰星，或謂之執法1。', '[音義-1]：火宿也', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '4631/6865;2798/4316;6648/6262', '2204/6865', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3909, '鎮星;地侯', '', '鎮星謂之地侯1。', '[音義-1]：土宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9360/4316;9022/3313', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3910, '太白;長庚;大囂', '', '太白謂之長庚，或謂之大囂1。', '[音義-1]：金宿也。晨見東方爲啟明，昏見西方爲長庚', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6598/4931;6069/9755;6598/1437', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3911, '辰星;爨星;兔星;鉤星', '', '辰星謂之爨星，〔或謂之〕兔星，或謂之鉤星1。', '[音義-1]：水宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9795/4316;1788/4316;6295/4316;1460/4316', '', 'null', 'null');
