INSERT INTO `t_han_nga` VALUES (3926, '天淵;三淵;紐茲', '', '天淵謂之三淵。天淵謂之紐兹{紐茲}。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/7211;77/7211;8653/526', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3927, '軒轅;路寢', '路寑', '軒轅謂之路寢[路寑]。', '', '釋天', '廣雅', '2', DEFAULT, DEFAULT, '9515/9558;1410/4686', '1410/4603', 'null', 'null');
